import{_ as t,o as r,c as e,e as n}from"./app.ddb5eff1.js";const s={},l=n('<p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4EEC\u4ECA\u5929\u6765\u5B66\u4E60\u82F1\u8BED\u8BED\u6CD5\u7684\u7EDD\u5BF9\u6838\u5FC3\u3001\u53E5\u5B50\u7684\u5FC3\u810F\u2014\u2014<strong>\u52A8\u8BCD</strong>\u3002\u6CA1\u6709\u5B83\uFF0C\u4E00\u5207\u8BED\u8A00\u8868\u8FBE\u90FD\u5C06\u5931\u53BB\u751F\u547D\u529B\u3002</p><h2 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u52A8\u8BCD-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u52A8\u8BCD-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u52A8\u8BCD\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h2><p>\u60F3\u8C61\u4E00\u4E0B\uFF0C\u4F60\u8981\u63CF\u8FF0\u4E00\u4E2A\u573A\u666F\uFF1A</p><ul><li>\u201C\u6211...\u82F9\u679C\u3002\u201D</li><li>\u201C\u5979...\u6F02\u4EAE\u3002\u201D \u8FD9\u4E24\u4E2A\u53E5\u5B50\u8BA9\u4F60\u611F\u89C9\u8BDD\u6CA1\u8BF4\u5B8C\uFF0C\u5BF9\u4E0D\u5BF9\uFF1F\u56E0\u4E3A\u4F60\u4E0D\u77E5\u9053\u53D1\u751F\u4E86\u4EC0\u4E48\u3002\u4F46\u5982\u679C\u4F60\u8BF4\uFF1A</li><li>\u201C\u6211<strong>\u5403</strong>\u82F9\u679C\u3002\u201D</li><li>\u201C\u5979<strong>\u662F</strong>\u6F02\u4EAE\u7684\u3002\u201D \u53E5\u5B50\u7ACB\u523B\u53D8\u5F97\u5B8C\u6574\u3001\u751F\u52A8\u3002</li></ul><p>\u8FD9\u91CC\u7684\u201C\u5403\u201D\u548C\u201C\u662F\u201D\u5C31\u662F<strong>\u52A8\u8BCD</strong>\u3002</p><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u52A8\u8BCD\u5C31\u662F\u8868\u793A\u52A8\u4F5C\u3001\u884C\u4E3A\u3001\u72B6\u6001\u6216\u5B58\u5728\u7684\u8BCD\u3002\u5B83\u662F\u53E5\u5B50\u7684\u53D1\u52A8\u673A\uFF0C\u6CA1\u6709\u52A8\u8BCD\u5C31\u4E0D\u80FD\u6784\u6210\u4E00\u4E2A\u5B8C\u6574\u7684\u53E5\u5B50\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong></p><ul><li>\u628A\u4E00\u53E5\u8BDD\u6BD4\u4F5C\u4E00\u90E8<strong>\u77ED\u89C6\u9891</strong>\u3002</li><li><strong>\u4E3B\u8BED</strong>\uFF08\u6211\u3001\u5979\uFF09\u662F\u89C6\u9891\u91CC\u7684<strong>\u4E3B\u89D2</strong>\u3002</li><li><strong>\u52A8\u8BCD</strong>\u5C31\u662F\u4E3B\u89D2\u7684<strong>\u6838\u5FC3\u52A8\u4F5C\u6216\u5448\u73B0\u7684\u72B6\u6001</strong>\uFF08\u5403\u3001\u8DD1\u3001\u662F\u3001\u53D8\u5F97\uFF09\u3002 \u6CA1\u6709\u8FD9\u4E2A\u6838\u5FC3\u52A8\u4F5C\u6216\u72B6\u6001\uFF0C\u89C6\u9891\u5C31\u662F\u4E00\u5F20\u9759\u6001\u7167\u7247\uFF0C\u4E0D\u6210\u5176\u4E3A\u201C\u89C6\u9891\u201D\u3002\u540C\u6837\uFF0C\u6CA1\u6709\u52A8\u8BCD\uFF0C\u4E00\u53E5\u8BDD\u5C31\u201C\u52A8\u201D\u4E0D\u8D77\u6765\u3002</li></ul><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u52A8\u8BCD-\u540D\u79F0\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u52A8\u8BCD-\u540D\u79F0\u7684\u7531\u6765" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u4E3A\u4EC0\u4E48\u53EB\u201C\u52A8\u8BCD\u201D\uFF1F\u540D\u79F0\u7684\u7531\u6765</h3><ol><li><p><strong>\u4E2D\u6587\u540D\u201C\u52A8\u8BCD\u201D</strong>\uFF1A</p><ul><li>\u201C\u52A8\u201D\u5C31\u662F\u201C\u52A8\u4F5C\u3001\u6D3B\u52A8\u201D\u3002</li><li>\u6240\u4EE5\u201C\u52A8\u8BCD\u201D\u5C31\u662F<strong>\u8868\u793A\u52A8\u4F5C\u7684\u8BCD</strong>\u3002\u8FD9\u4E2A\u540D\u5B57\u975E\u5E38\u76F4\u767D\u3002</li></ul></li><li><p><strong>\u82F1\u6587\u540D\u201CVerb\u201D</strong>\uFF1A</p><ul><li>\u8BFB\u97F3\uFF1A/v\u025C\u02D0rb/</li><li>\u8FD9\u4E2A\u8BCD\u6765\u6E90\u4E8E\u62C9\u4E01\u8BED <code>verbum</code>\uFF0C\u610F\u601D\u5C31\u662F <strong>\u201C\u8BCD\u201D</strong>\u3002</li><li>\u5728\u62C9\u4E01\u8BED\u4E2D\uFF0C<code>verbum</code> \u88AB\u4E13\u95E8\u7528\u6765\u6307\u4EE3\u201C\u52A8\u8BCD\u201D\uFF0C\u56E0\u4E3A\u5B83\u88AB\u8BA4\u4E3A\u662F<strong>\u6240\u6709\u8BCD\u7C7B\u4E2D\u6700\u91CD\u8981\u7684\u4E00\u4E2A</strong>\u3002\u4F60\u770B\uFF0C\u4ECE\u540D\u5B57\u4E0A\u5C31\u80FD\u770B\u51FA\u5B83\u7684\u6838\u5FC3\u5730\u4F4D\uFF01</li></ul></li></ol><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u52A8\u8BCD\u7684\u5927\u5BB6\u65CF-\u5206\u7C7B\u4E0E\u5F62\u6001" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u52A8\u8BCD\u7684\u5927\u5BB6\u65CF-\u5206\u7C7B\u4E0E\u5F62\u6001" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u52A8\u8BCD\u7684\u5927\u5BB6\u65CF\uFF08\u5206\u7C7B\u4E0E\u5F62\u6001\uFF09</h3><p>\u52A8\u8BCD\u5BB6\u65CF\u6210\u5458\u4F17\u591A\uFF0C\u5404\u53F8\u5176\u804C\u3002\u6211\u4EEC\u628A\u5B83\u5206\u6210\u51E0\u5927\u7C7B\u6765\u8BA4\u8BC6\u3002</p><h4 id="\u7B2C\u4E00\u7C7B-\u6309\u529F\u80FD\u548C\u4F5C\u7528\u5206" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u7C7B-\u6309\u529F\u80FD\u548C\u4F5C\u7528\u5206" aria-hidden="true">#</a> <strong>\u7B2C\u4E00\u7C7B\uFF1A\u6309\u529F\u80FD\u548C\u4F5C\u7528\u5206</strong></h4><p>\u8FD9\u662F\u7406\u89E3\u53E5\u5B50\u7ED3\u6784\u7684\u5173\u952E\u3002</p><table><thead><tr><th style="text-align:left;">\u52A8\u8BCD\u7C7B\u578B</th><th style="text-align:left;">\u6838\u5FC3\u4F5C\u7528</th><th style="text-align:left;">\u4F8B\u53E5</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u5B9E\u4E49\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u8868\u793A\u5177\u4F53\u7684\u52A8\u4F5C\u6216\u72B6\u6001</strong>\uFF0C\u6709\u5B9E\u9645\u610F\u4E49\u3002\u8FD9\u662F\u52A8\u8BCD\u7684\u7EDD\u5BF9\u4E3B\u529B\u3002</td><td style="text-align:left;">I <strong>love</strong> you. \uFF08\u6211\u7231\u4F60\u3002\uFF09<br>She <strong>works</strong> hard. \uFF08\u5979\u52AA\u529B\u5DE5\u4F5C\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u7CFB\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u4E0D\u8868\u793A\u52A8\u4F5C\uFF0C\u800C\u662F\u8FDE\u63A5\u4E3B\u8BED\u548C\u5B83\u7684\u8EAB\u4EFD/\u72B6\u6001</strong>\uFF0C\u50CF\u4E00\u6761\u7EBD\u5E26\u3002</td><td style="text-align:left;">He <strong>is</strong> a teacher. \uFF08\u4ED6\u662F\u4E00\u540D\u8001\u5E08\u3002\uFF09<br>The food <strong>smells</strong> good. \uFF08\u98DF\u7269\u95FB\u8D77\u6765\u5F88\u9999\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u52A9\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u672C\u8EAB\u65E0\u5B9E\u4E49\uFF0C\u662F\u201C\u8F85\u52A9\u5DE5\u5177\u201D</strong>\uFF0C\u5E2E\u52A9\u5B9E\u4E49\u52A8\u8BCD\u6784\u6210\u7591\u95EE\u3001\u5426\u5B9A\u3001\u65F6\u6001\u7B49\u3002</td><td style="text-align:left;"><strong>Do</strong> you like it? \uFF08\u4F60\u559C\u6B22\u5B83\u5417\uFF1F\uFF09<br>She <strong>is</strong> watching TV. \uFF08\u5979\u6B63\u5728\u770B\u7535\u89C6\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u60C5\u6001\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u8868\u8FBE\u60C5\u7EEA\u6001\u5EA6</strong>\uFF0C\u5982\u53EF\u80FD\u3001\u5E94\u8BE5\u3001\u5FC5\u987B\u7B49\u3002\u5B83\u7ED9\u52A8\u4F5C\u201C\u8C03\u5473\u201D\u3002</td><td style="text-align:left;">You <strong>should</strong> go. \uFF08\u4F60\u5E94\u8BE5\u53BB\u3002\uFF09<br>I <strong>can</strong> swim. \uFF08\u6211\u4F1A\u6E38\u6CF3\u3002\uFF09</td></tr></tbody></table><h4 id="\u7B2C\u4E8C\u7C7B-\u6309\u662F\u5426\u5E26\u5BBE\u8BED\u5206" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u7C7B-\u6309\u662F\u5426\u5E26\u5BBE\u8BED\u5206" aria-hidden="true">#</a> <strong>\u7B2C\u4E8C\u7C7B\uFF1A\u6309\u662F\u5426\u5E26\u5BBE\u8BED\u5206</strong></h4><p>\u8FD9\u51B3\u5B9A\u4E86\u53E5\u5B50\u540E\u9762\u80FD\u4E0D\u80FD\u76F4\u63A5\u8DDF\u201C\u52A8\u4F5C\u7684\u627F\u53D7\u8005\u201D\u3002</p><table><thead><tr><th style="text-align:left;">\u52A8\u8BCD\u7C7B\u578B</th><th style="text-align:left;">\u6838\u5FC3\u7279\u70B9</th><th style="text-align:left;">\u4F8B\u53E5</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u53CA\u7269\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u52A8\u4F5C\u9700\u8981\u4F5C\u7528\u5728\u4E00\u4E2A\u5BF9\u8C61\u4E0A</strong>\uFF0C\u540E\u9762<strong>\u5FC5\u987B</strong>\u63A5\u5BBE\u8BED\u3002</td><td style="text-align:left;">I <strong>made</strong> a cake. \uFF08\u6211\u505A\u4E86\u4E00\u4E2A\u86CB\u7CD5\u3002\uFF09-&gt; \u201C\u86CB\u7CD5\u201D\u662F\u201C\u505A\u201D\u7684\u5BF9\u8C61\u3002</td></tr><tr><td style="text-align:left;"><strong>\u4E0D\u53CA\u7269\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u52A8\u4F5C\u81EA\u8EAB\u5C31\u5B8C\u6574</strong>\uFF0C\u540E\u9762<strong>\u4E0D\u80FD</strong>\u76F4\u63A5\u63A5\u5BBE\u8BED\u3002</td><td style="text-align:left;">The sun <strong>rises</strong>. \uFF08\u592A\u9633\u5347\u8D77\u6765\u4E86\u3002\uFF09-&gt; \u201C\u5347\u201D\u81EA\u5DF1\u5C31\u610F\u601D\u5B8C\u6574\u3002</td></tr></tbody></table><p><strong>\u6CE8\u610F</strong>\uFF1A\u5F88\u591A\u52A8\u8BCD\u65E2\u662F\u53CA\u7269\u53C8\u662F\u4E0D\u53CA\u7269\uFF0C\u4F46\u610F\u601D\u53EF\u80FD\u4E0D\u540C\u3002</p><ul><li>She <strong>runs</strong> every day. \uFF08\u5979\u6BCF\u5929\u8DD1\u6B65\u3002\uFF09-&gt; \u4E0D\u53CA\u7269</li><li>She <strong>runs</strong> a company. \uFF08\u5979\u7ECF\u8425\u4E00\u5BB6\u516C\u53F8\u3002\uFF09-&gt; \u53CA\u7269</li></ul><h4 id="\u7B2C\u4E09\u7C7B-\u6309\u5F62\u5F0F\u53D8\u5316\u5206-\u52A8\u8BCD\u7684-\u53D8\u5F62\u91D1\u521A-\u5C5E\u6027" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u7C7B-\u6309\u5F62\u5F0F\u53D8\u5316\u5206-\u52A8\u8BCD\u7684-\u53D8\u5F62\u91D1\u521A-\u5C5E\u6027" aria-hidden="true">#</a> <strong>\u7B2C\u4E09\u7C7B\uFF1A\u6309\u5F62\u5F0F\u53D8\u5316\u5206\uFF08\u52A8\u8BCD\u7684\u201C\u53D8\u5F62\u91D1\u521A\u201D\u5C5E\u6027\uFF09</strong></h4><p>\u8FD9\u662F\u6784\u6210\u5404\u79CD\u65F6\u6001\u3001\u8BED\u6001\u7684\u57FA\u7840\u3002\u4E00\u4E2A\u82F1\u8BED\u52A8\u8BCD\u6709\u4E94\u79CD\u57FA\u672C\u5F62\u6001\u3002</p><table><thead><tr><th style="text-align:left;">\u5F62\u6001\u540D\u79F0</th><th style="text-align:left;">\u4F5C\u7528\u4E0E\u4F8B\u5B50\uFF08\u4EE5 work \u548C go \u4E3A\u4F8B\uFF09</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u52A8\u8BCD\u539F\u5F62</strong></td><td style="text-align:left;">\u5B57\u5178\u91CC\u7ED9\u51FA\u7684\u5F62\u5F0F\uFF0C\u4E5F\u662F\u9664\u4E86\u7B2C\u4E09\u4EBA\u79F0\u5355\u6570\u73B0\u5728\u65F6\u4E4B\u5916\u7684\u5F62\u5F0F\u3002</td></tr><tr><td style="text-align:left;"><strong>\u7B2C\u4E09\u4EBA\u79F0\u5355\u6570\u73B0\u5728\u65F6</strong></td><td style="text-align:left;">\u5F53\u4E3B\u8BED\u662F he, she, it \u6216\u5355\u6570\u540D\u8BCD\u65F6\uFF0C\u5728\u4E00\u822C\u73B0\u5728\u65F6\u4E2D\u4F7F\u7528\u3002</td></tr><tr><td style="text-align:left;"><strong>\u8FC7\u53BB\u5F0F</strong></td><td style="text-align:left;">\u8868\u793A\u8FC7\u53BB\u53D1\u751F\u7684\u52A8\u4F5C\u3002</td></tr><tr><td style="text-align:left;"><strong>\u8FC7\u53BB\u5206\u8BCD</strong></td><td style="text-align:left;">\u7528\u6765\u6784\u6210\u5B8C\u6210\u65F6\u6001\u548C\u88AB\u52A8\u8BED\u6001\u3002</td></tr><tr><td style="text-align:left;"><strong>\u73B0\u5728\u5206\u8BCD</strong></td><td style="text-align:left;">\u7528\u6765\u6784\u6210\u8FDB\u884C\u65F6\u6001\uFF0C\u6216\u5145\u5F53\u5F62\u5BB9\u8BCD\u3001\u52A8\u540D\u8BCD\u3002</td></tr></tbody></table><hr><h3 id="\u603B\u7ED3\u4E0E\u5FC3\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3\u4E0E\u5FC3\u6CD5" aria-hidden="true">#</a> <strong>\u603B\u7ED3\u4E0E\u5FC3\u6CD5</strong></h3><ol><li><strong>\u52A8\u8BCD (Verb)</strong> \u662F\u53E5\u5B50\u7684<strong>\u5FC3\u810F\u548C\u53D1\u52A8\u673A</strong>\uFF0C\u6CA1\u6709\u5B83\u5C31\u6CA1\u6709\u53E5\u5B50\u3002</li><li><strong>\u56DB\u5927\u529F\u80FD\u7C7B\u578B</strong>\uFF1A<strong>\u5B9E\u4E49\u52A8\u8BCD</strong>\uFF08\u5E72\u6D3B\u7684\u4E3B\u529B\uFF09\u3001<strong>\u7CFB\u52A8\u8BCD</strong>\uFF08\u8FDE\u63A5\u7684\u7EBD\u5E26\uFF09\u3001<strong>\u52A9\u52A8\u8BCD</strong>\uFF08\u8F85\u52A9\u7684\u5DE5\u5177\uFF09\u3001<strong>\u60C5\u6001\u52A8\u8BCD</strong>\uFF08\u8C03\u5473\u7684\u9999\u6599\uFF09\u3002</li><li><strong>\u4E24\u5927\u5BBE\u8BED\u5173\u7CFB</strong>\uFF1A<strong>\u53CA\u7269\u52A8\u8BCD</strong>\uFF08\u5FC5\u987B\u5E26\u201C\u9776\u5B50\u201D\uFF09\u3001<strong>\u4E0D\u53CA\u7269\u52A8\u8BCD</strong>\uFF08\u81EA\u5DF1\u5C31\u5B8C\u6574\uFF09\u3002</li><li><strong>\u4E94\u79CD\u5F62\u6001\u53D8\u5316</strong>\uFF1A<strong>\u539F\u5F62\u3001\u4E09\u5355\u3001\u8FC7\u53BB\u5F0F\u3001\u8FC7\u53BB\u5206\u8BCD\u3001\u73B0\u5728\u5206\u8BCD</strong>\uFF0C\u8FD9\u662F\u6784\u5EFA\u6240\u6709\u65F6\u6001\u548C\u8BED\u6001\u7684\u57FA\u77F3\u3002</li></ol><p>\u638C\u63E1\u4E86\u52A8\u8BCD\uFF0C\u4F60\u5C31\u638C\u63E1\u4E86\u82F1\u8BED\u53E5\u5B50\u7684\u751F\u547D\u7EBF\u3002\u63A5\u4E0B\u6765\uFF0C\u6211\u4EEC\u53EF\u4EE5\u6DF1\u5165\u63A2\u8BA8\u4EFB\u4F55\u4E00\u4E2A\u4F60\u611F\u5174\u8DA3\u7684\u52A8\u8BCD\u7EC6\u5206\u9886\u57DF\uFF0C\u6BD4\u5982\u201C\u65F6\u6001\u662F\u5982\u4F55\u901A\u8FC7\u52A8\u8BCD\u6784\u6210\u7684\u201D\u6216\u8005\u201C\u52A9\u52A8\u8BCD\u7684\u8BE6\u7EC6\u7528\u6CD5\u201D\u3002\u6211\u968F\u65F6\u5728\u6B64\u7B49\u5019\uFF01</p>',29),o=[l];function g(a,d){return r(),e("div",null,o)}var h=t(s,[["render",g],["__file","index.html.vue"]]);export{h as default};
