import{_ as t,o as e,c as o,e as r}from"./app.d1085557.js";const s={},n=r('<h2 id="\u9891\u7387\u526F\u8BCD" tabindex="-1"><a class="header-anchor" href="#\u9891\u7387\u526F\u8BCD" aria-hidden="true">#</a> \u9891\u7387\u526F\u8BCD</h2><p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4EEC\u4ECA\u5929\u6765\u5B66\u4E60\u4E00\u4E2A\u80FD\u8BA9\u4F60\u7684\u82F1\u8BED\u77AC\u95F4\u53D8\u5F97\u975E\u5E38\u5730\u9053\u7684\u201C\u795E\u5668\u201D\u2014\u2014<strong>\u9891\u7387\u526F\u8BCD</strong>\u3002\u5B83\u7528\u6765\u56DE\u7B54\u90A3\u4E2A\u7ECF\u5178\u7684\u95EE\u9898\uFF1A\u201C\u4F60<strong>\u591A\u4E45</strong>\u505A\u4E00\u6B21\u67D0\u4E8B\uFF1F\u201D</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u9891\u7387\u526F\u8BCD-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u9891\u7387\u526F\u8BCD-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u9891\u7387\u526F\u8BCD\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p>\u60F3\u8C61\u4E00\u4E0B\uFF0C\u4F60\u7684\u670B\u53CB\u95EE\u4F60\uFF1A\u201C\u4F60\u53BB\u5065\u8EAB\u623F\u5417\uFF1F\u201D</p><ul><li>\u5982\u679C\u4F60\u56DE\u7B54\uFF1A\u201C\u662F\u3002\u201D \u8FD9\u4E2A\u4FE1\u606F\u5F88\u6A21\u7CCA\u3002</li><li>\u4F46\u5982\u679C\u4F60\u56DE\u7B54\uFF1A\u201C\u6211<strong>\u603B\u662F</strong>\u53BB\u3002\u201D \u6216 \u201C\u6211<strong>\u6709\u65F6</strong>\u53BB\u3002\u201D \u6216 \u201C\u6211<strong>\u51E0\u4E4E\u4E0D</strong>\u53BB\u3002\u201D \u5BF9\u65B9\u7ACB\u523B\u5C31\u80FD\u660E\u767D\u4F60\u7684\u5065\u8EAB\u4E60\u60EF\u3002</li></ul><p>\u8FD9\u91CC\u7684\u201C\u603B\u662F\u201D\u3001\u201C\u6709\u65F6\u201D\u3001\u201C\u51E0\u4E4E\u4E0D\u201D\u5C31\u662F<strong>\u9891\u7387\u526F\u8BCD</strong>\u3002</p><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u9891\u7387\u526F\u8BCD\u5C31\u662F\u7528\u6765\u8868\u793A\u52A8\u4F5C\u53D1\u751F\u7684\u9891\u7387\u6216\u9891\u7E41\u7A0B\u5EA6\u7684\u526F\u8BCD\u3002\u5B83\u544A\u8BC9\u4F60\u4E00\u4EF6\u4E8B\u662F\u201C\u603B\u662F\u201D\u53D1\u751F\uFF0C\u201C\u7ECF\u5E38\u201D\u53D1\u751F\uFF0C\u8FD8\u662F\u201C\u4ECE\u4E0D\u201D\u53D1\u751F\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong> \u628A\u9891\u7387\u60F3\u8C61\u6210\u4E00\u4E2A\u4ECE0%\u5230100%\u7684\u8FDB\u5EA6\u6761\u3002</p><ul><li><strong>\u603B\u662F (always)</strong> = 100%</li><li><strong>\u901A\u5E38 (usually)</strong> = 80%</li><li><strong>\u7ECF\u5E38 (often)</strong> = 60%</li><li><strong>\u6709\u65F6 (sometimes)</strong> = 40%</li><li><strong>\u5076\u5C14 (occasionally)</strong> = 20%</li><li><strong>\u51E0\u4E4E\u4E0D (hardly ever)</strong> = 5%</li><li><strong>\u4ECE\u4E0D (never)</strong> = 0%</li></ul><p>\u9891\u7387\u526F\u8BCD\u5C31\u662F\u7ED9\u8FD9\u4E2A\u8FDB\u5EA6\u6761\u8D34\u4E0A\u5177\u4F53\u7684\u201C\u6807\u7B7E\u201D\u3002</p><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u9891\u7387\u526F\u8BCD\u7684\u6838\u5FC3\u6210\u5458\u4E0E-\u9891\u7387\u5149\u8C31" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u9891\u7387\u526F\u8BCD\u7684\u6838\u5FC3\u6210\u5458\u4E0E-\u9891\u7387\u5149\u8C31" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u9891\u7387\u526F\u8BCD\u7684\u6838\u5FC3\u6210\u5458\u4E0E\u201C\u9891\u7387\u5149\u8C31\u201D</h3><p>\u4E3A\u4E86\u8BA9\u4F60\u76F4\u89C2\u5730\u7406\u89E3\u5B83\u4EEC\u7684\u533A\u522B\uFF0C\u6211\u4EEC\u6765\u770B\u8FD9\u5F20\u201C\u9891\u7387\u5149\u8C31\u201D\uFF1A</p><p><strong>100% \u2014 Always (\u603B\u662F) \u2192 Usually (\u901A\u5E38) \u2192 Often (\u7ECF\u5E38) \u2192 Sometimes (\u6709\u65F6) \u2192 Occasionally (\u5076\u5C14) \u2192 Seldom (\u5F88\u5C11) \u2192 Hardly ever (\u51E0\u4E4E\u4E0D) \u2014 0% \u2014 Never (\u4ECE\u4E0D)</strong></p><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u6700\u91CD\u8981\u7684\u89C4\u5219\u2014\u2014\u9891\u7387\u526F\u8BCD\u5728\u53E5\u4E2D\u7684\u4F4D\u7F6E" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u6700\u91CD\u8981\u7684\u89C4\u5219\u2014\u2014\u9891\u7387\u526F\u8BCD\u5728\u53E5\u4E2D\u7684\u4F4D\u7F6E" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u6700\u91CD\u8981\u7684\u89C4\u5219\u2014\u2014\u9891\u7387\u526F\u8BCD\u5728\u53E5\u4E2D\u7684\u4F4D\u7F6E</h3><p>\u8FD9\u662F\u6700\u5173\u952E\u7684\u90E8\u5206\uFF0C\u4E5F\u662F\u4E2D\u56FD\u4EBA\u6700\u5BB9\u6613\u51FA\u9519\u7684\u5730\u65B9\uFF01\u8BF7\u4F60\u8BB0\u4F4F\u8FD9\u4E2A\u201C\u9EC4\u91D1\u6CD5\u5219\u201D\uFF1A</p><p><strong>\u9891\u7387\u526F\u8BCD\u901A\u5E38\u653E\u5728 <code>be\u52A8\u8BCD</code>\u3001<code>\u52A9\u52A8\u8BCD</code>\u3001<code>\u60C5\u6001\u52A8\u8BCD</code> \u4E4B\u540E\uFF0C<code>\u5B9E\u4E49\u52A8\u8BCD</code> \u4E4B\u524D\u3002</strong></p><p>\u8BA9\u6211\u4EEC\u901A\u8FC7\u8868\u683C\u6765\u5F7B\u5E95\u638C\u63E1\u5B83\uFF1A</p><table><thead><tr><th style="text-align:left;">\u53E5\u5B50\u7C7B\u578B</th><th style="text-align:left;">\u9EC4\u91D1\u6CD5\u5219</th><th style="text-align:left;">\u6B63\u786E\u4F8B\u53E5</th><th style="text-align:left;">\u9519\u8BEF\u4F8B\u53E5</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u53E5\u4E2D\u6709 Be \u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>Be \u52A8\u8BCD + \u9891\u7387\u526F\u8BCD</strong></td><td style="text-align:left;">He <strong>is always</strong> late. \uFF08\u4ED6<strong>\u603B\u662F</strong>\u8FDF\u5230\u3002\uFF09</td><td style="text-align:left;"><s>He always is late.</s></td></tr><tr><td style="text-align:left;"><strong>\u53E5\u4E2D\u6709 \u52A9\u52A8\u8BCD/\u60C5\u6001\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u52A9/\u60C5\u52A8\u8BCD + \u9891\u7387\u526F\u8BCD</strong></td><td style="text-align:left;">I <strong>can always</strong> help you. \uFF08\u6211<strong>\u603B\u662F\u80FD</strong>\u5E2E\u4F60\u3002\uFF09<br>She <strong>has never been</strong> to Paris. \uFF08\u5979<strong>\u4ECE\u6CA1</strong>\u53BB\u8FC7\u5DF4\u9ECE\u3002\uFF09</td><td style="text-align:left;"><s>I always can help you.</s></td></tr><tr><td style="text-align:left;"><strong>\u53E5\u4E2D\u53EA\u6709 \u5B9E\u4E49\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u5B9E\u4E49\u52A8\u8BCD + \u9891\u7387\u526F\u8BCD</strong></td><td style="text-align:left;">I <strong>often go</strong> to the library. \uFF08\u6211<strong>\u7ECF\u5E38\u53BB</strong>\u56FE\u4E66\u9986\u3002\uFF09</td><td style="text-align:left;"><s>I go often to the library.</s></td></tr></tbody></table><p><strong>\u4E00\u4E2A\u795E\u5947\u7684\u516C\u5F0F\u5E2E\u4F60\u8BB0\u5FC6\uFF1A</strong><strong>\u4E3B\u8BED + [Be/\u52A9/\u60C5] + [\u9891\u7387\u526F\u8BCD] + [\u5B9E\u4E49\u52A8\u8BCD] ...</strong></p><hr><h3 id="\u7B2C\u56DB\u90E8\u5206-\u9891\u7387\u526F\u8BCD\u7684-\u7279\u6743\u4F4D\u7F6E" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u56DB\u90E8\u5206-\u9891\u7387\u526F\u8BCD\u7684-\u7279\u6743\u4F4D\u7F6E" aria-hidden="true">#</a> \u7B2C\u56DB\u90E8\u5206\uFF1A\u9891\u7387\u526F\u8BCD\u7684\u201C\u7279\u6743\u4F4D\u7F6E\u201D</h3><p>\u9664\u4E86\u4E0A\u9762\u7684\u9EC4\u91D1\u6CD5\u5219\uFF0C\u6709\u4E9B\u9891\u7387\u526F\u8BCD\u8FD8\u6709\u4E00\u4E9B\u7279\u6B8A\u4F46\u5E38\u7528\u7684\u4F4D\u7F6E\uFF0C\u53EF\u4EE5\u8BA9\u4F60\u7684\u8868\u8FBE\u66F4\u5730\u9053\u3002</p><h4 id="_1-\u53E5\u9996\u4F4D\u7F6E-\u4E3A\u4E86\u5F3A\u8C03\u6216\u5BF9\u6BD4" tabindex="-1"><a class="header-anchor" href="#_1-\u53E5\u9996\u4F4D\u7F6E-\u4E3A\u4E86\u5F3A\u8C03\u6216\u5BF9\u6BD4" aria-hidden="true">#</a> <strong>1. \u53E5\u9996\u4F4D\u7F6E\uFF08\u4E3A\u4E86\u5F3A\u8C03\u6216\u5BF9\u6BD4\uFF09</strong></h4><p><code>Sometimes</code>, <code>Occasionally</code>, <code>Usually</code>, <code>Often</code> \u53EF\u4EE5\u79FB\u5230\u53E5\u9996\uFF0C\u7528\u9017\u53F7\u9694\u5F00\uFF0C\u4F7F\u8BED\u6C14\u66F4\u5F3A\u70C8\u3002</p><ul><li><strong>Usually</strong>, I have breakfast at 7. \uFF08<strong>\u901A\u5E38</strong>\uFF0C\u62117\u70B9\u5403\u65E9\u996D\u3002\uFF09</li><li><strong>Sometimes</strong>, I feel very tired. \uFF08<strong>\u6709\u65F6\u5019</strong>\uFF0C\u6211\u611F\u5230\u5F88\u7D2F\u3002\uFF09</li></ul><p><strong>\u6CE8\u610F</strong>\uFF1A<code>Always</code>, <code>Never</code>, <code>Seldom</code>, <code>Hardly ever</code> <strong>\u51E0\u4E4E\u4ECE\u4E0D</strong>\u7528\u4E8E\u53E5\u9996\uFF08\u9664\u975E\u5728\u975E\u5E38\u5F3A\u8C03\u7684\u7948\u4F7F\u53E5\u4E2D\uFF09\u3002</p><h4 id="_2-\u53E5\u672B\u4F4D\u7F6E-\u4E3A\u4E86\u542C\u8D77\u6765\u4E0D\u90A3\u4E48\u6B63\u5F0F" tabindex="-1"><a class="header-anchor" href="#_2-\u53E5\u672B\u4F4D\u7F6E-\u4E3A\u4E86\u542C\u8D77\u6765\u4E0D\u90A3\u4E48\u6B63\u5F0F" aria-hidden="true">#</a> <strong>2. \u53E5\u672B\u4F4D\u7F6E\uFF08\u4E3A\u4E86\u542C\u8D77\u6765\u4E0D\u90A3\u4E48\u6B63\u5F0F\uFF09</strong></h4><p><code>Sometimes</code>, <code>Often</code>, <code>Usually</code>, <code>Occasionally</code> \u4E5F\u53EF\u4EE5\u653E\u5728\u53E5\u672B\u3002</p><ul><li>I go to the cinema <strong>often</strong>. \uFF08\u6211\u7ECF\u5E38\u53BB\u770B\u7535\u5F71\u3002\uFF09- \u53E3\u8BED\u4E2D\u66F4\u81EA\u7136\u3002</li><li>We eat out <strong>sometimes</strong>. \uFF08\u6211\u4EEC\u6709\u65F6\u51FA\u53BB\u5403\u3002\uFF09</li></ul><hr><h3 id="\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-hardly-ever-\u548C-never" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-hardly-ever-\u548C-never" aria-hidden="true">#</a> <strong>\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192\uFF1AHardly Ever \u548C Never</strong></h3><p>\u8FD9\u4E24\u4E2A\u8BCD\u672C\u8EAB\u5C31\u5E26\u6709<strong>\u5426\u5B9A\u542B\u4E49</strong>\uFF0C\u6240\u4EE5\u53E5\u5B50\u4E2D<strong>\u4E0D\u80FD\u518D\u51FA\u73B0<code>not</code></strong>\u3002</p><ul><li>\u6B63\u786E\uFF1AI <strong>never</strong> eat fast food. \uFF08\u6211\u4ECE\u4E0D\u5403\u5FEB\u9910\u3002\uFF09</li><li>\u6B63\u786E\uFF1AI <strong>hardly ever</strong> see him. \uFF08\u6211\u51E0\u4E4E\u89C1\u4E0D\u5230\u4ED6\u3002\uFF09</li><li>\u9519\u8BEF\uFF1A<s>I don&#39;t never eat fast food.</s> (\u53CC\u91CD\u5426\u5B9A\uFF0C\u610F\u601D\u5C31\u9519\u4E86)</li></ul><hr><h3 id="\u5B9E\u6218-\u5982\u4F55\u63D0\u95EE\u548C\u56DE\u7B54" tabindex="-1"><a class="header-anchor" href="#\u5B9E\u6218-\u5982\u4F55\u63D0\u95EE\u548C\u56DE\u7B54" aria-hidden="true">#</a> <strong>\u5B9E\u6218\uFF1A\u5982\u4F55\u63D0\u95EE\u548C\u56DE\u7B54\uFF1F</strong></h3><p><strong>1. \u63D0\u95EE\uFF1A</strong> \u4F7F\u7528 <code>How often...?</code> \uFF08\u4F60\u591A\u4E45\u2026\u2026\u4E00\u6B21\uFF1F\uFF09</p><ul><li><strong>How often</strong> do you exercise? \uFF08\u4F60\u591A\u4E45\u953B\u70BC\u4E00\u6B21\uFF1F\uFF09</li></ul><p><strong>2. \u56DE\u7B54\uFF1A</strong></p><ul><li>\u76F4\u63A5\u7528\u9891\u7387\u526F\u8BCD\uFF1A<code>I always exercise.</code> \uFF08\u6211\u603B\u662F\u953B\u70BC\u3002\uFF09</li><li>\u7528\u201C\u6B21\u6570+\u65F6\u95F4\u6BB5\u201D\uFF1A<code>I exercise three times a week.</code> \uFF08\u6211\u6BCF\u5468\u953B\u70BC\u4E09\u6B21\u3002\uFF09</li><li>\u4E24\u8005\u7ED3\u5408\uFF1A<code>I usually exercise three times a week.</code> \uFF08\u6211\u901A\u5E38\u6BCF\u5468\u953B\u70BC\u4E09\u6B21\u3002\uFF09</li></ul><hr><h3 id="\u603B\u7ED3\u4E0E\u7EC8\u6781\u5FC3\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3\u4E0E\u7EC8\u6781\u5FC3\u6CD5" aria-hidden="true">#</a> <strong>\u603B\u7ED3\u4E0E\u7EC8\u6781\u5FC3\u6CD5</strong></h3><ol><li><strong>\u529F\u80FD</strong>\uFF1A\u9891\u7387\u526F\u8BCD\u544A\u8BC9\u4F60\u52A8\u4F5C\u53D1\u751F\u7684\u201C\u9891\u7E41\u5EA6\u201D\u3002</li><li><strong>\u4F4D\u7F6E\uFF08\u91CD\u4E2D\u4E4B\u91CD\uFF09</strong>\uFF1A <ul><li><strong>\u53E5\u4E2D\u5927\u539F\u5219</strong>\uFF1A<code>Be/\u52A9/\u60C5</code> <strong>\u4E4B\u540E</strong>\uFF0C<code>\u5B9E\u4E49\u52A8\u8BCD</code> <strong>\u4E4B\u524D</strong>\u3002</li><li><strong>\u53E5\u9996/\u53E5\u672B</strong>\uFF1A<code>Sometimes</code>, <code>Often</code> \u7B49\u53EF\u4EE5\u7075\u6D3B\u79FB\u52A8\u4EE5\u6539\u53D8\u8BED\u6C14\u3002</li></ul></li><li><strong>\u5426\u5B9A\u8BCD</strong>\uFF1A<code>Never</code> \u548C <code>Hardly ever</code> \u81EA\u8EAB\u5E26\u5426\u5B9A\u610F\u4E49\uFF0C\u4E0D\u8981\u518D\u52A0 <code>not</code>\u3002</li><li><strong>\u63D0\u95EE</strong>\uFF1A\u7528 <code>How often...?</code> \u6765\u8BE2\u95EE\u9891\u7387\u3002</li></ol><p>\u8BB0\u4F4F\u8FD9\u4E2A\u6838\u5FC3\u4F4D\u7F6E\u89C4\u5219\uFF0C\u4F60\u7684\u53E5\u5B50\u7ED3\u6784\u5C31\u4F1A\u7ACB\u523B\u53D8\u5F97\u975E\u5E38\u5730\u9053\u3002\u4E0B\u6B21\u60F3\u63CF\u8FF0\u4F60\u7684\u751F\u6D3B\u4E60\u60EF\u65F6\uFF0C\u8BD5\u7740\u7528\u4E0A\u8FD9\u4E9B\u9891\u7387\u526F\u8BCD\u5427\uFF01\u6BD4\u5982\uFF1A\u201CI <strong>sometimes</strong> drink coffee, but I <strong>usually</strong> prefer tea.\u201D \uFF08\u6211\u6709\u65F6\u559D\u5496\u5561\uFF0C\u4F46\u6211\u901A\u5E38\u66F4\u559C\u6B22\u8336\u3002\uFF09</p><p>\u63A5\u4E0B\u6765\u60F3\u4E86\u89E3\u54EA\u4E2A\u8BED\u6CD5\u70B9\uFF0C\u6BD4\u5982\u5176\u4ED6\u7C7B\u578B\u7684\u526F\u8BCD\uFF0C\u6216\u8005\u201C\u8FDE\u8BCD\u201D\uFF1F\u6211\u968F\u65F6\u5728\u6B64\u7B49\u5019\u3002</p>',46),l=[n];function a(d,g){return e(),o("div",null,l)}var c=t(s,[["render",a],["__file","index.html.vue"]]);export{c as default};
