import{_ as t,o as r,c as n,e}from"./app.38c04a07.js";const s={},o=e('<h2 id="\u6570\u8BCD-numeral" tabindex="-1"><a class="header-anchor" href="#\u6570\u8BCD-numeral" aria-hidden="true">#</a> \u6570\u8BCD Numeral</h2><p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4EEC\u4ECA\u5929\u6765\u5B66\u4E60\u4E00\u4E2A\u975E\u5E38\u5B9E\u7528\u3001\u89C4\u5219\u6027\u6781\u5F3A\u7684\u8BCD\u7C7B\u2014\u2014<strong>\u6570\u8BCD</strong>\u3002\u5B83\u5C31\u662F\u8BED\u8A00\u4E2D\u7684\u201C\u6570\u5B66\u4E16\u754C\u201D\uFF0C\u8D1F\u8D23\u8868\u8FBE\u4E00\u5207\u4E0E\u6570\u91CF\u548C\u987A\u5E8F\u76F8\u5173\u7684\u6982\u5FF5\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u6570\u8BCD-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u6570\u8BCD-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u6570\u8BCD\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p>\u60F3\u8C61\u4E00\u4E0B\uFF0C\u4F60\u8981\u63CF\u8FF0\u4E00\u4E2A\u573A\u666F\uFF1A</p><ul><li>\u201C\u6211\u770B\u5230\u6811\u4E0A\u6709\u82F9\u679C\uFF0C\u9E1F\u5728\u98DE\u3002\u201D \u8FD9\u4E2A\u63CF\u8FF0\u975E\u5E38\u6A21\u7CCA\u3002\u4F46\u5982\u679C\u4F60\u8BF4\uFF1A</li><li>\u201C\u6211\u770B\u5230\u6811\u4E0A\u6709<strong>\u4E09\u4E2A</strong>\u82F9\u679C\uFF0C<strong>\u4E24\u53EA</strong>\u9E1F\u5728\u98DE\u3002\u201D \u753B\u9762\u7ACB\u523B\u53D8\u5F97\u6E05\u6670\u5177\u4F53\u4E86\u3002</li></ul><p>\u8FD9\u91CC\u7684\u201C\u4E09\u201D\u3001\u201C\u4E24\u201D\u5C31\u662F\u6570\u8BCD\u3002</p><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u6570\u8BCD\u5C31\u662F\u8868\u793A\u6570\u91CF\u201C\u591A\u5C11\u201D\u548C\u987A\u5E8F\u201C\u7B2C\u51E0\u201D\u7684\u8BCD\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong></p><ul><li><strong>\u6570\u8BCD</strong>\u5C31\u50CF\u662F\u7ED9\u4E8B\u7269\u8D34\u4E0A\u7684<strong>\u6570\u91CF\u6807\u7B7E</strong>\u548C<strong>\u987A\u5E8F\u6807\u7B7E</strong>\u3002 <ul><li><strong>\u57FA\u6570\u8BCD</strong>\uFF08\u591A\u5C11\uFF09\u662F<strong>\u6570\u91CF\u6807\u7B7E</strong>\uFF0C\u50CF\u8D27\u67B6\u4E0A\u7684\u5E93\u5B58\u6570\u91CF\u3002</li><li><strong>\u5E8F\u6570\u8BCD</strong>\uFF08\u7B2C\u51E0\uFF09\u662F<strong>\u987A\u5E8F\u6807\u7B7E</strong>\uFF0C\u50CF\u8D5B\u8DD1\u8FD0\u52A8\u5458\u80F8\u53E3\u7684\u540D\u6B21\u53F7\u7801\u3002</li></ul></li></ul><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u6570\u8BCD-\u540D\u79F0\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u6570\u8BCD-\u540D\u79F0\u7684\u7531\u6765" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u4E3A\u4EC0\u4E48\u53EB\u201C\u6570\u8BCD\u201D\uFF1F\u540D\u79F0\u7684\u7531\u6765</h3><ol><li><p><strong>\u4E2D\u6587\u540D\u201C\u6570\u8BCD\u201D</strong>\uFF1A</p><ul><li>\u201C\u6570\u201D\u5C31\u662F\u201C\u6570\u91CF\u3001\u6570\u5B57\u201D\u7684\u610F\u601D\u3002</li><li>\u6240\u4EE5\u201C\u6570\u8BCD\u201D\u5C31\u662F<strong>\u8868\u793A\u6570\u5B57\u7684\u8BCD</strong>\u3002</li></ul></li><li><p><strong>\u82F1\u6587\u540D\u201CNumeral\u201D</strong>\uFF1A</p><ul><li>\u8BFB\u97F3\uFF1A/\u02C8nju\u02D0m\u0259r\u0259l/</li><li>\u8FD9\u4E2A\u8BCD\u6765\u6E90\u4E8E\u62C9\u4E01\u8BED <code>numer\u0101lis</code>\uFF08\u4E0E\u6570\u5B57\u6709\u5173\u7684\uFF09\uFF0C\u800C\u8FD9\u4E2A\u8BCD\u53C8\u6E90\u81EA <code>numerus</code>\uFF08\u6570\u5B57\uFF09\u3002</li><li>\u6240\u4EE5 <code>Numeral</code> \u5C31\u662F <strong>\u201C\u4E0E\u6570\u5B57\u76F8\u5173\u7684\u8BCD\u201D</strong>\u3002</li></ul></li></ol><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u6570\u8BCD\u7684\u4E24\u5927\u6838\u5FC3\u5BB6\u65CF" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u6570\u8BCD\u7684\u4E24\u5927\u6838\u5FC3\u5BB6\u65CF" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u6570\u8BCD\u7684\u4E24\u5927\u6838\u5FC3\u5BB6\u65CF</h3><p>\u6570\u8BCD\u4E3B\u8981\u5206\u4E3A\u4E24\u5927\u7C7B\uFF1A<strong>\u57FA\u6570\u8BCD</strong> \u548C <strong>\u5E8F\u6570\u8BCD</strong>\u3002</p><h4 id="\u5BB6\u65CF\u4E00-\u57FA\u6570\u8BCD-\u56DE\u7B54-\u591A\u5C11" tabindex="-1"><a class="header-anchor" href="#\u5BB6\u65CF\u4E00-\u57FA\u6570\u8BCD-\u56DE\u7B54-\u591A\u5C11" aria-hidden="true">#</a> <strong>\u5BB6\u65CF\u4E00\uFF1A\u57FA\u6570\u8BCD - \u56DE\u7B54\u201C\u591A\u5C11\uFF1F\u201D</strong></h4><ul><li><strong>\u82F1\u6587\u540D</strong>: Cardinal Numeral</li><li><strong>\u529F\u80FD</strong>\uFF1A\u8868\u793A\u6570\u91CF\u6216\u6570\u76EE\u3002\u76F8\u5F53\u4E8E\u6570\u5B66\u91CC\u7684\u201C\u6574\u6570\u201D\u3002</li><li><strong>\u6838\u5FC3\u6210\u5458</strong>\uFF1Aone, two, three, four...</li></ul><h4 id="\u5BB6\u65CF\u4E8C-\u5E8F\u6570\u8BCD-\u56DE\u7B54-\u7B2C\u51E0" tabindex="-1"><a class="header-anchor" href="#\u5BB6\u65CF\u4E8C-\u5E8F\u6570\u8BCD-\u56DE\u7B54-\u7B2C\u51E0" aria-hidden="true">#</a> <strong>\u5BB6\u65CF\u4E8C\uFF1A\u5E8F\u6570\u8BCD - \u56DE\u7B54\u201C\u7B2C\u51E0\uFF1F\u201D</strong></h4><ul><li><strong>\u82F1\u6587\u540D</strong>: Ordinal Numeral</li><li><strong>\u529F\u80FD</strong>\uFF1A\u8868\u793A\u987A\u5E8F\u6216\u6392\u540D\u3002\u901A\u5E38\u5728\u57FA\u6570\u8BCD\u540E\u52A0 <code>-th</code> \u6784\u6210\u3002</li><li><strong>\u6838\u5FC3\u6210\u5458</strong>\uFF1Afirst (1st), second (2nd), third (3rd), fourth (4th)...</li></ul><hr><h3 id="\u7B2C\u56DB\u90E8\u5206-\u6570\u8BCD\u7684\u6838\u5FC3\u7528\u6CD5\u8BE6\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u56DB\u90E8\u5206-\u6570\u8BCD\u7684\u6838\u5FC3\u7528\u6CD5\u8BE6\u89E3" aria-hidden="true">#</a> \u7B2C\u56DB\u90E8\u5206\uFF1A\u6570\u8BCD\u7684\u6838\u5FC3\u7528\u6CD5\u8BE6\u89E3</h3><h4 id="_1-\u57FA\u6570\u8BCD\u7684\u6784\u6210\u4E0E\u8BFB\u6CD5" tabindex="-1"><a class="header-anchor" href="#_1-\u57FA\u6570\u8BCD\u7684\u6784\u6210\u4E0E\u8BFB\u6CD5" aria-hidden="true">#</a> <strong>1. \u57FA\u6570\u8BCD\u7684\u6784\u6210\u4E0E\u8BFB\u6CD5</strong></h4><p>\u8FD9\u662F\u6570\u8BCD\u7684\u57FA\u7840\uFF0C\u6211\u4EEC\u901A\u8FC7\u4E00\u4E2A\u8868\u683C\u6765\u638C\u63E1\u5176\u89C4\u5F8B\uFF1A</p><table><thead><tr><th style="text-align:left;">\u6570\u5B57\u8303\u56F4</th><th style="text-align:left;">\u6784\u6210\u89C4\u5219</th><th style="text-align:left;">\u793A\u4F8B</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>1 - 12</strong></td><td style="text-align:left;"><strong>\u72EC\u7ACB\u5355\u8BCD</strong>\uFF0C\u65E0\u89C4\u5F8B\uFF0C\u9700\u719F\u8BB0\u3002</td><td style="text-align:left;">one, two, three, four, five, six, seven, eight, nine, ten, eleven, twelve</td></tr><tr><td style="text-align:left;"><strong>13 - 19</strong></td><td style="text-align:left;"><strong>\u4E2A\u4F4D\u6570 + teen</strong></td><td style="text-align:left;">thirteen, fourteen, fifteen, sixteen, seventeen, eighteen, nineteen</td></tr><tr><td style="text-align:left;"><strong>20, 30...90</strong></td><td style="text-align:left;"><strong>\u4E2A\u4F4D\u6570 + ty</strong></td><td style="text-align:left;">twenty, thirty, forty, fifty, sixty, seventy, eighty, ninety</td></tr><tr><td style="text-align:left;"><strong>21 - 99</strong></td><td style="text-align:left;"><strong>\u5341\u4F4D\u6570 + \u8FDE\u5B57\u7B26\u201C-\u201D + \u4E2A\u4F4D\u6570</strong></td><td style="text-align:left;">21: twenty-<strong>one</strong> <br> 32: thirty-<strong>two</strong> <br> 99: ninety-<strong>nine</strong></td></tr><tr><td style="text-align:left;"><strong>100\u53CA\u4EE5\u4E0A</strong></td><td style="text-align:left;"><strong>... hundred / thousand / million / billion</strong> <br> \u767E\u4F4D\u548C\u5341\u4F4D/\u4E2A\u4F4D\u4E4B\u95F4\u52A0 <strong>and</strong></td><td style="text-align:left;">101: one hundred <strong>and</strong> one <br> 1,001: one thousand <strong>and</strong> one <br> 5,643: five thousand, six hundred <strong>and</strong> forty-three</td></tr></tbody></table><p><strong>\u6CE8\u610F</strong>\uFF1A</p><ul><li><code>hundred</code>, <code>thousand</code>, <code>million</code> \u7B49\u8BCD\u5728\u8868\u793A<strong>\u5177\u4F53\u6570\u5B57</strong>\u65F6\uFF0C<strong>\u4E0D\u7528\u590D\u6570</strong>\u3002 <ul><li>\u6B63\u786E\uFF1Atwo hundred students (\u4E24\u767E\u540D\u5B66\u751F)</li><li>\u9519\u8BEF\uFF1Atwo hundreds students</li></ul></li><li>\u4F46\u5F53\u8868\u793A<strong>\u6210\u767E\u4E0A\u5343</strong>\u8FD9\u79CD\u6A21\u7CCA\u6982\u5FF5\u65F6\uFF0C\u8981\u7528\u590D\u6570\uFF1A<code>hundreds of students</code>\uFF08\u6570\u767E\u540D\u5B66\u751F\uFF09\u3002</li></ul><h4 id="_2-\u5E8F\u6570\u8BCD\u7684\u6784\u6210\u4E0E\u7F29\u5199" tabindex="-1"><a class="header-anchor" href="#_2-\u5E8F\u6570\u8BCD\u7684\u6784\u6210\u4E0E\u7F29\u5199" aria-hidden="true">#</a> <strong>2. \u5E8F\u6570\u8BCD\u7684\u6784\u6210\u4E0E\u7F29\u5199</strong></h4><table><thead><tr><th style="text-align:left;">\u6784\u6210\u89C4\u5219</th><th style="text-align:left;">\u793A\u4F8B\uFF08\u5B8C\u6574\u5F62\u5F0F + \u7F29\u5199\uFF09</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u7EDD\u5927\u90E8\u5206\uFF1A\u57FA\u6570\u8BCD + th</strong></td><td style="text-align:left;">four \u2192 four<strong>th</strong> (4th) <br> six \u2192 six<strong>th</strong> (6th) <br> ten \u2192 ten<strong>th</strong> (10th)</td></tr><tr><td style="text-align:left;"><strong>\u7279\u6B8A\u53D8\u5F62</strong></td><td style="text-align:left;">one \u2192 <strong>first</strong> (1<strong>st</strong>) <br> two \u2192 <strong>second</strong> (2<strong>nd</strong>) <br> three \u2192 <strong>third</strong> (3<strong>rd</strong>) <br> five \u2192 <strong>fif</strong>th (5th) <br> eight \u2192 eigh<strong>th</strong> (8th) <br> nine \u2192 nin<strong>th</strong> (9th) <br> twelve \u2192 twelf<strong>th</strong> (12th)</td></tr><tr><td style="text-align:left;"><strong>\u4EE5-ty\u7ED3\u5C3E\u7684\u57FA\u6570\u8BCD\uFF0C\u53D8y\u4E3Ai\u518D\u52A0eth</strong></td><td style="text-align:left;">twenty \u2192 twentie<strong>th</strong> (20th) <br> thirty \u2192 thirtie<strong>th</strong> (30th)</td></tr></tbody></table><hr><h3 id="\u7B2C\u4E94\u90E8\u5206-\u6570\u8BCD\u7684\u5B9E\u6218\u5E94\u7528\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E94\u90E8\u5206-\u6570\u8BCD\u7684\u5B9E\u6218\u5E94\u7528\u573A\u666F" aria-hidden="true">#</a> <strong>\u7B2C\u4E94\u90E8\u5206\uFF1A\u6570\u8BCD\u7684\u5B9E\u6218\u5E94\u7528\u573A\u666F</strong></h3><p>\u6570\u8BCD\u5728\u53E5\u5B50\u4E2D\u4E3B\u8981\u5145\u5F53<strong>\u5B9A\u8BED</strong>\uFF0C\u653E\u5728\u540D\u8BCD\u524D\u4FEE\u9970\u540D\u8BCD\u3002</p><table><thead><tr><th style="text-align:left;">\u5E94\u7528\u573A\u666F</th><th style="text-align:left;">\u4F8B\u53E5\uFF08\u6570\u8BCD\u7528<strong>\u7C97\u4F53</strong>\u6807\u51FA\uFF09</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u8868\u793A\u6570\u91CF\uFF08\u57FA\u6570\u8BCD\uFF09</strong></td><td style="text-align:left;">I have <strong>two</strong> brothers. \uFF08\u6211\u6709<strong>\u4E24\u4E2A</strong>\u5144\u5F1F\u3002\uFF09<br> There are <strong>three hundred</strong> employees in our company. \uFF08\u6211\u4EEC\u516C\u53F8\u6709<strong>\u4E09\u767E\u540D</strong>\u5458\u5DE5\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u8868\u793A\u987A\u5E8F\uFF08\u5E8F\u6570\u8BCD\uFF09</strong></td><td style="text-align:left;">This is my <strong>first</strong> time in Beijing. \uFF08\u8FD9\u662F\u6211<strong>\u7B2C\u4E00\u6B21</strong>\u6765\u5317\u4EAC\u3002\uFF09<br> He lives on the <strong>fifth</strong> floor. \uFF08\u4ED6\u4F4F\u5728<strong>\u7B2C\u4E94</strong>\u5C42\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u8868\u793A\u65E5\u671F</strong></td><td style="text-align:left;">Today is May <strong>the twenty-first</strong>. \u6216 Today is May <strong>21st</strong>. \uFF08\u4ECA\u5929\u662F5\u6708<strong>21\u65E5</strong>\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u8868\u793A\u5206\u6570</strong></td><td style="text-align:left;"><strong>1/3</strong>: one-third <br> <strong>2/5</strong>: two-fifths <br> <strong>1/2</strong>: a half <br> <strong>1/4</strong>: a quarter</td></tr><tr><td style="text-align:left;"><strong>\u8868\u793A\u5C0F\u6570</strong></td><td style="text-align:left;"><strong>0.5</strong>: zero point five <br> <strong>3.14</strong>: three point one four</td></tr><tr><td style="text-align:left;"><strong>\u8868\u793A\u5E74\u4EFD</strong></td><td style="text-align:left;"><strong>1998</strong>: nineteen ninety-eight <br> <strong>2008</strong>: two thousand (and) eight <br> <strong>2020</strong>: twenty twenty</td></tr><tr><td style="text-align:left;"><strong>\u8868\u793A\u7535\u8BDD\u53F7\u7801</strong></td><td style="text-align:left;"><strong>135-7900</strong>: one three five, seven nine zero zero (\u6216 double seven, double oh)</td></tr><tr><td style="text-align:left;"><strong>\u8868\u793A\u56DB\u5219\u8FD0\u7B97</strong></td><td style="text-align:left;"><strong>1+2=3</strong>: One plus two is three. <br> <strong>5-3=2</strong>: Five minus three is two. <br> <strong>6x7=42</strong>: Six times seven is forty-two. <br> <strong>8\xF72=4</strong>: Eight divided by two is four.</td></tr></tbody></table><hr><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> <strong>\u603B\u7ED3</strong></h3><ol><li><strong>\u6570\u8BCD (Numeral)</strong> \u662F\u8BED\u8A00\u7684\u201C\u6570\u5B66\u90E8\u5206\u201D\uFF0C\u8D1F\u8D23\u8868\u8FBE\u6570\u91CF\u548C\u987A\u5E8F\u3002</li><li>\u4E24\u5927\u6838\u5FC3\u662F<strong>\u57FA\u6570\u8BCD</strong>\uFF08\u591A\u5C11\uFF09\u548C<strong>\u5E8F\u6570\u8BCD</strong>\uFF08\u7B2C\u51E0\uFF09\u3002</li><li>\u57FA\u6570\u8BCD\u7684\u6784\u6210<strong>\u89C4\u5F8B\u6027\u5F3A</strong>\uFF081-12\u7279\u6B8A\uFF0C13-19\u52A0teen\uFF0C20-90\u52A0ty\uFF0C21-99\u52A0\u8FDE\u5B57\u7B26\uFF09\u3002</li><li>\u5E8F\u6570\u8BCD\u5927\u90E8\u5206\u662F\u201C\u57FA\u6570\u8BCD+th\u201D\uFF0C\u4F46\u8981\u8BB0\u4F4F<strong>\u7B2C\u4E00\u3001\u7B2C\u4E8C\u3001\u7B2C\u4E09</strong>\u7B49\u7279\u6B8A\u5F62\u5F0F\u3002</li><li>\u6570\u8BCD\u5728\u751F\u6D3B\u4E2D\u6709\u5E7F\u6CDB\u5E94\u7528\uFF0C\u4ECE\u65F6\u95F4\u3001\u65E5\u671F\u5230\u6570\u5B66\u8FD0\u7B97\uFF0C\u65E0\u5904\u4E0D\u5728\u3002</li></ol><p>\u638C\u63E1\u4E86\u6570\u8BCD\uFF0C\u4F60\u5C31\u80FD\u7CBE\u51C6\u5730\u63CF\u8FF0\u4E16\u754C\u3002\u63A5\u4E0B\u6765\u60F3\u4E86\u89E3\u54EA\u4E2A\u8BED\u6CD5\u70B9\uFF0C\u6BD4\u5982\u201C\u8FDE\u8BCD\u201D\u6216\u8005\u201C\u611F\u53F9\u8BCD\u201D\uFF1F\u6211\u968F\u65F6\u5728\u6B64\u7B49\u5019\u3002</p>',36),g=[o];function l(i,d){return r(),n("div",null,g)}var h=t(s,[["render",l],["__file","index.html.vue"]]);export{h as default};
