import{_ as t,o as e,c as s,e as r}from"./app.2f1d72d8.js";const n={},l=r('<h2 id="\u7CFB\u52A8\u8BCD-linking-verb" tabindex="-1"><a class="header-anchor" href="#\u7CFB\u52A8\u8BCD-linking-verb" aria-hidden="true">#</a> \u7CFB\u52A8\u8BCD Linking Verb</h2><p>\u6211\u4EEC\u4ECA\u5929\u6765\u653B\u514B\u4E00\u4E2A\u542C\u8D77\u6765\u5F88\u4E13\u4E1A\uFF0C\u4F46\u5B9E\u9645\u4E0A\u975E\u5E38\u7B80\u5355\u7684\u6982\u5FF5\u2014\u2014<strong>\u7CFB\u52A8\u8BCD</strong>\u3002\u5B83\u4E5F\u88AB\u79F0\u4E3A\u201C\u8FDE\u7CFB\u52A8\u8BCD\u201D\uFF0C\u662F\u8FDE\u63A5\u4E3B\u8BED\u548C\u5B83\u7684\u72B6\u6001\u3001\u8EAB\u4EFD\u6216\u7279\u5F81\u7684\u201C\u6865\u6881\u201D\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u7CFB\u52A8\u8BCD-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u7CFB\u52A8\u8BCD-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u7CFB\u52A8\u8BCD\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p>\u60F3\u8C61\u4E00\u4E0B\uFF0C\u4F60\u8981\u4ECB\u7ECD\u4E00\u4E2A\u670B\u53CB\uFF1A</p><ul><li>\u201C\u4ED6\u3002\u201D</li><li>\u201C\u4E00\u4E2A\u8001\u5E08\u3002\u201D \u8FD9\u4E24\u4E2A\u4FE1\u606F\u662F\u65AD\u88C2\u7684\uFF0C\u4F60\u9700\u8981\u4E00\u4E2A\u201C\u6865\u6881\u201D\u628A\u5B83\u4EEC\u8FDE\u63A5\u8D77\u6765\uFF0C\u53D8\u6210\u4E00\u53E5\u5B8C\u6574\u7684\u8BDD\uFF1A\u201C<strong>\u4ED6\u662F</strong>\u4E00\u4E2A\u8001\u5E08\u3002\u201D</li></ul><p>\u8FD9\u4E2A\u201C\u662F\u201D\uFF0C\u5728\u82F1\u8BED\u91CC\u5C31\u662F\u6700\u7ECF\u5178\u7684\u7CFB\u52A8\u8BCD <strong>be</strong>\uFF08am, is, are\u7B49\uFF09\u3002</p><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u7CFB\u52A8\u8BCD\u672C\u8EAB\u4E0D\u8868\u793A\u5177\u4F53\u7684\u52A8\u4F5C\uFF0C\u5B83\u5C31\u50CF\u4E00\u4E2A\u201C\u7B49\u53F7\u201D= \u6216\u8005\u4E00\u6761\u201C\u7EBD\u5E26\u201D\uFF0C\u5B83\u7684\u552F\u4E00\u5DE5\u4F5C\u5C31\u662F\u628A\u4E3B\u8BED\uFF08\u53E5\u5B50\u7684\u4E3B\u89D2\uFF09\u548C\u5B83\u7684\u8EAB\u4EFD\u3001\u72B6\u6001\u3001\u7279\u5F81\uFF08\u8868\u8BED\uFF09\u8FDE\u63A5\u8D77\u6765\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong></p><ul><li><strong>\u4E3B\u8BED</strong> \u662F\u7167\u7247\u91CC\u7684<strong>\u4E3B\u89D2</strong>\u3002</li><li><strong>\u8868\u8BED</strong> \u662F\u4E3B\u89D2\u7684<strong>\u8EAB\u4EFD\u724C</strong>\uFF08\u5982\u201C\u533B\u751F\u201D\uFF09\u6216<strong>\u72B6\u6001\u6807\u7B7E</strong>\uFF08\u5982\u201C\u5F00\u5FC3\u7684\u201D\uFF09\u3002</li><li><strong>\u7CFB\u52A8\u8BCD</strong> \u5C31\u662F<strong>\u628A\u90A3\u5757\u8EAB\u4EFD\u724C\u6216\u6807\u7B7E\u6302\u5728\u4E3B\u89D2\u8EAB\u4E0A\u7684\u90A3\u6839\u7EF3\u5B50</strong>\u3002\u6CA1\u6709\u8FD9\u6839\u7EF3\u5B50\uFF0C\u6807\u7B7E\u548C\u4E3B\u89D2\u5C31\u5931\u53BB\u4E86\u8054\u7CFB\u3002</li></ul><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u7CFB\u52A8\u8BCD-\u540D\u79F0\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u7CFB\u52A8\u8BCD-\u540D\u79F0\u7684\u7531\u6765" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u4E3A\u4EC0\u4E48\u53EB\u201C\u7CFB\u52A8\u8BCD\u201D\uFF1F\u540D\u79F0\u7684\u7531\u6765</h3><ol><li><p><strong>\u4E2D\u6587\u540D\u201C\u7CFB\u52A8\u8BCD\u201D\u6216\u201C\u8FDE\u7CFB\u52A8\u8BCD\u201D</strong>\uFF1A</p><ul><li>\u201C\u7CFB\u201D\u5B57\u6709\u201C\u8054\u7ED3\u3001\u6346\u7ED1\u201D\u7684\u610F\u601D\u3002</li><li>\u6240\u4EE5\u201C\u7CFB\u52A8\u8BCD\u201D\u5C31\u662F<strong>\u8D1F\u8D23\u8054\u7ED3\u4E3B\u8BED\u548C\u540E\u9762\u8BF4\u660E\u6210\u5206\uFF08\u8868\u8BED\uFF09\u7684\u52A8\u8BCD</strong>\u3002</li></ul></li><li><p><strong>\u82F1\u6587\u540D\u201CLinking Verb\u201D</strong>\uFF1A</p><ul><li>\u8BFB\u97F3\uFF1A/\u02C8l\u026A\u014Bk\u026A\u014B v\u025C\u02D0rb/</li><li>\u8FD9\u4E2A\u540D\u5B57\u6BD4\u4E2D\u6587\u540D\u66F4\u76F4\u767D\uFF01<code>Linking</code> \u5C31\u662F\u201C\u8FDE\u63A5\u201D\u7684\u610F\u601D\uFF0C<code>Verb</code> \u662F\u201C\u52A8\u8BCD\u201D\u3002</li><li>\u6240\u4EE5 <code>Linking Verb</code> \u5C31\u662F <strong>\u201C\u8D77\u8FDE\u63A5\u4F5C\u7528\u7684\u52A8\u8BCD\u201D</strong> \u3002\u8FD9\u4E2A\u540D\u5B57\u5B8C\u7F8E\u5730\u89E3\u91CA\u4E86\u5B83\u7684\u529F\u80FD\u3002</li></ul></li></ol><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u54EA\u4E9B\u8BCD\u53EF\u4EE5\u5F53\u7CFB\u52A8\u8BCD-\u6838\u5FC3\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u54EA\u4E9B\u8BCD\u53EF\u4EE5\u5F53\u7CFB\u52A8\u8BCD-\u6838\u5FC3\u5185\u5BB9" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u54EA\u4E9B\u8BCD\u53EF\u4EE5\u5F53\u7CFB\u52A8\u8BCD\uFF1F\uFF08\u6838\u5FC3\u5185\u5BB9\uFF09</h3><p>\u7CFB\u52A8\u8BCD\u4E0D\u4EC5\u4EC5\u53EA\u6709 <code>be</code> \u52A8\u8BCD\uFF0C\u5B83\u662F\u4E00\u4E2A\u5C0F\u5BB6\u65CF\u3002\u6211\u4EEC\u53EF\u4EE5\u628A\u5B83\u4EEC\u5206\u6210\u51E0\u7C7B\u6765\u8BB0\u5FC6\u3002</p><table><thead><tr><th style="text-align:left;">\u7CFB\u52A8\u8BCD\u7C7B\u578B</th><th style="text-align:left;">\u6838\u5FC3\u610F\u601D</th><th style="text-align:left;">\u5E38\u89C1\u6210\u5458</th><th style="text-align:left;">\u4F8B\u53E5\uFF08\u4E3B\u8BED\u4E0B\u5212\u7EBF\uFF0C\u7CFB\u52A8\u8BCD<strong>\u52A0\u7C97</strong>\uFF0C\u8868\u8BED<em>\u659C\u4F53</em>\uFF09</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>1. \u8868\u793A\u201C\u662F\u201D</strong></td><td style="text-align:left;">\u7EAF\u7CB9\u8868\u793A\u201C\u7B49\u4E8E\u201D\u6216\u201C\u5C5E\u4E8E\u201D\u3002</td><td style="text-align:left;"><strong>be</strong> (am, is, are, was, were)</td><td style="text-align:left;">He <strong>is</strong> <em>a teacher</em>. \uFF08\u4ED6<em>\u662F</em>\u4E00\u540D\u8001\u5E08\u3002\uFF09- \u8FDE\u63A5\u8EAB\u4EFD <br> I <strong>am</strong> <em>happy</em>. \uFF08\u6211<em>\u5F88\u5F00\u5FC3</em>\u3002\uFF09- \u8FDE\u63A5\u72B6\u6001</td></tr><tr><td style="text-align:left;"><strong>2. \u8868\u793A\u201C\u53D8\u5F97\u201D</strong></td><td style="text-align:left;">\u8868\u793A\u72B6\u6001\u7684\u53D8\u5316\u3002</td><td style="text-align:left;"><strong>become, get, go, turn, grow, fall</strong></td><td style="text-align:left;">She <strong>became</strong> <em>famous</em>. \uFF08\u5979<em>\u53D8\u5F97</em>\u51FA\u540D\u4E86\u3002\uFF09<br> The leaves <strong>turn</strong> <em>red</em> in autumn. \uFF08\u79CB\u5929\u53F6\u5B50<em>\u53D8</em>\u7EA2\u3002\uFF09<br> He <strong>fell</strong> <em>asleep</em>. \uFF08\u4ED6<em>\u7761\u7740\u4E86</em>\u3002\uFF09- \u72B6\u6001\u53D8\u4E3A\u201C\u7761\u7740\u201D</td></tr><tr><td style="text-align:left;"><strong>3. \u8868\u793A\u201C\u4FDD\u6301\u201D</strong></td><td style="text-align:left;">\u8868\u793A\u72B6\u6001\u7684\u6301\u7EED\u3002</td><td style="text-align:left;"><strong>keep, stay, remain</strong></td><td style="text-align:left;"><strong>Keep</strong> <em>quiet</em>, please. \uFF08\u8BF7<em>\u4FDD\u6301</em>\u5B89\u9759\u3002\uFF09<br> The weather <strong>stayed</strong> <em>fine</em> all week. \uFF08\u5929\u6C14<em>\u6301\u7EED</em>\u6674\u6717\u4E86\u4E00\u5468\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>4. \u8868\u793A\u201C\u611F\u89C9/\u770B\u8D77\u6765\u201D</strong></td><td style="text-align:left;">\u8868\u793A\u611F\u5B98\u4E0A\u7684\u72B6\u6001\u3002</td><td style="text-align:left;"><strong>look, sound, smell, taste, feel</strong></td><td style="text-align:left;">You <strong>look</strong> <em>tired</em>. \uFF08\u4F60<em>\u770B\u8D77\u6765</em>\u5F88\u7D2F\u3002\uFF09- \u89C6\u89C9 <br> This song <strong>sounds</strong> <em>beautiful</em>. \uFF08\u8FD9\u9996\u6B4C<em>\u542C\u8D77\u6765</em>\u5F88\u7F8E\u3002\uFF09- \u542C\u89C9 <br> The flower <strong>smells</strong> <em>sweet</em>. \uFF08\u8FD9\u82B1<em>\u95FB\u8D77\u6765</em>\u5F88\u9999\u3002\uFF09- \u55C5\u89C9 <br> The food <strong>tastes</strong> <em>delicious</em>. \uFF08\u8FD9\u98DF\u7269<em>\u5C1D\u8D77\u6765</em>\u5F88\u597D\u5403\u3002\uFF09- \u5473\u89C9 <br> Silk <strong>feels</strong> <em>smooth</em>. \uFF08\u4E1D\u7EF8<em>\u6478\u8D77\u6765</em>\u5F88\u5149\u6ED1\u3002\uFF09- \u89E6\u89C9</td></tr><tr><td style="text-align:left;"><strong>5. \u8868\u793A\u201C\u4F3C\u4E4E/\u597D\u50CF\u201D</strong></td><td style="text-align:left;">\u8868\u793A\u67D0\u79CD\u63A8\u6D4B\u6216\u8868\u8C61\u3002</td><td style="text-align:left;"><strong>seem, appear</strong></td><td style="text-align:left;">He <strong>seems</strong> <em>(to be) honest</em>. \uFF08\u4ED6<em>\u4F3C\u4E4E</em>\u5F88\u8BDA\u5B9E\u3002\uFF09<br> It <strong>appears</strong> <em>true</em>. \uFF08\u8FD9\u4E8B<em>\u770B\u6765</em>\u662F\u771F\u7684\u3002\uFF09</td></tr></tbody></table><hr><h3 id="\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u611F\u89C9-\u7C7B\u7CFB\u52A8\u8BCD\u7684\u7528\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u611F\u89C9-\u7C7B\u7CFB\u52A8\u8BCD\u7684\u7528\u6CD5" aria-hidden="true">#</a> <strong>\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192\uFF1A\u201C\u611F\u89C9\u201D\u7C7B\u7CFB\u52A8\u8BCD\u7684\u7528\u6CD5</strong></h3><p>\u8FD9\u662F\u5C0F\u767D\u6700\u5BB9\u6613\u51FA\u9519\u7684\u5730\u65B9\uFF01\u8BF7\u4E00\u5B9A\u8BB0\u4F4F\uFF1A</p><p><strong>\u5728\u8FD9\u4E9B\u201C\u611F\u89C9\u201D\u7C7B\u7CFB\u52A8\u8BCD\u540E\u9762\uFF0C\u8981\u7528\u5F62\u5BB9\u8BCD\u6765\u63CF\u8FF0\u201C\u4E3B\u8BED\u7684\u611F\u89C9\u600E\u4E48\u6837\u201D\uFF0C\u800C\u4E0D\u662F\u7528\u526F\u8BCD\u6765\u63CF\u8FF0\u201C\u52A8\u8BCD\u7684\u611F\u89C9\u65B9\u5F0F\u201D\u3002</strong></p><ul><li>\u6B63\u786E\uFF1AThe soup tastes <strong>delicious</strong>. \uFF08\u6C64\u5C1D\u8D77\u6765<strong>\u5F88\u7F8E\u5473</strong>\u3002\uFF09 <ul><li>\u8FD9\u91CC <code>delicious</code>\uFF08\u7F8E\u5473\u7684\uFF09\u662F\u5F62\u5BB9\u8BCD\uFF0C\u63CF\u8FF0\u7684\u662F\u4E3B\u8BED <code>The soup</code>\uFF08\u6C64\uFF09\u7684\u7279\u5F81\u3002</li></ul></li><li>\u9519\u8BEF\uFF1AThe soup tastes <s>deliciously</s>. <ul><li>\u5982\u679C\u7528\u526F\u8BCD <code>deliciously</code>\uFF0C\u5C31\u53D8\u6210\u4E86\u201C\u6C64\u4EE5\u4E00\u79CD\u7F8E\u5473\u7684\u65B9\u5F0F\u5728\u54C1\u5C1D\u201D\uFF0C\u8FD9\u903B\u8F91\u5C31\u4E0D\u901A\u4E86\u3002\u6C64\u81EA\u5DF1\u4E0D\u4F1A\u54C1\u5C1D\u3002</li></ul></li></ul><p><strong>\u7B80\u5355\u8BB0\uFF1A\u7CFB\u52A8\u8BCD\u540E\u9762\u662F\u7ED9\u4E3B\u8BED\u201C\u8D34\u5F62\u5BB9\u8BCD\u6807\u7B7E\u201D\uFF0C\u800C\u4E0D\u662F\u4FEE\u9970\u52A8\u8BCD\u201C\u5E72\u5F97\u600E\u4E48\u6837\u201D\u3002</strong></p><hr><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h3><ol><li><strong>\u7CFB\u52A8\u8BCD\uFF08Linking Verb\uFF09</strong> \u662F\u53E5\u5B50\u7684\u201C\u7B49\u53F7\u201D= \u6216\u201C\u7EBD\u5E26\u201D\uFF0C\u6CA1\u6709\u5177\u4F53\u52A8\u4F5C\u3002</li><li>\u5B83\u7684\u6838\u5FC3\u5DE5\u4F5C\u662F<strong>\u8FDE\u63A5\u4E3B\u8BED\u548C\u8BF4\u660E\u4E3B\u8BED\u8EAB\u4EFD\u3001\u72B6\u6001\u3001\u7279\u5F81\u7684\u201C\u8868\u8BED\u201D</strong>\u3002</li><li>\u6700\u5E38\u89C1\u7684\u7CFB\u52A8\u8BCD\u662F <strong>be</strong>\uFF0C\u4F46\u8FD8\u6709\u8868\u793A\u201C\u53D8\u5316\u201D\u3001\u201C\u4FDD\u6301\u201D\u3001\u201C\u611F\u89C9\u201D\u7B49\u591A\u4E2A\u5BB6\u65CF\u7684\u6210\u5458\u3002</li><li>\u5728\u201C\u611F\u89C9\u201D\u7C7B\u7CFB\u52A8\u8BCD\u540E\uFF0C\u7262\u8BB0\u8981\u4F7F\u7528<strong>\u5F62\u5BB9\u8BCD</strong>\u3002</li></ol><p>\u638C\u63E1\u4E86\u7CFB\u52A8\u8BCD\uFF0C\u4F60\u5C31\u80FD\u8F7B\u677E\u8BF4\u51FA\u201C\u4ED6\u662F\u4EC0\u4E48\u201D\u3001\u201C\u4ED6\u600E\u4E48\u6837\u201D\u3001\u201C\u4ED6\u53D8\u6210\u4EC0\u4E48\u201D\u8FD9\u6837\u7684\u6838\u5FC3\u53E5\u5B50\u4E86\uFF01</p>',26),o=[l];function g(i,a){return e(),s("div",null,o)}var m=t(n,[["render",g],["__file","index.html.vue"]]);export{m as default};
