import{_ as t,o,c as r,e as n}from"./app.1dccb4bf.js";const s={},g=n('<h2 id="\u60C5\u6001\u52A8\u8BCD" tabindex="-1"><a class="header-anchor" href="#\u60C5\u6001\u52A8\u8BCD" aria-hidden="true">#</a> \u60C5\u6001\u52A8\u8BCD</h2><p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4EEC\u4ECA\u5929\u6765\u89E3\u9501\u82F1\u8BED\u4E2D\u6700\u6709\u201C\u4EBA\u60C5\u5473\u201D\u7684\u4E00\u7C7B\u52A8\u8BCD\u2014\u2014<strong>\u60C5\u6001\u52A8\u8BCD</strong>\u3002\u5B83\u4EEC\u80FD\u8BA9\u4F60\u7684\u82F1\u8BED\u4ECE\u51B7\u51B0\u51B0\u7684\u201C\u4E8B\u5B9E\u9648\u8FF0\u201D\u53D8\u6210\u5145\u6EE1\u611F\u60C5\u548C\u6001\u5EA6\u7684\u201C\u751F\u52A8\u4EA4\u6D41\u201D\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u60C5\u6001\u52A8\u8BCD-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u60C5\u6001\u52A8\u8BCD-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u60C5\u6001\u52A8\u8BCD\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p>\u60F3\u8C61\u4E00\u4E0B\uFF0C\u4F60\u8981\u8868\u8FBE\u201C\u53BB\u516C\u56ED\u201D\u8FD9\u4E2A\u52A8\u4F5C\uFF1A</p><ul><li>\u4E8B\u5B9E\u9648\u8FF0\uFF1AI <strong>go</strong> to the park. \uFF08\u6211\u53BB\u516C\u56ED\u3002\uFF09\u2014\u2014 \u8FD9\u5C31\u662F\u4E00\u4E2A\u7B80\u5355\u7684\u4E8B\u5B9E\u3002</li><li>\u4F46\u5982\u679C\u4F60\u60F3\u8868\u8FBE\u4F60\u7684<strong>\u80FD\u529B\u3001\u8BB8\u53EF\u3001\u53EF\u80FD\u6027\u3001\u4E49\u52A1</strong>\u5462\uFF1F\u4F60\u5C31\u9700\u8981\u4E00\u4E9B\u201C\u60C5\u7EEA\u548C\u6001\u5EA6\u201D\u7684\u5E2E\u52A9\u3002</li></ul><p>\u8FD9\u65F6\uFF0C\u60C5\u6001\u52A8\u8BCD\u5C31\u767B\u573A\u4E86\uFF1A</p><ul><li>\u8868\u80FD\u529B\uFF1AI <strong>can</strong> go to the park. \uFF08\u6211<strong>\u80FD</strong>\u53BB\u516C\u56ED\u3002\uFF09</li><li>\u8868\u8BB8\u53EF\uFF1AI <strong>may</strong> go to the park. \uFF08\u6211<strong>\u53EF\u4EE5</strong>\u53BB\u516C\u56ED\u3002\uFF09</li><li>\u8868\u4E49\u52A1\uFF1AI <strong>must</strong> go to the park. \uFF08\u6211<strong>\u5FC5\u987B</strong>\u53BB\u516C\u56ED\u3002\uFF09</li></ul><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u60C5\u6001\u52A8\u8BCD\u672C\u8EAB\u6709\u4E00\u5B9A\u8BCD\u4E49\uFF0C\u4F46\u5B83\u4E0D\u8868\u793A\u52A8\u4F5C\u6216\u72B6\u6001\uFF0C\u800C\u662F\u8868\u8FBE\u8BF4\u8BDD\u4EBA\u5BF9\u540E\u9762\u52A8\u4F5C\u7684\u201C\u770B\u6CD5\u3001\u6001\u5EA6\u6216\u60C5\u7EEA\u201D\uFF0C\u5982\u53EF\u80FD\u3001\u613F\u610F\u3001\u5E94\u8BE5\u3001\u5FC5\u987B\u7B49\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong> \u628A\u4E00\u53E5\u8BDD\u6BD4\u4F5C\u4E00\u9053\u83DC\u3002</p><ul><li><strong>\u4E3B\u8981\u52A8\u8BCD</strong> \u662F<strong>\u4E3B\u83DC</strong>\uFF08\u6BD4\u5982\uFF1A\u53BB\u516C\u56ED\u3001\u5B66\u4E60\u3001\u5B8C\u6210\uFF09\u3002</li><li><strong>\u60C5\u6001\u52A8\u8BCD</strong> \u5C31\u662F<strong>\u8C03\u5473\u6599</strong>\uFF08\u6BD4\u5982\uFF1A\u4E00\u70B9\u201C\u80FD\u529B\u201D\u8FA3\u6912\u3001\u4E00\u70B9\u201C\u8BB8\u53EF\u201D\u76D0\u3001\u4E00\u70B9\u201C\u5FC5\u987B\u201D\u918B\uFF09\u3002 \u6CA1\u6709\u8C03\u5473\u6599\uFF0C\u83DC\u80FD\u5403\u4F46\u4E4F\u5473\uFF1B\u6709\u4E86\u8C03\u5473\u6599\uFF0C\u83DC\u624D\u6709\u4E86\u7279\u5B9A\u7684\u98CE\u5473\u548C\u611F\u89C9\u3002</li></ul><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u60C5\u6001\u52A8\u8BCD-\u540D\u79F0\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u60C5\u6001\u52A8\u8BCD-\u540D\u79F0\u7684\u7531\u6765" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u4E3A\u4EC0\u4E48\u53EB\u201C\u60C5\u6001\u52A8\u8BCD\u201D\uFF1F\u540D\u79F0\u7684\u7531\u6765</h3><ol><li><p><strong>\u4E2D\u6587\u540D\u201C\u60C5\u6001\u52A8\u8BCD\u201D</strong>\uFF1A</p><ul><li>\u201C\u60C5\u201D\u6307<strong>\u60C5\u7EEA\u3001\u60C5\u611F</strong>\uFF0C\u201C\u6001\u201D\u6307<strong>\u6001\u5EA6\u3001\u6A21\u5F0F</strong>\u3002</li><li>\u6240\u4EE5\u201C\u60C5\u6001\u52A8\u8BCD\u201D\u5C31\u662F<strong>\u7528\u6765\u8868\u8FBE\u60C5\u611F\u548C\u6001\u5EA6\u7684\u52A8\u8BCD</strong>\u3002\u8FD9\u4E2A\u540D\u5B57\u975E\u5E38\u7CBE\u51C6\u5730\u6982\u62EC\u4E86\u5B83\u7684\u6838\u5FC3\u529F\u80FD\u3002</li></ul></li><li><p><strong>\u82F1\u6587\u540D\u201CModal Verb\u201D</strong>\uFF1A</p><ul><li>\u8BFB\u97F3\uFF1A/\u02C8m\u0259\u028Adl v\u025C\u02D0rb/</li><li>\u8FD9\u4E2A\u8BCD\u6765\u6E90\u4E8E\u201C<strong>mode</strong>\u201D\uFF08\u6A21\u5F0F\u3001\u65B9\u5F0F\uFF09\u3002</li><li>\u6240\u4EE5 <code>Modal Verb</code> \u5C31\u662F <strong>\u201C\u8868\u8FBE\u8BF4\u8BDD\u65B9\u5F0F\u6216\u6001\u5EA6\u7684\u52A8\u8BCD\u201D</strong> \u3002\u5B83\u5B9A\u4E49\u4E86\u4F60\u8BF4\u4E00\u53E5\u8BDD\u7684\u201C\u6A21\u5F0F\u201D\u2014\u2014\u4F60\u662F\u5728\u9648\u8FF0\u4E8B\u5B9E\uFF0C\u8FD8\u662F\u5728\u8868\u8FBE\u53EF\u80FD\u6027\u3001\u5FC5\u8981\u6027\u7B49\u7B49\u3002</li></ul></li></ol><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u60C5\u6001\u52A8\u8BCD\u5BB6\u65CF\u6709\u54EA\u4E9B\u6210\u5458-\u6838\u5FC3\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u60C5\u6001\u52A8\u8BCD\u5BB6\u65CF\u6709\u54EA\u4E9B\u6210\u5458-\u6838\u5FC3\u5185\u5BB9" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u60C5\u6001\u52A8\u8BCD\u5BB6\u65CF\u6709\u54EA\u4E9B\u6210\u5458\uFF1F\uFF08\u6838\u5FC3\u5185\u5BB9\uFF09</h3><p>\u60C5\u6001\u52A8\u8BCD\u662F\u4E00\u4E2A\u5C01\u95ED\u7684\u5C0F\u5BB6\u65CF\uFF0C\u6838\u5FC3\u6210\u5458\u5C31\u90A3\u51E0\u4E2A\u3002\u5B83\u4EEC\u6709\u51E0\u4E2A<strong>\u975E\u5E38\u91CD\u8981\u7684\u5171\u540C\u7279\u70B9</strong>\uFF0C\u4F60\u5FC5\u987B\u8BB0\u4F4F\uFF1A</p><ol><li><strong>\u4E0D\u80FD\u5355\u72EC\u4F5C\u8C13\u8BED</strong>\uFF0C\u5FC5\u987B\u548C<strong>\u52A8\u8BCD\u539F\u5F62</strong>\u4E00\u8D77\u6784\u6210\u201C\u590D\u5408\u8C13\u8BED\u201D\u3002</li><li><strong>\u6CA1\u6709\u4EBA\u79F0\u548C\u6570\u7684\u53D8\u5316</strong>\u3002\u65E0\u8BBA\u662F he, she, it \u8FD8\u662F I, you, we, they\uFF0C\u60C5\u6001\u52A8\u8BCD\u90FD\u4FDD\u6301\u539F\u5F62\u3002</li><li><strong>\u6CA1\u6709\u4E0D\u5B9A\u5F0F\uFF08to do\uFF09\u3001-ing\u5F62\u5F0F\u7B49</strong>\u3002</li></ol><p>\u4E0B\u9762\u662F\u6838\u5FC3\u6210\u5458\u5217\u8868\uFF1A</p><table><thead><tr><th style="text-align:left;">\u60C5\u6001\u52A8\u8BCD</th><th style="text-align:left;">\u6838\u5FC3\u610F\u601D\u4E0E\u7528\u6CD5</th><th style="text-align:left;">\u4F8B\u53E5</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>can</strong></td><td style="text-align:left;"><strong>1. \u80FD\u529B\uFF08\u80FD\u591F\uFF09</strong> <br>2. \u8BF7\u6C42/\u8BB8\u53EF\uFF08\u53EF\u4EE5\uFF09\u3010\u53E3\u8BED\u3011<br>3. \u53EF\u80FD\u6027\uFF08\u53EF\u80FD\uFF09</td><td style="text-align:left;">1. I <strong>can</strong> swim. \uFF08\u6211<strong>\u4F1A</strong>\u6E38\u6CF3\u3002\uFF09<br>2. <strong>Can</strong> I use your phone? \uFF08\u6211<strong>\u53EF\u4EE5</strong>\u7528\u4E0B\u4F60\u7535\u8BDD\u5417\uFF1F\uFF09<br>3. It <strong>can</strong> be cold in winter. \uFF08\u51AC\u5929<strong>\u53EF\u80FD</strong>\u5F88\u51B7\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>could</strong></td><td style="text-align:left;"><strong>1. can\u7684\u8FC7\u53BB\u5F0F\uFF08\u8868\u8FC7\u53BB\u7684\u80FD\u529B\uFF09</strong> <br>2. \u66F4\u793C\u8C8C\u7684\u8BF7\u6C42<br>3. \u8F83\u5C0F\u7684\u53EF\u80FD\u6027</td><td style="text-align:left;">1. I <strong>could</strong> run fast when I was young. \uFF08\u6211\u5E74\u8F7B\u65F6<strong>\u80FD</strong>\u8DD1\u5F97\u5F88\u5FEB\u3002\uFF09<br>2. <strong>Could</strong> you help me? \uFF08\u60A8<strong>\u80FD</strong>\u5E2E\u6211\u4E00\u4E0B\u5417\uFF1F\uFF09<br>3. It <strong>could</strong> rain later. \uFF08\u665A\u70B9<strong>\u53EF\u80FD</strong>\u4F1A\u4E0B\u96E8\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>may</strong></td><td style="text-align:left;"><strong>1. \u8BF7\u6C42/\u8BB8\u53EF\uFF08\u53EF\u4EE5\uFF09\u3010\u6B63\u5F0F\u3011</strong> <br>2. \u53EF\u80FD\u6027\uFF08\u53EF\u80FD\uFF09\u3010\u6B63\u5F0F\u3011</td><td style="text-align:left;">1. <strong>May</strong> I come in? \uFF08\u6211<strong>\u53EF\u4EE5</strong>\u8FDB\u6765\u5417\uFF1F\uFF09<br>2. She <strong>may</strong> arrive late. \uFF08\u5979<strong>\u53EF\u80FD</strong>\u4F1A\u665A\u5230\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>might</strong></td><td style="text-align:left;"><strong>1. \u53EF\u80FD\u6027\uFF08\u53EF\u80FD\uFF09\u3010\u6BD4may\u66F4\u4E0D\u786E\u5B9A\u3011</strong></td><td style="text-align:left;">1. He <strong>might</strong> not come to the party. \uFF08\u4ED6<strong>\u6216\u8BB8</strong>\u4E0D\u6765\u53C2\u52A0\u6D3E\u5BF9\u4E86\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>must</strong></td><td style="text-align:left;"><strong>1. \u4E49\u52A1/\u5FC5\u8981\u6027\uFF08\u5FC5\u987B\uFF09\u3010\u4E3B\u89C2\u3011</strong> <br>2. \u80AF\u5B9A\u7684\u63A8\u6D4B\uFF08\u4E00\u5B9A\uFF09</td><td style="text-align:left;">1. You <strong>must</strong> finish your homework. \uFF08\u4F60<strong>\u5FC5\u987B</strong>\u5B8C\u6210\u4F5C\u4E1A\u3002\uFF09<br>2. He <strong>must</strong> be at home. \uFF08\u4ED6<strong>\u4E00\u5B9A</strong>\u5728\u5BB6\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>shall</strong></td><td style="text-align:left;"><strong>1. \u5F81\u6C42\u610F\u89C1\u3010\u4E3B\u8981\u7528\u4E8E\u82F1\u5F0F\u82F1\u8BED\u3011</strong> <br>2. \u8868\u793A\u5C06\u6765\u3010\u6B63\u5F0F\u3011</td><td style="text-align:left;">1. <strong>Shall</strong> we go? \uFF08\u6211\u4EEC<strong>\u53EF\u4EE5</strong>\u8D70\u4E86\u5417\uFF1F\uFF09<br>2. I <strong>shall</strong> return. \uFF08\u6211\u5C06\u56DE\u6765\u3002\uFF09\u3010\u6B63\u5F0F\u3011</td></tr><tr><td style="text-align:left;"><strong>should</strong></td><td style="text-align:left;"><strong>1. \u4E49\u52A1/\u5EFA\u8BAE\uFF08\u5E94\u8BE5\uFF09</strong> <br>2. \u63A8\u6D4B\uFF08\u6309\u7406\u8BF4\u5E94\u8BE5\uFF09</td><td style="text-align:left;">1. You <strong>should</strong> see a doctor. \uFF08\u4F60<strong>\u5E94\u8BE5</strong>\u53BB\u770B\u533B\u751F\u3002\uFF09<br>2. They <strong>should</strong> arrive by now. \uFF08\u4ED6\u4EEC<strong>\u5E94\u8BE5</strong>\u5230\u4E86\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>will</strong></td><td style="text-align:left;"><strong>1. \u5C06\u6765\u65F6\uFF08\u5C06\uFF09</strong> <br>2. \u610F\u613F/\u51B3\u5FC3\uFF08\u613F\u610F\uFF09</td><td style="text-align:left;">1. I <strong>will</strong> call you tomorrow. \uFF08\u6211\u660E\u5929<strong>\u4F1A</strong>\u7ED9\u4F60\u6253\u7535\u8BDD\u3002\uFF09<br>2. I <strong>will</strong> help you. \uFF08\u6211<strong>\u613F\u610F</strong>\u5E2E\u4F60\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>would</strong></td><td style="text-align:left;"><strong>1. will\u7684\u8FC7\u53BB\u5F0F</strong> <br>2. \u59D4\u5A49\u7684\u8BF7\u6C42/\u610F\u613F<br>3. \u8868\u793A\u8FC7\u53BB\u7684\u4E60\u60EF</td><td style="text-align:left;">1. He said he <strong>would</strong> come. \uFF08\u4ED6\u8BF4\u4ED6<strong>\u4F1A</strong>\u6765\u3002\uFF09<br>2. <strong>Would</strong> you like some tea? \uFF08\u60A8<strong>\u60F3</strong>\u6765\u70B9\u8336\u5417\uFF1F\uFF09<br>3. When I was a child, I <strong>would</strong> play here. \uFF08\u6211\u5C0F\u65F6\u5019<strong>\u5E38\u5E38</strong>\u5728\u8FD9\u91CC\u73A9\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>ought to</strong></td><td style="text-align:left;"><strong>1. \u4E49\u52A1/\u5EFA\u8BAE\uFF08\u5E94\u8BE5\uFF09\u3010\u8BED\u6C14\u6BD4should\u7A0D\u5F3A\u3011</strong></td><td style="text-align:left;">1. You <strong>ought to</strong> apologize to her. \uFF08\u4F60<strong>\u5E94\u8BE5</strong>\u5411\u5979\u9053\u6B49\u3002\uFF09</td></tr></tbody></table><hr><h3 id="\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u5426\u5B9A\u5F62\u5F0F\u7684\u542B\u4E49" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u5426\u5B9A\u5F62\u5F0F\u7684\u542B\u4E49" aria-hidden="true">#</a> <strong>\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192\uFF1A\u5426\u5B9A\u5F62\u5F0F\u7684\u542B\u4E49</strong></h3><p>\u60C5\u6001\u52A8\u8BCD\u7684\u5426\u5B9A\u5F62\u5F0F\uFF08\u76F4\u63A5\u5728\u540E\u9762\u52A0not\uFF09\u5F80\u5F80\u4E0D\u662F\u7B80\u5355\u5730\u5426\u5B9A\u52A8\u4F5C\uFF0C\u800C\u662F<strong>\u5426\u5B9A\u90A3\u79CD\u201C\u6001\u5EA6\u201D</strong>\u3002</p><ul><li>can \u2192 cannot (can&#39;t) <strong>\u4E0D\u80FD</strong>\uFF08\u6CA1\u80FD\u529B/\u4E0D\u5141\u8BB8\uFF09</li><li>may \u2192 may not <strong>\u4E0D\u53EF\u4EE5</strong>\uFF08\u4E0D\u5141\u8BB8\uFF09</li><li>must \u2192 must not (mustn&#39;t) <strong>\u7981\u6B62</strong>\uFF08\u5F3A\u70C8\u4E0D\u5141\u8BB8\uFF09</li><li>should \u2192 should not (shouldn&#39;t) <strong>\u4E0D\u5E94\u8BE5</strong>\uFF08\u5EFA\u8BAE\u4E0D\u8981\u505A\uFF09</li></ul><p><strong>\u7279\u522B\u6CE8\u610F\uFF1A</strong> <code>must</code> \u8868\u793A\u201C\u5FC5\u987B\u201D\uFF0C\u5176\u5426\u5B9A <code>mustn&#39;t</code> \u8868\u793A\u201C\u7981\u6B62\u201D\u3002\u800C\u8868\u793A\u201C\u4E0D\u5FC5\u201D\u65F6\uFF0C\u8981\u7528 <strong><code>needn&#39;t</code></strong> \u6216 <strong><code>don&#39;t have to</code></strong>\u3002</p><ul><li>You <strong>mustn&#39;t</strong> smoke here. \uFF08\u4F60<strong>\u7981\u6B62</strong>\u5728\u8FD9\u91CC\u5438\u70DF\u3002\uFF09</li><li>You <strong>needn&#39;t</strong> come tomorrow. \uFF08\u4F60\u660E\u5929<strong>\u4E0D\u5FC5</strong>\u6765\u3002\uFF09</li></ul><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> \u603B\u7ED3</h3><ol><li><strong>\u60C5\u6001\u52A8\u8BCD\uFF08Modal Verb\uFF09</strong> \u662F\u53E5\u5B50\u7684\u201C\u8C03\u5473\u6599\u201D\uFF0C\u8868\u8FBE\u60C5\u611F\u548C\u6001\u5EA6\u3002</li><li>\u5B83\u4EEC<strong>\u5FC5\u987B\u540E\u63A5\u52A8\u8BCD\u539F\u5F62</strong>\uFF0C\u4E14<strong>\u6CA1\u6709\u4EBA\u79F0\u548C\u6570\u7684\u53D8\u5316</strong>\u3002</li><li>\u6838\u5FC3\u529F\u80FD\u662F\u8868\u8FBE\uFF1A<strong>\u80FD\u529B\u3001\u8BB8\u53EF\u3001\u53EF\u80FD\u3001\u4E49\u52A1\u3001\u5EFA\u8BAE\u3001\u610F\u613F</strong>\u7B49\u3002</li><li>\u8BB0\u4F4F\u51E0\u4E2A\u6838\u5FC3\u6210\u5458\u7684\u201C\u4E3B\u6253\u98CE\u5473\u201D\uFF1A<code>can</code>\uFF08\u80FD\u529B\uFF09\uFF0C<code>may</code>\uFF08\u8BB8\u53EF\uFF09\uFF0C<code>must</code>\uFF08\u5FC5\u987B\uFF09\uFF0C<code>should</code>\uFF08\u5E94\u8BE5\uFF09\uFF0C<code>will</code>\uFF08\u5C06\u4F1A\uFF09\u3002</li></ol>',27),e=[g];function l(d,a){return o(),r("div",null,e)}var h=t(s,[["render",l],["__file","index.html.vue"]]);export{h as default};
