import{_ as t,o as r,c as e,b as o}from"./app.9fe7beff.js";const l={},n=o('<h3 id="\u5395\u6240\u76F8\u5173\u8BCD\u6C47" tabindex="-1"><a class="header-anchor" href="#\u5395\u6240\u76F8\u5173\u8BCD\u6C47" aria-hidden="true">#</a> \u5395\u6240\u76F8\u5173\u8BCD\u6C47</h3><p>Toilet [\u02C8t\u0254\u026Al\u026At] \u9A6C\u6876<br> Sink [s\u026A\u014Bk] \u6D17\u624B\u6C60<br> Shower [\u02C8\u0283a\u028A\u0259r] \u6DCB\u6D74<br> Bathtub [\u02C8b\xE6\u03B8t\u028Cb] \u6D74\u7F38<br> Mirror [\u02C8m\u026Ar\u0259r] \u955C\u5B50<br> Soap [so\u028Ap] \u80A5\u7682<br> Towel [\u02C8ta\u028A\u0259l] \u6BDB\u5DFE<br> Toilet paper [\u02C8t\u0254\u026Al\u026At \u02C8pe\u026Ap\u0259r] \u536B\u751F\u7EB8<br> Shampoo [\u0283\xE6m\u02C8pu\u02D0] \u6D17\u53D1\u6C34<br> Conditioner [k\u0259n\u02C8d\u026A\u0283\u0259n\u0259r] \u62A4\u53D1\u7D20<br> Flush [fl\u028C\u0283] \u51B2\u6C34<br> Wash [w\u0252\u0283] \u6D17\u6DA4<br> Brush [br\u028C\u0283] \u5237\u7259<br> Shower [\u02C8\u0283a\u028A\u0259r] (\u4F5C\u4E3A\u52A8\u8BCD) \u6DCB\u6D74<br> Bath [b\xE6\u03B8] \u6D17\u6FA1<br> Rinse [r\u026Ans] \u51B2\u6D17<br> Dry [dra\u026A] \u64E6\u5E72<br> Toiletries [\u02C8t\u0254\u026Al\u0259triz] \u6D17\u6F31\u7528\u54C1 Bathrobe [\u02C8b\xE6\u03B8ro\u028Ab] \u6D74\u888D Bath mat [b\xE6\u03B8 m\xE6t] \u9632\u6ED1\u57AB/\u6D74\u5BA4\u5730\u57AB Shower curtain [\u02C8\u0283a\u028A\u0259r \u02C8k\u025C\u02D0rtn] \u6DCB\u6D74\u5E18 Showerhead [\u02C8\u0283a\u028A\u0259rhed] \u6DCB\u6D74\u55B7\u5934 Shower gel [\u02C8\u0283a\u028A\u0259r d\u0292el] \u6C90\u6D74\u9732 Towel rack [\u02C8ta\u028A\u0259l r\xE6k] / Towel bar [\u02C8ta\u028A\u0259l b\u0251\u02D0r] \u6BDB\u5DFE\u67B6 Tissue [\u02C8t\u026A\u0283u\u02D0] \u7EB8\u5DFE Toilet brush [\u02C8t\u0254\u026Al\u026At br\u028C\u0283] \u9A6C\u6876\u5237 Scale [ske\u026Al] \u4F53\u91CD\u79E4 Cabinet [\u02C8k\xE6b\u026An\u0259t] \u50A8\u7269\u67DC Tiles [ta\u026Alz] \u74F7\u7816 Drain [dre\u026An] \u5730\u6F0F</p><p><strong>\u52A8\u8BCD/\u52A8\u4F5C</strong> Flush the toilet [fl\u028C\u0283 \xF0\u0259 \u02C8t\u0254\u026Al\u026At] \u51B2\u9A6C\u6876 Take a shower [te\u026Ak \u0259 \u02C8\u0283a\u028A\u0259r] \u6D17\u6DCB\u6D74 Take a bath [te\u026Ak \u0259 b\xE6\u03B8] \u6CE1\u6FA1 Scrub [skr\u028Cb] \u64E6\u6D17 Soak [so\u028Ak] \u6D78\u6CE1 Steam [sti\u02D0m] \u84B8/\u5145\u6EE1\u6C34\u84B8\u6C14 Wipe [wa\u026Ap] \u64E6\u62ED</p><h3 id="\u9633\u53F0\u76F8\u5173\u8BCD\u6C47" tabindex="-1"><a class="header-anchor" href="#\u9633\u53F0\u76F8\u5173\u8BCD\u6C47" aria-hidden="true">#</a> \u9633\u53F0\u76F8\u5173\u8BCD\u6C47</h3><p>Balcony [\u02C8b\xE6lk\u0259ni] \u9633\u53F0<br> Clothesline [\u02C8klo\u028A\xF0zla\u026An] \u667E\u8863\u7EF3<br> Drying rack [\u02C8dra\u026A\u026A\u014B r\xE6k] / Clothes horse [\u02C8klo\u028A\xF0z h\u0254\u02D0rs] \u667E\u8863\u67B6<br> Plant [pl\xE6nt] \u690D\u7269<br> Chair [t\u0283er] \u6905\u5B50<br> Table [\u02C8te\u026Abl] \u684C\u5B50<br> Dry [dra\u026A] \u667E\u5E72<br> Hang [h\xE6\u014B] \u60AC\u6302<br> Laundry [\u02C8l\u0254\u02D0ndri] \u6D17\u8863\u7269<br> Hanger [\u02C8h\xE6\u014B\u0259r] \u8863\u67B6<br> Clothes [klo\u028A\xF0z] \u8863\u670D<br> Sunning [\u02C8s\u028Cn\u026A\u014B] \u6652<br> Relax [r\u026A\u02C8l\xE6ks] \u653E\u677E<br> View [vju\u02D0] \u89C6\u91CE<br> Railing [\u02C8re\u026Al\u026A\u014B] \u680F\u6746 Flooring [\u02C8fl\u0254\u02D0r\u026A\u014B] \u5730\u677F\u6750\u6599 Potted plant [\u02C8p\u0251\u02D0t\u026Ad pl\xE6nt] \u76C6\u683D\u690D\u7269 Flowerpot [\u02C8fla\u028A\u0259rp\u0251\u02D0t] \u82B1\u76C6 Watering can [\u02C8w\u0254\u02D0t\u0259r\u026A\u014B k\xE6n] \u6D47\u6C34\u58F6 Sunlight [\u02C8s\u028Cnla\u026At] \u9633\u5149 Fresh air [fre\u0283 er] \u65B0\u9C9C\u7A7A\u6C14 Awning [\u02C8\u0254\u02D0n\u026A\u014B] \u906E\u9633\u7BF7 Storage cabinet [\u02C8st\u0254\u02D0r\u026Ad\u0292 \u02C8k\xE6b\u026An\u0259t] \u50A8\u7269\u67DC Clothes peg [klo\u028A\xF0z pe\u0261] / Clothes pin [klo\u028A\xF0z p\u026An] \u667E\u8863\u5939</p><p><strong>\u52A8\u8BCD/\u52A8\u4F5C</strong> Air-dry [er dra\u026A] \u98CE\u5E72 Water the plants [\u02C8w\u0254\u02D0t\u0259r \xF0\u0259 pl\xE6nts] \u7ED9\u690D\u7269\u6D47\u6C34 Enjoy the sun [\u026An\u02C8d\u0292\u0254\u026A \xF0\u0259 s\u028Cn] \u6652\u592A\u9633 Take in the laundry [te\u026Ak \u026An \xF0\u0259 \u02C8l\u0254\u02D0ndri] \u6536\u8863\u670D Fold the laundry [fo\u028Ald \xF0\u0259 \u02C8l\u0254\u02D0ndri] \u53E0\u8863\u670D</p><h3 id="\u6D17\u624B\u53F0\u76F8\u5173\u8BCD\u6C47" tabindex="-1"><a class="header-anchor" href="#\u6D17\u624B\u53F0\u76F8\u5173\u8BCD\u6C47" aria-hidden="true">#</a> \u6D17\u624B\u53F0\u76F8\u5173\u8BCD\u6C47</h3><p>Sink [s\u026A\u014Bk] \u6D17\u624B\u6C60<br> Faucet [\u02C8f\u0254\u02D0s\u026At] / Tap [t\xE6p] \u6C34\u9F99\u5934<br> Soap dispenser [so\u028Ap d\u026A\u02C8spens\u0259r] \u7682\u6DB2\u5668<br> Hand dryer [h\xE6nd \u02C8dra\u026A\u0259r] \u5E72\u624B\u5668<br> Wash hands [w\u0252\u0283 h\xE6ndz] \u6D17\u624B<br> Hand soap [h\xE6nd so\u028Ap] \u6D17\u624B\u6DB2<br> Towel [\u02C8ta\u028A\u0259l] \u6BDB\u5DFE<br> Rinse [r\u026Ans] \u51B2\u6D17<br> Water [\u02C8w\u0254\u02D0t\u0259r] \u6C34<br> Splash [spl\xE6\u0283] \u6E85\u6C34<br> Clean [kli\u02D0n] \u6E05\u6D01 Vanity [\u02C8v\xE6n\u0259ti] / Bathroom vanity \u6D74\u5BA4\u6D17\u624B\u53F0\uFF08\u5E26\u67DC\u5B50\u7684\uFF09 Countertop [\u02C8ka\u028Ant\u0259rt\u0251\u02D0p] \u53F0\u9762 Porcelain [\u02C8p\u0254\u02D0rs\u0259l\u026An] \u74F7\u5668 Hand sanitizer [h\xE6nd \u02C8s\xE6n\u026Ata\u026Az\u0259r] \u6D17\u624B\u6D88\u6BD2\u6DB2 Lotion [\u02C8lo\u028A\u0283n] \u62A4\u624B\u971C Mouthwash [\u02C8ma\u028A\u03B8w\u0251\u02D0\u0283] \u6F31\u53E3\u6C34 Toothbrush [\u02C8tu\u02D0\u03B8br\u028C\u0283] \u7259\u5237 Toothpaste [\u02C8tu\u02D0\u03B8pe\u026Ast] \u7259\u818F Electric toothbrush [\u026A\u02C8lektr\u026Ak \u02C8tu\u02D0\u03B8br\u028C\u0283] \u7535\u52A8\u7259\u5237 Dental floss [\u02C8dentl fl\u0254\u02D0s] \u7259\u7EBF Razor [\u02C8re\u026Az\u0259r] \u5243\u987B\u5200 Shaving cream [\u02C8\u0283e\u026Av\u026A\u014B kri\u02D0m] \u5243\u987B\u818F Faucet handle [\u02C8f\u0254\u02D0s\u026At \u02C8h\xE6ndl] \u6C34\u9F99\u5934\u628A\u624B Hot water [h\u0251\u02D0t \u02C8w\u0254\u02D0t\u0259r] \u70ED\u6C34 Cold water [ko\u028Ald \u02C8w\u0254\u02D0t\u0259r] \u51B7\u6C34 Stain [ste\u026An] \u6C61\u6E0D</p><p><strong>\u52A8\u8BCD/\u52A8\u4F5C</strong> Lather [\u02C8l\xE6\xF0\u0259r] \u8D77\u6CE1\u6CAB Scrub hands [skr\u028Cb h\xE6ndz] \u6413\u6D17\u53CC\u624B Rinse off [r\u026Ans \u0254\u02D0f] \u51B2\u6389 Wipe down the counter [wa\u026Ap da\u028An \xF0\u0259 \u02C8ka\u028Ant\u0259r] \u64E6\u62ED\u53F0\u9762 Refill the soap [\u02CCri\u02D0\u02C8f\u026Al \xF0\u0259 so\u028Ap] \u8865\u5145\u7682\u6DB2 Brush teeth [br\u028C\u0283 ti\u02D0\u03B8] \u5237\u7259 Gargle [\u02C8\u0261\u0251\u02D0r\u0261l] \u6F31\u53E3 Shave [\u0283e\u026Av] \u5243\u987B</p><hr><h3 id="\u573A\u666F\u4E00-\u65E9\u6668\u6D17\u6F31-morning-routine" tabindex="-1"><a class="header-anchor" href="#\u573A\u666F\u4E00-\u65E9\u6668\u6D17\u6F31-morning-routine" aria-hidden="true">#</a> \u573A\u666F\u4E00\uFF1A\u65E9\u6668\u6D17\u6F31 (Morning Routine)</h3><p><strong>Characters:</strong> Sarah and Mark (Couple)</p><p><strong>\u5BF9\u8BDD\u5185\u5BB9:</strong></p><ul><li><strong>Sarah:</strong> Mark, you&#39;ve been in the bathroom for 20 minutes. What are you doing? <ul><li><em>\u9A6C\u514B\uFF0C\u4F60\u5728\u6D74\u5BA4\u91CC\u5F85\u4E8620\u5206\u949F\u4E86\u3002\u4F60\u5728\u5E72\u4EC0\u4E48\uFF1F</em></li></ul></li><li><strong>Mark:</strong> I&#39;m just brushing my teeth. I&#39;ll be out in a second. Could you please refill the hand soap? It&#39;s almost empty. <ul><li><em>\u6211\u53EA\u662F\u5728\u5237\u7259\u3002\u9A6C\u4E0A\u5C31\u597D\u3002\u4F60\u80FD\u5E2E\u5FD9\u7ED9\u7682\u6DB2\u5668\u8865\u5145\u4E00\u4E0B\u6D17\u624B\u6DB2\u5417\uFF1F\u5FEB\u7528\u5B8C\u4E86\u3002</em></li></ul></li><li><strong>Sarah:</strong> Sure. And after you rinse, could you wipe down the counter? There&#39;s water everywhere. <ul><li><em>\u597D\u7684\u3002\u4F60\u51B2\u6D17\u5B8C\u540E\uFF0C\u80FD\u628A\u6D17\u624B\u53F0\u53F0\u9762\u64E6\u4E00\u4E0B\u5417\uFF1F\u5230\u5904\u90FD\u662F\u6C34\u3002</em></li></ul></li><li><strong>Mark:</strong> Oh, sorry about that. I was in a hurry to take a shower earlier. By the way, have you seen my razor? <ul><li><em>\u54E6\uFF0C\u62B1\u6B49\u3002\u6211\u521A\u624D\u6025\u7740\u53BB\u6DCB\u6D74\u4E86\u3002\u5BF9\u4E86\uFF0C\u4F60\u770B\u5230\u6211\u7684\u5243\u987B\u5200\u4E86\u5417\uFF1F</em></li></ul></li><li><strong>Sarah:</strong> I think it&#39;s in the cabinet, next to my shampoo and conditioner. <ul><li><em>\u6211\u60F3\u5728\u50A8\u7269\u67DC\u91CC\uFF0C\u5728\u6211\u7684\u6D17\u53D1\u6C34\u548C\u62A4\u53D1\u7D20\u65C1\u8FB9\u3002</em></li></ul></li><li><strong>Mark:</strong> Found it! Thanks. I&#39;ll make sure to clean up after I shave. <ul><li><em>\u627E\u5230\u4E86\uFF01\u8C22\u8C22\u3002\u6211\u522E\u5B8C\u80E1\u5B50\u540E\u4E00\u5B9A\u4F1A\u6536\u62FE\u5E72\u51C0\u7684\u3002</em></li></ul></li></ul><hr><h4 id="\u8BED\u6CD5\u8BB2\u89E3\u4E0E\u539F\u578B\u5C55\u793A" tabindex="-1"><a class="header-anchor" href="#\u8BED\u6CD5\u8BB2\u89E3\u4E0E\u539F\u578B\u5C55\u793A" aria-hidden="true">#</a> <strong>\u8BED\u6CD5\u8BB2\u89E3\u4E0E\u539F\u578B\u5C55\u793A</strong></h4><ol><li><p><strong>\u73B0\u5728\u5B8C\u6210\u8FDB\u884C\u65F6:</strong></p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;You&#39;ve been in the bathroom for 20 minutes.&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> Subject + have/has + been + present participle (\u4E3B\u8BED + have/has + been + \u73B0\u5728\u5206\u8BCD)</li><li><strong>\u8BB2\u89E3:</strong> \u8FD9\u4E2A\u65F6\u6001\u7528\u6765\u63CF\u8FF0\u4ECE\u8FC7\u53BB\u67D0\u65F6\u5F00\u59CB\u4E00\u76F4\u6301\u7EED\u5230\u73B0\u5728\u7684\u52A8\u4F5C\uFF0C\u5E76\u4E14\u53EF\u80FD\u4ECD\u5728\u8FDB\u884C\u3002\u8FD9\u91CC\u5F3A\u8C03\u9A6C\u514B\u5728\u6D74\u5BA4\u91CC\u5F85\u4E8620\u5206\u949F\uFF0C\u5E76\u4E14\u6B64\u523B\u4ECD\u5728\u91CC\u9762\u3002</li></ul></li><li><p><strong>\u4E00\u822C\u5C06\u6765\u65F6 (\u8868\u793A\u5373\u5C06\u53D1\u751F):</strong></p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;I&#39;ll be out in a second.&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> Subject + will + verb (\u4E3B\u8BED + will + \u52A8\u8BCD\u539F\u5F62)</li><li><strong>\u8BB2\u89E3:</strong> &quot;Will&quot; \u5728\u8FD9\u91CC\u8868\u793A\u7ACB\u5373\u5C06\u8981\u53D1\u751F\u7684\u52A8\u4F5C\uFF0C\u662F\u4E00\u79CD\u627F\u8BFA\u6216\u9884\u6D4B\u3002</li></ul></li><li><p>\u7948\u4F7F\u53E5 (\u8868\u793A\u8BF7\u6C42):</p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;Could you please refill the hand soap?&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> Could you (please) + verb...? (Could you (\u8BF7) + \u52A8\u8BCD...?)</li><li><strong>\u8BB2\u89E3:</strong> &quot;Could you...&quot; \u662F\u4E00\u79CD\u975E\u5E38\u793C\u8C8C\u7684\u63D0\u51FA\u8BF7\u6C42\u7684\u65B9\u5F0F\uFF0C\u6BD4 &quot;Please refill...&quot; \u66F4\u59D4\u5A49\u3002</li></ul></li><li><p><strong>\u8FC7\u53BB\u8FDB\u884C\u65F6:</strong></p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;I was in a hurry to take a shower earlier.&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> Subject + was/were + present participle (\u4E3B\u8BED + was/were + \u73B0\u5728\u5206\u8BCD)</li><li><strong>\u8BB2\u89E3:</strong> \u8FD9\u91CC\u63CF\u8FF0\u7684\u662F\u5728\u8FC7\u53BB\u7684\u67D0\u4E2A\u65F6\u95F4\u70B9\uFF08\u65E9\u4E9B\u65F6\u5019\uFF09\u6B63\u5728\u8FDB\u884C\u7684\u52A8\u4F5C\u6216\u72B6\u6001\uFF08\u5306\u5FD9\u7684\u72B6\u6001\uFF09\u3002</li></ul></li></ol><hr><h3 id="\u573A\u666F\u4E8C-\u9633\u53F0\u667E\u8863-drying-clothes-on-the-balcony" tabindex="-1"><a class="header-anchor" href="#\u573A\u666F\u4E8C-\u9633\u53F0\u667E\u8863-drying-clothes-on-the-balcony" aria-hidden="true">#</a> \u573A\u666F\u4E8C\uFF1A\u9633\u53F0\u667E\u8863 (Drying Clothes on the Balcony)</h3><p><strong>Characters:</strong> Lisa and Tom (Roommates)</p><p><strong>\u5BF9\u8BDD\u5185\u5BB9:</strong></p><ul><li><strong>Lisa:</strong> Tom, I&#39;ve just finished the laundry. Could you help me hang these clothes on the drying rack? <ul><li><em>\u6C64\u59C6\uFF0C\u6211\u521A\u6D17\u5B8C\u8863\u670D\u3002\u4F60\u80FD\u5E2E\u6211\u628A\u8FD9\u4E9B\u8863\u670D\u6302\u5728\u667E\u8863\u67B6\u4E0A\u5417\uFF1F</em></li></ul></li><li><strong>Tom:</strong> Of course. Pass me the clothes pegs. If you hang them properly, they&#39;ll dry faster in the sunlight. <ul><li><em>\u5F53\u7136\u3002\u628A\u667E\u8863\u5939\u9012\u7ED9\u6211\u3002\u5982\u679C\u4F60\u628A\u8863\u670D\u6302\u597D\uFF0C\u5B83\u4EEC\u5728\u9633\u5149\u4E0B\u4F1A\u5E72\u5F97\u66F4\u5FEB\u3002</em></li></ul></li><li><strong>Lisa:</strong> I know. I love how the fresh air makes them smell. Oh, don&#39;t forget to shake the wrinkles out before you hang up your shirts. <ul><li><em>\u6211\u77E5\u9053\u3002\u6211\u559C\u6B22\u65B0\u9C9C\u7A7A\u6C14\u8BA9\u8863\u670D\u95FB\u8D77\u6765\u7684\u5473\u9053\u3002\u54E6\uFF0C\u6302\u4F60\u7684\u886C\u886B\u4E4B\u524D\uFF0C\u522B\u5FD8\u4E86\u628A\u76B1\u8936\u6296\u5F00\u3002</em></li></ul></li><li><strong>Tom:</strong> Good point. I&#39;ll do that. After we finish, let&#39;s relax on the balcony for a while. The view is great today. <ul><li><em>\u8BF4\u5F97\u5BF9\u3002\u6211\u4F1A\u7684\u3002\u6211\u4EEC\u5F04\u5B8C\u540E\uFF0C\u5728\u9633\u53F0\u4F11\u606F\u4E00\u4F1A\u513F\u5427\u3002\u4ECA\u5929\u7684\u89C6\u91CE\u5F88\u68D2\u3002</em></li></ul></li><li><strong>Lisa:</strong> That sounds perfect. I&#39;ll bring out some water for us. <ul><li><em>\u542C\u8D77\u6765\u68D2\u6781\u4E86\u3002\u6211\u7ED9\u6211\u4EEC\u62FF\u70B9\u6C34\u51FA\u6765\u3002</em></li></ul></li></ul><hr><h4 id="\u8BED\u6CD5\u8BB2\u89E3\u4E0E\u539F\u578B\u5C55\u793A-1" tabindex="-1"><a class="header-anchor" href="#\u8BED\u6CD5\u8BB2\u89E3\u4E0E\u539F\u578B\u5C55\u793A-1" aria-hidden="true">#</a> <strong>\u8BED\u6CD5\u8BB2\u89E3\u4E0E\u539F\u578B\u5C55\u793A</strong></h4><ol><li><p><strong>\u73B0\u5728\u5B8C\u6210\u65F6:</strong></p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;I&#39;ve just finished the laundry.&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> Subject + have/has + past participle (\u4E3B\u8BED + have/has + \u8FC7\u53BB\u5206\u8BCD)</li><li><strong>\u8BB2\u89E3:</strong> \u8FD9\u4E2A\u65F6\u6001\u8868\u793A\u8FC7\u53BB\u53D1\u751F\u7684\u52A8\u4F5C\u5BF9\u73B0\u5728\u9020\u6210\u4E86\u5F71\u54CD\u6216\u7ED3\u679C\u3002&quot;just&quot;\uFF08\u521A\u521A\uFF09\u5F3A\u8C03\u52A8\u4F5C\u624D\u5B8C\u6210\u4E0D\u4E45\u3002\u7ED3\u679C\u662F\u201C\u8863\u670D\u6D17\u597D\u4E86\uFF0C\u9700\u8981\u667E\u201D\u3002</li></ul></li><li><p><strong>\u7B2C\u96F6\u6761\u4EF6\u53E5 / \u771F\u5B9E\u6761\u4EF6\u53E5 (\u7528\u4E8E\u666E\u904D\u771F\u7406\u6216\u4E60\u60EF):</strong></p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;If you hang them properly, they&#39;ll dry faster...&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> If + present simple, ... will + verb (If + \u4E00\u822C\u73B0\u5728\u65F6, ... will + \u52A8\u8BCD\u539F\u5F62)</li><li><strong>\u8BB2\u89E3:</strong> \u8FD9\u4E2A\u7ED3\u6784\u7528\u4E8E\u63CF\u8FF0\u6709\u56E0\u679C\u5173\u7CFB\u3001\u901A\u5E38\u4F1A\u53D1\u751F\u7684\u4E8B\u60C5\u3002\u5982\u679C\u6761\u4EF6\uFF08\u6302\u597D\u8863\u670D\uFF09\u6EE1\u8DB3\uFF0C\u7ED3\u679C\uFF08\u5E72\u5F97\u66F4\u5FEB\uFF09\u5C31\u4F1A\u53D1\u751F\u3002</li></ul></li><li><p><strong>\u5BBE\u8BED\u4ECE\u53E5:</strong></p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;I love how the fresh air makes them smell.&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> Main Clause + how/what/when/why + Clause (\u4E3B\u53E5 + \u8FDE\u63A5\u526F\u8BCD + \u4ECE\u53E5)</li><li><strong>\u8BB2\u89E3:</strong> &quot;how the fresh air makes them smell&quot; \u6574\u4E2A\u53E5\u5B50\u4F5C\u4E3A\u52A8\u8BCD &quot;love&quot; \u7684\u5BBE\u8BED\uFF0C\u8BF4\u660E\u4E86\u201C\u7231\u201D\u7684\u5185\u5BB9\u3002</li></ul></li><li><p><strong>\u7948\u4F7F\u53E5 (\u8868\u793A\u63D0\u9192):</strong></p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;Don&#39;t forget to shake the wrinkles out...&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> (Do not) Don&#39;t + verb... ((Do not) Don&#39;t + \u52A8\u8BCD...)</li><li><strong>\u8BB2\u89E3:</strong> \u8FD9\u662F\u5426\u5B9A\u5F62\u5F0F\u7684\u7948\u4F7F\u53E5\uFF0C\u7528\u4E8E\u53D1\u51FA\u6307\u4EE4\u6216\u63D0\u9192\u5BF9\u65B9\u4E0D\u8981\u505A\u67D0\u4E8B\u3002</li></ul></li></ol><hr><h3 id="\u573A\u666F\u4E09-\u6E05\u6D01\u6D74\u5BA4-cleaning-the-bathroom" tabindex="-1"><a class="header-anchor" href="#\u573A\u666F\u4E09-\u6E05\u6D01\u6D74\u5BA4-cleaning-the-bathroom" aria-hidden="true">#</a> \u573A\u666F\u4E09\uFF1A\u6E05\u6D01\u6D74\u5BA4 (Cleaning the Bathroom)</h3><p><strong>Characters:</strong> David and his Mom</p><p><strong>\u5BF9\u8BDD\u5185\u5BB9:</strong></p><ul><li><strong>Mom:</strong> David, would you mind cleaning the bathroom today? I noticed that the sink is a bit dirty and the toilet paper needs to be replaced. <ul><li><em>\u5927\u536B\uFF0C\u4F60\u4ECA\u5929\u80FD\u6253\u626B\u4E00\u4E0B\u6D74\u5BA4\u5417\uFF1F\u6211\u53D1\u73B0\u6D17\u624B\u6C60\u6709\u70B9\u810F\uFF0C\u800C\u4E14\u536B\u751F\u7EB8\u9700\u8981\u66F4\u6362\u4E86\u3002</em></li></ul></li><li><strong>David:</strong> Okay, I&#39;ll do it after I finish my homework. Where do we keep the toilet brush and the cleaning supplies? <ul><li><em>\u597D\u7684\uFF0C\u6211\u505A\u5B8C\u4F5C\u4E1A\u5C31\u6253\u626B\u3002\u6211\u4EEC\u7684\u9A6C\u6876\u5237\u548C\u6E05\u6D01\u7528\u54C1\u653E\u5728\u54EA\u91CC\uFF1F</em></li></ul></li><li><strong>Mom:</strong> They&#39;re in the cabinet under the sink. And please remember to scrub the bathtub as well. The shower gel has left some stains. <ul><li><em>\u5728\u6D17\u624B\u6C60\u4E0B\u9762\u7684\u67DC\u5B50\u91CC\u3002\u8FD8\u6709\u8BF7\u8BB0\u5F97\u628A\u6D74\u7F38\u4E5F\u5237\u4E00\u4E0B\u3002\u6C90\u6D74\u9732\u7559\u4E0B\u4E86\u4E00\u4E9B\u6C61\u6E0D\u3002</em></li></ul></li><li><strong>David:</strong> Got it. I&#39;ll use the towel that&#39;s on the towel rack to wipe the mirror after I&#39;m done, right? <ul><li><em>\u660E\u767D\u4E86\u3002\u6211\u5F04\u5B8C\u540E\u4F1A\u7528\u6BDB\u5DFE\u67B6\u4E0A\u7684\u90A3\u6761\u6BDB\u5DFE\u64E6\u955C\u5B50\uFF0C\u5BF9\u5427\uFF1F</em></li></ul></li><li><strong>Mom:</strong> No, dear! Use the microfiber cloth that I left on the countertop. The towel on the rack is clean. <ul><li><em>\u4E0D\uFF0C\u4EB2\u7231\u7684\uFF01\u7528\u6211\u653E\u5728\u53F0\u9762\u4E0A\u7684\u8D85\u7EC6\u7EA4\u7EF4\u5E03\u3002\u67B6\u5B50\u4E0A\u7684\u6BDB\u5DFE\u662F\u5E72\u51C0\u7684\u3002</em></li></ul></li></ul><hr><h4 id="\u8BED\u6CD5\u8BB2\u89E3\u4E0E\u539F\u578B\u5C55\u793A-2" tabindex="-1"><a class="header-anchor" href="#\u8BED\u6CD5\u8BB2\u89E3\u4E0E\u539F\u578B\u5C55\u793A-2" aria-hidden="true">#</a> <strong>\u8BED\u6CD5\u8BB2\u89E3\u4E0E\u539F\u578B\u5C55\u793A</strong></h4><ol><li><p><strong>\u59D4\u5A49\u8BF7\u6C42:</strong></p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;Would you mind cleaning the bathroom today?&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> Would you mind + verb-ing...? (Would you mind + \u52A8\u540D\u8BCD...?)</li><li><strong>\u8BB2\u89E3:</strong> \u8FD9\u662F\u975E\u5E38\u793C\u8C8C\u7684\u8BF7\u6C42\u65B9\u5F0F\uFF0C\u5B57\u9762\u610F\u601D\u662F\u201C\u4F60\u4ECB\u610F\u505A...\u5417\uFF1F\u201D\uFF0C\u56DE\u7B54 &quot;Not at all&quot; \u6216 &quot;Okay&quot; \u8868\u793A\u4E0D\u4ECB\u610F\uFF0C\u4E5F\u5C31\u662F\u540C\u610F\u3002</li></ul></li><li><p><strong>\u5BBE\u8BED\u4ECE\u53E5:</strong></p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;I noticed (that) the sink is a bit dirty...&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> Main Clause + (that) + Clause (\u4E3B\u53E5 + (that) + \u4ECE\u53E5)</li><li><strong>\u8BB2\u89E3:</strong> \u8FDE\u8BCD &quot;that&quot; \u5F15\u5BFC\u7684\u4ECE\u53E5 &quot;the sink is a bit dirty&quot; \u662F\u6574\u4E2A\u53E5\u5B50\u7684\u5BBE\u8BED\uFF0C\u8BF4\u660E\u4E86\u201C\u6CE8\u610F\u5230\u201D\u7684\u5185\u5BB9\u3002\u53E3\u8BED\u4E2D &quot;that&quot; \u5E38\u53EF\u7701\u7565\u3002</li></ul></li><li><p><strong>\u73B0\u5728\u5B8C\u6210\u65F6 (\u8868\u793A\u8FC7\u53BB\u52A8\u4F5C\u5BF9\u73B0\u5728\u7684\u5F71\u54CD):</strong></p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;The shower gel has left some stains.&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> Subject + have/has + past participle (\u4E3B\u8BED + have/has + \u8FC7\u53BB\u5206\u8BCD)</li><li><strong>\u8BB2\u89E3:</strong> \u4F7F\u7528\u73B0\u5728\u5B8C\u6210\u65F6\u8868\u660E\u201C\u7559\u4E0B\u6C61\u6E0D\u201D\u8FD9\u4E2A\u8FC7\u53BB\u7684\u52A8\u4F5C\u9020\u6210\u4E86\u73B0\u5728\u7684\u72B6\u6001\uFF08\u6D74\u7F38\u73B0\u5728\u662F\u810F\u7684\uFF09\u3002</li></ul></li><li><p><strong>\u88AB\u52A8\u8BED\u6001:</strong></p><ul><li><strong>\u5BF9\u8BDD\u53E5\u5B50:</strong> &quot;The towel on the rack is clean.&quot;</li><li><strong>\u539F\u578B\u5C55\u793A:</strong> Subject + be + past participle (\u4E3B\u8BED + be\u52A8\u8BCD + \u8FC7\u53BB\u5206\u8BCD)</li><li><strong>\u8BB2\u89E3:</strong> \u8FD9\u91CC &quot;is cleaned&quot; \u662F\u4E0D\u5BF9\u7684\uFF0C\u56E0\u4E3A&quot;clean&quot;\u5728\u8FD9\u91CC\u662F\u5F62\u5BB9\u8BCD\uFF0C\u63CF\u8FF0\u72B6\u6001\u3002\u4F46\u5982\u679C\u5F3A\u8C03\u52A8\u4F5C\uFF0C\u53EF\u4EE5\u8BF4 &quot;The towel was cleaned yesterday&quot;\uFF08\u6BDB\u5DFE\u6628\u5929\u88AB\u6D17\u4E86\uFF09\u3002\u8FD9\u91CC\u7528 &quot;is clean&quot; \u662F\u7CFB\u8868\u7ED3\u6784\uFF0C\u8868\u793A\u72B6\u6001\u3002</li></ul></li></ol>',33),i=[n];function s(a,h){return r(),e("div",null,i)}var g=t(l,[["render",s],["__file","index.html.vue"]]);export{g as default};
