import{_ as t,o as e,c as r,e as n}from"./app.0b533659.js";const s={},l=n('<h2 id="\u8FDE\u8BCD-conjunction" tabindex="-1"><a class="header-anchor" href="#\u8FDE\u8BCD-conjunction" aria-hidden="true">#</a> \u8FDE\u8BCD Conjunction</h2><p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4EEC\u4ECA\u5929\u6765\u5B66\u4E60\u82F1\u8BED\u7684\u201C\u7C98\u5408\u5242\u201D\u548C\u201C\u4EA4\u901A\u6307\u6325\u201D\u2014\u2014<strong>\u8FDE\u8BCD</strong>\u3002\u5B83\u5C31\u662F\u8BA9\u96F6\u6563\u7684\u5355\u8BCD\u548C\u53E5\u5B50\u8FDE\u63A5\u6210\u6D41\u7545\u6BB5\u843D\u548C\u6587\u7AE0\u7684\u5173\u952E\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u8FDE\u8BCD-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u8FDE\u8BCD-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u8FDE\u8BCD\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p>\u60F3\u8C61\u4E00\u4E0B\uFF0C\u4F60\u8981\u8868\u8FBE\u51E0\u4E2A\u60F3\u6CD5\uFF1A</p><ul><li>\u201C\u6211\u60F3\u53BB\u516C\u56ED\u3002\u5929\u8981\u4E0B\u96E8\u4E86\u3002\u6211\u51B3\u5B9A\u5F85\u5728\u5BB6\u91CC\u3002\u201D \u8FD9\u4E09\u4E2A\u53E5\u5B50\u50CF\u4E09\u5757\u5B64\u7ACB\u7684\u79EF\u6728\uFF0C\u5173\u7CFB\u677E\u6563\u3002\u4F46\u5982\u679C\u4F60\u8BF4\uFF1A</li><li>\u201C\u6211\u60F3\u53BB\u516C\u56ED\uFF0C<strong>\u4F46\u662F</strong>\u5929\u8981\u4E0B\u96E8\u4E86\uFF0C<strong>\u6240\u4EE5</strong>\u6211\u51B3\u5B9A\u5F85\u5728\u5BB6\u91CC\u3002\u201D \u8FD9\u4E09\u5757\u79EF\u6728\u5C31\u88AB\u5B8C\u7F8E\u5730\u7C98\u5408\u5728\u4E00\u8D77\uFF0C\u903B\u8F91\u5173\u7CFB\u4E00\u76EE\u4E86\u7136\u3002</li></ul><p>\u8FD9\u91CC\u7684\u201C\u4F46\u662F\u201D\u3001\u201C\u6240\u4EE5\u201D\u5C31\u662F<strong>\u8FDE\u8BCD</strong>\u3002</p><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u8FDE\u8BCD\u662F\u7528\u6765\u8FDE\u63A5\u5355\u8BCD\u3001\u77ED\u8BED\u6216\u53E5\u5B50\u7684\u8BCD\uFF0C\u5B83\u8868\u660E\u6240\u8FDE\u63A5\u90E8\u5206\u4E4B\u95F4\u7684\u903B\u8F91\u5173\u7CFB\uFF08\u5982\u5E76\u5217\u3001\u8F6C\u6298\u3001\u56E0\u679C\u7B49\uFF09\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong></p><ul><li><strong>\u5355\u8BCD\u3001\u77ED\u8BED\u3001\u53E5\u5B50</strong> \u5C31\u50CF\u4E00\u5757\u5757<strong>\u79EF\u6728</strong>\u3002</li><li><strong>\u8FDE\u8BCD</strong> \u5C31\u662F<strong>\u7C98\u5408\u8FD9\u4E9B\u79EF\u6728\u7684\u201C\u80F6\u6C34\u201D</strong>\uFF0C\u6216\u8005\u662F\u6307\u6325\u5B83\u4EEC\u5982\u4F55\u7EC4\u5408\u7684**\u201C\u6865\u6881\u201D\u548C\u201C\u4EA4\u901A\u8B66\u5BDF\u201D**\u3002</li></ul><p>\u6CA1\u6709\u8FDE\u8BCD\uFF0C\u601D\u60F3\u5C31\u662F\u4E00\u5806\u6563\u843D\u7684\u788E\u7247\uFF1B\u6709\u4E86\u8FDE\u8BCD\uFF0C\u4F60\u624D\u80FD\u6784\u5EFA\u51FA\u590D\u6742\u800C\u6E05\u6670\u7684\u601D\u60F3\u5927\u53A6\u3002</p><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u8FDE\u8BCD-\u540D\u79F0\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u8FDE\u8BCD-\u540D\u79F0\u7684\u7531\u6765" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u4E3A\u4EC0\u4E48\u53EB\u201C\u8FDE\u8BCD\u201D\uFF1F\u540D\u79F0\u7684\u7531\u6765</h3><ol><li><p><strong>\u4E2D\u6587\u540D\u201C\u8FDE\u8BCD\u201D</strong>\uFF1A</p><ul><li>\u201C\u8FDE\u201D\u5C31\u662F\u201C\u8FDE\u63A5\u3001\u8054\u5408\u201D\u7684\u610F\u601D\u3002</li><li>\u6240\u4EE5\u201C\u8FDE\u8BCD\u201D\u5C31\u662F<strong>\u8D77\u8FDE\u63A5\u4F5C\u7528\u7684\u8BCD</strong>\u3002</li></ul></li><li><p><strong>\u82F1\u6587\u540D\u201CConjunction\u201D</strong>\uFF1A</p><ul><li>\u8BFB\u97F3\uFF1A/k\u0259n\u02C8d\u0292\u028C\u014Bk\u0283n/</li><li>\u8FD9\u4E2A\u8BCD\u6765\u6E90\u4E8E\u62C9\u4E01\u8BED <code>conjungere</code>\uFF0C\u7531 <code>con-</code>\uFF08\u610F\u4E3A\u201C\u4E00\u8D77\u201D\uFF09\u548C <code>jungere</code>\uFF08\u610F\u4E3A\u201C\u8FDE\u63A5\u3001\u52A0\u5165\u201D\uFF09\u4E24\u90E8\u5206\u7EC4\u6210\u3002</li><li>\u6240\u4EE5 <code>Conjunction</code> \u7684\u5B57\u9762\u610F\u601D\u5C31\u662F <strong>\u201C\u8FDE\u63A5\u5230\u4E00\u8D77\u201D</strong> \uFF0C\u975E\u5E38\u5F62\u8C61\u3002</li></ul></li></ol><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u8FDE\u8BCD\u7684\u4E09\u5927\u6838\u5FC3\u5BB6\u65CF" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u8FDE\u8BCD\u7684\u4E09\u5927\u6838\u5FC3\u5BB6\u65CF" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u8FDE\u8BCD\u7684\u4E09\u5927\u6838\u5FC3\u5BB6\u65CF</h3><p>\u6839\u636E\u5B83\u4EEC\u8FDE\u63A5\u7684\u201C\u5355\u4F4D\u201D\u548C\u8868\u8FBE\u7684\u201C\u5173\u7CFB\u201D\uFF0C\u6211\u4EEC\u53EF\u4EE5\u628A\u8FDE\u8BCD\u5206\u6210\u4E09\u7C7B\u3002</p><h4 id="\u5BB6\u65CF\u4E00-\u5E76\u5217\u8FDE\u8BCD\u2014\u2014\u8FDE\u63A5-\u5E73\u7B49-\u7684\u5355\u4F4D" tabindex="-1"><a class="header-anchor" href="#\u5BB6\u65CF\u4E00-\u5E76\u5217\u8FDE\u8BCD\u2014\u2014\u8FDE\u63A5-\u5E73\u7B49-\u7684\u5355\u4F4D" aria-hidden="true">#</a> <strong>\u5BB6\u65CF\u4E00\uFF1A\u5E76\u5217\u8FDE\u8BCD\u2014\u2014\u8FDE\u63A5\u201C\u5E73\u7B49\u201D\u7684\u5355\u4F4D</strong></h4><p>\u8FD9\u662F\u6700\u57FA\u7840\u7684\u8FDE\u8BCD\uFF0C\u7528\u6765\u8FDE\u63A5\u4E24\u4E2A\u8BED\u6CD5\u5730\u4F4D\u5B8C\u5168\u5E73\u7B49\u7684\u8BCD\u3001\u77ED\u8BED\u6216\u53E5\u5B50\u3002\u5B83\u4EEC\u5C31\u50CF\u5929\u5E73\u7684\u4E24\u7AEF\u3002\u8BB0\u4F4F\u4E00\u4E2A\u795E\u5947\u7684\u53E3\u8BC0\uFF1A<strong>FANBOYS</strong>\u3002</p><table><thead><tr><th style="text-align:left;">\u8FDE\u8BCD</th><th style="text-align:left;">\u6838\u5FC3\u542B\u4E49</th><th style="text-align:left;">\u4F8B\u53E5</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>F</strong>or</td><td style="text-align:left;">\u56E0\u4E3A\uFF08\u8868\u539F\u56E0\uFF0C\u8F83\u6B63\u5F0F\uFF09</td><td style="text-align:left;">I stayed home, <strong>for</strong> I was tired. \uFF08\u6211\u5F85\u5728\u5BB6\u91CC\uFF0C\u56E0\u4E3A\u6211\u7D2F\u4E86\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>A</strong>nd</td><td style="text-align:left;">\u548C\uFF0C\u5E76\u4E14\uFF08\u8868\u5E76\u5217\u3001\u987A\u627F\uFF09</td><td style="text-align:left;">He is tall <strong>and</strong> handsome. \uFF08\u4ED6\u53C8\u9AD8\u53C8\u5E05\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>N</strong>or</td><td style="text-align:left;">\u4E5F\u4E0D\uFF08\u8868\u5426\u5B9A\u5E76\u5217\uFF09</td><td style="text-align:left;">I don&#39;t smoke, <strong>nor</strong> do I drink. \uFF08\u6211\u4E0D\u62BD\u70DF\uFF0C\u4E5F\u4E0D\u559D\u9152\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>B</strong>ut</td><td style="text-align:left;">\u4F46\u662F\uFF08\u8868\u8F6C\u6298\uFF09</td><td style="text-align:left;">She is small <strong>but</strong> strong. \uFF08\u5979\u4E2A\u5B50\u5C0F\u4F46\u5F88\u5F3A\u58EE\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>O</strong>r</td><td style="text-align:left;">\u6216\u8005\uFF08\u8868\u9009\u62E9\uFF09</td><td style="text-align:left;">Would you like tea <strong>or</strong> coffee? \uFF08\u4F60\u60F3\u559D\u8336\u8FD8\u662F\u5496\u5561\uFF1F\uFF09</td></tr><tr><td style="text-align:left;"><strong>Y</strong>et</td><td style="text-align:left;">\u7136\u800C\uFF0C\u4F46\u662F\uFF08\u8868\u8F6C\u6298\uFF0C\u8BED\u6C14\u5F3A\u4E8Ebut\uFF09</td><td style="text-align:left;">He worked hard, <strong>yet</strong> he failed. \uFF08\u4ED6\u5F88\u52AA\u529B\uFF0C\u7136\u800C\u8FD8\u662F\u5931\u8D25\u4E86\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>S</strong>o</td><td style="text-align:left;">\u6240\u4EE5\uFF08\u8868\u7ED3\u679C\uFF09</td><td style="text-align:left;">It was raining, <strong>so</strong> we canceled the trip. \uFF08\u4E0B\u96E8\u4E86\uFF0C\u6240\u4EE5\u6211\u4EEC\u53D6\u6D88\u4E86\u884C\u7A0B\u3002\uFF09</td></tr></tbody></table><p><strong>\u4F7F\u7528\u5E76\u5217\u8FDE\u8BCD\u7684\u91CD\u8981\u89C4\u5219</strong>\uFF1A \u5F53\u8FDE\u63A5\u4E24\u4E2A<strong>\u5B8C\u6574\u7684\u53E5\u5B50</strong>\u65F6\uFF0C\u5E76\u5217\u8FDE\u8BCD\u524D\u9762<strong>\u5FC5\u987B</strong>\u6709\u9017\u53F7\uFF01</p><ul><li>\u6B63\u786E\uFF1AI like apples, <strong>but</strong> my brother likes oranges.</li><li>\u9519\u8BEF\uFF1A<s>I like apples but my brother likes oranges.</s> (\u5728\u4E66\u9762\u8BED\u4E2D\uFF0C\u8FD9\u662F\u9519\u8BEF\u7684)</li></ul><hr><h4 id="\u5BB6\u65CF\u4E8C-\u4ECE\u5C5E\u8FDE\u8BCD\u2014\u2014\u8FDE\u63A5-\u4E3B\u4ECE-\u7684\u53E5\u5B50" tabindex="-1"><a class="header-anchor" href="#\u5BB6\u65CF\u4E8C-\u4ECE\u5C5E\u8FDE\u8BCD\u2014\u2014\u8FDE\u63A5-\u4E3B\u4ECE-\u7684\u53E5\u5B50" aria-hidden="true">#</a> <strong>\u5BB6\u65CF\u4E8C\uFF1A\u4ECE\u5C5E\u8FDE\u8BCD\u2014\u2014\u8FDE\u63A5\u201C\u4E3B\u4ECE\u201D\u7684\u53E5\u5B50</strong></h4><p>\u4ECE\u5C5E\u8FDE\u8BCD\u7528\u6765\u5F15\u5BFC\u4E00\u4E2A<strong>\u4ECE\u53E5</strong>\uFF08\u4E00\u4E2A\u4E0D\u80FD\u72EC\u7ACB\u5B58\u5728\u7684\u53E5\u5B50\uFF09\uFF0C\u5E76\u5C06\u5176\u4E0E<strong>\u4E3B\u53E5</strong>\uFF08\u53EF\u4EE5\u72EC\u7ACB\u5B58\u5728\u7684\u53E5\u5B50\uFF09\u8FDE\u63A5\u8D77\u6765\u3002\u5B83\u8868\u660E\u4ECE\u53E5\u548C\u4E3B\u53E5\u4E4B\u95F4\u7684\u903B\u8F91\u5173\u7CFB\uFF0C\u5982\u65F6\u95F4\u3001\u539F\u56E0\u3001\u6761\u4EF6\u7B49\u3002</p><table><thead><tr><th style="text-align:left;">\u5173\u7CFB\u7C7B\u578B</th><th style="text-align:left;">\u6838\u5FC3\u8FDE\u8BCD</th><th style="text-align:left;">\u4F8B\u53E5\u4E0E\u89E3\u91CA</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u65F6\u95F4</strong></td><td style="text-align:left;">when, while, as, before, after, since, until</td><td style="text-align:left;"><strong>When</strong> he arrived, I was cooking. \uFF08<strong>\u5F53</strong>\u4ED6\u5230\u7684\u65F6\u5019\uFF0C\u6211\u6B63\u5728\u505A\u996D\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u539F\u56E0</strong></td><td style="text-align:left;">because, as, since</td><td style="text-align:left;">I stayed home <strong>because</strong> I was sick. \uFF08\u6211\u5F85\u5728\u5BB6\u91CC\uFF0C<strong>\u56E0\u4E3A</strong>\u6211\u75C5\u4E86\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u6761\u4EF6</strong></td><td style="text-align:left;">if, unless, as long as</td><td style="text-align:left;"><strong>If</strong> it rains, we will stay home. \uFF08<strong>\u5982\u679C</strong>\u4E0B\u96E8\uFF0C\u6211\u4EEC\u5C31\u5F85\u5728\u5BB6\u91CC\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u8BA9\u6B65</strong></td><td style="text-align:left;">although, though, even though</td><td style="text-align:left;"><strong>Although</strong> it was expensive, I bought it. \uFF08<strong>\u5C3D\u7BA1</strong>\u5F88\u8D35\uFF0C\u6211\u8FD8\u662F\u4E70\u4E86\u5B83\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u76EE\u7684</strong></td><td style="text-align:left;">so that, in order that</td><td style="text-align:left;">I study hard <strong>so that</strong> I can pass the exam. \uFF08\u6211\u52AA\u529B\u5B66\u4E60\uFF0C<strong>\u4E3A\u4E86</strong>\u80FD\u901A\u8FC7\u8003\u8BD5\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u7ED3\u679C</strong></td><td style="text-align:left;">so...that, such...that</td><td style="text-align:left;">He was <strong>so</strong> tired <strong>that</strong> he fell asleep. \uFF08\u4ED6\u7D2F<strong>\u5F97</strong>\u7761\u7740\u4E86\u3002\uFF09</td></tr></tbody></table><p><strong>\u4ECE\u5C5E\u8FDE\u8BCD\u7684\u6838\u5FC3\u5FC3\u6CD5</strong>\uFF1A</p><ul><li>\u4ECE\u53E5\uFF08\u7531\u4ECE\u5C5E\u8FDE\u8BCD\u5F15\u5BFC\uFF09\u672C\u8EAB<strong>\u4E0D\u80FD\u72EC\u7ACB\u6210\u53E5</strong>\uFF0C\u5B83\u50CF\u4E00\u4E2A\u201C\u9644\u4EF6\u201D\uFF0C\u9700\u8981\u4F9D\u8D56\u4E3B\u53E5\u3002</li><li><strong>Although he is rich</strong>... \uFF08\u5C3D\u7BA1\u4ED6\u5F88\u6709\u94B1\u2026\u2026\uFF09 -&gt; \u8FD9\u53E5\u8BDD\u6CA1\u8BF4\u5B8C\uFF0C\u542C\u7740\u5F88\u5947\u602A\u3002</li><li><strong>Although he is rich, he is not happy.</strong> \uFF08\u5C3D\u7BA1\u4ED6\u5F88\u6709\u94B1\uFF0C\u4F46\u4ED6\u5E76\u4E0D\u5FEB\u4E50\u3002\uFF09 -&gt; \u8FD9\u624D\u662F\u4E00\u4E2A\u5B8C\u6574\u7684\u53E5\u5B50\u3002</li></ul><hr><h4 id="\u5BB6\u65CF\u4E09-\u5173\u8054\u8FDE\u8BCD\u2014\u2014\u603B\u662F-\u6210\u53CC\u6210\u5BF9-\u51FA\u73B0" tabindex="-1"><a class="header-anchor" href="#\u5BB6\u65CF\u4E09-\u5173\u8054\u8FDE\u8BCD\u2014\u2014\u603B\u662F-\u6210\u53CC\u6210\u5BF9-\u51FA\u73B0" aria-hidden="true">#</a> <strong>\u5BB6\u65CF\u4E09\uFF1A\u5173\u8054\u8FDE\u8BCD\u2014\u2014\u603B\u662F\u201C\u6210\u53CC\u6210\u5BF9\u201D\u51FA\u73B0</strong></h4><p>\u8FD9\u4E9B\u8FDE\u8BCD\u50CF\u4E00\u5BF9\u5F62\u5F71\u4E0D\u79BB\u7684\u597D\u670B\u53CB\uFF0C\u5FC5\u987B\u540C\u65F6\u4F7F\u7528\uFF0C\u7528\u6765\u8FDE\u63A5\u4E24\u4E2A\u5E73\u884C\u7684\u7ED3\u6784\uFF0C\u5F62\u6210\u5F3A\u70C8\u7684\u5BF9\u6BD4\u6216\u9009\u62E9\u5173\u7CFB\u3002</p><table><thead><tr><th style="text-align:left;">\u5173\u8054\u8FDE\u8BCD</th><th style="text-align:left;">\u7528\u6CD5</th><th style="text-align:left;">\u4F8B\u53E5</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>both...and...</strong></td><td style="text-align:left;">...\u548C...\u90FD\uFF08\u4E24\u8005\u90FD\uFF09</td><td style="text-align:left;">She is <strong>both</strong> smart <strong>and</strong> kind. \uFF08\u5979\u65E2\u806A\u660E\u53C8\u5584\u826F\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>either...or...</strong></td><td style="text-align:left;">\u8981\u4E48...\u8981\u4E48...\uFF08\u4E8C\u9009\u4E00\uFF09</td><td style="text-align:left;"><strong>Either</strong> you leave, <strong>or</strong> I will call the police. \uFF08\u8981\u4E48\u4F60\u79BB\u5F00\uFF0C\u8981\u4E48\u6211\u53EB\u8B66\u5BDF\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>neither...nor...</strong></td><td style="text-align:left;">\u65E2\u4E0D...\u4E5F\u4E0D...\uFF08\u4E24\u8005\u90FD\u4E0D\uFF09</td><td style="text-align:left;">He <strong>neither</strong> smokes <strong>nor</strong> drinks. \uFF08\u4ED6\u65E2\u4E0D\u62BD\u70DF\u4E5F\u4E0D\u559D\u9152\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>not only...but also...</strong></td><td style="text-align:left;">\u4E0D\u4EC5...\u800C\u4E14...</td><td style="text-align:left;">She <strong>not only</strong> sings <strong>but also</strong> dances. \uFF08\u5979\u4E0D\u4EC5\u4F1A\u5531\u6B4C\uFF0C\u8FD8\u4F1A\u8DF3\u821E\u3002\uFF09</td></tr></tbody></table><p><strong>\u4F7F\u7528\u5173\u8054\u8FDE\u8BCD\u7684\u5173\u952E</strong>\uFF1A\u786E\u4FDD\u5B83\u4EEC\u8FDE\u63A5\u7684\u4E24\u4E2A\u90E8\u5206\u5728\u8BED\u6CD5\u7ED3\u6784\u4E0A\u662F\u5BF9\u7B49\u7684\uFF08\u5373\u201C\u5E73\u884C\u7ED3\u6784\u201D\uFF09\u3002</p><ul><li>\u6B63\u786E\uFF1AShe likes <strong>reading</strong> and <strong>writing</strong>. \uFF08\u5979\u559C\u6B22\u9605\u8BFB\u548C\u5199\u4F5C\u3002\uFF09- \u8FDE\u63A5\u4E24\u4E2A\u52A8\u540D\u8BCD\u3002</li><li>\u9519\u8BEF\uFF1A<s>She likes <strong>to read</strong> and <strong>writing</strong>.</s> - \u4E00\u4E2A\u662F\u4E0D\u5B9A\u5F0F\uFF0C\u4E00\u4E2A\u662F\u52A8\u540D\u8BCD\uFF0C\u7ED3\u6784\u4E0D\u5E73\u8861\u3002</li></ul><hr><h3 id="\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u8FDE\u8BCD-vs-\u526F\u8BCD-\u5982-however-therefore" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u8FDE\u8BCD-vs-\u526F\u8BCD-\u5982-however-therefore" aria-hidden="true">#</a> <strong>\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192\uFF1A\u8FDE\u8BCD vs. \u526F\u8BCD\uFF08\u5982 however, therefore\uFF09</strong></h3><p>\u8FD9\u662F\u6700\u9AD8\u9891\u7684\u9519\u8BEF\u4E4B\u4E00\uFF01\u50CF <code>however</code>\uFF08\u7136\u800C\uFF09, <code>therefore</code>\uFF08\u56E0\u6B64\uFF09, <code>moreover</code>\uFF08\u800C\u4E14\uFF09\u8FD9\u6837\u7684\u8BCD\uFF0C\u5B83\u4EEC\u8868\u793A\u903B\u8F91\u5173\u7CFB\uFF0C\u4F46<strong>\u4E0D\u662F</strong>\u8FDE\u8BCD\uFF01\u5B83\u4EEC\u662F<strong>\u8FDE\u63A5\u526F\u8BCD</strong>\u3002</p><p><strong>\u533A\u522B\u5728\u4E8E\uFF1A</strong></p><ul><li><strong>\u8FDE\u8BCD</strong> \u53EF\u4EE5\u76F4\u63A5\u8FDE\u63A5\u4E24\u4E2A\u53E5\u5B50\u3002</li><li><strong>\u8FDE\u63A5\u526F\u8BCD</strong> \u4E0D\u80FD\u76F4\u63A5\u8FDE\u63A5\u4E24\u4E2A\u53E5\u5B50\uFF0C\u5FC5\u987B\u7528<strong>\u5206\u53F7\uFF08\uFF1B\uFF09</strong> \u6216<strong>\u53E5\u53F7\uFF08.\uFF09</strong> \u6765\u9694\u5F00\u4E24\u4E2A\u53E5\u5B50\u3002</li></ul><table><thead><tr><th style="text-align:left;">\u6B63\u786E\u7528\u6CD5</th><th style="text-align:left;">\u9519\u8BEF\u7528\u6CD5</th></tr></thead><tbody><tr><td style="text-align:left;">I was tired; <strong>however</strong>, I finished the work. <br> I was tired. <strong>However</strong>, I finished the work.</td><td style="text-align:left;"><s>I was tired, <strong>however</strong>, I finished the work.</s></td></tr><tr><td style="text-align:left;">\u6211\u7D2F\u4E86\uFF1B<strong>\u7136\u800C</strong>\uFF0C\u6211\u5B8C\u6210\u4E86\u5DE5\u4F5C\u3002 <br> \u6211\u7D2F\u4E86\u3002<strong>\u7136\u800C</strong>\uFF0C\u6211\u5B8C\u6210\u4E86\u5DE5\u4F5C\u3002</td><td style="text-align:left;"><s>\u6211\u7D2F\u4E86\uFF0C\u7136\u800C\uFF0C\u6211\u5B8C\u6210\u4E86\u5DE5\u4F5C\u3002</s></td></tr></tbody></table><hr><h3 id="\u603B\u7ED3\u4E0E\u5FC3\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3\u4E0E\u5FC3\u6CD5" aria-hidden="true">#</a> <strong>\u603B\u7ED3\u4E0E\u5FC3\u6CD5</strong></h3><ol><li><strong>\u8FDE\u8BCD (Conjunction)</strong> \u662F\u8BED\u8A00\u7684\u201C\u80F6\u6C34\u201D\u548C\u201C\u6865\u6881\u201D\uFF0C\u8D1F\u8D23\u8FDE\u63A5\u5E76\u8868\u660E\u903B\u8F91\u3002</li><li><strong>\u4E09\u5927\u5BB6\u65CF</strong>\uFF1A <ul><li><strong>\u5E76\u5217\u8FDE\u8BCD (FANBOYS)</strong>\uFF1A\u8FDE\u63A5\u5E73\u7B49\u5355\u4F4D\uFF0C\u8FDE\u53E5\u5B50\u65F6\u524D\u52A0\u9017\u53F7\u3002</li><li><strong>\u4ECE\u5C5E\u8FDE\u8BCD (because, if...)</strong>\uFF1A\u5F15\u5BFC\u4ECE\u53E5\uFF0C\u6784\u6210\u4E3B\u4ECE\u590D\u5408\u53E5\u3002</li><li><strong>\u5173\u8054\u8FDE\u8BCD (both...and...)</strong>\uFF1A\u6210\u5BF9\u4F7F\u7528\uFF0C\u8FDE\u63A5\u5E73\u884C\u7ED3\u6784\u3002</li></ul></li><li><strong>\u4E25\u9632\u6B7B\u5B88</strong>\uFF1A\u533A\u5206\u771F\u6B63\u7684\u8FDE\u8BCD\u548C\u201C\u8FDE\u63A5\u526F\u8BCD\u201D\uFF08however, therefore\u7B49\uFF09\uFF0C\u540E\u8005\u4E0D\u80FD\u7528\u9017\u53F7\u76F4\u63A5\u8FDE\u63A5\u4E24\u4E2A\u53E5\u5B50\u3002</li></ol><p>\u638C\u63E1\u4E86\u8FDE\u8BCD\uFF0C\u4F60\u7684\u82F1\u8BED\u8868\u8FBE\u5C31\u4E0D\u518D\u662F\u96F6\u6563\u7684\u77ED\u53E5\uFF0C\u800C\u662F\u903B\u8F91\u6E05\u6670\u3001\u5C42\u6B21\u5206\u660E\u7684\u590D\u5408\u53E5\u548C\u6BB5\u843D\u4E86\uFF01\u63A5\u4E0B\u6765\u60F3\u4E86\u89E3\u54EA\u4E2A\u8BED\u6CD5\u70B9\uFF0C\u6BD4\u5982\u66F4\u6DF1\u5165\u5730\u5B66\u4E60\u201C\u72B6\u8BED\u4ECE\u53E5\u201D\uFF0C\u6216\u8005\u8F6C\u5411\u201C\u53E5\u5B50\u7ED3\u6784\u5206\u6790\u201D\uFF1F\u6211\u968F\u65F6\u5728\u6B64\u7B49\u5019\u3002</p>',43),o=[l];function g(i,d){return e(),r("div",null,o)}var h=t(s,[["render",g],["__file","index.html.vue"]]);export{h as default};
