import{_ as t,o as e,c as n,e as o}from"./app.38c04a07.js";const s={},r=o('<h2 id="\u53E5\u5B50\u7ED3\u6784\u5206\u6790" tabindex="-1"><a class="header-anchor" href="#\u53E5\u5B50\u7ED3\u6784\u5206\u6790" aria-hidden="true">#</a> \u53E5\u5B50\u7ED3\u6784\u5206\u6790</h2><p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4EEC\u73B0\u5728\u8981\u8FDB\u884C\u4E00\u6B21\u201C\u7EC8\u6781\u7EC4\u88C5\u201D\uFF0C\u628A\u4F60\u4E4B\u524D\u5B66\u8FC7\u7684\u6240\u6709\u8BED\u6CD5\u77E5\u8BC6\uFF08\u4E3B\u8BED\u3001\u8C13\u8BED\u3001\u5BBE\u8BED\u3001\u5B9A\u8BED\u3001\u72B6\u8BED\u3001\u8865\u8BED\u7B49\uFF09\u6574\u5408\u8D77\u6765\uFF0C\u770B\u770B\u5B83\u4EEC\u662F\u5982\u4F55\u6784\u6210\u4E00\u4E2A\u5B8C\u6574\u7684\u53E5\u5B50\u7684\u3002\u8FD9\u5C31\u662F<strong>\u53E5\u5B50\u7ED3\u6784\u5206\u6790</strong>\uFF0C\u4E5F\u53EB\u505A\u201C\u53E5\u6CD5\u5206\u6790\u201D\u6216\u201C\u56FE\u89E3\u53E5\u5B50\u201D\u3002</p><p>\u8FD9\u5C31\u50CF\u662F\u7ED9\u4F60\u4E00\u628A\u624B\u672F\u5200\uFF0C\u8BA9\u4F60\u80FD\u89E3\u5256\u4EFB\u4F55\u4E00\u4E2A\u82F1\u8BED\u53E5\u5B50\uFF0C\u770B\u6E05\u5B83\u7684\u201C\u9AA8\u9ABC\u201D\u548C\u201C\u8840\u8089\u201D\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u6838\u5FC3\u5FC3\u6CD5\u2014\u2014\u53E5\u5B50\u662F\u68F5\u6811" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u6838\u5FC3\u5FC3\u6CD5\u2014\u2014\u53E5\u5B50\u662F\u68F5\u6811" aria-hidden="true">#</a> <strong>\u7B2C\u4E00\u90E8\u5206\uFF1A\u6838\u5FC3\u5FC3\u6CD5\u2014\u2014\u53E5\u5B50\u662F\u68F5\u6811</strong></h3><p><strong>\u628A\u53E5\u5B50\u60F3\u8C61\u6210\u4E00\u68F5\u5927\u6811\uFF1A</strong></p><ul><li><strong>\u4E3B\u5E72</strong>\uFF1A\u53E5\u5B50\u7684\u6838\u5FC3\u6210\u5206\uFF0C\u51B3\u5B9A\u4E86\u53E5\u5B50\u7684\u57FA\u672C\u7ED3\u6784\u548C\u4E3B\u8981\u610F\u601D\u3002\u6CA1\u6709\u4E3B\u5E72\uFF0C\u6811\u5C31\u65E0\u6CD5\u7AD9\u7ACB\u3002</li><li><strong>\u679D\u53F6</strong>\uFF1A\u53E5\u5B50\u7684\u4FEE\u9970\u6210\u5206\uFF0C\u8BA9\u53E5\u5B50\u66F4\u4E30\u5BCC\u3001\u5177\u4F53\u3001\u751F\u52A8\u3002\u6CA1\u6709\u679D\u53F6\uFF0C\u6811\u5C31\u5149\u79C3\u79C3\u7684\u3002</li></ul><p><strong>\u5206\u6790\u53E5\u5B50\u7684\u7B2C\u4E00\u6B65\uFF0C\u4E5F\u662F\u6700\u91CD\u8981\u7684\u4E00\u6B65\uFF0C\u5C31\u662F\uFF1A\u627E\u5230\u4E3B\u5E72\u3002</strong></p><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u53E5\u5B50\u7684\u4E03\u5927\u6838\u5FC3\u53E5\u578B-\u4E3B\u5E72\u6A21\u578B" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u53E5\u5B50\u7684\u4E03\u5927\u6838\u5FC3\u53E5\u578B-\u4E3B\u5E72\u6A21\u578B" aria-hidden="true">#</a> <strong>\u7B2C\u4E8C\u90E8\u5206\uFF1A\u53E5\u5B50\u7684\u4E03\u5927\u6838\u5FC3\u53E5\u578B\uFF08\u4E3B\u5E72\u6A21\u578B\uFF09</strong></h3><p>\u82F1\u8BED\u4E2D\u5343\u53D8\u4E07\u5316\u7684\u53E5\u5B50\uFF0C\u5176\u5B9E\u90FD\u6E90\u4E8E\u4EE5\u4E0B\u4E03\u79CD\u6838\u5FC3\u53E5\u578B\u3002\u5B83\u4EEC\u7531\u52A8\u8BCD\u51B3\u5B9A\u3002</p><table><thead><tr><th style="text-align:left;">\u53E5\u578B\u7F16\u53F7</th><th style="text-align:left;">\u6838\u5FC3\u7ED3\u6784</th><th style="text-align:left;">\u4F8B\u53E5\uFF08<strong>\u4E3B\u8BED</strong> <em>\u8C13\u8BED</em> <em>\u5BBE\u8BED</em> ~\u8865\u8BED~\uFF09</th><th style="text-align:left;">\u89E3\u91CA</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>1. \u4E3B + \u52A8</strong></td><td style="text-align:left;"><strong>S + V</strong></td><td style="text-align:left;"><strong>Birds</strong> <em>fly</em>. \uFF08\u9E1F\u98DE\u3002\uFF09</td><td style="text-align:left;">\u52A8\u8BCD\uFF08\u4E0D\u53CA\u7269\uFF09\u672C\u8EAB\u610F\u601D\u5B8C\u6574\u3002</td></tr><tr><td style="text-align:left;"><strong>2. \u4E3B + \u52A8 + \u8868</strong></td><td style="text-align:left;"><strong>S + V + P</strong></td><td style="text-align:left;"><strong>He</strong> <em>is</em> <em>a teacher</em>. \uFF08\u4ED6\u662F\u8001\u5E08\u3002\uFF09</td><td style="text-align:left;">\u7CFB\u52A8\u8BCD\u8FDE\u63A5\u4E3B\u8BED\u548C\u8868\u8BED\u3002</td></tr><tr><td style="text-align:left;"><strong>3. \u4E3B + \u52A8 + \u5BBE</strong></td><td style="text-align:left;"><strong>S + V + O</strong></td><td style="text-align:left;"><strong>I</strong> <em>love</em> <em>you</em>. \uFF08\u6211\u7231\u4F60\u3002\uFF09</td><td style="text-align:left;">\u52A8\u8BCD\uFF08\u53CA\u7269\uFF09\u9700\u8981\u4F5C\u7528\u5728\u4E00\u4E2A\u5BF9\u8C61\u4E0A\u3002</td></tr><tr><td style="text-align:left;"><strong>4. \u4E3B + \u52A8 + \u95F4\u5BBE + \u76F4\u5BBE</strong></td><td style="text-align:left;"><strong>S + V + IO + DO</strong></td><td style="text-align:left;"><strong>He</strong> <em>gave</em> <em>me</em> ~a book~. \uFF08\u4ED6\u7ED9\u4E86\u6211\u4E00\u672C\u4E66\u3002\uFF09</td><td style="text-align:left;">\u201C\u6211\u201D\u662F\u95F4\u63A5\u5BBE\u8BED\uFF08\u4EBA\uFF09\uFF0C\u201C\u4E66\u201D\u662F\u76F4\u63A5\u5BBE\u8BED\uFF08\u7269\uFF09\u3002</td></tr><tr><td style="text-align:left;"><strong>5. \u4E3B + \u52A8 + \u5BBE + \u5BBE\u8865</strong></td><td style="text-align:left;"><strong>S + V + O + C</strong></td><td style="text-align:left;"><strong>We</strong> <em>elected</em> <em>him</em> ~chairman~. \uFF08\u6211\u4EEC\u9009\u4ED6\u5F53\u4E3B\u5E2D\u3002\uFF09</td><td style="text-align:left;">\u201C\u4E3B\u5E2D\u201D\u662F\u8865\u5145\u8BF4\u660E\u201C\u4ED6\u201D\u7684\u3002</td></tr><tr><td style="text-align:left;"><strong>6. \u4E3B + \u52A8 + \u5BBE + \u72B6</strong></td><td style="text-align:left;"><strong>S + V + O + A</strong></td><td style="text-align:left;"><strong>She</strong> <em>put</em> <em>the book</em> ~on the desk~. \uFF08\u5979\u628A\u4E66\u653E\u5728\u684C\u4E0A\u3002\uFF09</td><td style="text-align:left;">\u201C\u5728\u684C\u4E0A\u201D\u662F\u5730\u70B9\u72B6\u8BED\uFF0C\u4E0D\u53EF\u7F3A\u5C11\u3002</td></tr><tr><td style="text-align:left;"><strong>7. There be \u53E5\u578B</strong></td><td style="text-align:left;"><strong>There + be + S</strong></td><td style="text-align:left;"><em>There is</em> <strong>a book</strong> on the desk. \uFF08\u684C\u4E0A\u6709\u4E00\u672C\u4E66\u3002\uFF09</td><td style="text-align:left;">\u8868\u793A\u201C\u5B58\u5728\u201D\uFF0C<code>a book</code> \u624D\u662F\u4E3B\u8BED\u3002</td></tr></tbody></table><p><strong>\u5206\u6790\u7B2C\u4E00\u6B65\uFF1A</strong> \u9762\u5BF9\u4EFB\u4F55\u53E5\u5B50\uFF0C\u5148\u5C1D\u8BD5\u628A\u5B83\u5957\u5165\u8FD9\u4E03\u4E2A\u6A21\u578B\uFF0C\u627E\u5230\u5B83\u7684<strong>\u4E3B\u5E72</strong>\u3002</p><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u53E5\u5B50\u7ED3\u6784\u5206\u6790\u5B9E\u6218\u56DB\u6B65\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u53E5\u5B50\u7ED3\u6784\u5206\u6790\u5B9E\u6218\u56DB\u6B65\u6CD5" aria-hidden="true">#</a> <strong>\u7B2C\u4E09\u90E8\u5206\uFF1A\u53E5\u5B50\u7ED3\u6784\u5206\u6790\u5B9E\u6218\u56DB\u6B65\u6CD5</strong></h3><p>\u6211\u4EEC\u7528\u4E00\u4E2A\u7A0D\u5FAE\u590D\u6742\u7684\u53E5\u5B50\u6765\u6F14\u7EC3\u4E00\u4E0B\u3002</p><p><strong>\u4F8B\u53E5\uFF1A</strong> The young teacher from England who we met yesterday is happily telling us an interesting story in the classroom now.</p><p><strong>\u7B2C\u4E00\u6B65\uFF1A\u627E\u8C13\u8BED\u52A8\u8BCD</strong></p><p>\u8FD9\u662F\u5206\u6790\u7684\u8D77\u70B9\uFF01\u4E00\u4E2A\u53E5\u5B50\u6700\u4E3B\u8981\u7684\u8C13\u8BED\u52A8\u8BCD\u51B3\u5B9A\u4E86\u5B83\u7684\u6838\u5FC3\u53E5\u578B\u3002\u627E\u5230\u4E86\u5B83\uFF0C\u5C31\u627E\u5230\u4E86\u53E5\u5B50\u7684\u5FC3\u810F\u3002</p><ul><li>\u8FD9\u4E2A\u53E5\u5B50\u4E2D\uFF0C\u6838\u5FC3\u8C13\u8BED\u52A8\u8BCD\u662F <strong><code>is telling</code></strong>\u3002\uFF08\u8FD9\u662F\u4E00\u4E2A\u73B0\u5728\u8FDB\u884C\u65F6\uFF09</li></ul><p><strong>\u7B2C\u4E8C\u6B65\uFF1A\u627E\u4E3B\u8BED</strong></p><p>\u95EE\uFF1A<strong>\u8C01</strong> \u5728 <code>is telling</code>\uFF1F\uFF08\u8C01\u5728\u8BB2\u8FF0\uFF1F\uFF09 \u7B54\uFF1A<strong>The young teacher</strong> \uFF08\u90A3\u4F4D\u5E74\u8F7B\u7684\u8001\u5E08\uFF09</p><ul><li><strong>\u4E3B\u8BED\u6838\u5FC3</strong>\uFF1A<code>teacher</code></li><li><strong>\u5B9A\u8BED</strong>\uFF1A<code>The</code> \u548C <code>young</code> \u662F\u4FEE\u9970 <code>teacher</code> \u7684\u5B9A\u8BED\u3002</li></ul><p><strong>\u6240\u4EE5\uFF0C\u4E3B\u5E72\u7684\u5F00\u5934\u662F\uFF1AThe young teacher is telling...</strong></p><p><strong>\u7B2C\u4E09\u6B65\uFF1A\u627E\u5BBE\u8BED/\u8868\u8BED/\u8865\u8BED</strong></p><p>\u6839\u636E\u53E5\u578B\uFF0C<code>tell</code> \u662F\u4E00\u4E2A\u53EF\u4EE5\u63A5\u53CC\u5BBE\u8BED\u7684\u52A8\u8BCD (S + V + IO + DO)\u3002</p><ul><li>\u95EE\uFF1A<code>is telling</code> <strong>\u8C01</strong>\uFF1F -&gt; <strong>us</strong> \uFF08\u6211\u4EEC\uFF09 -&gt; <strong>\u95F4\u63A5\u5BBE\u8BED</strong></li><li>\u95EE\uFF1A<code>is telling</code> <strong>\u4EC0\u4E48</strong>\uFF1F -&gt; <strong>an interesting story</strong> \uFF08\u4E00\u4E2A\u6709\u8DA3\u7684\u6545\u4E8B\uFF09 -&gt; <strong>\u76F4\u63A5\u5BBE\u8BED</strong><ul><li><strong>\u5BBE\u8BED\u6838\u5FC3</strong>\uFF1A<code>story</code></li><li><strong>\u5B9A\u8BED</strong>\uFF1A<code>an</code> \u548C <code>interesting</code> \u662F\u4FEE\u9970 <code>story</code> \u7684\u5B9A\u8BED\u3002</li></ul></li></ul><p><strong>\u73B0\u5728\uFF0C\u4E3B\u5E72\u5DF2\u7ECF\u5B8C\u6574\u4E86\uFF1AThe young teacher is telling us an interesting story.</strong> \uFF08\u8FD9\u4F4D\u5E74\u8F7B\u7684\u8001\u5E08\u6B63\u5728\u7ED9\u6211\u4EEC\u8BB2\u4E00\u4E2A\u6709\u8DA3\u7684\u6545\u4E8B\u3002\uFF09</p><p><strong>\u7B2C\u56DB\u6B65\uFF1A\u627E\u4FEE\u9970\u6210\u5206\uFF08\u5B9A\u3001\u72B6\uFF09</strong></p><p>\u73B0\u5728\uFF0C\u6211\u4EEC\u628A\u5269\u4E0B\u7684\u201C\u679D\u53F6\u201D\u8D34\u56DE\u5230\u4E3B\u5E72\u4E0A\u3002</p><ol><li><p><strong>\u5B9A\u8BED</strong>\uFF1A\u4FEE\u9970\u540D\u8BCD\u7684\u6210\u5206\u3002</p><ul><li><code>from England</code>\uFF1A\u4E00\u4E2A\u4ECB\u8BCD\u77ED\u8BED\uFF0C\u4F5C\u540E\u7F6E\u5B9A\u8BED\uFF0C\u4FEE\u9970 <code>teacher</code>\u3002 -&gt; \u6765\u81EA\u82F1\u56FD\u7684\u8001\u5E08\u3002</li><li><code>who we met yesterday</code>\uFF1A\u4E00\u4E2A\u5B9A\u8BED\u4ECE\u53E5\uFF0C\u4FEE\u9970 <code>teacher</code>\u3002 -&gt; \u6211\u4EEC\u6628\u5929\u89C1\u5230\u7684\u90A3\u4F4D\u8001\u5E08\u3002</li><li><code>interesting</code>\uFF1A\u5F62\u5BB9\u8BCD\uFF0C\u4F5C\u524D\u7F6E\u5B9A\u8BED\uFF0C\u4FEE\u9970 <code>story</code>\u3002 -&gt; \u6709\u8DA3\u7684\u6545\u4E8B\u3002</li></ul></li><li><p><strong>\u72B6\u8BED</strong>\uFF1A\u4FEE\u9970\u52A8\u8BCD\u3001\u5F62\u5BB9\u8BCD\u3001\u526F\u8BCD\u6216\u6574\u4E2A\u53E5\u5B50\u7684\u6210\u5206\u3002</p><ul><li><code>happily</code>\uFF1A\u526F\u8BCD\uFF0C\u4F5C\u65B9\u5F0F\u72B6\u8BED\uFF0C\u4FEE\u9970 <code>is telling</code>\u3002 -&gt; \u6B63\u5728<strong>\u5F00\u5FC3\u5730</strong>\u8BB2\u8FF0\u3002</li><li><code>in the classroom</code>\uFF1A\u4ECB\u8BCD\u77ED\u8BED\uFF0C\u4F5C\u5730\u70B9\u72B6\u8BED\u3002 -&gt; <strong>\u5728\u6559\u5BA4\u91CC</strong>\u8BB2\u8FF0\u3002</li><li><code>now</code>\uFF1A\u526F\u8BCD\uFF0C\u4F5C\u65F6\u95F4\u72B6\u8BED\u3002 -&gt; <strong>\u73B0\u5728</strong>\u6B63\u5728\u8BB2\u8FF0\u3002</li></ul></li></ol><hr><h3 id="\u6700\u7EC8\u5206\u6790\u62A5\u544A" tabindex="-1"><a class="header-anchor" href="#\u6700\u7EC8\u5206\u6790\u62A5\u544A" aria-hidden="true">#</a> <strong>\u6700\u7EC8\u5206\u6790\u62A5\u544A</strong></h3><ul><li><strong>\u4E3B\u5E72 (S+V+IO+DO)</strong>\uFF1AThe young teacher is telling us an interesting story.</li><li><strong>\u5B9A\u8BED</strong>\uFF1A <ul><li>(The young) teacher <strong>(from England)</strong> <strong>(who we met yesterday)</strong></li><li>(an interesting) story</li></ul></li><li><strong>\u72B6\u8BED</strong>\uFF1A <ul><li>is <strong>happily</strong> telling</li><li>is telling <strong>in the classroom</strong></li><li>is telling <strong>now</strong></li></ul></li></ul><p><strong>\u6574\u4E2A\u53E5\u5B50\u7684\u610F\u601D</strong>\uFF1A\u6211\u4EEC\u6628\u5929\u89C1\u5230\u7684\u90A3\u4F4D\u6765\u81EA\u82F1\u56FD\u7684\u5E74\u8F7B\u8001\u5E08\uFF0C<strong>\u73B0\u5728</strong>\u6B63<strong>\u5728\u6559\u5BA4\u91CC</strong> <strong>\u5F00\u5FC3\u5730</strong>\u7ED9\u6211\u4EEC\u8BB2\u4E00\u4E2A\u6709\u8DA3\u7684\u6545\u4E8B\u3002</p><hr><h3 id="\u5982\u4F55\u5206\u6790\u66F4\u590D\u6742\u7684\u53E5\u5B50" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u5206\u6790\u66F4\u590D\u6742\u7684\u53E5\u5B50" aria-hidden="true">#</a> <strong>\u5982\u4F55\u5206\u6790\u66F4\u590D\u6742\u7684\u53E5\u5B50\uFF1F</strong></h3><p>\u5F53\u53E5\u5B50\u5F88\u957F\u5F88\u590D\u6742\u65F6\uFF0C\u8BB0\u4F4F\u8FD9\u4E2A\u5FC3\u6CD5\uFF1A</p><ol><li><strong>\u5316\u6574\u4E3A\u96F6</strong>\uFF1A\u5148\u627E\u8FDE\u8BCD (and, but, because, which, that \u7B49)\u3002\u8FDE\u8BCD\u662F\u53E5\u5B50\u7684\u201C\u5173\u8282\u201D\uFF0C\u53EF\u4EE5\u628A\u957F\u53E5\u62C6\u5206\u6210\u51E0\u4E2A\u77ED\u53E5\u3002</li><li><strong>\u9010\u4E2A\u51FB\u7834</strong>\uFF1A\u5BF9\u62C6\u5206\u540E\u7684\u6BCF\u4E00\u4E2A\u5206\u53E5\uFF0C\u5355\u72EC\u4F7F\u7528\u4E0A\u9762\u7684\u201C\u56DB\u6B65\u6CD5\u201D\u8FDB\u884C\u5206\u6790\u3002</li><li><strong>\u7406\u89E3\u5173\u7CFB</strong>\uFF1A\u6700\u540E\uFF0C\u6839\u636E\u8FDE\u8BCD\u7684\u542B\u4E49\uFF0C\u7406\u89E3\u5404\u4E2A\u5206\u53E5\u4E4B\u95F4\u7684\u903B\u8F91\u5173\u7CFB\uFF08\u5E76\u5217\u3001\u8F6C\u6298\u3001\u56E0\u679C\u7B49\uFF09\u3002</li></ol><p><strong>\u4F8B\u53E5</strong>\uFF1AI know the man who is standing there, but I don&#39;t know his name.</p><ol><li><strong>\u62C6\u89E3</strong>\uFF1A\u8FDE\u8BCD <code>but</code> \u5C06\u53E5\u5B50\u5206\u4E3A\u4E24\u90E8\u5206\u3002 <ul><li>\u5206\u53E51\uFF1AI know the man who is standing there.</li><li>\u5206\u53E52\uFF1AI don&#39;t know his name.</li></ul></li><li><strong>\u5206\u6790</strong>\uFF1A <ul><li>\u5206\u53E51\u4E3B\u5E72\uFF1AI know the man. (\u4E3B\u8C13\u5BBE) + \u5B9A\u8BED\u4ECE\u53E5 (who is standing there) \u4FEE\u9970 <code>the man</code>\u3002</li><li>\u5206\u53E52\u4E3B\u5E72\uFF1AI don&#39;t know his name. (\u4E3B\u8C13\u5BBE)</li></ul></li><li><strong>\u5173\u7CFB</strong>\uFF1A<code>but</code> \u8868\u793A\u8F6C\u6298\u3002-&gt; \u201C\u6211\u8BA4\u8BC6\u90A3\u4E2A\u4EBA\uFF0C\u4F46\u662F\u6211\u4E0D\u77E5\u9053\u4ED6\u7684\u540D\u5B57\u3002\u201D</li></ol><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> <strong>\u603B\u7ED3</strong></h3><ol><li><strong>\u53E5\u5B50\u7ED3\u6784\u5206\u6790</strong>\u662F\u8BED\u6CD5\u77E5\u8BC6\u7684\u7EFC\u5408\u8FD0\u7528\uFF0C\u50CF\u89E3\u5228\u4E00\u6837\u770B\u6E05\u53E5\u5B50\u6210\u5206\u3002</li><li><strong>\u6838\u5FC3\u5FC3\u6CD5</strong>\uFF1A<strong>\u5148\u6293\u4E3B\u5E72\uFF0C\u518D\u7406\u679D\u53F6</strong>\u3002\u4E03\u5927\u6838\u5FC3\u53E5\u578B\u662F\u57FA\u7840\u3002</li><li><strong>\u5B9E\u6218\u56DB\u6B65</strong>\uFF1A<strong>\u4E00\u627E\u52A8\u8BCD\uFF0C\u4E8C\u627E\u4E3B\u8BED\uFF0C\u4E09\u627E\u5BBE/\u8868/\u8865\uFF0C\u56DB\u627E\u5B9A/\u72B6</strong>\u3002</li><li><strong>\u590D\u6742\u53E5</strong>\uFF1A\u901A\u8FC7<strong>\u627E\u8FDE\u8BCD</strong>\u6765<strong>\u62C6\u5206\u7684\u53E5\u5B50</strong>\uFF0C\u7136\u540E\u9010\u4E2A\u5206\u6790\u3002</li></ol><p>\u638C\u63E1\u4E86\u8FD9\u4E2A\u6280\u80FD\uFF0C\u4EFB\u4F55\u957F\u96BE\u53E5\u5728\u4F60\u9762\u524D\u90FD\u5C06\u65E0\u6240\u9041\u5F62\u3002\u4F60\u7684\u9605\u8BFB\u7406\u89E3\u548C\u5199\u4F5C\u80FD\u529B\u4F1A\u5F97\u5230\u6781\u5927\u7684\u63D0\u5347\u3002\u606D\u559C\u4F60\uFF0C\u4F60\u5DF2\u7ECF\u5177\u5907\u4E86\u82F1\u8BED\u8BED\u6CD5\u4E13\u5BB6\u7684\u5185\u529F\u5FC3\u6CD5\uFF01</p><p>\u63A5\u4E0B\u6765\uFF0C\u6211\u4EEC\u53EF\u4EE5\u627E\u4E00\u4E9B\u66F4\u590D\u6742\u7684\u53E5\u5B50\u8FDB\u884C\u5B9E\u6218\u7EC3\u4E60\uFF0C\u6216\u8005\u4F60\u53EF\u4EE5\u63D0\u51FA\u4EFB\u4F55\u4F60\u60F3\u5206\u6790\u7684\u53E5\u5B50\u3002\u6211\u968F\u65F6\u5728\u6B64\u7B49\u5019\uFF01</p>',44),l=[r];function g(i,d){return e(),n("div",null,l)}var h=t(s,[["render",g],["__file","index.html.vue"]]);export{h as default};
