import{_ as t,o as e,c as r,e as n}from"./app.0b533659.js";const o={},s=n('<h2 id="\u52A9\u52A8\u8BCD-auxiliary-verb" tabindex="-1"><a class="header-anchor" href="#\u52A9\u52A8\u8BCD-auxiliary-verb" aria-hidden="true">#</a> \u52A9\u52A8\u8BCD Auxiliary Verb</h2><p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4EEC\u4ECA\u5929\u6765\u62C6\u89E3\u82F1\u8BED\u53E5\u5B50\u91CC\u7684\u201C\u5E55\u540E\u82F1\u96C4\u201D\u2014\u2014<strong>\u52A9\u52A8\u8BCD</strong>\u3002\u5B83\u4E0D\u50CF\u4E3B\u8981\u52A8\u8BCD\u90A3\u6837\u5149\u5F69\u593A\u76EE\uFF0C\u4F46\u6CA1\u6709\u5B83\uFF0C\u5F88\u591A\u91CD\u8981\u7684\u53E5\u5B50\u7ED3\u6784\u6839\u672C\u5C31\u642D\u5EFA\u4E0D\u8D77\u6765\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u52A9\u52A8\u8BCD-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u52A9\u52A8\u8BCD-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u52A9\u52A8\u8BCD\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p>\u60F3\u8C61\u4E00\u4E0B\uFF0C\u4F60\u8981\u5EFA\u9020\u4E00\u4E2A\u53E5\u5B50\u623F\u5B50\uFF1A</p><ul><li><strong>\u4E3B\u8981\u52A8\u8BCD</strong> \u662F\u623F\u5B50\u7684<strong>\u4E3B\u4F53\u7ED3\u6784</strong>\uFF08\u6BD4\u5982\uFF1A\u5403\u3001\u5B66\u4E60\u3001\u53BB\uFF09\u3002\u5B83\u8868\u8FBE\u4E86\u6838\u5FC3\u52A8\u4F5C\u3002</li><li><strong>\u52A9\u52A8\u8BCD</strong> \u5C31\u662F\u5EFA\u9020\u5E08\u624B\u91CC\u7684<strong>\u5DE5\u5177\u548C\u811A\u624B\u67B6</strong>\uFF0C\u6BD4\u5982\u6C34\u5E73\u4EEA\u3001\u87BA\u4E1D\u5200\u3002\u5B83\u4EEC\u672C\u8EAB\u4E0D\u662F\u623F\u5B50\uFF0C\u4F46\u80FD\u5E2E\u4F60\u628A\u623F\u5B50\u5EFA\u5F97\u66F4\u590D\u6742\u3001\u66F4\u7CBE\u786E\uFF08\u6BD4\u5982\u8868\u8FBE\u65F6\u6001\u3001\u7591\u95EE\u3001\u5426\u5B9A\uFF09\u3002</li></ul><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u52A9\u52A8\u8BCD\u672C\u8EAB\u6CA1\u6709\u5B8C\u6574\u7684\u8BCD\u4E49\uFF0C\u4E0D\u80FD\u5355\u72EC\u4F5C\u8C13\u8BED\uFF0C\u5B83\u7684\u552F\u4E00\u5DE5\u4F5C\u5C31\u662F\u201C\u5E2E\u52A9\u201D\u4E3B\u8981\u52A8\u8BCD\u6765\u6784\u6210\u7591\u95EE\u53E5\u3001\u5426\u5B9A\u53E5\u3001\u65F6\u6001\u3001\u8BED\u6001\u7B49\u8BED\u6CD5\u7ED3\u6784\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong></p><ul><li><strong>\u4E3B\u8981\u52A8\u8BCD</strong> \u662F\u7535\u5F71\u91CC\u7684<strong>\u4E3B\u89D2</strong>\uFF0C\u8D1F\u8D23\u6838\u5FC3\u8868\u6F14\u3002</li><li><strong>\u52A9\u52A8\u8BCD</strong> \u662F<strong>\u5BFC\u6F14\u3001\u706F\u5149\u5E08\u548C\u526A\u8F91\u5E08</strong>\u3002\u4ED6\u4EEC\u81EA\u5DF1\u4E0D\u6F14\u620F\uFF0C\u4F46\u8D1F\u8D23\u544A\u8BC9\u4E3B\u89D2\u201C\u4EC0\u4E48\u65F6\u5019\u6F14\uFF08\u65F6\u6001\uFF09\u201D\u3001\u201C\u600E\u4E48\u6F14\uFF08\u8BED\u6001\uFF09\u201D\uFF0C\u5E76\u5904\u7406\u5404\u79CD\u5E55\u540E\u5DE5\u4F5C\uFF08\u6784\u6210\u7591\u95EE\u548C\u5426\u5B9A\uFF09\u3002</li></ul><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u52A9\u52A8\u8BCD-\u540D\u79F0\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u52A9\u52A8\u8BCD-\u540D\u79F0\u7684\u7531\u6765" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u4E3A\u4EC0\u4E48\u53EB\u201C\u52A9\u52A8\u8BCD\u201D\uFF1F\u540D\u79F0\u7684\u7531\u6765</h3><ol><li><p><strong>\u4E2D\u6587\u540D\u201C\u52A9\u52A8\u8BCD\u201D</strong>\uFF1A</p><ul><li>\u201C\u52A9\u201D\u5C31\u662F\u201C\u5E2E\u52A9\u201D\u7684\u610F\u601D\u3002</li><li>\u6240\u4EE5\u201C\u52A9\u52A8\u8BCD\u201D\u5C31\u662F<strong>\u5E2E\u52A9\u4E3B\u8981\u52A8\u8BCD\u5B8C\u6210\u4EFB\u52A1\u7684\u52A8\u8BCD</strong>\u3002\u8FD9\u4E2A\u540D\u5B57\u76F4\u767D\u5730\u8BF4\u660E\u4E86\u5B83\u7684\u529F\u80FD\u3002</li></ul></li><li><p><strong>\u82F1\u6587\u540D\u201CAuxiliary Verb\u201D</strong>\uFF1A</p><ul><li>\u8BFB\u97F3\uFF1A/\u0254\u02D0\u0261\u02C8z\u026Ali\u0259ri v\u025C\u02D0rb/</li><li>\u8FD9\u4E2A\u8BCD\u6765\u6E90\u4E8E\u62C9\u4E01\u8BED <code>auxilium</code>\uFF0C\u610F\u601D\u5C31\u662F\u201C<strong>\u5E2E\u52A9\u3001\u63F4\u52A9</strong>\u201D\u3002</li><li>\u6240\u4EE5 <code>Auxiliary Verb</code> \u5C31\u662F <strong>\u201C\u8D77\u8F85\u52A9\u4F5C\u7528\u7684\u52A8\u8BCD\u201D</strong> \u3002\u5B83\u6709\u65F6\u4E5F\u88AB\u7B80\u79F0\u4E3A <strong>\u201CHelping Verb\u201D</strong>\uFF0C\u610F\u601D\u5B8C\u5168\u4E00\u6837\u3002</li></ul></li></ol><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u52A9\u52A8\u8BCD\u5BB6\u65CF\u6709\u54EA\u4E9B\u6210\u5458-\u6838\u5FC3\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u52A9\u52A8\u8BCD\u5BB6\u65CF\u6709\u54EA\u4E9B\u6210\u5458-\u6838\u5FC3\u5185\u5BB9" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u52A9\u52A8\u8BCD\u5BB6\u65CF\u6709\u54EA\u4E9B\u6210\u5458\uFF1F\uFF08\u6838\u5FC3\u5185\u5BB9\uFF09</h3><p>\u52A9\u52A8\u8BCD\u5BB6\u65CF\u6210\u5458\u4E0D\u591A\uFF0C\u4F46\u4E2A\u4E2A\u90FD\u662F\u52B3\u6A21\u3002\u5B83\u4EEC\u53EF\u4EE5\u5206\u4E3A<strong>\u57FA\u672C\u52A9\u52A8\u8BCD</strong>\u548C<strong>\u60C5\u6001\u52A9\u52A8\u8BCD</strong>\u4E24\u5927\u7C7B\u3002\u6211\u4EEC\u4ECA\u5929\u805A\u7126<strong>\u57FA\u672C\u52A9\u52A8\u8BCD</strong>\uFF0C\u5B83\u4EEC\u53EA\u6709\u4E09\u4E2A\uFF1A<strong>be, do, have</strong>\u3002</p><p>\u60C5\u6001\u52A9\u52A8\u8BCD\uFF08can, may, must\u7B49\uFF09\u6211\u4EEC\u4E0A\u6B21\u5DF2\u7ECF\u5B66\u8FC7\u4E86\uFF0C\u5B83\u4EEC\u65E2\u6709\u8BCD\u6C47\u610F\u4E49\uFF0C\u4E5F\u8D77\u8F85\u52A9\u4F5C\u7528\u3002</p><h4 id="\u4E09\u5927\u57FA\u672C\u52A9\u52A8\u8BCD\u7684\u4F7F\u547D" tabindex="-1"><a class="header-anchor" href="#\u4E09\u5927\u57FA\u672C\u52A9\u52A8\u8BCD\u7684\u4F7F\u547D" aria-hidden="true">#</a> <strong>\u4E09\u5927\u57FA\u672C\u52A9\u52A8\u8BCD\u7684\u4F7F\u547D</strong></h4><table><thead><tr><th style="text-align:left;">\u52A9\u52A8\u8BCD</th><th style="text-align:left;">\u6838\u5FC3\u4F7F\u547D\uFF08\u5E72\u4EC0\u4E48\u6D3B\uFF09</th><th style="text-align:left;">\u8BED\u6CD5\u5F62\u6001\u548C\u610F\u601D</th><th style="text-align:left;">\u4F8B\u53E5\uFF08\u52A9\u52A8\u8BCD<strong>\u52A0\u7C97</strong>\uFF0C\u4E3B\u8981\u52A8\u8BCD<em>\u659C\u4F53</em>\uFF09</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>Do</strong></td><td style="text-align:left;"><strong>1. \u6784\u6210\u3010\u4E00\u822C\u73B0\u5728\u65F6/\u8FC7\u53BB\u65F6\u3011\u7684\u7591\u95EE\u53E5\u548C\u5426\u5B9A\u53E5\u3002</strong> <br>2. \u7528\u4E8E<strong>\u5F3A\u8C03</strong>\u52A8\u8BCD\u3002</td><td style="text-align:left;"><strong>do, does, did</strong> <br>\u672C\u8EAB\u65E0\u8BCD\u4E49\uFF0C\u7EAF\u7CB9\u662F\u8BED\u6CD5\u5DE5\u5177\u3002</td><td style="text-align:left;">1. <strong>Do</strong> you <em>like</em> it? \uFF08\u4F60\u559C\u6B22\u5B83\u5417\uFF1F\uFF09- <strong>\u6784\u6210\u7591\u95EE</strong> <br>2. He <strong>doesn\u2018t</strong> <em>know</em> that. \uFF08\u4ED6\u4E0D\u77E5\u9053\u90A3\u4EF6\u4E8B\u3002\uFF09- <strong>\u6784\u6210\u5426\u5B9A</strong> <br>3. I <strong>do</strong> <em>love</em> you! \uFF08\u6211<strong>\u771F\u7684</strong>\u7231\u4F60\uFF01\uFF09- <strong>\u8868\u793A\u5F3A\u8C03</strong></td></tr><tr><td style="text-align:left;"><strong>Be</strong></td><td style="text-align:left;"><strong>1. \u6784\u6210\u3010\u8FDB\u884C\u65F6\u6001\u3011\uFF08be + doing\uFF09</strong> <br>2. \u6784\u6210\u3010\u88AB\u52A8\u8BED\u6001\u3011\uFF08be + done\uFF09</td><td style="text-align:left;"><strong>am, is, are, was, were, been</strong> <br>\u4F5C\u4E3A\u52A9\u52A8\u8BCD\u65F6\uFF0C\u672C\u8EAB\u65E0\u8BCD\u4E49\u3002</td><td style="text-align:left;">1. He <strong>is</strong> <em>watching</em> TV. \uFF08\u4ED6\u6B63\u5728\u770B\u7535\u89C6\u3002\uFF09- <strong>\u73B0\u5728\u8FDB\u884C\u65F6</strong> <br>2. The window <strong>was</strong> <em>broken</em>. \uFF08\u7A97\u6237\u88AB\u6253\u788E\u4E86\u3002\uFF09- <strong>\u88AB\u52A8\u8BED\u6001</strong></td></tr><tr><td style="text-align:left;"><strong>Have</strong></td><td style="text-align:left;"><strong>\u6784\u6210\u3010\u5B8C\u6210\u65F6\u6001\u3011\uFF08have + done\uFF09</strong></td><td style="text-align:left;"><strong>have, has, had</strong> <br>\u4F5C\u4E3A\u52A9\u52A8\u8BCD\u65F6\uFF0C\u672C\u8EAB\u65E0\u8BCD\u4E49\u3002</td><td style="text-align:left;">1. I <strong>have</strong> <em>finished</em> my work. \uFF08\u6211\u5DF2\u7ECF\u5B8C\u6210\u4E86\u6211\u7684\u5DE5\u4F5C\u3002\uFF09- <strong>\u73B0\u5728\u5B8C\u6210\u65F6</strong> <br>2. They <strong>had</strong> <em>left</em> when I arrived. \uFF08\u6211\u5230\u7684\u65F6\u5019\u4ED6\u4EEC\u5DF2\u7ECF\u79BB\u5F00\u4E86\u3002\uFF09- <strong>\u8FC7\u53BB\u5B8C\u6210\u65F6</strong></td></tr></tbody></table><hr><h3 id="\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u52A9\u52A8\u8BCD\u7684-\u6838\u5FC3\u5DE5\u4F5C\u573A\u666F" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u52A9\u52A8\u8BCD\u7684-\u6838\u5FC3\u5DE5\u4F5C\u573A\u666F" aria-hidden="true">#</a> <strong>\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192\uFF1A\u52A9\u52A8\u8BCD\u7684\u201C\u6838\u5FC3\u5DE5\u4F5C\u573A\u666F\u201D</strong></h3><p>\u5728\u4EE5\u4E0B\u4E09\u79CD\u573A\u666F\u4E2D\uFF0C<strong>\u52A9\u52A8\u8BCD\u662F\u7EDD\u5BF9\u7684\u4E3B\u89D2</strong>\uFF0C\u4E3B\u8981\u52A8\u8BCD\u5FC5\u987B\u4FDD\u6301\u539F\u5F62\u6216\u5206\u8BCD\u5F62\u6001\u3002</p><ol><li><p><strong>\u7591\u95EE\u53E5\u7684\u642D\u5EFA</strong>\uFF1A\u5728\u7EDD\u5927\u591A\u6570\u7591\u95EE\u53E5\u4E2D\uFF0C\u9700\u8981\u52A9\u52A8\u8BCD\u6765\u201C\u6253\u5934\u9635\u201D\u3002</p><ul><li>\u6B63\u786E\uFF1A<strong>Do</strong> you <em>speak</em> English? \uFF08\u4F60\u8BF4\u82F1\u8BED\u5417\uFF1F\uFF09</li><li>\u9519\u8BEF\uFF1A<s>Speak you English?</s> \uFF08\u4E2D\u6587\u5F0F\u8BED\u5E8F\uFF09</li></ul></li><li><p><strong>\u5426\u5B9A\u53E5\u7684\u6784\u6210</strong>\uFF1A\u5426\u5B9A\u8BCD <code>not</code> \u5FC5\u987B\u7D27\u8DDF\u5728\u52A9\u52A8\u8BCD\u4E4B\u540E\u3002</p><ul><li>\u6B63\u786E\uFF1AShe <strong>does not</strong> <em>like</em> coffee. \uFF08\u5979\u4E0D\u559C\u6B22\u5496\u5561\u3002\uFF09</li><li>\u9519\u8BEF\uFF1A<s>She likes not coffee.</s></li></ul></li><li><p><strong>\u7B80\u7565\u56DE\u7B54\u548C\u66FF\u4EE3</strong>\uFF1A\u4E3A\u4E86\u907F\u514D\u91CD\u590D\uFF0C\u5E38\u7528\u52A9\u52A8\u8BCD\u6765\u4EE3\u66FF\u6574\u4E2A\u52A8\u8BCD\u90E8\u5206\u3002</p><ul><li><ul><li>Do you like it? \uFF08\u4F60\u559C\u6B22\u5417\uFF1F\uFF09</li></ul></li><li><ul><li>Yes, I <strong>do</strong>. \uFF08\u662F\u7684\uFF0C\u6211\u559C\u6B22\u3002\uFF09- \u8FD9\u91CC\u7684 <code>do</code> \u5C31\u66FF\u4EE3\u4E86 <code>like it</code>\u3002</li></ul></li></ul></li></ol><hr><h3 id="\u603B\u7ED3\u4E0E\u5BF9\u6BD4-\u52A9\u52A8\u8BCD-vs-\u60C5\u6001\u52A8\u8BCD-vs-\u4E3B\u8981\u52A8\u8BCD" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3\u4E0E\u5BF9\u6BD4-\u52A9\u52A8\u8BCD-vs-\u60C5\u6001\u52A8\u8BCD-vs-\u4E3B\u8981\u52A8\u8BCD" aria-hidden="true">#</a> <strong>\u603B\u7ED3\u4E0E\u5BF9\u6BD4\uFF1A\u52A9\u52A8\u8BCD vs. \u60C5\u6001\u52A8\u8BCD vs. \u4E3B\u8981\u52A8\u8BCD</strong></h3><p>\u4E3A\u4E86\u8BA9\u4F60\u66F4\u6E05\u6670\uFF0C\u6211\u4EEC\u6765\u505A\u4E00\u4E2A\u7EC8\u6781\u5BF9\u6BD4\uFF1A</p><table><thead><tr><th style="text-align:left;">\u52A8\u8BCD\u7C7B\u578B</th><th style="text-align:left;">\u89D2\u8272\u6BD4\u55BB</th><th style="text-align:left;">\u6709\u7121\u8BCD\u4E49\uFF1F</th><th style="text-align:left;">\u4F8B\u5B50</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u4E3B\u8981\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u4E3B\u89D2</strong></td><td style="text-align:left;"><strong>\u6709</strong></td><td style="text-align:left;">I <strong>love</strong> you. \uFF08\u6211\u7231\u4F60\u3002\uFF09- <code>love</code> \u662F\u6838\u5FC3\u52A8\u4F5C\u3002</td></tr><tr><td style="text-align:left;"><strong>\u52A9\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u5BFC\u6F14/\u5DE5\u5177</strong></td><td style="text-align:left;"><strong>\u65E0</strong>\uFF08\u57FA\u672C\u52A9\u52A8\u8BCD\uFF09</td><td style="text-align:left;">I <strong>do</strong> love you. \uFF08\u6211\u786E\u5B9E\u7231\u4F60\u3002\uFF09- <code>do</code> \u53EA\u8D77\u5F3A\u8C03\u4F5C\u7528\u3002</td></tr><tr><td style="text-align:left;"><strong>\u60C5\u6001\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u5E26\u8D44\u8FDB\u7EC4\u7684\u6F14\u5458\uFF08\u81EA\u5E26\u6001\u5EA6\uFF09</strong></td><td style="text-align:left;"><strong>\u6709</strong>\uFF08\u6001\u5EA6\u8272\u5F69\uFF09</td><td style="text-align:left;">I <strong>can</strong> love you. \uFF08\u6211\u80FD\u7231\u4F60\u3002\uFF09- <code>can</code> \u8868\u8FBE\u4E86\u201C\u80FD\u529B\u201D\u8FD9\u79CD\u6001\u5EA6\u3002</td></tr></tbody></table><p><strong>\u6838\u5FC3\u5FC3\u6CD5\uFF1A</strong></p><ul><li>\u5728\u4E00\u4E2A\u53E5\u5B50\u4E2D\uFF0C<strong>\u7B2C\u4E00\u4E2A\u52A8\u8BCD\u6216\u52A8\u8BCD\u8BCD\u7EC4</strong>\u51B3\u5B9A\u4E86\u53E5\u5B50\u7684\u65F6\u6001\u3001\u7591\u95EE\u548C\u5426\u5B9A\u5F62\u5F0F\uFF0C\u5B83\u5F80\u5F80\u662F<strong>\u52A9\u52A8\u8BCD\u6216\u60C5\u6001\u52A8\u8BCD</strong>\u3002</li><li>\u4E3B\u8981\u52A8\u8BCD\u5219\u4E56\u4E56\u5730\u5F85\u5728\u5B83\u4EEC\u540E\u9762\uFF0C\u53D8\u6210\u539F\u5F62\u3001-ing\u5F62\u5F0F\u6216\u8FC7\u53BB\u5206\u8BCD\u3002</li></ul>',27),l=[s];function g(i,a){return e(),r("div",null,l)}var h=t(o,[["render",g],["__file","index.html.vue"]]);export{h as default};
