import{_ as r,o as n,c as t,e as o}from"./app.2527c71d.js";const s={},g=o('<h2 id="\u8FC7\u53BB\u5206\u8BCD" tabindex="-1"><a class="header-anchor" href="#\u8FC7\u53BB\u5206\u8BCD" aria-hidden="true">#</a> \u8FC7\u53BB\u5206\u8BCD</h2><p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u975E\u5E38\u7406\u89E3\u4F60\u7684\u611F\u53D7\u3002\u201C\u8FC7\u53BB\u5206\u8BCD\u201D\u8FD9\u4E2A\u6982\u5FF5\u542C\u8D77\u6765\u5F88\u62BD\u8C61\uFF0C\u4F46\u5B83\u5176\u5B9E\u662F\u82F1\u8BED\u4E2D\u6700\u6709\u7528\u3001\u6700\u795E\u5947\u7684\u8BED\u6CD5\u5DE5\u5177\u4E4B\u4E00\u3002\u4ECA\u5929\uFF0C\u6211\u4EEC\u5C31\u6765\u5F7B\u5E95\u641E\u61C2\u5B83\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u8FC7\u53BB\u5206\u8BCD-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u8FC7\u53BB\u5206\u8BCD-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u8FC7\u53BB\u5206\u8BCD\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u8FC7\u53BB\u5206\u8BCD\u662F\u52A8\u8BCD\u7684\u4E00\u79CD\u975E\u8C13\u8BED\u5F62\u5F0F\uFF08\u5373\u4E0D\u80FD\u5355\u72EC\u4F5C\u8C13\u8BED\uFF09\uFF0C\u5B83\u672C\u8EAB\u8574\u542B\u7740\u201C\u88AB\u52A8\u201D\u548C\u201C\u5B8C\u6210\u201D\u7684\u6838\u5FC3\u542B\u4E49\u3002</strong></p><p>\u4F60\u53EF\u4EE5\u628A\u5B83\u60F3\u8C61\u6210\u52A8\u8BCD\u7684\u201C\u6210\u679C\u5F62\u6001\u201D\u6216\u201C\u88AB\u52A8\u5F62\u6001\u201D\u3002\u5B83\u8868\u793A\u4E00\u4E2A\u52A8\u4F5C<strong>\u5DF2\u7ECF\u88AB\u505A\u5B8C\u4E86</strong>\uFF0C\u6216\u8005\u4E00\u4E2A\u4E8B\u7269<strong>\u88AB\u52A8\u5730\u627F\u53D7\u4E86\u67D0\u4E2A\u52A8\u4F5C\u540E\u7684\u72B6\u6001</strong>\u3002</p><p><strong>\u6838\u5FC3\u610F\u8C61\uFF1A</strong></p><ul><li><strong>\u201C\u88AB\u2026\u2026\u201D</strong> \uFF08\u8868\u88AB\u52A8\uFF09</li><li><strong>\u201C\u5DF2\u7ECF\u2026\u2026\u201D</strong> \uFF08\u8868\u5B8C\u6210\uFF09</li></ul><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong></p><ul><li><strong>\u52A8\u8BCD\u539F\u5F62</strong> \u662F <strong>\u201C\u539F\u6750\u6599\u201D</strong>\uFF08\u5982\uFF1A\u6728\u5934\uFF09\u3002</li><li><strong>\u8FC7\u53BB\u5206\u8BCD</strong> \u5C31\u662F <strong>\u201C\u88AB\u52A0\u5DE5\u540E\u7684\u6210\u54C1\u201D</strong>\uFF08\u5982\uFF1A\u4E00\u628A<strong>\u88AB\u505A\u597D\u7684</strong>\u6905\u5B50\uFF09\u3002 <ul><li>\u8FD9\u4E2A\u6210\u54C1\uFF08\u6905\u5B50\uFF09\u8EAB\u4E0A\u5E26\u7740\u201C\u88AB\u5236\u4F5C\u201D\u8FD9\u4E2A\u52A8\u4F5C\u7684\u75D5\u8FF9\u548C\u7ED3\u679C\u3002</li></ul></li></ul><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u8FC7\u53BB\u5206\u8BCD\u957F\u4EC0\u4E48\u6837-\u5F62\u6001" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u8FC7\u53BB\u5206\u8BCD\u957F\u4EC0\u4E48\u6837-\u5F62\u6001" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u8FC7\u53BB\u5206\u8BCD\u957F\u4EC0\u4E48\u6837\uFF1F\uFF08\u5F62\u6001\uFF09</h3><p>\u8FC7\u53BB\u5206\u8BCD\u4E3B\u8981\u6709\u4E24\u79CD\u5F62\u5F0F\uFF1A</p><ol><li><p><strong>\u89C4\u5219\u53D8\u5316</strong>\uFF1A\u52A8\u8BCD\u539F\u5F62 + <strong>-ed</strong></p><ul><li>work -&gt; work<strong>ed</strong></li><li>play -&gt; play<strong>ed</strong></li><li>want -&gt; want<strong>ed</strong></li></ul></li><li><p><strong>\u4E0D\u89C4\u5219\u53D8\u5316</strong>\uFF1A\u9700\u8981\u5355\u72EC\u8BB0\u5FC6\uFF08\u8FD9\u662F\u82F1\u8BED\u5B66\u4E60\u7684\u5FC5\u7ECF\u4E4B\u8DEF\uFF09\u3002</p><ul><li>do -&gt; <strong>done</strong></li><li>eat -&gt; <strong>eaten</strong></li><li>see -&gt; <strong>seen</strong></li><li>go -&gt; <strong>gone</strong></li><li>write -&gt; <strong>written</strong></li></ul></li></ol><p><strong>\u4F60\u7684\u56F0\u60D1\u6765\u6E90</strong>\uFF1A\u5F88\u591A\u4E0D\u89C4\u5219\u52A8\u8BCD\u7684<strong>\u8FC7\u53BB\u5F0F</strong>\u548C<strong>\u8FC7\u53BB\u5206\u8BCD</strong>\u957F\u5F97\u4E0D\u4E00\u6837\uFF0C\u8FD9\u6B63\u662F\u533A\u5206\u65F6\u6001\u548C\u8BED\u6001\u7684\u5173\u952E\uFF01</p><ul><li>\u8FC7\u53BB\u5F0F\uFF1AI <strong>ate</strong> an apple. \uFF08\u6211\u5403\u4E86\u4E00\u4E2A\u82F9\u679C\u3002\uFF09- \u63CF\u8FF0\u8FC7\u53BB\u53D1\u751F\u7684\u52A8\u4F5C\u3002</li><li>\u8FC7\u53BB\u5206\u8BCD\uFF1AThe apple <strong>was eaten</strong>. \uFF08\u82F9\u679C\u88AB\u5403\u4E86\u3002\uFF09- \u63CF\u8FF0\u82F9\u679C\u201C\u88AB\u5403\u201D\u7684\u72B6\u6001\u3002</li></ul><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u8FC7\u53BB\u5206\u8BCD\u7684\u4E09\u5927\u6838\u5FC3\u529F\u80FD-\u7528\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u8FC7\u53BB\u5206\u8BCD\u7684\u4E09\u5927\u6838\u5FC3\u529F\u80FD-\u7528\u6CD5" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u8FC7\u53BB\u5206\u8BCD\u7684\u4E09\u5927\u6838\u5FC3\u529F\u80FD\uFF08\u7528\u6CD5\uFF09</h3><p>\u8FD9\u662F\u6700\u5173\u952E\u7684\u90E8\u5206\uFF01\u8FC7\u53BB\u5206\u8BCD\u4E0D\u80FD\u5355\u6253\u72EC\u6597\uFF0C\u5B83\u5FC5\u987B\u548C\u5176\u4ED6\u8BCD\u7EC4\u5408\uFF0C\u626E\u6F14\u4E09\u79CD\u91CD\u8981\u7684\u89D2\u8272\u3002</p><h4 id="\u529F\u80FD\u4E00-\u6784\u6210-\u5B8C\u6210\u65F6\u6001-\u2014\u2014-\u7ED3\u679C\u955C" tabindex="-1"><a class="header-anchor" href="#\u529F\u80FD\u4E00-\u6784\u6210-\u5B8C\u6210\u65F6\u6001-\u2014\u2014-\u7ED3\u679C\u955C" aria-hidden="true">#</a> <strong>\u529F\u80FD\u4E00\uFF1A\u6784\u6210\u201C\u5B8C\u6210\u65F6\u6001\u201D\u2014\u2014 \u201C\u7ED3\u679C\u955C\u201D</strong></h4><ul><li><strong>\u529F\u80FD</strong>\uFF1A\u5F3A\u8C03\u52A8\u4F5C\u7684\u201C<strong>\u6210\u679C</strong>\u201D\u548C\u201C<strong>\u5F71\u54CD</strong>\u201D\u3002</li><li><strong>\u7ED3\u6784</strong>\uFF1A<strong>have/has/had + \u8FC7\u53BB\u5206\u8BCD</strong></li><li><strong>\u4F8B\u53E5</strong>\uFF1A <ul><li>I <strong>have finished</strong> my homework. \uFF08\u6211<strong>\u5DF2\u7ECF\u5B8C\u6210</strong>\u4E86\u4F5C\u4E1A\u3002\uFF09- \u201C\u5B8C\u6210\u201D\u8FD9\u4E2A\u52A8\u4F5C\u7684\u7ED3\u679C\u662F\uFF1A\u6211\u73B0\u5728\u6709\u7A7A\u4E86\u3002</li><li>She <strong>has seen</strong> the movie. \uFF08\u5979<strong>\u5DF2\u7ECF\u770B\u8FC7</strong>\u90A3\u90E8\u7535\u5F71\u4E86\u3002\uFF09- \u201C\u770B\u201D\u8FD9\u4E2A\u52A8\u4F5C\u7684\u7ED3\u679C\u662F\uFF1A\u5979\u77E5\u9053\u7535\u5F71\u5185\u5BB9\u3002</li></ul></li></ul><p><strong>\u5728\u8FD9\u91CC\uFF0C\u8FC7\u53BB\u5206\u8BCD\u8D1F\u8D23\u544A\u8BC9\u4F60\u201C\u4EC0\u4E48\u52A8\u4F5C\u5DF2\u7ECF\u505A\u5B8C\u4E86\u201D\u3002</strong></p><h4 id="\u529F\u80FD\u4E8C-\u6784\u6210-\u88AB\u52A8\u8BED\u6001-\u2014\u2014-\u53D7\u5BB3\u8005\u6807\u7B7E" tabindex="-1"><a class="header-anchor" href="#\u529F\u80FD\u4E8C-\u6784\u6210-\u88AB\u52A8\u8BED\u6001-\u2014\u2014-\u53D7\u5BB3\u8005\u6807\u7B7E" aria-hidden="true">#</a> <strong>\u529F\u80FD\u4E8C\uFF1A\u6784\u6210\u201C\u88AB\u52A8\u8BED\u6001\u201D\u2014\u2014 \u201C\u53D7\u5BB3\u8005\u6807\u7B7E\u201D</strong></h4><ul><li><strong>\u529F\u80FD</strong>\uFF1A\u8868\u793A\u4E3B\u8BED\u662F\u52A8\u4F5C\u7684<strong>\u627F\u53D7\u8005</strong>\uFF08\u53D7\u5BB3\u8005\uFF09\u3002</li><li><strong>\u7ED3\u6784</strong>\uFF1A<strong>be + \u8FC7\u53BB\u5206\u8BCD</strong></li><li><strong>\u4F8B\u53E5</strong>\uFF1A <ul><li>The window <strong>was broken</strong>. \uFF08\u7A97\u6237<strong>\u88AB\u6253\u788E\u4E86</strong>\u3002\uFF09- \u7A97\u6237\u662F\u201C\u6253\u788E\u201D\u8FD9\u4E2A\u52A8\u4F5C\u7684\u53D7\u5BB3\u8005\u3002</li><li>English <strong>is spoken</strong> worldwide. \uFF08\u82F1\u8BED<strong>\u88AB</strong>\u5168\u4E16\u754C<strong>\u4F7F\u7528</strong>\u3002\uFF09- \u82F1\u8BED\u662F\u201C\u4F7F\u7528\u201D\u8FD9\u4E2A\u52A8\u4F5C\u7684\u627F\u53D7\u8005\u3002</li></ul></li></ul><p><strong>\u5728\u8FD9\u91CC\uFF0C\u8FC7\u53BB\u5206\u8BCD\u8D1F\u8D23\u544A\u8BC9\u4F60\u201C\u4E3B\u8BED\u88AB\u65BD\u52A0\u4E86\u4EC0\u4E48\u52A8\u4F5C\u201D\u3002</strong></p><h4 id="\u529F\u80FD\u4E09-\u4F5C\u4E3A-\u5F62\u5BB9\u8BCD-\u4F7F\u7528\u2014\u2014-\u72B6\u6001\u63CF\u8FF0\u5668" tabindex="-1"><a class="header-anchor" href="#\u529F\u80FD\u4E09-\u4F5C\u4E3A-\u5F62\u5BB9\u8BCD-\u4F7F\u7528\u2014\u2014-\u72B6\u6001\u63CF\u8FF0\u5668" aria-hidden="true">#</a> <strong>\u529F\u80FD\u4E09\uFF1A\u4F5C\u4E3A\u201C\u5F62\u5BB9\u8BCD\u201D\u4F7F\u7528\u2014\u2014 \u201C\u72B6\u6001\u63CF\u8FF0\u5668\u201D</strong></h4><p>\u8FD9\u662F\u975E\u5E38\u9AD8\u7EA7\u4E14\u5730\u9053\u7684\u7528\u6CD5\uFF01\u8FC7\u53BB\u5206\u8BCD\u53EF\u4EE5\u76F4\u63A5\u5F53\u4F5C\u5F62\u5BB9\u8BCD\u6765\u7528\uFF0C\u63CF\u8FF0\u4E00\u4E2A\u4E8B\u7269<strong>\u88AB\u52A8\u5B8C\u6210\u67D0\u79CD\u52A8\u4F5C\u540E\u6240\u5904\u7684\u72B6\u6001</strong>\u3002</p><ul><li><strong>\u4F4D\u7F6E1\uFF1A\u653E\u5728\u540D\u8BCD\u524D\u4F5C\u5B9A\u8BED</strong><ul><li>We need <strong>boiled</strong> water. \uFF08\u6211\u4EEC\u9700\u8981<strong>\u716E\u5F00\u8FC7\u7684</strong>\u6C34\u3002\uFF09- \u6C34\u662F\u201C\u88AB\u716E\u5F00\u201D\u7684\u3002</li><li>They bought a <strong>used</strong> car. \uFF08\u4ED6\u4EEC\u4E70\u4E86\u4E00\u8F86<strong>\u4E8C\u624B\u7684</strong>\uFF08\u88AB\u7528\u8FC7\u7684\uFF09\u6C7D\u8F66\u3002\uFF09</li></ul></li><li><strong>\u4F4D\u7F6E2\uFF1A\u653E\u5728\u7CFB\u52A8\u8BCD\u540E\u4F5C\u8868\u8BED</strong><ul><li>I am <strong>tired</strong>. \uFF08\u6211<strong>\u7D2F\u4E86</strong>\u3002\uFF09- \u201C\u6211\u201D\u5904\u4E8E\u4E00\u79CD\u201C\u88AB\u6D88\u8017\u6B86\u5C3D\u201D\u7684\u72B6\u6001\u3002</li><li>The door is <strong>locked</strong>. \uFF08\u95E8\u662F<strong>\u9501\u7740\u7684</strong>\u3002\uFF09- \u95E8\u5904\u4E8E\u201C\u88AB\u9501\u4E0A\u201D\u540E\u7684\u72B6\u6001\u3002</li></ul></li></ul><p><strong>\u5728\u8FD9\u91CC\uFF0C\u8FC7\u53BB\u5206\u8BCD\u4E0D\u518D\u5F3A\u8C03\u201C\u52A8\u4F5C\u201D\u672C\u8EAB\uFF0C\u800C\u662F\u5F3A\u8C03\u52A8\u4F5C\u5B8C\u6210\u540E\u7559\u4E0B\u7684\u201C\u72B6\u6001\u201D\u3002</strong></p><hr><h3 id="\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u5BF9\u6BD4-\u73B0\u5728\u5206\u8BCD-vs-\u8FC7\u53BB\u5206\u8BCD" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u5BF9\u6BD4-\u73B0\u5728\u5206\u8BCD-vs-\u8FC7\u53BB\u5206\u8BCD" aria-hidden="true">#</a> <strong>\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u5BF9\u6BD4\uFF1A\u73B0\u5728\u5206\u8BCD vs. \u8FC7\u53BB\u5206\u8BCD</strong></h3><p>\u5F53\u5B83\u4EEC\u90FD\u4F5C\u5F62\u5BB9\u8BCD\u65F6\uFF0C\u533A\u522B\u662F\uFF1A</p><ul><li><strong>\u73B0\u5728\u5206\u8BCD (-ing)</strong>\uFF1A<strong>\u201C\u4EE4\u4EBA\u2026\u2026\u7684\u201D</strong>\uFF0C\u8868\u793A<strong>\u4E3B\u52A8</strong>\u548C<strong>\u8FDB\u884C</strong>\uFF0C\u63CF\u8FF0\u4E8B\u7269\u6240\u5177\u6709\u7684<strong>\u7279\u6027</strong>\u3002 <ul><li>The movie is <strong>boring</strong>. \uFF08\u8FD9\u90E8\u7535\u5F71<strong>\u4EE4\u4EBA\u65E0\u804A</strong>\u3002\uFF09- \u7535\u5F71\u672C\u8EAB\u5177\u6709\u8BA9\u4EBA\u65E0\u804A\u7684\u7279\u6027\u3002</li></ul></li><li><strong>\u8FC7\u53BB\u5206\u8BCD (-ed)</strong>\uFF1A<strong>\u201C\u611F\u5230\u2026\u2026\u7684\u201D</strong>\uFF0C\u8868\u793A<strong>\u88AB\u52A8</strong>\u548C<strong>\u5B8C\u6210</strong>\uFF0C\u63CF\u8FF0\u4EBA\u7684<strong>\u611F\u53D7</strong>\u6216\u4E8B\u7269\u7684<strong>\u72B6\u6001</strong>\u3002 <ul><li>I am <strong>bored</strong>. \uFF08\u6211\u611F\u5230<strong>\u5F88\u65E0\u804A</strong>\u3002\uFF09- \u6211\u53D7\u5230\u4E86\u7535\u5F71\u7684\u5F71\u54CD\uFF0C\u4EA7\u751F\u4E86\u65E0\u804A\u7684\u611F\u53D7\u3002</li></ul></li></ul><p><strong>\u7B80\u5355\u8BB0\uFF1A-ing \u662F\u201C\u4EE4\u4EBA\u201D\uFF0C-ed \u662F\u201C\u611F\u5230\u201D\u3002</strong></p><hr><h3 id="\u7EC8\u6781\u603B\u7ED3\u4E0E\u5FC3\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u7EC8\u6781\u603B\u7ED3\u4E0E\u5FC3\u6CD5" aria-hidden="true">#</a> <strong>\u7EC8\u6781\u603B\u7ED3\u4E0E\u5FC3\u6CD5</strong></h3><ol><li><strong>\u8FC7\u53BB\u5206\u8BCD\u7684\u6838\u5FC3\u542B\u4E49</strong>\uFF1A<strong>\u88AB\u52A8</strong> \u548C <strong>\u5B8C\u6210</strong>\u3002</li><li><strong>\u4E09\u5927\u529F\u80FD</strong>\uFF1A <ul><li><strong>\u642D\u53F0\u5531\u620F1</strong>\uFF1A\u4E0E <strong>have/has/had</strong> \u642D\u6863\uFF0C\u5531\u201C\u5B8C\u6210\u65F6\u201D\u7684\u620F\u3002</li><li><strong>\u642D\u53F0\u5531\u620F2</strong>\uFF1A\u4E0E <strong>be</strong> \u52A8\u8BCD\u642D\u6863\uFF0C\u5531\u201C\u88AB\u52A8\u8BED\u6001\u201D\u7684\u620F\u3002</li><li><strong>\u72EC\u7ACB\u5DE5\u4F5C</strong>\uFF1A\u81EA\u5DF1\u626E\u6F14\u201C\u5F62\u5BB9\u8BCD\u201D\u7684\u89D2\u8272\uFF0C\u63CF\u8FF0\u88AB\u52A8\u6216\u5B8C\u6210\u540E\u7684\u72B6\u6001\u3002</li></ul></li><li><strong>\u5F62\u6001</strong>\uFF1A\u89C4\u5219\u52A0 <code>-ed</code>\uFF0C\u4E0D\u89C4\u5219\u9700\u6B7B\u8BB0\uFF08\u5982 done, seen, written\uFF09\u3002</li><li><strong>vs. \u73B0\u5728\u5206\u8BCD</strong>\uFF1A\u8BB0\u4F4F\u201C<strong>\u4EE4\u4EBA\u2026 (-ing)</strong>\u201D \u548C \u201C<strong>\u611F\u5230\u2026 (-ed)</strong>\u201D \u8FD9\u4E2A\u9EC4\u91D1\u6CD5\u5219\u3002</li></ol><p>\u8FC7\u53BB\u5206\u8BCD\u5C31\u50CF\u4E00\u5757\u201C\u4E07\u80FD\u79EF\u6728\u201D\uFF0C\u901A\u8FC7\u548C\u4E0D\u540C\u7684\u52A9\u624B\uFF08be\u52A8\u8BCD\u3001have\u52A8\u8BCD\uFF09\u7EC4\u5408\uFF0C\u5C31\u80FD\u62FC\u51FA\u5B8C\u5168\u4E0D\u540C\u7684\u8BED\u6CD5\u7ED3\u6784\u3002\u7406\u89E3\u4E86\u5B83\uFF0C\u4F60\u7684\u82F1\u8BED\u8BED\u6CD5\u80FD\u529B\u4F1A\u63D0\u5347\u4E00\u5927\u622A\uFF01</p><p>\u5E0C\u671B\u8FD9\u4E2A\u8BB2\u89E3\u80FD\u8BA9\u4F60\u89C9\u5F97\u8FC7\u53BB\u5206\u8BCD\u4E0D\u518D\u795E\u79D8\u548C\u53EF\u6015\u3002\u63A5\u4E0B\u6765\u60F3\u4E86\u89E3\u54EA\u4E2A\u8BED\u6CD5\u70B9\uFF0C\u6BD4\u5982\u201C\u73B0\u5728\u5206\u8BCD\u201D\u7684\u8BE6\u7EC6\u7528\u6CD5\uFF0C\u6216\u8005\u201C\u975E\u8C13\u8BED\u52A8\u8BCD\u201D\u7684\u603B\u7ED3\uFF1F\u6211\u968F\u65F6\u5728\u6B64\u7B49\u5019\u3002</p>',38),i=[g];function l(e,a){return n(),t("div",null,i)}var d=r(s,[["render",l],["__file","index.html.vue"]]);export{d as default};
