import{_ as t,o,c as n,e as r}from"./app.2f1d72d8.js";const s={},e=r('<h2 id="\u4E0D\u5B9A\u5F0F-infinitive" tabindex="-1"><a class="header-anchor" href="#\u4E0D\u5B9A\u5F0F-infinitive" aria-hidden="true">#</a> \u4E0D\u5B9A\u5F0F Infinitive</h2><p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4EEC\u4ECA\u5929\u6765\u89E3\u9501\u82F1\u8BED\u4E2D\u4E00\u4E2A\u529F\u80FD\u6700\u5F3A\u5927\u3001\u6700\u7075\u6D3B\u591A\u53D8\u7684\u8BED\u6CD5\u7ED3\u6784\u2014\u2014<strong>\u52A8\u8BCD\u4E0D\u5B9A\u5F0F</strong>\u3002\u5B83\u88AB\u79F0\u4E3A\u201C\u975E\u8C13\u8BED\u52A8\u8BCD\u7684\u738B\u8005\u201D\uFF0C\u7EDD\u5BF9\u540D\u4E0D\u865A\u4F20\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u4E0D\u5B9A\u5F0F-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u4E0D\u5B9A\u5F0F-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u4E0D\u5B9A\u5F0F\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u4E0D\u5B9A\u5F0F\u662F\u52A8\u8BCD\u7684\u4E00\u79CD\u975E\u8C13\u8BED\u5F62\u5F0F\uFF08\u4E0D\u80FD\u5355\u72EC\u4F5C\u8C13\u8BED\uFF09\uFF0C\u5176\u57FA\u672C\u5F62\u6001\u662F <code>to + \u52A8\u8BCD\u539F\u5F62</code>\u3002\u5B83\u4FDD\u7559\u7740\u52A8\u8BCD\u7684\u201C\u610F\u5473\u201D\uFF0C\u4F46\u53C8\u5728\u53E5\u4E2D\u626E\u6F14\u7740\u540D\u8BCD\u3001\u5F62\u5BB9\u8BCD\u6216\u526F\u8BCD\u7684\u89D2\u8272\u3002</strong></p><p><strong>\u6838\u5FC3\u610F\u8C61\uFF1A\u4E0D\u5B9A\u5F0F\u8868\u8FBE\u4E00\u79CD\u201C\u6F5C\u5728\u7684\u3001\u672A\u6765\u7684\u3001\u6216\u76EE\u7684\u6027\u7684\u52A8\u4F5C\u201D\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong></p><ul><li><strong>\u8C13\u8BED\u52A8\u8BCD</strong> \u662F<strong>\u6B63\u5728\u6267\u884C\u4EFB\u52A1\u7684\u5458\u5DE5</strong>\uFF08\u5982\uFF1A\u4ED6<strong>\u5B66\u4E60</strong>\uFF09\u3002</li><li><strong>\u4E0D\u5B9A\u5F0F</strong> \u5C31\u50CF\u662F<strong>\u4E00\u4E2A\u5458\u5DE5\u7684\u201C\u6F5C\u529B\u201D\u6216\u201C\u5F85\u529E\u4E8B\u9879\u201D</strong>\uFF08\u5982\uFF1A\u4ED6\u60F3<strong>\u53BB\u5B66\u4E60</strong>\uFF09\u3002 <ul><li>\u8FD9\u4E2A\u201C\u53BB\u5B66\u4E60\u201D\u8FD8\u4E0D\u662F\u6B63\u5728\u53D1\u751F\u7684\u52A8\u4F5C\uFF0C\u800C\u662F\u4E00\u4E2A<strong>\u76EE\u6807\u3001\u8BA1\u5212\u6216\u6F5C\u5728\u7684\u53EF\u80FD</strong>\u3002</li></ul></li></ul><p>\u8FD9\u4E2A <code>to</code> \u5C31\u50CF\u662F\u4E00\u4E2A <strong>\u201C\u884C\u52A8\u6307\u5411\u7BAD\u5934\u201D</strong>\uFF0C\u6307\u5411\u4E00\u4E2A\u5C06\u8981\u53D1\u751F\u6216\u610F\u56FE\u53D1\u751F\u7684\u52A8\u4F5C\u3002</p><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u4E0D\u5B9A\u5F0F-\u540D\u79F0\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u4E0D\u5B9A\u5F0F-\u540D\u79F0\u7684\u7531\u6765" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u4E3A\u4EC0\u4E48\u53EB\u201C\u4E0D\u5B9A\u5F0F\u201D\uFF1F\u540D\u79F0\u7684\u7531\u6765</h3><ol><li><p><strong>\u4E2D\u6587\u540D\u201C\u4E0D\u5B9A\u5F0F\u201D</strong>\uFF1A</p><ul><li>\u201C\u4E0D\u5B9A\u201D\u6307\u7684\u662F <strong>\u201C\u4E0D\u88AB\u9650\u5B9A\u201D</strong> \u3002</li><li>\u4E4B\u6240\u4EE5\u53EB\u4E0D\u5B9A\u5F0F\uFF0C\u662F\u56E0\u4E3A\u5B83<strong>\u4E0D\u53D7\u4E3B\u8BED\u7684\u4EBA\u79F0\u548C\u6570\u7684\u9650\u5B9A</strong>\u3002\u65E0\u8BBA\u4E3B\u8BED\u662FI, you, he, we\uFF0C\u4E0D\u5B9A\u5F0F\u90FD\u4FDD\u6301 <code>to + \u52A8\u8BCD\u539F\u5F62</code> \u7684\u5F62\u6001\uFF0C\u4E0D\u50CF\u8C13\u8BED\u52A8\u8BCD\u9700\u8981\u6839\u636E\u4E3B\u8BED\u53D8\u6210 <code>loves</code>, <code>loved</code> \u7B49\u3002</li></ul></li><li><p><strong>\u82F1\u6587\u540D\u201CInfinitive\u201D</strong>\uFF1A</p><ul><li>\u8BFB\u97F3\uFF1A/\u026An\u02C8f\u026An\u0259t\u026Av/</li><li>\u8FD9\u4E2A\u8BCD\u6765\u6E90\u4E8E\u62C9\u4E01\u8BED <code>infinitivus</code>\uFF0C\u7531 <code>in-</code>\uFF08\u610F\u4E3A\u201C\u4E0D\u3001\u65E0\u201D\uFF09\u548C <code>finitus</code>\uFF08\u610F\u4E3A\u201C\u88AB\u9650\u5B9A\u7684\u201D\uFF09\u7EC4\u6210\u3002</li><li>\u6240\u4EE5 <code>Infinitive</code> \u7684\u5B57\u9762\u610F\u601D\u5C31\u662F <strong>\u201C\u4E0D\u88AB\u9650\u5B9A\u7684\u201D</strong> \uFF0C\u548C\u4E2D\u6587\u540D\u5B8C\u7F8E\u5BF9\u5E94\u3002</li></ul></li></ol><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u4E0D\u5B9A\u5F0F\u7684\u4E09\u5927\u6838\u5FC3\u529F\u80FD-\u7528\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u4E0D\u5B9A\u5F0F\u7684\u4E09\u5927\u6838\u5FC3\u529F\u80FD-\u7528\u6CD5" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u4E0D\u5B9A\u5F0F\u7684\u4E09\u5927\u6838\u5FC3\u529F\u80FD\uFF08\u7528\u6CD5\uFF09</h3><p>\u8FD9\u662F\u6700\u5173\u952E\u7684\u90E8\u5206\uFF01\u4E0D\u5B9A\u5F0F\u5C31\u50CF\u4E00\u4E2A\u201C\u591A\u9762\u624B\u201D\uFF0C\u53EF\u4EE5\u5728\u53E5\u5B50\u4E2D\u626E\u6F14\u4E09\u79CD\u4E0D\u540C\u7684\u89D2\u8272\u3002</p><h4 id="\u529F\u80FD\u4E00-\u4F5C\u540D\u8BCD\u2014\u2014-\u884C\u52A8\u7684\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u529F\u80FD\u4E00-\u4F5C\u540D\u8BCD\u2014\u2014-\u884C\u52A8\u7684\u6982\u5FF5" aria-hidden="true">#</a> <strong>\u529F\u80FD\u4E00\uFF1A\u4F5C\u540D\u8BCD\u2014\u2014 \u201C\u884C\u52A8\u7684\u6982\u5FF5\u201D</strong></h4><p>\u5F53\u4E0D\u5B9A\u5F0F\u4F5C\u540D\u8BCD\u65F6\uFF0C\u5B83\u628A\u201C\u505A\u67D0\u4E8B\u201D\u8FD9\u4E2A\u52A8\u4F5C\u672C\u8EAB\uFF0C\u5F53\u4F5C\u4E00\u4E2A<strong>\u4E8B\u7269\u6216\u6982\u5FF5</strong>\u6765\u770B\u5F85\u3002\u6B64\u65F6\uFF0C\u5B83\u53EF\u4EE5\u5145\u5F53\u53E5\u5B50\u7684<strong>\u4E3B\u8BED\u3001\u5BBE\u8BED\u6216\u8868\u8BED</strong>\u3002</p><table><thead><tr><th style="text-align:left;">\u5145\u5F53\u6210\u5206</th><th style="text-align:left;">\u4F8B\u53E5</th><th style="text-align:left;">\u89E3\u91CA</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u4E3B\u8BED</strong></td><td style="text-align:left;"><strong>To learn</strong> a language is fun. \uFF08<strong>\u5B66\u4E60</strong>\u4E00\u95E8\u8BED\u8A00\u662F\u6709\u8DA3\u7684\u3002\uFF09</td><td style="text-align:left;">\u628A\u201C\u5B66\u4E60\u8BED\u8A00\u201D\u8FD9\u4E2A\u52A8\u4F5C\u5F53\u4F5C\u4E00\u4E2A\u62BD\u8C61\u6982\u5FF5\u6765\u4F5C\u4E3B\u8BED\u3002</td></tr><tr><td style="text-align:left;"><strong>\u5BBE\u8BED</strong></td><td style="text-align:left;">I want <strong>to leave</strong>. \uFF08\u6211\u60F3<strong>\u79BB\u5F00</strong>\u3002\uFF09</td><td style="text-align:left;">\u201C\u79BB\u5F00\u201D\u8FD9\u4E2A\u52A8\u4F5C\u662F\u201C\u60F3\u201D\u7684\u5185\u5BB9\u548C\u76EE\u6807\u3002</td></tr><tr><td style="text-align:left;"><strong>\u8868\u8BED</strong></td><td style="text-align:left;">His dream is <strong>to travel</strong>. \uFF08\u4ED6\u7684\u68A6\u60F3\u662F<strong>\u53BB\u65C5\u884C</strong>\u3002\uFF09</td><td style="text-align:left;">\u201C\u53BB\u65C5\u884C\u201D\u662F\u5BF9\u4E3B\u8BED\u201C\u68A6\u60F3\u201D\u5185\u5BB9\u7684\u8BF4\u660E\u3002</td></tr></tbody></table><h4 id="\u529F\u80FD\u4E8C-\u4F5C\u5F62\u5BB9\u8BCD\u2014\u2014-\u5F85\u505A\u7684\u6807\u7B7E" tabindex="-1"><a class="header-anchor" href="#\u529F\u80FD\u4E8C-\u4F5C\u5F62\u5BB9\u8BCD\u2014\u2014-\u5F85\u505A\u7684\u6807\u7B7E" aria-hidden="true">#</a> <strong>\u529F\u80FD\u4E8C\uFF1A\u4F5C\u5F62\u5BB9\u8BCD\u2014\u2014 \u201C\u5F85\u505A\u7684\u6807\u7B7E\u201D</strong></h4><p>\u5F53\u4E0D\u5B9A\u5F0F\u4F5C\u5F62\u5BB9\u8BCD\u65F6\uFF0C\u5B83\u653E\u5728<strong>\u540D\u8BCD\u540E\u9762</strong>\uFF0C\u7528\u6765\u4FEE\u9970\u8FD9\u4E2A\u540D\u8BCD\uFF0C\u8BF4\u660E\u8FD9\u4E2A\u540D\u8BCD\u662F <strong>\u201C\u9700\u8981\u88AB\u2026\u2026\u7684\u201D</strong> \u6216 <strong>\u201C\u7528\u6765\u2026\u2026\u7684\u201D</strong>\u3002</p><ul><li>I have a lot of work <strong>to do</strong>. \uFF08\u6211\u6709\u5F88\u591A<strong>\u8981\u505A\u7684</strong>\u5DE5\u4F5C\u3002\uFF09</li><li>She is the person <strong>to ask</strong>. \uFF08\u5979\u5C31\u662F<strong>\u8BE5\u53BB\u95EE\u7684</strong>\u90A3\u4E2A\u4EBA\u3002\uFF09</li><li>I need a pen <strong>to write with</strong>. \uFF08\u6211\u9700\u8981\u4E00\u652F<strong>\u7528\u6765\u5199\u5B57\u7684</strong>\u7B14\u3002\uFF09</li></ul><p><strong>\u5728\u8FD9\u91CC\uFF0C\u4E0D\u5B9A\u5F0F\u5C31\u50CF\u4E00\u4E2A\u201C\u5F85\u529E\u4E8B\u9879\u201D\u6807\u7B7E\uFF0C\u8D34\u5728\u4E86\u540D\u8BCD\u540E\u9762\u3002</strong></p><h4 id="\u529F\u80FD\u4E09-\u4F5C\u526F\u8BCD\u2014\u2014-\u76EE\u7684\u7684\u7BAD\u5934" tabindex="-1"><a class="header-anchor" href="#\u529F\u80FD\u4E09-\u4F5C\u526F\u8BCD\u2014\u2014-\u76EE\u7684\u7684\u7BAD\u5934" aria-hidden="true">#</a> <strong>\u529F\u80FD\u4E09\uFF1A\u4F5C\u526F\u8BCD\u2014\u2014 \u201C\u76EE\u7684\u7684\u7BAD\u5934\u201D</strong></h4><p>\u5F53\u4E0D\u5B9A\u5F0F\u4F5C\u526F\u8BCD\u65F6\uFF0C\u5B83\u7528\u6765\u4FEE\u9970<strong>\u52A8\u8BCD</strong>\uFF0C\u8BF4\u660E\u8FD9\u4E2A\u52A8\u4F5C\u53D1\u751F\u7684 <strong>\u201C\u539F\u56E0\u201D</strong> \u6216 <strong>\u201C\u76EE\u7684\u201D</strong>\u3002\u8FD9\u662F\u6700\u5E38\u89C1\u3001\u6700\u76F4\u89C2\u7684\u7528\u6CD5\u3002</p><ul><li><strong>\u8868\u76EE\u7684</strong>\uFF1AHe saved money <strong>to buy a car</strong>. \uFF08\u4ED6\u7701\u94B1<strong>\u662F\u4E3A\u4E86\u4E70\u8F86\u8F66</strong>\u3002\uFF09</li><li><strong>\u8868\u539F\u56E0</strong>\uFF1AI&#39;m happy <strong>to see you</strong>. \uFF08\u6211\u5F88\u9AD8\u5174<strong>\u89C1\u5230\u4F60</strong>\u3002\u2014\u2014 \u56E0\u4E3A\u89C1\u5230\u4F60\uFF0C\u6240\u4EE5\u9AD8\u5174\u3002\uFF09</li></ul><p><strong>\u5728\u8FD9\u91CC\uFF0C\u4E0D\u5B9A\u5F0F\u56DE\u7B54\u4E86\u201C\u4E3A\u4EC0\u4E48\uFF1F\u201D\u8FD9\u4E2A\u95EE\u9898\u3002</strong> <code>to buy a car</code> \u56DE\u7B54\u4E86\u201C\u4E3A\u4EC0\u4E48\u7701\u94B1\uFF1F\u201D\uFF1B<code>to see you</code> \u56DE\u7B54\u4E86\u201C\u4E3A\u4EC0\u4E48\u9AD8\u5174\uFF1F\u201D\u3002</p><hr><h3 id="\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u4E0D\u5E26-to-\u7684\u4E0D\u5B9A\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u4E0D\u5E26-to-\u7684\u4E0D\u5B9A\u5F0F" aria-hidden="true">#</a> <strong>\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192\uFF1A\u4E0D\u5E26 <code>to</code> \u7684\u4E0D\u5B9A\u5F0F</strong></h3><p>\u5728\u5C11\u6570\u60C5\u51B5\u4E0B\uFF0C\u4E0D\u5B9A\u5F0F\u524D\u7684 <code>to</code> \u5FC5\u987B\u7701\u7565\uFF0C\u8FD9\u5C31\u662F <strong>\u201C\u88F8\u4E0D\u5B9A\u5F0F\u201D</strong>\u3002</p><ol><li><strong>\u5728\u60C5\u6001\u52A8\u8BCD\u540E</strong>\uFF1Acan, will, may, must, should... <ul><li>I <strong>can speak</strong> English. \uFF08\u6211\u4F1A\u8BF4\u82F1\u8BED\u3002\uFF09</li></ul></li><li><strong>\u5728\u4F7F\u5F79\u52A8\u8BCD\u540E</strong>\uFF1Alet, make, have... <ul><li>Let <strong>me go</strong>. \uFF08\u8BA9\u6211\u8D70\u3002\uFF09</li><li>He made <strong>me laugh</strong>. \uFF08\u4ED6\u628A\u6211\u9017\u7B11\u4E86\u3002\uFF09</li></ul></li><li><strong>\u5728\u611F\u5B98\u52A8\u8BCD\u540E</strong>\uFF1Asee, hear, watch, feel... <ul><li>I saw <strong>him enter</strong> the room. \uFF08\u6211\u770B\u89C1\u4ED6\u8FDB\u4E86\u623F\u95F4\u3002\uFF09</li></ul></li></ol><hr><h3 id="\u7B2C\u56DB\u90E8\u5206-\u4E0D\u5B9A\u5F0F\u7684-\u4F53-\u548C-\u6001" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u56DB\u90E8\u5206-\u4E0D\u5B9A\u5F0F\u7684-\u4F53-\u548C-\u6001" aria-hidden="true">#</a> <strong>\u7B2C\u56DB\u90E8\u5206\uFF1A\u4E0D\u5B9A\u5F0F\u7684\u201C\u4F53\u201D\u548C\u201C\u6001\u201D</strong></h3><p>\u4E0D\u5B9A\u5F0F\u672C\u8EAB\u4E5F\u53EF\u4EE5\u8868\u8FBE\u66F4\u590D\u6742\u7684\u65F6\u95F4\u5173\u7CFB\u548C\u4E3B\u88AB\u52A8\u5173\u7CFB\uFF0C\u8FD9\u4F53\u73B0\u4E86\u5B83\u4FDD\u7559\u7684\u201C\u52A8\u8BCD\u201D\u5C5E\u6027\u3002</p><table><thead><tr><th style="text-align:left;">\u5F62\u5F0F</th><th style="text-align:left;">\u7ED3\u6784</th><th style="text-align:left;">\u542B\u4E49\u4E0E\u4F8B\u53E5</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u4E00\u822C\u5F0F</strong></td><td style="text-align:left;">to do</td><td style="text-align:left;">\u8868\u793A\u52A8\u4F5C\u4E0E\u8C13\u8BED\u52A8\u8BCD<strong>\u540C\u65F6</strong>\u6216<strong>\u4E4B\u540E</strong>\u53D1\u751F\u3002 <br> I hope <strong>to see</strong> you. \uFF08\u6211\u5E0C\u671B<strong>\u89C1\u5230</strong>\u4F60\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u5B8C\u6210\u5F0F</strong></td><td style="text-align:left;">to have done</td><td style="text-align:left;">\u8868\u793A\u52A8\u4F5C\u5728\u8C13\u8BED\u52A8\u8BCD<strong>\u4E4B\u524D</strong>\u5DF2\u7ECF\u5B8C\u6210\u3002 <br> I&#39;m sorry <strong>to have disturbed</strong> you. \uFF08\u5F88\u62B1\u6B49<strong>\u6253\u6270\u4E86</strong>\u60A8\u3002\uFF08\u6253\u6270\u5DF2\u7ECF\u53D1\u751F\uFF09\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u8FDB\u884C\u5F0F</strong></td><td style="text-align:left;">to be doing</td><td style="text-align:left;">\u8868\u793A\u52A8\u4F5C\u5728\u8C13\u8BED\u52A8\u8BCD\u53D1\u751F\u65F6<strong>\u6B63\u5728\u8FDB\u884C</strong>\u3002 <br> He seems <strong>to be sleeping</strong>. \uFF08\u4ED6\u597D\u50CF<strong>\u6B63\u5728\u7761\u89C9</strong>\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u88AB\u52A8\u5F0F</strong></td><td style="text-align:left;">to be done</td><td style="text-align:left;">\u8868\u793A<strong>\u88AB\u52A8</strong>\u542B\u4E49\u3002 <br> The book is <strong>to be published</strong> next year. \uFF08\u8FD9\u672C\u4E66<strong>\u5C06\u88AB</strong>\u5728\u660E\u5E74<strong>\u51FA\u7248</strong>\u3002\uFF09</td></tr></tbody></table><hr><h3 id="\u603B\u7ED3\u4E0E\u5347\u534E" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3\u4E0E\u5347\u534E" aria-hidden="true">#</a> <strong>\u603B\u7ED3\u4E0E\u5347\u534E</strong></h3><ol><li><strong>\u4E0D\u5B9A\u5F0F\uFF08Infinitive\uFF09</strong> \u662F\u201C\u975E\u8C13\u8BED\u52A8\u8BCD\u7684\u738B\u8005\u201D\uFF0C\u5F62\u6001\u662F <code>to + do</code>\uFF0C\u6838\u5FC3\u610F\u4E49\u662F <strong>\u201C\u6F5C\u5728\u3001\u76EE\u7684\u3001\u672A\u6765\u201D</strong>\u3002</li><li><strong>\u4E09\u5927\u529F\u80FD</strong>\uFF1A <ul><li><strong>\u4F5C\u540D\u8BCD</strong>\uFF1A\u628A\u52A8\u4F5C\u5F53\u6982\u5FF5\uFF08\u4E3B\u8BED\u3001\u5BBE\u8BED\u3001\u8868\u8BED\uFF09\u3002</li><li><strong>\u4F5C\u5F62\u5BB9\u8BCD</strong>\uFF1A\u7ED9\u540D\u8BCD\u8D34\u201C\u5F85\u505A\u201D\u6807\u7B7E\uFF08\u653E\u5728\u540D\u8BCD\u540E\uFF09\u3002</li><li><strong>\u4F5C\u526F\u8BCD</strong>\uFF1A\u8868\u793A\u52A8\u4F5C\u7684\u76EE\u7684\u6216\u539F\u56E0\uFF08\u4FEE\u9970\u52A8\u8BCD\uFF09\u3002</li></ul></li><li><strong>\u7279\u6B8A\u5F62\u5F0F</strong>\uFF1A\u8BB0\u4F4F\u5728\u60C5\u6001\u52A8\u8BCD\u3001\u4F7F\u5F79\u52A8\u8BCD\u3001\u611F\u5B98\u52A8\u8BCD\u540E\u8981\u7528 <strong>\u201C\u88F8\u4E0D\u5B9A\u5F0F\u201D</strong>\uFF08\u7701\u7565to\uFF09\u3002</li><li><strong>\u9AD8\u7EA7\u7528\u6CD5</strong>\uFF1A\u4E0D\u5B9A\u5F0F\u672C\u8EAB\u4E5F\u6709\u5B8C\u6210\u3001\u8FDB\u884C\u3001\u88AB\u52A8\u7B49\u590D\u6742\u5F62\u5F0F\uFF0C\u6765\u8868\u8FBE\u66F4\u7CBE\u786E\u7684\u610F\u601D\u3002</li></ol><p>\u638C\u63E1\u4E86\u4E0D\u5B9A\u5F0F\uFF0C\u4F60\u7684\u82F1\u8BED\u8868\u8FBE\u80FD\u529B\u5C06\u83B7\u5F97\u8D28\u7684\u98DE\u8DC3\u3002\u4F60\u4E0D\u518D\u53EA\u80FD\u8BF4\u201CI study English\u201D\uFF08\u6211\u5B66\u82F1\u8BED\uFF09\uFF0C\u800C\u80FD\u8BF4\u51FA\u201CMy goal is <strong>to study English</strong>\u201D\uFF08\u6211\u7684\u76EE\u6807\u662F\u5B66\u82F1\u8BED\uFF09\u3001\u201CI have a book <strong>to study</strong>\u201D\uFF08\u6211\u6709\u4E00\u672C\u8981\u5B66\u7684\u4E66\uFF09\u548C\u201CI came here <strong>to study English</strong>\u201D\uFF08\u6211\u6765\u8FD9\u91CC\u662F\u4E3A\u4E86\u5B66\u82F1\u8BED\uFF09\u8FD9\u6837\u5C42\u6B21\u4E30\u5BCC\u7684\u53E5\u5B50\u3002</p><p>\u63A5\u4E0B\u6765\u60F3\u4E86\u89E3\u5B83\u7684\u5144\u5F1F\u201C\u52A8\u540D\u8BCD\u201D\u5417\uFF1F\u5B83\u548C\u4E0D\u5B9A\u5F0F\u4F5C\u540D\u8BCD\u65F6\u6709\u5565\u533A\u522B\uFF1F\u6211\u968F\u65F6\u5728\u6B64\u7B49\u5019\u3002</p>',38),g=[e];function l(i,a){return o(),n("div",null,g)}var h=t(s,[["render",l],["__file","index.html.vue"]]);export{h as default};
