import{_ as t,o,c as r,b as n}from"./app.195d8321.js";const s={},e=n('<h3 id="\u751F\u7269" tabindex="-1"><a class="header-anchor" href="#\u751F\u7269" aria-hidden="true">#</a> \u751F\u7269</h3><p><strong>\u54FA\u4E73\u52A8\u7269</strong></p><ul><li>squirrel [\u02C8skw\u025C\u02D0r\u0259l] \u677E\u9F20</li><li>deer [d\u026A\u0259r] / doe [do\u028A] (\u6BCD\u9E7F) / buck [b\u028Ck] (\u96C4\u9E7F) \u9E7F / \u6BCD\u9E7F / \u96C4\u9E7F</li><li>rabbit [\u02C8r\xE6b\u026At] \u5154\u5B50</li><li>raccoon [r\xE6\u02C8ku\u02D0n] \u6D63\u718A</li></ul><p><strong>\u9E1F\u7C7B</strong></p><ul><li>bird [b\u025C\u02D0rd] \u9E1F</li><li>woodpecker [\u02C8w\u028Adpek\u0259r] \u5544\u6728\u9E1F</li><li>crow [kro\u028A] \u4E4C\u9E26</li><li>sparrow [\u02C8sp\xE6ro\u028A] \u9EBB\u96C0</li><li>hawk [h\u0254\u02D0k] \u9E70</li><li>duck [d\u028Ck] \u9E2D\u5B50</li><li>goose [\u0261u\u02D0s] (\u590D\u6570\uFF1Ageese [\u0261i\u02D0s]) \u9E45</li></ul><p><strong>\u6606\u866B\u4E0E\u722C\u884C\u52A8\u7269</strong></p><ul><li>insect [\u02C8\u026Ansekt] \u6606\u866B</li><li>butterfly [\u02C8b\u028Ct\u0259rfla\u026A] \u8774\u8776</li><li>dragonfly [\u02C8dr\xE6\u0261\u0259nfla\u026A] \u873B\u8713</li><li>ant [\xE6nt] \u8682\u8681</li><li>bee [bi\u02D0] \u871C\u8702</li><li>spider [\u02C8spa\u026Ad\u0259r] \u8718\u86DB</li><li>snake [sne\u026Ak] \u86C7</li><li>turtle [\u02C8t\u025C\u02D0rtl] \u4E4C\u9F9F</li></ul><p><strong>\u690D\u7269</strong></p><ul><li>tree [tri\u02D0] \u6811</li><li>pine tree [pa\u026An tri\u02D0] \u677E\u6811</li><li>oak tree [o\u028Ak tri\u02D0] \u6A61\u6811</li><li>leaf [li\u02D0f] (\u590D\u6570\uFF1Aleaves [li\u02D0vz]) \u53F6\u5B50</li><li>branch [br\xE6nt\u0283] \u6811\u679D</li><li>root [ru\u02D0t] \u6811\u6839</li><li>flower [\u02C8fla\u028A\u0259r] \u82B1</li><li>wildflower [\u02C8wa\u026Aldfla\u028A\u0259r] \u91CE\u82B1</li><li>grass [\u0261r\xE6s] \u8349</li><li>mushroom [\u02C8m\u028C\u0283ru\u02D0m] \u8611\u83C7</li><li>bush [b\u028A\u0283] / shrub [\u0283r\u028Cb] \u704C\u6728</li></ul><hr><h3 id="\u7269\u54C1\u4E0E\u5730\u70B9" tabindex="-1"><a class="header-anchor" href="#\u7269\u54C1\u4E0E\u5730\u70B9" aria-hidden="true">#</a> \u7269\u54C1\u4E0E\u5730\u70B9</h3><p><strong>\u5730\u5F62\u4E0E\u5730\u8C8C</strong></p><ul><li>trail [tre\u026Al] / path [p\xE6\u03B8] \u5C0F\u5F84\uFF0C\u6B65\u9053</li><li>hill [h\u026Al] \u5C0F\u5C71\u4E18</li><li>mountain [\u02C8ma\u028Antn] \u5C71</li><li>valley [\u02C8v\xE6li] \u5C71\u8C37</li><li>forest [\u02C8f\u0254\u02D0r\u026Ast] \u68EE\u6797</li><li>lake [le\u026Ak] \u6E56</li><li>river [\u02C8r\u026Av\u0259r] \u6CB3</li><li>stream [stri\u02D0m] \u6EAA\u6D41</li><li>waterfall [\u02C8w\u0254\u02D0t\u0259rf\u0254\u02D0l] \u7011\u5E03</li><li>view [vju\u02D0] / viewpoint [\u02C8vju\u02D0p\u0254\u026Ant] \u666F\u8272 / \u89C2\u666F\u70B9</li></ul><p><strong>\u88C5\u5907\u4E0E\u7269\u54C1</strong></p><ul><li>backpack [\u02C8b\xE6kp\xE6k] \u80CC\u5305</li><li>water bottle [\u02C8w\u0254\u02D0t\u0259r \u02C8b\u0251\u02D0tl] \u6C34\u74F6</li><li>hiking boots [\u02C8ha\u026Ak\u026A\u014B bu\u02D0ts] \u5F92\u6B65\u9774</li><li>map [m\xE6p] \u5730\u56FE</li><li>compass [\u02C8k\u028Cmp\u0259s] \u6307\u5357\u9488</li><li>snacks [sn\xE6ks] \u96F6\u98DF</li><li>picnic [\u02C8p\u026Akn\u026Ak] \u91CE\u9910</li><li>camera [\u02C8k\xE6m\u0259r\u0259] \u76F8\u673A</li><li>binoculars [b\u026A\u02C8n\u0251\u02D0kj\u0259l\u0259rz] \u53CC\u7B52\u671B\u8FDC\u955C</li><li>first-aid kit [\u02CCf\u025C\u02D0rst \u02C8e\u026Ad k\u026At] \u6025\u6551\u5305</li></ul><p><strong>\u516C\u56ED\u8BBE\u65BD</strong></p><ul><li>sign [sa\u026An] \u6807\u5FD7</li><li>map board [m\xE6p b\u0254\u02D0rd] \u5BFC\u89C8\u56FE</li><li>bench [bent\u0283] \u957F\u6905</li><li>trash can [tr\xE6\u0283 k\xE6n] / litter bin [\u02C8l\u026At\u0259r b\u026An] \u5783\u573E\u6876</li><li>restroom [\u02C8restru\u02D0m] / toilet [\u02C8t\u0254\u026Al\u0259t] \u6D17\u624B\u95F4</li><li>parking lot [\u02C8p\u0251\u02D0rk\u026A\u014B l\u0251\u02D0t] \u505C\u8F66\u573A</li><li>information center [\u02CC\u026Anf\u0259r\u02C8me\u026A\u0283\u0259n \u02C8sent\u0259r] \u6E38\u5BA2\u4E2D\u5FC3</li></ul><hr><h3 id="\u52A8\u4F5C\u4E0E\u72B6\u6001" tabindex="-1"><a class="header-anchor" href="#\u52A8\u4F5C\u4E0E\u72B6\u6001" aria-hidden="true">#</a> \u52A8\u4F5C\u4E0E\u72B6\u6001</h3><p><strong>\u5F92\u6B65\u52A8\u4F5C</strong></p><ul><li>hike [ha\u026Ak] / walk [w\u0254\u02D0k] \u5F92\u6B65 / \u884C\u8D70</li><li>climb [kla\u026Am] \u6500\u722C</li><li>follow [\u02C8f\u0251\u02D0lo\u028A] \u8DDF\u968F</li><li>explore [\u026Ak\u02C8spl\u0254\u02D0r] \u63A2\u7D22</li><li>rest [rest] \u4F11\u606F</li><li>descend [d\u026A\u02C8send] / go down \u4E0B\u5C71\uFF0C\u4E0B\u964D</li><li>ascend [\u0259\u02C8send] / go up \u4E0A\u5C71\uFF0C\u6500\u767B</li><li>cross [kr\u0254\u02D0s] \u6A2A\u8DE8</li><li>slip [sl\u026Ap] \u6ED1\u5012</li></ul><p><strong>\u89C2\u5BDF\u4E0E\u611F\u53D7</strong></p><ul><li>see [si\u02D0] \u770B\u89C1</li><li>watch [w\u0251\u02D0t\u0283] \u89C2\u770B</li><li>listen [\u02C8l\u026Asn] \u542C</li><li>hear [h\u026Ar] \u542C\u5230</li><li>smell [smel] \u95FB</li><li>enjoy [\u026An\u02C8d\u0292\u0254\u026A] \u4EAB\u53D7</li><li>feel [fi\u02D0l] \u611F\u89C9</li><li>breathe [bri\u02D0\xF0] \u547C\u5438</li></ul><p><strong>\u5176\u4ED6\u72B6\u6001</strong></p><ul><li>scenery [\u02C8si\u02D0n\u0259ri] \u98CE\u666F</li><li>nature [\u02C8ne\u026At\u0283\u0259r] \u81EA\u7136</li><li>fresh air [fre\u0283 er] \u65B0\u9C9C\u7A7A\u6C14</li><li>sunny [\u02C8s\u028Cni] \u9633\u5149\u660E\u5A9A\u7684</li><li>shady [\u02C8\u0283e\u026Adi] \u9634\u51C9\u7684</li><li>muddy [\u02C8m\u028Cdi] \u6CE5\u6CDE\u7684</li><li>steep [sti\u02D0p] \u9661\u5CED\u7684</li><li>flat [fl\xE6t] \u5E73\u5766\u7684</li></ul><hr><h3 id="\u573A\u666F\u4E00-\u51FA\u53D1\u524D\u7684\u51C6\u5907" tabindex="-1"><a class="header-anchor" href="#\u573A\u666F\u4E00-\u51FA\u53D1\u524D\u7684\u51C6\u5907" aria-hidden="true">#</a> \u573A\u666F\u4E00\uFF1A\u51FA\u53D1\u524D\u7684\u51C6\u5907</h3><p><strong>\u4EBA\u7269\uFF1A</strong> Alex (A) \u548C Ben (B)</p><p><strong>(A\u548CB\u5728\u505C\u8F66\u573A\u76F8\u9047\uFF0C\u51C6\u5907\u5F00\u59CB\u5F92\u6B65)</strong></p><p><strong>A:</strong> Hey Ben! Ready for our hike? The weather is perfect today - <strong>sunny</strong> but with a nice breeze. <strong>A:</strong> \u563F\uFF0CBen\uFF01\u51C6\u5907\u597D\u6211\u4EEC\u7684\u5F92\u6B65\u4E86\u5417\uFF1F\u4ECA\u5929\u5929\u6C14\u592A\u597D\u4E86\u2014\u2014\u9633\u5149\u660E\u5A9A\uFF0C\u4F46\u8FD8\u6709\u5FAE\u98CE\u3002</p><p><strong>B:</strong> Absolutely! I&#39;ve packed my <strong>backpack</strong> with lots of <strong>snacks</strong> and a big <strong>water bottle</strong>. I also brought a <strong>map</strong> and a <strong>compass</strong>, just in case. <strong>B:</strong> \u5F53\u7136\uFF01\u6211\u5728\u6211\u7684<strong>\u80CC\u5305</strong>\u91CC\u88C5\u4E86\u5F88\u591A<strong>\u96F6\u98DF</strong>\u548C\u4E00\u4E2A\u5927<strong>\u6C34\u74F6</strong>\u3002\u6211\u8FD8\u5E26\u4E86<strong>\u5730\u56FE</strong>\u548C<strong>\u6307\u5357\u9488</strong>\uFF0C\u4EE5\u9632\u4E07\u4E00\u3002</p><p><strong>A:</strong> Good thinking. Let me check the <strong>map board</strong> over there to confirm which <strong>trail</strong> we&#39;re taking. I heard the Pine <strong>Forest trail</strong> has a great <strong>viewpoint</strong> at the top. <strong>A:</strong> \u60F3\u5F97\u5468\u5230\u3002\u8BA9\u6211\u53BB\u770B\u770B\u90A3\u8FB9\u7684<strong>\u5BFC\u89C8\u56FE</strong>\uFF0C\u786E\u8BA4\u4E00\u4E0B\u6211\u4EEC\u8981\u8D70\u54EA\u6761<strong>\u5C0F\u5F84</strong>\u3002\u6211\u542C\u8BF4\u677E\u6797<strong>\u5C0F\u5F84</strong>\u7684\u9876\u4E0A\u6709\u4E00\u4E2A\u5F88\u68D2\u7684<strong>\u89C2\u666F\u70B9</strong>\u3002</p><p><strong>B:</strong> Great! I hope we can see some <strong>wildlife</strong>. Maybe we&#39;ll spot a <strong>deer</strong> or a <strong>rabbit</strong>. <strong>B:</strong> \u592A\u597D\u4E86\uFF01\u6211\u5E0C\u671B\u6211\u4EEC\u80FD\u770B\u89C1\u4E00\u4E9B<strong>\u91CE\u751F\u52A8\u7269</strong>\u3002\u4E5F\u8BB8\u6211\u4EEC\u80FD\u53D1\u73B0\u4E00\u53EA<strong>\u9E7F</strong>\u6216\u8005\u4E00\u53EA<strong>\u5154\u5B50</strong>\u3002</p><p><strong>A:</strong> Maybe! Don&#39;t forget your <strong>hiking boots</strong>. The <strong>path</strong> might be <strong>muddy</strong> near the <strong>stream</strong>. <strong>A:</strong> \u4E5F\u8BB8\u5427\uFF01\u522B\u5FD8\u4E86\u4F60\u7684<strong>\u5F92\u6B65\u9774</strong>\u3002\u9760\u8FD1<strong>\u6EAA\u6D41</strong>\u7684<strong>\u5C0F\u8DEF</strong>\u53EF\u80FD\u4F1A\u6709\u70B9<strong>\u6CE5\u6CDE</strong>\u3002</p><p><strong>B:</strong> Got them on! Let&#39;s <strong>go</strong>! <strong>B:</strong> \u5DF2\u7ECF\u7A7F\u4E0A\u4E86\uFF01\u6211\u4EEC<strong>\u51FA\u53D1</strong>\u5427\uFF01</p><hr><h4 id="\u8BED\u6CD5\u8BB2\u89E3-\u573A\u666F\u4E00" tabindex="-1"><a class="header-anchor" href="#\u8BED\u6CD5\u8BB2\u89E3-\u573A\u666F\u4E00" aria-hidden="true">#</a> <strong>\u8BED\u6CD5\u8BB2\u89E3 (\u573A\u666F\u4E00)</strong></h4><ol><li><p><strong>\u4E00\u822C\u5C06\u6765\u65F6 (\u8868\u8FBE\u8BA1\u5212\u6216\u610F\u56FE):</strong></p><ul><li>\u539F\u578B: <code>be going to + verb</code> \u6216 <code>will + verb</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;<strong>we&#39;re taking</strong>&quot; \u662F\u73B0\u5728\u8FDB\u884C\u65F6\u8868\u5C06\u6765\uFF0C\u8868\u793A\u8BA1\u5212\u597D\u7684\u884C\u52A8\u3002</li><li>\u4F8B\u53E5: We <strong>are going to take</strong> the Pine Forest trail.</li></ul></li><li><p><strong>\u4ECB\u8BCD\u77ED\u8BED (\u63CF\u8FF0\u4F4D\u7F6E\u6216\u65B9\u5F0F):</strong></p><ul><li>\u539F\u578B: <code>Preposition + Noun Phrase</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;<strong>with</strong> lots of snacks&quot; (\u65B9\u5F0F)\uFF0C &quot;<strong>over there</strong>&quot; (\u4F4D\u7F6E)\uFF0C &quot;<strong>at</strong> the top&quot; (\u4F4D\u7F6E)\uFF0C &quot;<strong>near</strong> the stream&quot; (\u4F4D\u7F6E)\u3002</li><li>\u8FD9\u4E9B\u77ED\u8BED\u4E3A\u53E5\u5B50\u63D0\u4F9B\u4E86\u989D\u5916\u7684\u4E0A\u4E0B\u6587\u3002</li></ul></li></ol><hr><h3 id="\u573A\u666F\u4E8C-\u5F92\u6B65\u9014\u4E2D" tabindex="-1"><a class="header-anchor" href="#\u573A\u666F\u4E8C-\u5F92\u6B65\u9014\u4E2D" aria-hidden="true">#</a> \u573A\u666F\u4E8C\uFF1A\u5F92\u6B65\u9014\u4E2D</h3><p><strong>\u4EBA\u7269\uFF1A</strong> Alex (A) \u548C Ben (B)</p><p><strong>(\u4ED6\u4EEC\u6B63\u5728\u68EE\u6797\u91CC\u884C\u8D70)</strong></p><p><strong>B:</strong> Wow, <strong>listen</strong> to that sound! I think I <strong>hear</strong> a <strong>woodpecker</strong>. <strong>B:</strong> \u54C7\uFF0C<strong>\u542C</strong>\u90A3\u4E2A\u58F0\u97F3\uFF01\u6211\u89C9\u5F97\u6211<strong>\u542C\u5230</strong>\u4E86\u4E00\u53EA<strong>\u5544\u6728\u9E1F</strong>\u3002</p><p><strong>A:</strong> You&#39;re right! Look, up in that <strong>pine tree</strong>! There it is. <strong>A:</strong> \u4F60\u8BF4\u5F97\u5BF9\uFF01\u770B\uFF0C\u4E0A\u9762\u90A3\u68F5<strong>\u677E\u6811</strong>\u91CC\uFF01\u5C31\u5728\u90A3\u513F\u3002</p><p><strong>B:</strong> Amazing! And <strong>look at</strong> all these beautiful <strong>wildflowers</strong>. <strong>B:</strong> \u592A\u795E\u5947\u4E86\uFF01\u518D<strong>\u770B\u770B</strong>\u8FD9\u4E9B\u7F8E\u4E3D\u7684<strong>\u91CE\u82B1</strong>\u3002</p><p><strong>A:</strong> Be careful where you step. I just <strong>saw</strong> a <strong>squirrel</strong> run across the <strong>path</strong>, and there are a lot of <strong>ants</strong> on the ground. <strong>A:</strong> \u5C0F\u5FC3\u811A\u4E0B\u3002\u6211\u521A<strong>\u770B\u89C1</strong>\u4E00\u53EA<strong>\u677E\u9F20</strong>\u8DD1\u8FC7<strong>\u5C0F\u5F84</strong>\uFF0C\u800C\u4E14\u5730\u4E0A\u6709\u5F88\u591A<strong>\u8682\u8681</strong>\u3002</p><p><strong>B:</strong> Thanks. Hey, can we <strong>rest</strong> for a minute on that <strong>bench</strong>? I need to drink some water. <strong>B:</strong> \u8C22\u8C22\u3002\u563F\uFF0C\u6211\u4EEC\u80FD\u5728\u90A3\u4E2A<strong>\u957F\u6905</strong>\u4E0A<strong>\u4F11\u606F</strong>\u4E00\u4F1A\u513F\u5417\uFF1F\u6211\u9700\u8981\u559D\u70B9\u6C34\u3002</p><p><strong>A:</strong> Sure. <strong>While we&#39;re resting</strong>, we can <strong>enjoy</strong> the <strong>scenery</strong>. The <strong>fresh air</strong> here feels so good. <strong>A:</strong> \u5F53\u7136\u3002<strong>\u6211\u4EEC\u4F11\u606F\u7684\u65F6\u5019</strong>\uFF0C\u53EF\u4EE5<strong>\u6B23\u8D4F</strong>\u4E00\u4E0B<strong>\u98CE\u666F</strong>\u3002\u8FD9\u91CC\u7684<strong>\u65B0\u9C9C\u7A7A\u6C14</strong>\u611F\u89C9\u771F\u8212\u670D\u3002</p><p><strong>(Ben\u5DEE\u70B9\u6ED1\u5012)</strong></p><p><strong>B:</strong> Whoa! I almost <strong>slipped</strong> on that <strong>muddy root</strong>! <strong>B:</strong> \u54C7\uFF01\u6211\u5DEE\u70B9\u5728\u90A3\u4E2A<strong>\u6CE5\u6CDE\u7684\u6811\u6839</strong>\u4E0A<strong>\u6ED1\u5012</strong>\uFF01</p><p><strong>A:</strong> Are you okay? This part of the <strong>trail</strong> is indeed quite <strong>steep</strong> and <strong>muddy</strong>. We need to <strong>climb</strong> carefully. <strong>A:</strong> \u4F60\u6CA1\u4E8B\u5427\uFF1F\u8FD9\u6BB5<strong>\u5C0F\u5F84</strong>\u786E\u5B9E\u76F8\u5F53<strong>\u9661\u5CED</strong>\u548C<strong>\u6CE5\u6CDE</strong>\u3002\u6211\u4EEC\u9700\u8981\u5C0F\u5FC3\u5730<strong>\u722C</strong>\u3002</p><hr><h4 id="\u8BED\u6CD5\u8BB2\u89E3-\u573A\u666F\u4E8C" tabindex="-1"><a class="header-anchor" href="#\u8BED\u6CD5\u8BB2\u89E3-\u573A\u666F\u4E8C" aria-hidden="true">#</a> <strong>\u8BED\u6CD5\u8BB2\u89E3 (\u573A\u666F\u4E8C)</strong></h4><ol><li><p><strong>\u7948\u4F7F\u53E5 (\u53D1\u51FA\u6307\u4EE4\u6216\u5EFA\u8BAE):</strong></p><ul><li>\u539F\u578B: <code>Base form of the verb</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;<strong>Look</strong> at all these...&quot;, &quot;<strong>Be</strong> careful...&quot;, &quot;<strong>Listen</strong> to that sound!&quot;\u3002</li><li>\u8FD9\u4E9B\u53E5\u5B50\u76F4\u63A5\u5BF9\u542C\u8005\u53D1\u51FA\u547C\u5524\u6216\u6307\u4EE4\u3002</li></ul></li><li><p><strong>\u65F6\u95F4\u72B6\u8BED\u4ECE\u53E5:</strong></p><ul><li>\u539F\u578B: <code>While + Subject + Verb</code> (\u8868\u793A\u201C\u5F53...\u65F6\u201D)</li><li>\u5BF9\u8BDD\u4E2D: &quot;<strong>While we&#39;re resting</strong>, we can enjoy the scenery.&quot;</li><li>\u8FD9\u4E2A\u4ECE\u53E5\u8BF4\u660E\u4E86\u4E3B\u53E5\u52A8\u4F5C\u53D1\u751F\u7684\u65F6\u95F4\u3002</li></ul></li><li><p><strong>\u73B0\u5728\u8FDB\u884C\u65F6 (\u63CF\u8FF0\u6B63\u5728\u53D1\u751F\u7684\u52A8\u4F5C):</strong></p><ul><li>\u539F\u578B: <code>am/is/are + verb-ing</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;<strong>we&#39;re resting</strong>&quot;</li><li>\u8868\u793A\u8BF4\u8BDD\u65F6\u6B63\u5728\u8FDB\u884C\u7684\u52A8\u4F5C\u3002</li></ul></li></ol><hr><h3 id="\u573A\u666F\u4E09-\u5728\u89C2\u666F\u70B9\u4F11\u606F" tabindex="-1"><a class="header-anchor" href="#\u573A\u666F\u4E09-\u5728\u89C2\u666F\u70B9\u4F11\u606F" aria-hidden="true">#</a> \u573A\u666F\u4E09\uFF1A\u5728\u89C2\u666F\u70B9\u4F11\u606F</h3><p><strong>\u4EBA\u7269\uFF1A</strong> Alex (A) \u548C Ben (B)</p><p><strong>(\u4ED6\u4EEC\u5230\u8FBE\u4E86\u5C71\u9876\u7684\u89C2\u666F\u70B9)</strong></p><p><strong>A:</strong> We <strong>have ascended</strong> to the top! This <strong>view</strong> is breathtaking! You can see the entire <strong>valley</strong> and the <strong>river</strong> from here. <strong>A:</strong> \u6211\u4EEC<strong>\u722C\u5230</strong>\u9876\u4E86\uFF01\u8FD9<strong>\u666F\u8272</strong>\u771F\u4EE4\u4EBA\u60CA\u53F9\uFF01\u4ECE\u8FD9\u91CC\u4F60\u53EF\u4EE5\u770B\u5230\u6574\u4E2A<strong>\u5C71\u8C37</strong>\u548C\u90A3\u6761<strong>\u6CB3</strong>\u3002</p><p><strong>B:</strong> It was totally worth the <strong>climb</strong>. I&#39;m so glad we <strong>followed</strong> this <strong>trail</strong>. Let&#39;s have our <strong>picnic</strong> here. <strong>B:</strong> \u8FD9\u4E00\u8DEF<strong>\u722C</strong>\u4E0A\u6765\u5B8C\u5168\u503C\u5F97\u3002\u771F\u9AD8\u5174\u6211\u4EEC<strong>\u6CBF\u7740</strong>\u8FD9\u6761<strong>\u5C0F\u5F84</strong>\u8D70\u4E86\u3002\u6211\u4EEC\u5728\u8FD9\u513F<strong>\u91CE\u9910</strong>\u5427\u3002</p><p><strong>(\u4ED6\u4EEC\u5750\u5728\u957F\u6905\u4E0A)</strong></p><p><strong>A:</strong> Good idea. I brought my <strong>binoculars</strong>. I want to <strong>see</strong> if I can spot any <strong>ducks</strong> or <strong>geese</strong> on the <strong>lake</strong> down there. <strong>A:</strong> \u597D\u4E3B\u610F\u3002\u6211\u5E26\u4E86<strong>\u53CC\u7B52\u671B\u8FDC\u955C</strong>\u3002\u6211\u60F3<strong>\u770B\u770B</strong>\u80FD\u4E0D\u80FD\u627E\u5230\u5728\u4E0B\u9762<strong>\u6E56</strong>\u91CC\u7684<strong>\u9E2D\u5B50</strong>\u6216<strong>\u9E45</strong>\u3002</p><p><strong>B:</strong> Pass me the <strong>binoculars</strong> when you&#39;re done. I think I <strong>saw</strong> a <strong>hawk</strong> circling earlier. <strong>B:</strong> \u4F60\u770B\u5B8C\u4E86\u628A<strong>\u671B\u8FDC\u955C</strong>\u9012\u7ED9\u6211\u3002\u6211\u89C9\u5F97\u6211\u4E4B\u524D<strong>\u770B\u5230</strong>\u4E00\u53EA<strong>\u9E70</strong>\u5728\u76D8\u65CB\u3002</p><p><strong>A:</strong> Sure. <strong>If we had taken the shorter path</strong>, we <strong>would have missed</strong> this amazing <strong>view</strong>. <strong>A:</strong> \u5F53\u7136\u3002<strong>\u5982\u679C\u6211\u4EEC\u8D70\u4E86\u90A3\u6761\u66F4\u77ED\u7684\u5C0F\u8DEF</strong>\uFF0C\u6211\u4EEC<strong>\u5C31\u9519\u8FC7</strong>\u8FD9\u7F8E\u4E3D\u7684<strong>\u666F\u8272</strong>\u4E86\u3002</p><p><strong>B:</strong> That&#39;s true. <strong>Exploring nature</strong> is always full of surprises. I feel so relaxed <strong>when I&#39;m surrounded by trees and birdsong</strong>. <strong>B:</strong> \u6CA1\u9519\u3002<strong>\u63A2\u7D22\u81EA\u7136</strong>\u603B\u662F\u5145\u6EE1\u60CA\u559C\u3002<strong>\u5F53\u6211\u88AB\u6811\u6728\u548C\u9E1F\u9E23\u5305\u56F4\u65F6</strong>\uFF0C\u6211\u611F\u5230\u975E\u5E38\u653E\u677E\u3002</p><hr><h4 id="\u8BED\u6CD5\u8BB2\u89E3-\u573A\u666F\u4E09" tabindex="-1"><a class="header-anchor" href="#\u8BED\u6CD5\u8BB2\u89E3-\u573A\u666F\u4E09" aria-hidden="true">#</a> <strong>\u8BED\u6CD5\u8BB2\u89E3 (\u573A\u666F\u4E09)</strong></h4><ol><li><p><strong>\u73B0\u5728\u5B8C\u6210\u65F6 (\u8868\u793A\u8FC7\u53BB\u53D1\u751F\u7684\u52A8\u4F5C\u5BF9\u73B0\u5728\u9020\u6210\u7684\u5F71\u54CD\u6216\u7ED3\u679C):</strong></p><ul><li>\u539F\u578B: <code>have/has + past participle</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;We <strong>have ascended</strong> to the top!&quot;</li><li>\u5F3A\u8C03\u6211\u4EEC\u5DF2\u7ECF\u5728\u4E0A\u5C71\u5C71\u9876\u7684\u8FD9\u4E2A\u72B6\u6001\u3002</li></ul></li><li><p><strong>\u5BBE\u8BED\u4ECE\u53E5:</strong></p><ul><li>\u539F\u578B: <code>Main Clause + that + Clause</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;I&#39;m so glad (<strong>that</strong>) we followed this trail.&quot; (\u8FDE\u63A5\u8BCD<code>that</code>\u88AB\u7701\u7565\u4E86)</li><li>&quot;we followed this trail&quot; \u8FD9\u4E2A\u5B8C\u6574\u7684\u53E5\u5B50\u4F5C\u4E3A\u5F62\u5BB9\u8BCD&quot;glad&quot;\u7684\u5BBE\u8BED\u3002</li></ul></li><li><p><strong>\u865A\u62DF\u8BED\u6C14 (\u4E0E\u8FC7\u53BB\u4E8B\u5B9E\u76F8\u53CD):</strong></p><ul><li>\u539F\u578B: <code>If + past perfect, ... would have + past participle</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;<strong>If we had taken</strong> the shorter path, we <strong>would have missed</strong> this amazing view.&quot;</li><li>\u4E8B\u5B9E\u662F\uFF1A\u6211\u4EEC\u6CA1\u6709\u8D70\u90A3\u6761\u77ED\u7684\u8DEF\uFF0C\u6240\u4EE5\u6211\u4EEC\u6CA1\u6709\u9519\u8FC7\u98CE\u666F\u3002\u8FD9\u662F\u4E00\u79CD\u5047\u8BBE\u3002</li></ul></li><li><p><strong>\u65F6\u95F4\u72B6\u8BED\u4ECE\u53E5 (\u7531<code>when</code>\u5F15\u5BFC):</strong></p><ul><li>\u539F\u578B: <code>when + Subject + Verb</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;I feel so relaxed <strong>when I&#39;m surrounded by trees and birdsong</strong>.&quot;</li><li>\u8FD9\u4E2A\u4ECE\u53E5\u8BF4\u660E\u4E86\u4E3B\u53E5\u611F\u89C9\u653E\u677E\u7684\u65F6\u95F4\u6216\u6761\u4EF6\u3002</li></ul></li></ol><hr><h3 id="\u573A\u666F\u56DB-\u610F\u5916\u7684\u5C0F\u63D2\u66F2" tabindex="-1"><a class="header-anchor" href="#\u573A\u666F\u56DB-\u610F\u5916\u7684\u5C0F\u63D2\u66F2" aria-hidden="true">#</a> \u573A\u666F\u56DB\uFF1A\u610F\u5916\u7684\u5C0F\u63D2\u66F2</h3><p><strong>\u4EBA\u7269\uFF1A</strong> Alex (A) \u548C Ben (B)</p><p><strong>(Ben\u6B63\u5728\u89C2\u5BDF\u4E00\u6735\u82B1\uFF0C\u7A81\u7136\u7F29\u56DE\u4E86\u624B)</strong></p><p><strong>B:</strong> Ouch! <strong>B:</strong> \u54CE\u54DF\uFF01</p><p><strong>A:</strong> What happened? Are you okay? <strong>A:</strong> \u600E\u4E48\u4E86\uFF1F\u4F60\u6CA1\u4E8B\u5427\uFF1F</p><p><strong>B:</strong> I&#39;m fine. I was trying to get a closer look at that <strong>flower</strong> when I almost touched a huge <strong>spider</strong> on the <strong>leaf</strong>! It gave me a fright. <strong>B:</strong> \u6211\u6CA1\u4E8B\u3002\u6211\u6B63\u60F3\u51D1\u8FD1\u770B\u90A3\u6735<strong>\u82B1</strong>\uFF0C\u7ED3\u679C\u5DEE\u70B9\u6478\u5230\u53F6\u5B50\u4E0A\u4E00\u53EA\u5DE8\u5927\u7684<strong>\u8718\u86DB</strong>\uFF01\u5413\u4E86\u6211\u4E00\u8DF3\u3002</p><p><strong>A:</strong> Oh, wow! You should always check before you touch plants. <strong>Having said that</strong>, I&#39;m glad you didn&#39;t get hurt. <strong>If you had been bitten</strong>, we <strong>might have needed</strong> the <strong>first-aid kit</strong>. <strong>A:</strong> \u54E6\uFF0C\u54C7\uFF01\u4F60\u78B0\u690D\u7269\u524D\u771F\u5E94\u8BE5\u5148\u770B\u6E05\u695A\u3002<strong>\u8BDD\u867D\u5982\u6B64</strong>\uFF0C\u6211\u5F88\u9AD8\u5174\u4F60\u6CA1\u53D7\u4F24\u3002<strong>\u5982\u679C\u4F60\u88AB\u54AC\u4E86</strong>\uFF0C\u6211\u4EEC<strong>\u53EF\u80FD\u5C31\u9700\u8981</strong>\u90A3\u4E2A<strong>\u6025\u6551\u5305</strong>\u4E86\u3002</p><p><strong>B:</strong> Yeah, that was a close call. <strong>Looking back</strong>, I shouldn&#39;t have reached out so quickly. Let&#39;s <strong>continue</strong> walking. I&#39;ll be more careful from now on. <strong>B:</strong> \u662F\u554A\uFF0C\u771F\u60AC\u3002<strong>\u56DE\u60F3\u8D77\u6765</strong>\uFF0C\u6211\u4E0D\u8BE5\u90A3\u4E48\u5FEB\u4F38\u624B\u7684\u3002\u6211\u4EEC<strong>\u7EE7\u7EED</strong>\u8D70\u5427\u3002\u4EE5\u540E\u6211\u4F1A\u66F4\u5C0F\u5FC3\u7684\u3002</p><hr><h4 id="\u8BED\u6CD5\u8BB2\u89E3-\u573A\u666F\u56DB" tabindex="-1"><a class="header-anchor" href="#\u8BED\u6CD5\u8BB2\u89E3-\u573A\u666F\u56DB" aria-hidden="true">#</a> <strong>\u8BED\u6CD5\u8BB2\u89E3 (\u573A\u666F\u56DB)</strong></h4><ol><li><p><strong>\u8FC7\u53BB\u8FDB\u884C\u65F6\u4E0E\u4E00\u822C\u8FC7\u53BB\u65F6\u8FDE\u7528 (\u8868\u793A\u4E00\u4E2A\u52A8\u4F5C\u53D1\u751F\u65F6\uFF0C\u53E6\u4E00\u4E2A\u52A8\u4F5C\u6B63\u5728\u8FDB\u884C\u7684\u80CC\u666F):</strong></p><ul><li>\u539F\u578B: <code>...was/were + verb-ing... when + past simple...</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;I <strong>was trying</strong> to get a closer look... <strong>when</strong> I almost <strong>touched</strong> a huge spider...&quot;</li><li>\u8FD9\u8868\u793A\u201C\u6211\u6B63\u8981\u53BB\u770B\u201D\u8FD9\u4E2A\u6301\u7EED\u7684\u52A8\u4F5C\u88AB\u201C\u5DEE\u70B9\u6478\u5230\u201D\u8FD9\u4E2A\u77AC\u95F4\u52A8\u4F5C\u6253\u65AD\u4E86\u3002</li></ul></li><li><p><strong>\u5206\u8BCD\u77ED\u8BED\u4F5C\u72B6\u8BED (\u8868\u793A\u65F6\u95F4\u3001\u539F\u56E0\u3001\u6761\u4EF6\u7B49):</strong></p><ul><li>\u539F\u578B: <code>Verb-ing / Having + past participle, ...</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;<strong>Looking back</strong>, I shouldn&#39;t have...&quot; (\u8868\u793A\u65F6\u95F4\uFF0C\u76F8\u5F53\u4E8E &quot;When I look back...&quot;)</li><li>&quot;<strong>Having said that</strong>, I&#39;m glad...&quot; (\u8868\u793A\u8BA9\u6B65\uFF0C\u76F8\u5F53\u4E8E &quot;Although I have said that...&quot;)</li><li>\u8FD9\u79CD\u7ED3\u6784\u8BA9\u8BED\u8A00\u66F4\u7B80\u6D01\u3001\u5730\u9053\u3002</li></ul></li><li><p><strong>\u4E0E\u8FC7\u53BB\u4E8B\u5B9E\u76F8\u53CD\u7684\u865A\u62DF\u8BED\u6C14 (\u5347\u7EA7\u7248):</strong></p><ul><li>\u539F\u578B: <code>If + past perfect, ... would/might/could have + past participle</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;<strong>If you had been bitten</strong>, we <strong>might have needed</strong> the first-aid kit.&quot;</li><li>\u4E8B\u5B9E\u662F\uFF1A\u4ED6\u6CA1\u6709\u88AB\u54AC\uFF0C\u6240\u4EE5\u6211\u4EEC\u4E0D\u9700\u8981\u6025\u6551\u5305\u3002\u8FD9\u91CC\u7528\u4E86<code>might have needed</code>\u800C\u4E0D\u662F<code>would have needed</code>\uFF0C\u8868\u793A\u4E00\u79CD\u53EF\u80FD\u6027\uFF0C\u800C\u4E0D\u662F\u5FC5\u7136\u6027\u3002</li></ul></li></ol><hr><h3 id="\u573A\u666F\u4E94-\u8FD4\u7A0B\u4E0E\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u573A\u666F\u4E94-\u8FD4\u7A0B\u4E0E\u603B\u7ED3" aria-hidden="true">#</a> \u573A\u666F\u4E94\uFF1A\u8FD4\u7A0B\u4E0E\u603B\u7ED3</h3><p><strong>\u4EBA\u7269\uFF1A</strong> Alex (A) \u548C Ben (B)</p><p><strong>(\u4ED6\u4EEC\u5F00\u59CB\u4E0B\u5C71\uFF0C\u8D70\u5411\u505C\u8F66\u573A)</strong></p><p><strong>A:</strong> Okay, let&#39;s <strong>descend</strong> carefully. This part is still <strong>steep</strong>. We need to <strong>cross</strong> the <strong>stream</strong> again on our way back to the <strong>parking lot</strong>. <strong>A:</strong> \u597D\u4E86\uFF0C\u6211\u4EEC\u5C0F\u5FC3\u70B9<strong>\u4E0B\u5C71</strong>\u3002\u8FD9\u6BB5\u8DEF\u8FD8\u662F\u5F88<strong>\u9661\u5CED</strong>\u3002\u6211\u4EEC\u8FD4\u56DE<strong>\u505C\u8F66\u573A</strong>\u7684\u8DEF\u4E0A\u9700\u8981\u518D\u6B21<strong>\u7A7F\u8FC7</strong>\u90A3\u6761<strong>\u6EAA\u6D41</strong>\u3002</p><p><strong>B:</strong> Alright. You know, this <strong>hike</strong> has been fantastic. <strong>What I enjoyed the most was the sound of the waterfall</strong> we passed earlier. <strong>B:</strong> \u597D\u7684\u3002\u4F60\u77E5\u9053\u561B\uFF0C\u8FD9\u6B21<strong>\u5F92\u6B65</strong>\u771F\u662F\u592A\u68D2\u4E86\u3002<strong>\u6211\u6700\u559C\u6B22\u7684\u662F\u6211\u4EEC\u4E4B\u524D\u7ECF\u8FC7\u7684\u7011\u5E03\u7684\u58F0\u97F3</strong>\u3002</p><p><strong>A:</strong> Me too. <strong>By the time we get back to the car, we will have walked</strong> about ten kilometers. <strong>A:</strong> \u6211\u4E5F\u662F\u3002<strong>\u7B49\u6211\u4EEC\u56DE\u5230\u8F66\u90A3\u91CC\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u5C31\u5DF2\u7ECF\u8D70\u4E86</strong>\u5927\u6982\u5341\u516C\u91CC\u4E86\u3002</p><p><strong>B:</strong> Really? No wonder I&#39;m feeling a bit tired. But it&#39;s a good tired. <strong>I&#39;m really looking forward to taking off these hiking boots</strong>! <strong>B:</strong> \u771F\u7684\u5417\uFF1F\u96BE\u602A\u6211\u89C9\u5F97\u6709\u70B9\u7D2F\u4E86\u3002\u4F46\u8FD9\u662F\u6109\u5FEB\u7684\u75B2\u60EB\u3002<strong>\u6211\u771F\u76FC\u7740\u8131\u4E0B\u8FD9\u53CC\u5F92\u6B65\u9774</strong>\uFF01</p><p><strong>A:</strong> (Laughs) Same here. Let&#39;s throw our <strong>trash</strong> in that <strong>trash can</strong> over there before we leave. We should <strong>explore</strong> the <strong>forest</strong> on the other side of the <strong>valley</strong> next time. <strong>A:</strong> (\u7B11) \u6211\u4E5F\u662F\u3002\u6211\u4EEC\u79BB\u5F00\u524D\u628A<strong>\u5783\u573E</strong>\u6254\u5230\u90A3\u8FB9\u7684<strong>\u5783\u573E\u6876</strong>\u91CC\u5427\u3002\u4E0B\u6B21\u6211\u4EEC\u5E94\u8BE5\u53BB<strong>\u63A2\u7D22****\u5C71\u8C37</strong>\u53E6\u4E00\u8FB9\u7684\u90A3\u4E2A<strong>\u68EE\u6797</strong>\u3002</p><p><strong>B:</strong> That&#39;s a great plan! I&#39;m in. <strong>B:</strong> \u597D\u8BA1\u5212\uFF01\u6211\u52A0\u5165\u3002</p><hr><h4 id="\u8BED\u6CD5\u8BB2\u89E3-\u573A\u666F\u4E94" tabindex="-1"><a class="header-anchor" href="#\u8BED\u6CD5\u8BB2\u89E3-\u573A\u666F\u4E94" aria-hidden="true">#</a> <strong>\u8BED\u6CD5\u8BB2\u89E3 (\u573A\u666F\u4E94)</strong></h4><ol><li><p><strong>\u540D\u8BCD\u6027\u4ECE\u53E5-\u4E3B\u8BED\u4ECE\u53E5:</strong></p><ul><li>\u539F\u578B: <code>What + Clause + Verb...</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;<strong>What I enjoyed the most</strong> was the sound of the waterfall.&quot;</li><li>\u8FD9\u91CC\u7684 &quot;What I enjoyed the most&quot; \u662F\u4E00\u4E2A\u5B8C\u6574\u7684\u4ECE\u53E5\uFF0C\u5145\u5F53\u6574\u4E2A\u53E5\u5B50\u7684\u4E3B\u8BED\u3002\u5B83\u76F8\u5F53\u4E8E &quot;The thing that I enjoyed the most&quot;\u3002</li></ul></li><li><p><strong>\u5C06\u6765\u5B8C\u6210\u65F6 (\u8868\u793A\u5230\u5C06\u6765\u67D0\u4E2A\u65F6\u95F4\u70B9\u4E3A\u6B62\u4F1A\u5B8C\u6210\u7684\u52A8\u4F5C):</strong></p><ul><li>\u539F\u578B: <code>will have + past participle</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;<strong>By the time we get back to the car, we will have walked</strong> about ten kilometers.&quot;</li><li>&quot;By the time we get back&quot; \u662F\u5C06\u6765\u7684\u4E00\u4E2A\u65F6\u95F4\u70B9\u3002\u5728\u8FD9\u4E2A\u65F6\u95F4\u70B9\u4E4B\u524D\uFF0C\u201C\u8D70\u5341\u516C\u91CC\u201D\u8FD9\u4E2A\u52A8\u4F5C\u5C06\u4F1A\u5B8C\u6210\u3002</li></ul></li><li><p><strong>\u52A8\u8BCD\u77ED\u8BED (look forward to):</strong></p><ul><li>\u539F\u578B: <code>look forward to + noun/verb-ing</code></li><li>\u5BF9\u8BDD\u4E2D: &quot;I&#39;m really <strong>looking forward to taking off</strong> these hiking boots!&quot;</li><li>\u8FD9\u91CC\u7684 <code>to</code> \u662F\u4ECB\u8BCD\uFF0C\u6240\u4EE5\u540E\u9762\u8981\u63A5\u52A8\u540D\u8BCD <code>taking</code>\uFF0C\u800C\u4E0D\u662F\u52A8\u8BCD\u539F\u5F62\u3002\u8FD9\u662F\u4E00\u4E2A\u975E\u5E38\u9AD8\u9891\u4E14\u91CD\u8981\u7684\u7528\u6CD5\u3002</li></ul></li></ol>',93),g=[e];function i(l,a){return o(),r("div",null,g)}var d=t(s,[["render",i],["__file","index.html.vue"]]);export{d as default};
