import{_ as t,o,c as r,e as s}from"./app.e8649b0f.js";const n={},e=s('<p>\u8FD9\u662F\u4E00\u4E2A\u975E\u5E38\u6838\u5FC3\u7684\u82F1\u8BED\u95EE\u9898\uFF01<code>for</code> \u548C <code>to</code> \u90FD\u662F\u6700\u5E38\u7528\u7684\u4ECB\u8BCD\uFF0C\u4F46\u5B83\u4EEC\u7684\u7528\u6CD5\u548C\u542B\u4E49\u6709\u5F88\u5927\u7684\u4E0D\u540C\u3002\u4E2D\u56FD\u5B66\u4E60\u8005\u7ECF\u5E38\u4F1A\u6DF7\u6DC6\u5B83\u4EEC\u3002</p><p>\u6838\u5FC3\u533A\u522B\u53EF\u4EE5\u8FD9\u6837\u6982\u62EC\uFF1A</p><ul><li><strong><code>to</code> \u7684\u6838\u5FC3\u610F\u601D\u662F\u201C\u65B9\u5411\u3001\u671D\u5411\u3001\u5230\u8FBE\u201D\u3002</strong> \u5B83\u5F3A\u8C03\u7684\u662F\u79FB\u52A8\u3001\u6307\u5411\u6216\u8F6C\u79FB\u5230\u4E00\u4E2A\u76EE\u6807\u3001\u5730\u70B9\u6216\u4EBA\u3002</li><li><strong><code>for</code> \u7684\u6838\u5FC3\u610F\u601D\u662F\u201C\u76EE\u7684\u3001\u5229\u76CA\u3001\u539F\u56E0\u3001\u4EE3\u8868\u201D\u3002</strong> \u5B83\u5F3A\u8C03\u7684\u662F\u4E3A\u4E86\u67D0\u4E2A\u76EE\u7684\u3001\u4E3A\u4E86\u67D0\u4EBA\u7684\u5229\u76CA\uFF0C\u6216\u8005\u662F\u67D0\u4E2A\u4E8B\u4EF6\u7684\u539F\u56E0\u3002</li></ul><p>\u4E0B\u9762\u6211\u4EEC\u5206\u70B9\u8BE6\u7EC6\u8BB2\u89E3\u3002</p><hr><h3 id="\u4E00\u3001\u6838\u5FC3\u7528\u6CD5\u4E0E\u542B\u4E49\u5BF9\u6BD4" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u6838\u5FC3\u7528\u6CD5\u4E0E\u542B\u4E49\u5BF9\u6BD4" aria-hidden="true">#</a> \u4E00\u3001\u6838\u5FC3\u7528\u6CD5\u4E0E\u542B\u4E49\u5BF9\u6BD4</h3><h4 id="_1-to-\u7684\u7528\u6CD5" tabindex="-1"><a class="header-anchor" href="#_1-to-\u7684\u7528\u6CD5" aria-hidden="true">#</a> 1. <code>To</code> \u7684\u7528\u6CD5</h4><p><strong>\u6838\u5FC3\u610F\u8C61\uFF1A\u65B9\u5411\u4E0E\u6307\u5411</strong></p><ol><li><p><strong>\u8868\u793A\u65B9\u5411\u3001\u76EE\u7684\u5730</strong></p><ul><li>I&#39;m going <strong>to</strong> the park. (\u6211\u8981\u53BB\u516C\u56ED\u3002)</li><li>She pointed <strong>to</strong> the map. (\u5979\u6307\u5411\u5730\u56FE\u3002)</li><li>This train is bound <strong>to</strong> London. (\u8FD9\u5217\u706B\u8F66\u662F\u5F00\u5F80\u4F26\u6566\u7684\u3002)</li></ul></li><li><p><strong>\u8868\u793A\u63A5\u6536\u8005\uFF08\u52A8\u4F5C\u7684\u201C\u65B9\u5411\u201D\u662F\u4EBA\u6216\u7269\uFF09</strong></p><ul><li>I gave a gift <strong>to</strong> my friend. (\u6211\u7ED9\u4E86\u6211\u7684\u670B\u53CB\u4E00\u4EFD\u793C\u7269\u3002)</li><li>Can you send this email <strong>to</strong> the manager? (\u4F60\u80FD\u628A\u8FD9\u5C01\u90AE\u4EF6\u53D1\u7ED9\u7ECF\u7406\u5417\uFF1F)</li><li>This letter is <strong>to</strong> you. (\u8FD9\u5C01\u4FE1\u662F\u7ED9\u4F60\u7684\u3002)</li></ul></li><li><p><strong>\u8868\u793A\u65F6\u95F4\u7EC8\u70B9\uFF08\u65F6\u95F4\u7684\u201C\u65B9\u5411\u201D\uFF09</strong></p><ul><li>The shop is open from 9 am <strong>to</strong> 5 pm. (\u5546\u5E97\u4ECE\u65E9\u4E0A9\u70B9\u5F00\u5230\u4E0B\u53485\u70B9\u3002)</li><li>I stayed up <strong>to</strong> midnight. (\u6211\u71AC\u591C\u5230\u5348\u591C\u3002)</li></ul></li><li><p><strong>\u4E0E\u52A8\u8BCD\u539F\u5F62\u8FDE\u7528\uFF0C\u6784\u6210\u52A8\u8BCD\u4E0D\u5B9A\u5F0F\uFF08\u8868\u793A\u76EE\u7684\u6216\u5C06\u8981\u53D1\u751F\u7684\u52A8\u4F5C\uFF09</strong></p><ul><li>He wants <strong>to</strong> learn English. (\u4ED6\u60F3\u5B66\u82F1\u8BED\u3002)</li><li>I need <strong>to</strong> go now. (\u6211\u73B0\u5728\u5F97\u8D70\u4E86\u3002)</li><li>It&#39;s important <strong>to</strong> drink water. (\u559D\u6C34\u5F88\u91CD\u8981\u3002)</li></ul></li></ol><h4 id="_2-for-\u7684\u7528\u6CD5" tabindex="-1"><a class="header-anchor" href="#_2-for-\u7684\u7528\u6CD5" aria-hidden="true">#</a> 2. <code>For</code> \u7684\u7528\u6CD5</h4><p><strong>\u6838\u5FC3\u610F\u8C61\uFF1A\u76EE\u7684\u4E0E\u5229\u76CA</strong></p><ol><li><p><strong>\u8868\u793A\u76EE\u7684\u3001\u7528\u9014</strong></p><ul><li>This is a tool <strong>for</strong> cutting paper. (\u8FD9\u662F\u4E00\u4E2A\u7528\u6765\u526A\u7EB8\u7684\u5DE5\u5177\u3002)</li><li>What do you need this <strong>for</strong>? (\u4F60\u9700\u8981\u8FD9\u4E2A\u6765\u505A\u4EC0\u4E48\uFF1F)</li><li>She&#39;s studying <strong>for</strong> her exam. (\u5979\u6B63\u5728\u4E3A\u8003\u8BD5\u800C\u5B66\u4E60\u3002)</li></ul></li><li><p><strong>\u8868\u793A\u53D7\u76CA\u8005\u3001\u4EE3\u8868</strong></p><ul><li>I bought a gift <strong>for</strong> my mother. (\u6211\u4E3A\u6211\u5988\u5988\u4E70\u4E86\u4E00\u4EFD\u793C\u7269\u3002)</li><li>Can you do this <strong>for</strong> me? (\u4F60\u80FD\u4E3A\u6211\u505A\u8FD9\u4EF6\u4E8B\u5417\uFF1F)</li><li>He works <strong>for</strong> a big company. (\u4ED6\u4E3A\u4E00\u5BB6\u5927\u516C\u53F8\u5DE5\u4F5C\u3002)</li></ul></li><li><p><strong>\u8868\u793A\u539F\u56E0</strong></p><ul><li>Thank you <strong>for</strong> your help. (\u8C22\u8C22\u4F60\u7684\u5E2E\u52A9\u3002)</li><li>The city is famous <strong>for</strong> its food. (\u8FD9\u4E2A\u57CE\u5E02\u4EE5\u5176\u7F8E\u98DF\u800C\u95FB\u540D\u3002)</li><li>He was punished <strong>for</strong> being late. (\u4ED6\u56E0\u4E3A\u8FDF\u5230\u800C\u53D7\u5230\u60E9\u7F5A\u3002)</li></ul></li><li><p><strong>\u8868\u793A\u4E00\u6BB5\u65F6\u95F4\uFF08\u65F6\u95F4\u7684\u201C\u8DE8\u5EA6\u201D\uFF09</strong></p><ul><li>I lived there <strong>for</strong> three years. (\u6211\u5728\u90A3\u91CC\u4F4F\u4E86\u4E09\u5E74\u3002)</li><li>We waited <strong>for</strong> an hour. (\u6211\u4EEC\u7B49\u4E86\u4E00\u4E2A\u5C0F\u65F6\u3002)</li></ul></li></ol><hr><h3 id="\u4E8C\u3001\u7ECF\u5178\u5BF9\u6BD4\u573A\u666F-\u6700\u5BB9\u6613\u6DF7\u6DC6\u7684\u5730\u65B9" tabindex="-1"><a class="header-anchor" href="#\u4E8C\u3001\u7ECF\u5178\u5BF9\u6BD4\u573A\u666F-\u6700\u5BB9\u6613\u6DF7\u6DC6\u7684\u5730\u65B9" aria-hidden="true">#</a> \u4E8C\u3001\u7ECF\u5178\u5BF9\u6BD4\u573A\u666F\uFF08\u6700\u5BB9\u6613\u6DF7\u6DC6\u7684\u5730\u65B9\uFF09</h3><h4 id="\u573A\u666F1-\u7ED9\u4E88\u4E0E\u63A5\u6536" tabindex="-1"><a class="header-anchor" href="#\u573A\u666F1-\u7ED9\u4E88\u4E0E\u63A5\u6536" aria-hidden="true">#</a> \u573A\u666F1\uFF1A\u7ED9\u4E88\u4E0E\u63A5\u6536</h4><ul><li><strong>Give [\u4E1C\u897F] <code>to</code> [\u67D0\u4EBA]</strong>\uFF1A\u5F3A\u8C03\u4E1C\u897F\u7684\u79FB\u52A8<strong>\u65B9\u5411</strong>\u662F\u201C\u67D0\u4EBA\u201D\u3002 <ul><li>Please give this book <strong>to</strong> John. (\u8BF7\u628A\u8FD9\u672C\u4E66\u7ED9\u7EA6\u7FF0\u3002)</li></ul></li><li><strong>Give/Buy/Make [\u4E1C\u897F] <code>for</code> [\u67D0\u4EBA]</strong>\uFF1A\u5F3A\u8C03\u505A\u8FD9\u4E2A\u52A8\u4F5C\u662F<strong>\u4E3A\u4E86</strong>\u67D0\u4EBA\u7684\u5229\u76CA\u3002 <ul><li>I bought a book <strong>for</strong> John. (\u6211\u4E70\u4E86\u4E00\u672C\u4E66\u7ED9\u7EA6\u7FF0\u3002/ \u6211\u4E3A\u7EA6\u7FF0\u4E70\u4E86\u4E00\u672C\u4E66\u3002)</li></ul></li></ul><p><strong>\u6CE8\u610F</strong>\uFF1A\u5728\u50CF <code>give</code>, <code>send</code>, <code>show</code> \u8FD9\u6837\u7684\u52A8\u8BCD\u540E\uFF0C\u5982\u679C\u63A5\u6536\u8005\u76F4\u63A5\u8DDF\u5728\u52A8\u8BCD\u540E\u9762\uFF0C\u53EF\u4EE5\u7701\u7565 <code>to</code>\u3002</p><ul><li>Give John the book. = Give the book <strong>to</strong> John.</li></ul><h4 id="\u573A\u666F2-\u76EE\u7684\u5730-vs-\u76EE\u7684" tabindex="-1"><a class="header-anchor" href="#\u573A\u666F2-\u76EE\u7684\u5730-vs-\u76EE\u7684" aria-hidden="true">#</a> \u573A\u666F2\uFF1A\u76EE\u7684\u5730 vs. \u76EE\u7684</h4><ul><li><strong>Go <code>to</code> [\u5730\u65B9]</strong>\uFF1A\u53BB\u67D0\u4E2A<strong>\u5730\u65B9</strong>\u3002 <ul><li>Let&#39;s go <strong>to</strong> the cinema. (\u6211\u4EEC\u53BB\u7535\u5F71\u9662\u5427\u3002)</li></ul></li><li><strong>Go <code>for</code> [\u6D3B\u52A8/\u4E8B\u7269]</strong>\uFF1A\u53BB\u8FDB\u884C\u67D0\u4E2A<strong>\u6D3B\u52A8</strong>\u3002 <ul><li>Let&#39;s go <strong>for</strong> a walk. (\u6211\u4EEC\u53BB\u6563\u6B65\u5427\u3002)</li><li>Let&#39;s go <strong>for</strong> a coffee. (\u6211\u4EEC\u53BB\u559D\u676F\u5496\u5561\u5427\u3002)</li></ul></li></ul><h4 id="\u573A\u666F3-\u5BF9\u67D0\u4EBA\u6765\u8BF4" tabindex="-1"><a class="header-anchor" href="#\u573A\u666F3-\u5BF9\u67D0\u4EBA\u6765\u8BF4" aria-hidden="true">#</a> \u573A\u666F3\uFF1A\u5BF9\u67D0\u4EBA\u6765\u8BF4</h4><ul><li><strong>It&#39;s <code>to</code> [\u67D0\u4EBA]</strong>\uFF1A\u8FD9\u662F<strong>\u7ED9</strong>\u67D0\u4EBA\u7684\uFF08\u5F3A\u8C03\u63A5\u6536\uFF09\u3002 <ul><li>This letter is <strong>to</strong> you. (\u8FD9\u5C01\u4FE1\u662F\u7ED9\u4F60\u7684\u3002)</li></ul></li><li><strong>It&#39;s <code>for</code> [\u67D0\u4EBA]</strong>\uFF1A\u8FD9\u662F<strong>\u4E3A</strong>\u67D0\u4EBA\u51C6\u5907\u7684\uFF08\u5F3A\u8C03\u53D7\u76CA\u6216\u610F\u56FE\uFF09\u3002 <ul><li>This gift is <strong>for</strong> you. (\u8FD9\u4E2A\u793C\u7269\u662F\u7ED9\u4F60\u7684\u3002)</li></ul></li><li><strong>It&#39;s important <code>to</code> me.</strong>\uFF1A\u8FD9\u5BF9\u6211\u5F88\u91CD\u8981\u3002\uFF08\u5F3A\u8C03\u6211\u7684<strong>\u611F\u53D7</strong>\uFF0C\u8FD9\u4EF6\u4E8B\u201C\u6307\u5411\u201D\u6211\u5185\u5FC3\u3002\uFF09</li><li><strong>It&#39;s important <code>for</code> me.</strong>\uFF1A\u8FD9\u5BF9\u6211\u5F88\u91CD\u8981\u3002\uFF08\u5F3A\u8C03\u5BF9\u6211\u7684<strong>\u5229\u76CA\u6216\u5FC5\u8981\u6027</strong>\uFF0C\u6BD4\u5982\u5BF9\u6211\u7684\u4E8B\u4E1A\u3001\u5065\u5EB7\u6709\u597D\u5904\u3002\uFF09</li></ul><p>\u8FD9\u4E2A\u533A\u522B\u5F88\u5FAE\u5999\uFF0C\u6709\u65F6\u53EF\u4EE5\u4E92\u6362\uFF0C\u4F46\u4FA7\u91CD\u70B9\u4E0D\u540C\u3002</p><hr><h3 id="\u4E09\u3001\u56FA\u5B9A\u642D\u914D" tabindex="-1"><a class="header-anchor" href="#\u4E09\u3001\u56FA\u5B9A\u642D\u914D" aria-hidden="true">#</a> \u4E09\u3001\u56FA\u5B9A\u642D\u914D</h3><p>\u6709\u4E9B\u52A8\u8BCD\u3001\u540D\u8BCD\u548C\u5F62\u5BB9\u8BCD\u540E\u9762\u56FA\u5B9A\u8DDF <code>for</code> \u6216 <code>to</code>\uFF0C\u9700\u8981\u8BB0\u5FC6\u3002</p><p><strong>\u5E38\u7528 <code>for</code> \u7684\u642D\u914D\uFF1A</strong></p><ul><li><strong>Ask for</strong> (\u8BF7\u6C42)\uFF1AHe asked <strong>for</strong> help.</li><li><strong>Look for</strong> (\u5BFB\u627E)\uFF1AI&#39;m looking <strong>for</strong> my keys.</li><li><strong>Wait for</strong> (\u7B49\u5F85)\uFF1AWait <strong>for</strong> me!</li><li><strong>Thank you for</strong> (\u56E0...\u611F\u8C22)\uFF1AThanks <strong>for</strong> coming.</li><li><strong>Be responsible for</strong> (\u5BF9...\u8D1F\u8D23)\uFF1AWho is responsible <strong>for</strong> this?</li><li><strong>Be good/bad for</strong> (\u5BF9...\u6709\u76CA/\u5BB3)\uFF1AExercise is good <strong>for</strong> you.</li></ul><p><strong>\u5E38\u7528 <code>to</code> \u7684\u642D\u914D\uFF1A</strong></p><ul><li><strong>Listen to</strong> (\u542C)\uFF1AListen <strong>to</strong> this song.</li><li><strong>Talk/Speak to</strong> (\u548C...\u8BF4\u8BDD)\uFF1AI spoke <strong>to</strong> my boss.</li><li><strong>Belong to</strong> (\u5C5E\u4E8E)\uFF1AThis book belongs <strong>to</strong> me.</li><li><strong>Be used to</strong> (\u4E60\u60EF\u4E8E...)\uFF1AI am used <strong>to</strong> the weather here.</li><li><strong>Look forward to</strong> (\u671F\u5F85)\uFF1AI look forward <strong>to</strong> seeing you.</li></ul><h3 id="\u603B\u7ED3\u8868\u683C" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3\u8868\u683C" aria-hidden="true">#</a> \u603B\u7ED3\u8868\u683C</h3><table><thead><tr><th style="text-align:left;">\u7279\u5F81</th><th style="text-align:left;">To</th><th style="text-align:left;">For</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u6838\u5FC3\u542B\u4E49</strong></td><td style="text-align:left;">\u65B9\u5411\uFF0C\u79FB\u52A8\uFF0C\u63A5\u6536\u8005</td><td style="text-align:left;">\u76EE\u7684\uFF0C\u5229\u76CA\uFF0C\u539F\u56E0\uFF0C\u4EE3\u8868</td></tr><tr><td style="text-align:left;"><strong>\u7A7A\u95F4</strong></td><td style="text-align:left;">\u53BB<strong>\u5230</strong>\u67D0\u5730</td><td style="text-align:left;">(\u901A\u5E38\u4E0D\u76F4\u63A5\u8868\u793A\u7A7A\u95F4)</td></tr><tr><td style="text-align:left;"><strong>\u65F6\u95F4</strong></td><td style="text-align:left;">\u5230...\u4E3A\u6B62 (\u7EC8\u70B9)</td><td style="text-align:left;">\u6301\u7EED...\u65F6\u95F4 (\u8DE8\u5EA6)</td></tr><tr><td style="text-align:left;"><strong>\u4EBA\u7269</strong></td><td style="text-align:left;"><strong>\u7ED9</strong>\u67D0\u4EBA (\u63A5\u6536)</td><td style="text-align:left;"><strong>\u4E3A</strong>\u67D0\u4EBA (\u53D7\u76CA)</td></tr><tr><td style="text-align:left;"><strong>\u52A8\u8BCD\u540E</strong></td><td style="text-align:left;">\u63A5\u52A8\u8BCD\u539F\u5F62 (\u4E0D\u5B9A\u5F0F)</td><td style="text-align:left;">\u63A5\u52A8\u540D\u8BCD (V-ing)</td></tr><tr><td style="text-align:left;"><strong>\u4F8B\u53E5</strong></td><td style="text-align:left;">I go <strong>to</strong> school.</td><td style="text-align:left;">This is <strong>for</strong> cooking.</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">I gave it <strong>to</strong> her.</td><td style="text-align:left;">I bought it <strong>for</strong> her.</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">It&#39;s important <strong>to</strong> me.</td><td style="text-align:left;">It&#39;s important <strong>for</strong> me.</td></tr></tbody></table><p><strong>\u6700\u540E\u7684\u8BC0\u7A8D</strong>\uFF1A \u5F53\u4F60\u4E0D\u786E\u5B9A\u8BE5\u7528\u54EA\u4E2A\u65F6\uFF0C\u95EE\u81EA\u5DF1\u4E00\u4E2A\u95EE\u9898\uFF1A</p><ul><li>\u6211\u60F3\u8868\u8FBE\u7684\u662F <strong>\u201C\u53BB\u5F80/\u7ED9\u4E88\u201D (<code>to</code>)</strong> \u7684\u6982\u5FF5\uFF0C\u8FD8\u662F <strong>\u201C\u4E3A\u4E86/\u539F\u56E0\u201D (<code>for</code>)</strong> \u7684\u6982\u5FF5\uFF1F</li></ul>',34),l=[e];function g(i,d){return o(),r("div",null,l)}var h=t(n,[["render",g],["__file","index.html.vue"]]);export{h as default};
