import{_ as t,o as e,c as r,e as n}from"./app.303070c9.js";const s={},o=n('<h2 id="\u526F\u8BCD-adverb" tabindex="-1"><a class="header-anchor" href="#\u526F\u8BCD-adverb" aria-hidden="true">#</a> \u526F\u8BCD Adverb</h2><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u526F\u8BCD-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u526F\u8BCD-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u526F\u8BCD\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p>\u8FD8\u8BB0\u5F97\u6211\u4EEC\u4E0A\u6B21\u5B66\u7684\u201C\u8C13\u8BED\u201D\u5417\uFF1F\u8C13\u8BED\u662F\u53E5\u5B50\u7684\u53D1\u52A8\u673A\uFF0C\u8BF4\u660E\u4E3B\u8BED\u201C\u5E72\u4EC0\u4E48\u201D\u3002\u90A3\u4E48\uFF0C<strong>\u526F\u8BCD\uFF0C\u5C31\u662F\u7ED9\u8FD9\u4E2A\u201C\u53D1\u52A8\u673A\u201D\u52A0\u7279\u6548\u3001\u4E0A\u6DA6\u6ED1\u6CB9\u3001\u6216\u8005\u63CF\u8FF0\u53D1\u52A8\u673A\u8FD0\u884C\u72B6\u51B5\u7684\u5DE5\u5177\u3002</strong></p><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u526F\u8BCD\u662F\u7528\u6765\u4FEE\u9970\u52A8\u8BCD\u3001\u5F62\u5BB9\u8BCD\u3001\u5176\u4ED6\u526F\u8BCD\u751A\u81F3\u6574\u4E2A\u53E5\u5B50\u7684\u8BCD\u3002\u5B83\u8D1F\u8D23\u56DE\u7B54\u201C\u600E\u6837\u5730\uFF1F\u201D\u3001\u201C\u4F55\u65F6\uFF1F\u201D\u3001\u201C\u4F55\u5730\uFF1F\u201D\u3001\u201C\u5230\u4EC0\u4E48\u7A0B\u5EA6\uFF1F\u201D\u8FD9\u7C7B\u95EE\u9898\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong> \u628A\u4E00\u53E5\u8BDD\u6BD4\u4F5C\u4E00\u5E45\u753B\u3002</p><ul><li><strong>\u4E3B\u8BED\u548C\u8C13\u8BED</strong> \u662F\u753B\u91CC\u7684<strong>\u4EBA\u7269\u548C\u6838\u5FC3\u52A8\u4F5C</strong>\uFF08\u6BD4\u5982\uFF1A\u4E00\u4E2A\u5973\u5B69\u5728\u8DD1\u6B65\uFF09\u3002</li><li><strong>\u526F\u8BCD</strong> \u5C31\u662F\u753B\u91CC\u7684<strong>\u80CC\u666F\u3001\u5149\u5F71\u548C\u901F\u5EA6\u7EBF</strong>\u3002 <ul><li>\u5979<strong>\u5FEB\u901F\u5730</strong>\u8DD1\u3002\uFF08\u201C\u5FEB\u901F\u5730\u201D\u4E3A\u52A8\u4F5C\u201C\u8DD1\u201D\u52A0\u4E0A\u901F\u5EA6\u7EBF\uFF09</li><li>\u5979<strong>\u5728\u8FD9\u91CC</strong>\u8DD1\u3002\uFF08\u201C\u5728\u8FD9\u91CC\u201D\u4E3A\u52A8\u4F5C\u8BBE\u5B9A\u80CC\u666F\u5730\u70B9\uFF09</li><li>\u5979<strong>\u6628\u5929</strong>\u8DD1\u3002\uFF08\u201C\u6628\u5929\u201D\u4E3A\u52A8\u4F5C\u8BBE\u5B9A\u65F6\u95F4\u80CC\u666F\uFF09</li><li>\u5979\u8DD1\u5F97<strong>\u975E\u5E38</strong>\u5FEB\u3002\uFF08\u201C\u975E\u5E38\u201D\u4FEE\u9970\u53E6\u4E00\u4E2A\u526F\u8BCD\u201C\u5FEB\u201D\uFF0C\u8868\u793A\u7A0B\u5EA6\uFF09</li></ul></li></ul><p>\u6CA1\u6709\u526F\u8BCD\uFF0C\u53E5\u5B50\u57FA\u672C\u610F\u601D\u5B8C\u6574\uFF0C\u4F46\u6709\u4E86\u526F\u8BCD\uFF0C\u53E5\u5B50\u5C31\u53D8\u5F97\u66F4\u751F\u52A8\u3001\u5177\u4F53\u3001\u5BCC\u6709\u753B\u9762\u611F\u3002</p><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u526F\u8BCD-\u540D\u79F0\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u526F\u8BCD-\u540D\u79F0\u7684\u7531\u6765" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u4E3A\u4EC0\u4E48\u53EB\u201C\u526F\u8BCD\u201D\uFF1F\u540D\u79F0\u7684\u7531\u6765</h3><ol><li><p><strong>\u4E2D\u6587\u540D\u201C\u526F\u8BCD\u201D</strong>\uFF1A</p><ul><li>\u201C\u526F\u201D\u7684\u610F\u601D\u662F\u201C\u6B21\u8981\u7684\u201D\u3001\u201C\u8F85\u52A9\u7684\u201D\u3002</li><li>\u201C\u526F\u8BCD\u201D\u5C31\u662F<strong>\u8F85\u52A9\u548C\u4FEE\u9970\u52A8\u8BCD\uFF08\u4EE5\u53CA\u5F62\u5BB9\u8BCD\u7B49\u5176\u4ED6\u8BCD\uFF09\u7684\u8BCD</strong>\u3002\u5B83\u662F\u52A8\u8BCD\u7684\u201C\u526F\u624B\u201D\u548C\u201C\u5E2E\u624B\u201D\u3002</li></ul></li><li><p><strong>\u82F1\u6587\u540D\u201CAdverb\u201D</strong>\uFF1A</p><ul><li>\u8BFB\u97F3\uFF1A/\u02C8\xE6dv\u025C\u02D0rb/</li><li>\u8FD9\u4E2A\u8BCD\u6765\u6E90\u4E8E\u62C9\u4E01\u8BED <code>adverbium</code>\uFF0C\u7531 <code>ad-</code>\uFF08\u610F\u4E3A\u201C\u5411\u3001\u9760\u8FD1\u3001\u9644\u52A0\u201D\uFF09\u548C <code>verbum</code>\uFF08\u610F\u4E3A\u201C\u52A8\u8BCD\u201D\uFF09\u4E24\u90E8\u5206\u7EC4\u6210\u3002</li><li>\u6240\u4EE5\u4ECE\u5B57\u9762\u4E0A\u770B\uFF0CAdverb \u5C31\u662F\u201C<strong>\u9644\u52A0\u5728\u52A8\u8BCD\u65C1\u8FB9\u7684\u8BCD</strong>\u201D\u3002\u8FD9\u4E2A\u540D\u5B57\u975E\u5E38\u76F4\u767D\u5730\u63ED\u793A\u4E86\u5B83\u7684\u6838\u5FC3\u529F\u80FD\u3002</li></ul></li></ol><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u54EA\u4E9B\u4E1C\u897F\u53EF\u4EE5\u5F53\u526F\u8BCD-\u6838\u5FC3\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u54EA\u4E9B\u4E1C\u897F\u53EF\u4EE5\u5F53\u526F\u8BCD-\u6838\u5FC3\u5185\u5BB9" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u54EA\u4E9B\u4E1C\u897F\u53EF\u4EE5\u5F53\u526F\u8BCD\uFF1F\uFF08\u6838\u5FC3\u5185\u5BB9\uFF09</h3><p>\u526F\u8BCD\u662F\u4E00\u4E2A\u975E\u5E38\u5E9E\u5927\u7684\u5BB6\u65CF\uFF0C\u6210\u5458\u5F88\u591A\uFF0C\u800C\u4E14\u5F62\u6001\u5404\u5F02\u3002\u6211\u4EEC\u628A\u5B83\u5206\u6210\u51E0\u5927\u7C7B\u6765\u770B\u3002</p><table><thead><tr><th style="text-align:left;">\u5145\u5F53\u526F\u8BCD\u7684\u8BED\u6CD5\u5355\u4F4D</th><th style="text-align:left;">\u8BED\u6CD5\u5F62\u6001\u548C\u610F\u601D</th><th style="text-align:left;">\u4F8B\u53E5\uFF08\u526F\u8BCD\u7528<strong>\u7C97\u4F53</strong>\u6807\u51FA\uFF0C\u5B83\u4FEE\u9970\u7684\u5BF9\u8C61\u7528<em>\u659C\u4F53</em>\u6807\u51FA\uFF09</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>1. \u7B80\u5355\u526F\u8BCD</strong></td><td style="text-align:left;">\u6700\u5E38\u89C1\u7684\u5355\u4E2A\u526F\u8BCD\uFF0C\u5F88\u591A\u4EE5 <strong>-ly</strong> \u7ED3\u5C3E\u3002\u8868\u793A\u65B9\u5F0F\u3001\u7A0B\u5EA6\u3001\u65F6\u95F4\u3001\u5730\u70B9\u7B49\u3002</td><td style="text-align:left;">She <em>sings</em> <strong>beautifully</strong>. \uFF08\u5979\u5531\u5F97\u5F88\u7F8E\u3002\uFF09- <strong>\u4FEE\u9970\u52A8\u8BCD</strong> <em>sings</em> <br> He is <strong>extremely</strong> <em>rich</em>. \uFF08\u4ED6\u6781\u5176\u5BCC\u6709\u3002\uFF09- <strong>\u4FEE\u9970\u5F62\u5BB9\u8BCD</strong> <em>rich</em> <br> He runs <strong>very</strong> <em>quickly</em>. \uFF08\u4ED6\u8DD1\u5F97\u975E\u5E38\u5FEB\u3002\uFF09- <strong>\u4FEE\u9970\u53E6\u4E00\u4E2A\u526F\u8BCD</strong> <em>quickly</em></td></tr><tr><td style="text-align:left;"><strong>2. \u526F\u8BCD\u77ED\u8BED</strong></td><td style="text-align:left;">\u7531\u4ECB\u8BCD+\u540D\u8BCD\u6784\u6210\uFF0C\u529F\u80FD\u4E0A\u76F8\u5F53\u4E8E\u4E00\u4E2A\u526F\u8BCD\uFF0C\u5E38\u8868\u793A\u65F6\u95F4\u3001\u5730\u70B9\u3001\u65B9\u5F0F\u7B49\u3002</td><td style="text-align:left;">We <em>met</em> <strong>in the park</strong>. \uFF08\u6211\u4EEC\u5728\u516C\u56ED\u89C1\u4E86\u9762\u3002\uFF09- \u8868\u5730\u70B9 <br> He <em>arrived</em> <strong>at five o&#39;clock</strong>. \uFF08\u4ED6\u4E94\u70B9\u949F\u5230\u7684\u3002\uFF09- \u8868\u65F6\u95F4 <br> She <em>succeeded</em> <strong>by working hard</strong>. \uFF08\u5979\u901A\u8FC7\u52AA\u529B\u5DE5\u4F5C\u53D6\u5F97\u4E86\u6210\u529F\u3002\uFF09- \u8868\u65B9\u5F0F</td></tr><tr><td style="text-align:left;"><strong>3. \u7591\u95EE\u526F\u8BCD</strong></td><td style="text-align:left;">\u7528\u4E8E\u5F15\u5BFC\u7279\u6B8A\u7591\u95EE\u53E5\uFF0C\u8BE2\u95EE\u65B9\u5F0F\u3001\u65F6\u95F4\u3001\u5730\u70B9\u3001\u539F\u56E0\u3002<strong>how, when, where, why</strong>\u3002</td><td style="text-align:left;"><strong>How</strong> did you <em>come</em> here? \uFF08\u4F60\u600E\u4E48\u6765\u7684\uFF1F\uFF09<br> <strong>When</strong> will you <em>leave</em>? \uFF08\u4F60\u4EC0\u4E48\u65F6\u5019\u8D70\uFF1F\uFF09<br> <strong>Where</strong> is my <em>book</em>? \uFF08\u6211\u7684\u4E66\u5728\u54EA\u513F\uFF1F\uFF09</td></tr><tr><td style="text-align:left;"><strong>4. \u5173\u7CFB\u526F\u8BCD</strong></td><td style="text-align:left;">\u7528\u4E8E\u5F15\u5BFC\u5B9A\u8BED\u4ECE\u53E5\uFF0C\u5728\u4ECE\u53E5\u4E2D\u5145\u5F53\u72B6\u8BED\uFF0C\u4FEE\u9970\u4E3B\u53E5\u7684\u67D0\u4E2A\u540D\u8BCD\u3002<strong>when, where, why</strong>\u3002</td><td style="text-align:left;">This is the house <strong>where</strong> I <em>was born</em>. \uFF08\u8FD9\u5C31\u662F\u6211\u51FA\u751F\u7684\u623F\u5B50\u3002\uFF09<br> I remember the day <strong>when</strong> we first <em>met</em>. \uFF08\u6211\u8BB0\u5F97\u6211\u4EEC\u521D\u6B21\u89C1\u9762\u7684\u90A3\u5929\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>5. \u8FDE\u63A5\u526F\u8BCD</strong></td><td style="text-align:left;">\u7528\u4E8E\u8FDE\u63A5\u4E24\u4E2A\u53E5\u5B50\u6216\u6982\u5FF5\uFF0C\u8868\u793A\u8F6C\u6298\u3001\u56E0\u679C\u3001\u987A\u5E8F\u7B49\u5173\u7CFB\u3002<strong>however, therefore, moreover</strong>\u7B49\u3002</td><td style="text-align:left;">It was raining; <strong>however</strong>, we <em>went</em> out. \uFF08\u4E0B\u96E8\u4E86\uFF0C\u7136\u800C\u6211\u4EEC\u8FD8\u662F\u51FA\u53BB\u4E86\u3002\uFF09<br> He was sick; <strong>therefore</strong>, he <em>didn&#39;t come</em>. \uFF08\u4ED6\u75C5\u4E86\uFF0C\u56E0\u6B64\u6CA1\u6765\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>6. \u6574\u4E2A\u53E5\u5B50\uFF08\u53E5\u5B50\u526F\u8BCD\uFF09</strong></td><td style="text-align:left;">\u4FEE\u9970\u6574\u4E2A\u53E5\u5B50\uFF0C\u8868\u8FBE\u8BF4\u8BDD\u8005\u7684\u770B\u6CD5\u6216\u6001\u5EA6\u3002\u901A\u5E38\u7528\u9017\u53F7\u4E0E\u4E3B\u53E5\u9694\u5F00\u3002</td><td style="text-align:left;"><strong>Fortunately</strong>, he <em>passed</em> the exam. \uFF08\u5E78\u8FD0\u7684\u662F\uFF0C\u4ED6\u901A\u8FC7\u4E86\u8003\u8BD5\u3002\uFF09<br> <strong>Honestly</strong>, I don&#39;t <em>like</em> it. \uFF08\u8001\u5B9E\u8BF4\uFF0C\u6211\u4E0D\u559C\u6B22\u5B83\u3002\uFF09</td></tr></tbody></table><hr><h3 id="\u603B\u7ED3\u4E0E\u63D0\u9192" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3\u4E0E\u63D0\u9192" aria-hidden="true">#</a> \u603B\u7ED3\u4E0E\u63D0\u9192</h3><ol><li><strong>\u526F\u8BCD\u7684\u4E07\u80FD\u6027</strong>\uFF1A\u8BB0\u4F4F\uFF0C\u526F\u8BCD\u662F\u201C\u4FEE\u4FEE\u8865\u8865\u201D\u7684\u4E07\u80FD\u5DE5\u5177\u3002\u5B83\u53EF\u4EE5\u4FEE\u9970<strong>\u52A8\u8BCD\u3001\u5F62\u5BB9\u8BCD\u3001\u5176\u4ED6\u526F\u8BCD\u3001\u4E43\u81F3\u6574\u4E2A\u53E5\u5B50</strong>\u3002</li><li><strong>-ly \u4E0D\u662F\u4E07\u80FD\u6807\u7B7E</strong>\uFF1A\u867D\u7136\u5F88\u591A\u526F\u8BCD\u4EE5 -ly \u7ED3\u5C3E\uFF08\u5982 quickly, happily\uFF09\uFF0C\u4F46\u5E76\u975E\u6240\u6709 -ly \u7ED3\u5C3E\u7684\u8BCD\u90FD\u662F\u526F\u8BCD\uFF08\u5982 friendly \u662F\u5F62\u5BB9\u8BCD\uFF09\uFF0C\u4E5F\u5E76\u975E\u6240\u6709\u526F\u8BCD\u90FD\u4EE5 -ly \u7ED3\u5C3E\uFF08\u5982 fast, hard, here, there, now, then, very\uFF09\u3002</li><li><strong>\u5982\u4F55\u5FEB\u901F\u8BC6\u522B\u526F\u8BCD\uFF1F</strong> \u95EE\u5173\u4E8E\u4F60\u6240\u4FEE\u9970\u7684\u90A3\u4E2A\u8BCD\u7684\u95EE\u9898\uFF1A <ul><li><em>\u52A8\u8BCD</em>\uFF1A\u4ED6<em>\u8DD1</em>\u5F97\u600E\u4E48\u6837\uFF1F -&gt; <strong>\u5FEB\u901F\u5730</strong> (quickly)</li><li><em>\u5F62\u5BB9\u8BCD</em>\uFF1A\u5979\u6709\u591A<em>\u806A\u660E</em>\uFF1F -&gt; <strong>\u975E\u5E38</strong> (very)</li><li><em>\u53E5\u5B50</em>\uFF1A<strong>\u603B\u7684\u6765\u8BF4</strong>\uFF0C\u8FD9\u6B21\u7ECF\u5386\u662F\u597D\u7684\u3002 (Overall)</li></ul></li></ol>',17),l=[o];function g(i,d){return e(),r("div",null,l)}var h=t(s,[["render",g],["__file","index.html.vue"]]);export{h as default};
