import{_ as t,o as r,c as o,e as n}from"./app.c96210a9.js";const s={},e=n('<h2 id="\u52A8\u540D\u8BCD\u548C\u4E0D\u5B9A\u5F0F\u6709\u5565\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#\u52A8\u540D\u8BCD\u548C\u4E0D\u5B9A\u5F0F\u6709\u5565\u533A\u522B" aria-hidden="true">#</a> \u52A8\u540D\u8BCD\u548C\u4E0D\u5B9A\u5F0F\u6709\u5565\u533A\u522B</h2><p>\u592A\u597D\u4E86\uFF01\u8FD9\u4E2A\u95EE\u9898\u95EE\u5230\u4E86\u82F1\u8BED\u8BED\u6CD5\u4E2D\u7684\u4E00\u4E2A\u6838\u5FC3\u7CBE\u9AD3\u3002\u4F5C\u4E3A\u4F60\u7684\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u5B8C\u5168\u7406\u89E3\u4F60\u7684\u56F0\u60D1\uFF0C\u56E0\u4E3A\u5F88\u591A\u65F6\u5019\u4E2D\u6587\u91CC\u90FD\u7528\u201C\u505A\u67D0\u4E8B\u201D\u6765\u8868\u8FBE\uFF0C\u4F46\u5728\u82F1\u8BED\u91CC\uFF0C\u7528 <code>to do</code> \u8FD8\u662F <code>doing</code>\uFF0C\u610F\u601D\u53EF\u80FD\u5929\u5DEE\u5730\u522B\u3002</p><p>\u6211\u4EEC\u4ECA\u5929\u5C31\u6765\u5F7B\u5E95\u641E\u5B9A\u5B83\u3002</p><h3 id="\u6838\u5FC3\u5FC3\u6CD5-\u540D\u95E8\u6B63\u6D3E-vs-\u6C5F\u6E56\u4FA0\u5BA2" tabindex="-1"><a class="header-anchor" href="#\u6838\u5FC3\u5FC3\u6CD5-\u540D\u95E8\u6B63\u6D3E-vs-\u6C5F\u6E56\u4FA0\u5BA2" aria-hidden="true">#</a> <strong>\u6838\u5FC3\u5FC3\u6CD5\uFF1A\u540D\u95E8\u6B63\u6D3E vs. \u6C5F\u6E56\u4FA0\u5BA2</strong></h3><p>\u6211\u4EEC\u53EF\u4EE5\u7528\u4E00\u4E2A\u6BD4\u55BB\u6765\u5F00\u573A\uFF1A</p><ul><li><strong>\u52A8\u540D\u8BCD (doing)</strong> \u50CF\u4E00\u4E2A <strong>\u201C\u540D\u95E8\u6B63\u6D3E\u201D</strong>\u3002\u5B83\u867D\u7136\u51FA\u8EAB\u4E8E\u52A8\u8BCD\uFF0C\u4F46\u5DF2\u7ECF\u5B8C\u5168<strong>\u5F52\u5316\u6210\u4E86\u540D\u8BCD</strong>\uFF0C\u884C\u4E3A\u4E3E\u6B62\u90FD\u548C\u540D\u8BCD\u4E00\u6837\uFF0C\u4EE3\u8868\u4E00\u4E2A<strong>\u65E2\u6210\u7684\u4E8B\u5B9E</strong>\u6216<strong>\u4E00\u9879\u5177\u4F53\u7684\u6D3B\u52A8</strong>\u3002</li><li><strong>\u52A8\u8BCD\u4E0D\u5B9A\u5F0F (to do)</strong> \u50CF\u4E00\u4E2A <strong>\u201C\u6C5F\u6E56\u4FA0\u5BA2\u201D</strong>\u3002\u5B83\u867D\u7136\u53EF\u4EE5\u5728\u53E5\u4E2D\u626E\u6F14\u540D\u8BCD\u3001\u5F62\u5BB9\u8BCD\u3001\u526F\u8BCD\u7684\u89D2\u8272\uFF0C\u4F46\u5185\u5FC3\u59CB\u7EC8\u4FDD\u7559\u7740\u52A8\u8BCD\u7684\u201C\u4FA0\u6C14\u201D\uFF0C\u4EE3\u8868\u7740 <strong>\u201C\u6F5C\u5728\u7684\u3001\u672A\u6765\u7684\u3001\u6709\u76EE\u7684\u6027\u7684\u201D</strong> \u52A8\u4F5C\u3002</li></ul><p>\u8BB0\u4F4F\u8FD9\u4E2A\u611F\u89C9\uFF0C\u6211\u4EEC\u5F80\u4E0B\u770B\u3002</p><hr><h3 id="\u7B2C\u4E00\u90E8\u5206-\u6839\u672C\u6027\u7684\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u6839\u672C\u6027\u7684\u533A\u522B" aria-hidden="true">#</a> <strong>\u7B2C\u4E00\u90E8\u5206\uFF1A\u6839\u672C\u6027\u7684\u533A\u522B</strong></h3><table><thead><tr><th style="text-align:left;">\u5BF9\u6BD4\u7EF4\u5EA6</th><th style="text-align:left;"><strong>\u52A8\u540D\u8BCD</strong></th><th style="text-align:left;"><strong>\u52A8\u8BCD\u4E0D\u5B9A\u5F0F</strong></th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u6838\u5FC3\u8EAB\u4EFD</strong></td><td style="text-align:left;"><strong>\u672C\u8D28\u662F\u540D\u8BCD</strong>\uFF0C\u53EA\u662F\u7531\u52A8\u8BCD\u53D8\u6765\u7684\u3002</td><td style="text-align:left;"><strong>\u591A\u529F\u80FD\u6210\u5206</strong>\uFF0C\u53EF\u4F5C\u540D\u8BCD\u3001\u5F62\u5BB9\u8BCD\u3001\u526F\u8BCD\u3002</td></tr><tr><td style="text-align:left;"><strong>\u6838\u5FC3\u542B\u4E49</strong></td><td style="text-align:left;"><strong>\u5DF2\u53D1\u751F\u3001\u6CDB\u6307\u7684\u3001\u4F5C\u4E3A\u7ECF\u9A8C\u7684\u6D3B\u52A8</strong>\u3002</td><td style="text-align:left;"><strong>\u672A\u53D1\u751F\u3001\u7279\u5B9A\u7684\u3001\u6709\u76EE\u7684\u7684\u52A8\u4F5C</strong>\u3002</td></tr><tr><td style="text-align:left;"><strong>\u751F\u6D3B\u5316\u6BD4\u55BB</strong></td><td style="text-align:left;"><strong>\u4E00\u672C\u201C\u76F8\u518C\u201D\u6216\u201C\u6210\u5C31\u6E05\u5355\u201D</strong>\uFF0C\u8BB0\u5F55\u548C\u5C55\u793A\u5DF2\u7ECF\u53D1\u751F\u6216\u7ECF\u5E38\u505A\u7684\u4E8B\u3002</td><td style="text-align:left;"><strong>\u4E00\u5F20\u201C\u5F85\u529E\u4E8B\u9879\u6E05\u5355\u201D\u6216\u201C\u68A6\u60F3\u677F\u201D</strong>\uFF0C\u5217\u51FA\u8BA1\u5212\u3001\u76EE\u6807\u548C\u65B0\u60F3\u6CD5\u3002</td></tr></tbody></table><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u6700\u76F4\u89C2\u7684\u5BF9\u6BD4\u2014\u2014\u5728\u52A8\u8BCD\u540E\u9762" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u6700\u76F4\u89C2\u7684\u5BF9\u6BD4\u2014\u2014\u5728\u52A8\u8BCD\u540E\u9762" aria-hidden="true">#</a> <strong>\u7B2C\u4E8C\u90E8\u5206\uFF1A\u6700\u76F4\u89C2\u7684\u5BF9\u6BD4\u2014\u2014\u5728\u52A8\u8BCD\u540E\u9762</strong></h3><p>\u8FD9\u662F\u533A\u522B\u5B83\u4EEC\u6700\u5E38\u7528\u3001\u6700\u91CD\u8981\u7684\u573A\u666F\u3002\u67D0\u4E9B\u52A8\u8BCD\u540E\u9762\u53EA\u80FD\u8DDF <code>doing</code>\uFF0C\u67D0\u4E9B\u53EA\u80FD\u8DDF <code>to do</code>\uFF0C\u8FD9\u9700\u8981\u8BB0\u5FC6\u3002\u4F46\u66F4\u91CD\u8981\u7684\u662F\u7406\u89E3\u5176\u80CC\u540E\u7684\u903B\u8F91\u3002</p><h4 id="_1-\u53EA\u80FD\u7528-doing-\u4F5C\u5BBE\u8BED\u7684\u52A8\u8BCD" tabindex="-1"><a class="header-anchor" href="#_1-\u53EA\u80FD\u7528-doing-\u4F5C\u5BBE\u8BED\u7684\u52A8\u8BCD" aria-hidden="true">#</a> <strong>1. \u53EA\u80FD\u7528 <code>doing</code> \u4F5C\u5BBE\u8BED\u7684\u52A8\u8BCD</strong></h4><p>\u8FD9\u4E9B\u52A8\u8BCD\u901A\u5E38\u4E0E <strong>\u201C\u7ECF\u9A8C\u3001\u907F\u514D\u3001\u5EFA\u8BAE\u3001\u627F\u8BA4\u201D</strong> \u7B49\u542B\u4E49\u76F8\u5173\uFF0C\u5904\u7406\u7684\u662F\u4E00\u4EF6<strong>\u5DF2\u7ECF\u88AB\u8003\u8651\u6216\u5DF2\u7ECF\u53D1\u751F\u7684\u4E8B</strong>\u3002</p><table><thead><tr><th style="text-align:left;">\u52A8\u8BCD\u4E3E\u4F8B</th><th style="text-align:left;">\u4F8B\u53E5</th><th style="text-align:left;">\u903B\u8F91\u5206\u6790</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>enjoy</strong> (\u4EAB\u53D7)</td><td style="text-align:left;">I enjoy <strong>reading</strong>. \uFF08\u6211\u4EAB\u53D7<strong>\u9605\u8BFB</strong>\u3002\uFF09</td><td style="text-align:left;">\u201C\u9605\u8BFB\u201D\u662F\u4E00\u9879\u6211\u6B63\u5728\u8FDB\u884C\u5E76\u4ECE\u4E2D\u83B7\u5F97\u4E50\u8DA3\u7684<strong>\u6D3B\u52A8</strong>\u3002</td></tr><tr><td style="text-align:left;"><strong>avoid</strong> (\u907F\u514D)</td><td style="text-align:left;">Avoid <strong>making</strong> mistakes. \uFF08\u907F\u514D<strong>\u72AF\u9519</strong>\u3002\uFF09</td><td style="text-align:left;">\u201C\u72AF\u9519\u201D\u662F\u4E00\u4EF6\u6211\u8981\u8BBE\u6CD5\u8EB2\u907F\u7684<strong>\u4E8B\u60C5</strong>\u3002</td></tr><tr><td style="text-align:left;"><strong>practice</strong> (\u7EC3\u4E60)</td><td style="text-align:left;">She practices <strong>playing</strong> the piano. \uFF08\u5979\u7EC3\u4E60<strong>\u5F39\u94A2\u7434</strong>\u3002\uFF09</td><td style="text-align:left;">\u201C\u5F39\u94A2\u7434\u201D\u662F\u5979\u53CD\u590D\u8FDB\u884C\u7684\u7EC3\u4E60<strong>\u5185\u5BB9</strong>\u3002</td></tr><tr><td style="text-align:left;"><strong>consider</strong> (\u8003\u8651)</td><td style="text-align:left;">He considered <strong>changing</strong> his job. \uFF08\u4ED6\u8003\u8651\u8FC7<strong>\u6362\u5DE5\u4F5C</strong>\u3002\uFF09</td><td style="text-align:left;">\u201C\u6362\u5DE5\u4F5C\u201D\u662F\u4ED6\u8111\u6D77\u4E2D\u601D\u8003\u7684\u4E00\u4E2A<strong>\u9009\u9879/\u4E8B\u60C5</strong>\u3002</td></tr></tbody></table><p><strong>\u7B80\u5355\u8BB0</strong>\uFF1A\u8FD9\u4E9B\u52A8\u8BCD\u5904\u7406\u7684\u90FD\u662F\u4E00\u4E2A <strong>\u201C\u4E8B\u60C5\u672C\u8EAB\u201D</strong> \uFF0C\u6240\u4EE5\u7528\u66F4\u50CF\u540D\u8BCD\u7684\u52A8\u540D\u8BCD\u3002</p><h4 id="_2-\u53EA\u80FD\u7528-to-do-\u4F5C\u5BBE\u8BED\u7684\u52A8\u8BCD" tabindex="-1"><a class="header-anchor" href="#_2-\u53EA\u80FD\u7528-to-do-\u4F5C\u5BBE\u8BED\u7684\u52A8\u8BCD" aria-hidden="true">#</a> <strong>2. \u53EA\u80FD\u7528 <code>to do</code> \u4F5C\u5BBE\u8BED\u7684\u52A8\u8BCD</strong></h4><p>\u8FD9\u4E9B\u52A8\u8BCD\u901A\u5E38\u4E0E <strong>\u201C\u51B3\u5B9A\u3001\u8BA1\u5212\u3001\u613F\u671B\u3001\u627F\u8BFA\u201D</strong> \u76F8\u5173\uFF0C\u5904\u7406\u7684\u662F\u4E00\u4E2A<strong>\u5C1A\u672A\u53D1\u751F\u7684\u3001\u672A\u6765\u7684\u52A8\u4F5C</strong>\u3002</p><table><thead><tr><th style="text-align:left;">\u52A8\u8BCD\u4E3E\u4F8B</th><th style="text-align:left;">\u4F8B\u53E5</th><th style="text-align:left;">\u903B\u8F91\u5206\u6790</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>decide</strong> (\u51B3\u5B9A)</td><td style="text-align:left;">I decided <strong>to leave</strong>. \uFF08\u6211\u51B3\u5B9A<strong>\u79BB\u5F00</strong>\u3002\uFF09</td><td style="text-align:left;">\u201C\u79BB\u5F00\u201D\u662F\u4E00\u4E2A\u5173\u4E8E\u672A\u6765\u7684<strong>\u51B3\u5B9A</strong>\u3002</td></tr><tr><td style="text-align:left;"><strong>plan</strong> (\u8BA1\u5212)</td><td style="text-align:left;">We plan <strong>to travel</strong>. \uFF08\u6211\u4EEC\u8BA1\u5212<strong>\u53BB\u65C5\u884C</strong>\u3002\uFF09</td><td style="text-align:left;">\u201C\u53BB\u65C5\u884C\u201D\u662F\u4E00\u4E2A<strong>\u672A\u6765\u7684\u8BA1\u5212</strong>\u3002</td></tr><tr><td style="text-align:left;"><strong>hope</strong> (\u5E0C\u671B)</td><td style="text-align:left;">She hopes <strong>to win</strong>. \uFF08\u5979\u5E0C\u671B<strong>\u83B7\u80DC</strong>\u3002\uFF09</td><td style="text-align:left;">\u201C\u83B7\u80DC\u201D\u662F\u4E00\u4E2A<strong>\u672A\u6765\u7684\u613F\u671B</strong>\u3002</td></tr><tr><td style="text-align:left;"><strong>promise</strong> (\u627F\u8BFA)</td><td style="text-align:left;">He promised <strong>to help</strong>. \uFF08\u4ED6\u627F\u8BFA<strong>\u5E2E\u5FD9</strong>\u3002\uFF09</td><td style="text-align:left;">\u201C\u5E2E\u5FD9\u201D\u662F\u4E00\u4E2A<strong>\u672A\u6765\u7684\u627F\u8BFA</strong>\u3002</td></tr></tbody></table><p><strong>\u7B80\u5355\u8BB0</strong>\uFF1A\u8FD9\u4E9B\u52A8\u8BCD\u90FD\u6307\u5411 <strong>\u201C\u672A\u6765\u201D</strong> \uFF0C\u6240\u4EE5\u7528\u5E26\u6709\u201C\u672A\u6765\u6307\u5411\u201D\u7684\u4E0D\u5B9A\u5F0F\u3002</p><h4 id="_3-\u63A5-doing-\u548C-to-do-\u610F\u4E49\u622A\u7136\u4E0D\u540C\u7684\u52A8\u8BCD" tabindex="-1"><a class="header-anchor" href="#_3-\u63A5-doing-\u548C-to-do-\u610F\u4E49\u622A\u7136\u4E0D\u540C\u7684\u52A8\u8BCD" aria-hidden="true">#</a> <strong>3. \u63A5 <code>doing</code> \u548C <code>to do</code> \u610F\u4E49\u622A\u7136\u4E0D\u540C\u7684\u52A8\u8BCD</strong></h4><p>\u8FD9\u662F\u91CD\u4E2D\u4E4B\u91CD\uFF01</p><table><thead><tr><th style="text-align:left;">\u52A8\u8BCD</th><th style="text-align:left;">+ <strong>doing</strong> (\u52A8\u540D\u8BCD)</th><th style="text-align:left;">+ <strong>to do</strong> (\u4E0D\u5B9A\u5F0F)</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>remember</strong></td><td style="text-align:left;"><strong>\u8BB0\u5F97\u505A\u8FC7\u67D0\u4E8B</strong> (\u4E8B\u60C5\u5DF2\u53D1\u751F) <br> I remember <strong>locking</strong> the door. \uFF08\u6211\u8BB0\u5F97<strong>\u9501\u4E86</strong>\u95E8\u3002\uFF09</td><td style="text-align:left;"><strong>\u8BB0\u4F4F\u8981\u53BB\u505A\u67D0\u4E8B</strong> (\u4E8B\u60C5\u672A\u53D1\u751F) <br> Remember <strong>to lock</strong> the door. \uFF08\u8BB0\u5F97<strong>\u53BB\u9501</strong>\u95E8\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>forget</strong></td><td style="text-align:left;"><strong>\u5FD8\u8BB0\u505A\u8FC7\u67D0\u4E8B</strong> (\u4E8B\u60C5\u5DF2\u53D1\u751F\uFF0C\u4F46\u5FD8\u4E86) <br> I forgot <strong>telling</strong> her. \uFF08\u6211\u5FD8\u4E86<strong>\u544A\u8BC9\u8FC7</strong>\u5979\u3002\uFF09</td><td style="text-align:left;"><strong>\u5FD8\u8BB0\u8981\u53BB\u505A\u67D0\u4E8B</strong> (\u4E8B\u60C5\u672A\u505A) <br> I forgot <strong>to tell</strong> her. \uFF08\u6211\u5FD8\u4E86<strong>\u53BB\u544A\u8BC9</strong>\u5979\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>stop</strong></td><td style="text-align:left;"><strong>\u505C\u6B62\u505A\u67D0\u4E8B</strong> (\u505C\u6B62\u6B63\u5728\u505A\u7684\u4E8B) <br> He stopped <strong>smoking</strong>. \uFF08\u4ED6<strong>\u6212</strong>\u70DF\u4E86\u3002\uFF09</td><td style="text-align:left;"><strong>\u505C\u4E0B\u6765\u53BB\u505A\u53E6\u4E00\u4EF6\u4E8B</strong> (\u4E2D\u65AD\u624B\u5934\u4E8B\uFF0C\u53BB\u505A\u65B0\u7684) <br> He stopped <strong>to smoke</strong>. \uFF08\u4ED6\u505C\u4E0B\u6765\uFF0C<strong>\u53BB\u62BD\u6839\u70DF</strong>\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>try</strong></td><td style="text-align:left;"><strong>\u5C1D\u8BD5\u505A\u67D0\u4E8B</strong> (\u4F5C\u4E3A\u4E00\u79CD\u5B9E\u9A8C) <br> Try <strong>pressing</strong> this button. \uFF08\u8BD5\u8BD5<strong>\u6309</strong>\u8FD9\u4E2A\u6309\u94AE\u3002\uFF09</td><td style="text-align:left;"><strong>\u52AA\u529B/\u8BD5\u56FE\u505A\u67D0\u4E8B</strong> (\u9700\u8981\u4ED8\u51FA\u52AA\u529B) <br> I tried <strong>to open</strong> the window. \uFF08\u6211\u8BD5\u56FE<strong>\u6253\u5F00</strong>\u7A97\u6237\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>regret</strong></td><td style="text-align:left;"><strong>\u540E\u6094\u505A\u8FC7\u67D0\u4E8B</strong> (\u5BF9\u8FC7\u53BB\u7684\u4E8B\u540E\u6094) <br> I regret <strong>saying</strong> that. \uFF08\u6211\u540E\u6094<strong>\u8BF4\u4E86</strong>\u90A3\u4E9B\u8BDD\u3002\uFF09</td><td style="text-align:left;"><strong>\u9057\u61BE\u5730\u8981\u505A\u67D0\u4E8B</strong> (\u901A\u5E38\u7528\u4E8E\u544A\u77E5\u574F\u6D88\u606F) <br> We regret <strong>to inform</strong> you... \uFF08\u6211\u4EEC\u5F88\u9057\u61BE\u5730<strong>\u901A\u77E5</strong>\u60A8\u2026\u2026\uFF09</td></tr></tbody></table><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u5728\u5176\u4ED6\u4F4D\u7F6E\u4E0A\u7684\u533A\u522B" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u5728\u5176\u4ED6\u4F4D\u7F6E\u4E0A\u7684\u533A\u522B" aria-hidden="true">#</a> <strong>\u7B2C\u4E09\u90E8\u5206\uFF1A\u5728\u5176\u4ED6\u4F4D\u7F6E\u4E0A\u7684\u533A\u522B</strong></h3><h4 id="_1-\u4F5C\u4E3B\u8BED" tabindex="-1"><a class="header-anchor" href="#_1-\u4F5C\u4E3B\u8BED" aria-hidden="true">#</a> <strong>1. \u4F5C\u4E3B\u8BED</strong></h4><p>\u4E24\u8005\u90FD\u53EF\u4EE5\uFF0C\u6709\u65F6\u533A\u522B\u4E0D\u5927\u3002\u4F46\u52A8\u540D\u8BCD\u66F4\u5E38\u7528\u6765\u8868\u793A<strong>\u6CDB\u6307\u3001\u62BD\u8C61\u7684\u6982\u5FF5</strong>\uFF0C\u800C\u4E0D\u5B9A\u5F0F\u591A\u8868\u793A<strong>\u5177\u4F53\u7684\u3001\u4E00\u6B21\u6027\u7684\u884C\u4E3A</strong>\u3002</p><ul><li><strong>\u6CDB\u6307</strong>\uFF1A<strong>Swimming</strong> is good for health. \uFF08<strong>\u6E38\u6CF3</strong>\u5BF9\u5065\u5EB7\u6709\u76CA\u3002\uFF09</li><li><strong>\u5177\u4F53</strong>\uFF1A<strong>To swim</strong> here is dangerous. (<strong>\u5728\u8FD9\u91CC\u6E38\u6CF3</strong>\u662F\u5371\u9669\u7684\u3002)</li></ul><h4 id="_2-\u4F5C\u8868\u8BED" tabindex="-1"><a class="header-anchor" href="#_2-\u4F5C\u8868\u8BED" aria-hidden="true">#</a> <strong>2. \u4F5C\u8868\u8BED</strong></h4><ul><li><strong>\u52A8\u540D\u8BCD</strong>\uFF1A\u8BF4\u660E\u4E3B\u8BED\u7684<strong>\u5185\u5BB9</strong>\uFF08\u662F\u4EC0\u4E48\uFF09\uFF0C\u901A\u5E38\u53EF\u4EE5\u548C\u4E3B\u8BED\u5212\u7B49\u53F7\u3002 <ul><li>His job is <strong>teaching</strong>. \uFF08\u4ED6\u7684\u5DE5\u4F5C\u662F<strong>\u6559\u5B66</strong>\u3002\uFF09-&gt; \u4ED6\u7684\u5DE5\u4F5C = \u6559\u5B66</li></ul></li><li><strong>\u4E0D\u5B9A\u5F0F</strong>\uFF1A\u8BF4\u660E\u4E3B\u8BED\u7684<strong>\u76EE\u7684</strong>\u6216<strong>\u5C06\u8981\u53D1\u751F\u7684\u52A8\u4F5C</strong>\u3002 <ul><li>His dream is <strong>to become</strong> a pilot. \uFF08\u4ED6\u7684\u68A6\u60F3\u662F<strong>\u6210\u4E3A\u4E00\u540D</strong>\u98DE\u884C\u5458\u3002\uFF09-&gt; \u68A6\u60F3\u7684\u5185\u5BB9\u662F\u201C\u6210\u4E3A\u201D</li></ul></li></ul><hr><h3 id="\u7EC8\u6781\u603B\u7ED3\u4E0E\u5FC3\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u7EC8\u6781\u603B\u7ED3\u4E0E\u5FC3\u6CD5" aria-hidden="true">#</a> <strong>\u7EC8\u6781\u603B\u7ED3\u4E0E\u5FC3\u6CD5</strong></h3><ol><li><p><strong>\u6839\u672C\u903B\u8F91</strong>\uFF1A</p><ul><li><strong>doing</strong> = <strong>\u76F8\u518C</strong> -&gt; \u5173\u4E8E<strong>\u7ECF\u5386\u3001\u4E8B\u5B9E\u3001\u6D3B\u52A8</strong></li><li><strong>to do</strong> = <strong>\u5F85\u529E\u6E05\u5355</strong> -&gt; \u5173\u4E8E<strong>\u76EE\u7684\u3001\u8BA1\u5212\u3001\u672A\u6765</strong></li></ul></li><li><p><strong>\u52A8\u8BCD\u540E\u8DDF\u4EC0\u4E48\uFF1F</strong> \u8FD9\u662F\u8BB0\u5FC6\u548C\u8FD0\u7528\u7684\u6838\u5FC3\u3002</p><ul><li>\u60F3\u201C\u4E8B\u60C5\u672C\u8EAB\u201D\uFF08\u5982\u4EAB\u53D7\u3001\u907F\u514D\u3001\u7EC3\u4E60\uFF09 -&gt; \u7528 <strong>doing</strong></li><li>\u60F3\u201C\u672A\u6765\u52A8\u4F5C\u201D\uFF08\u5982\u51B3\u5B9A\u3001\u8BA1\u5212\u3001\u5E0C\u671B\uFF09 -&gt; \u7528 <strong>to do</strong></li><li>\u7262\u8BB0\u90A3\u51E0\u4E2A\u201C\u53D8\u8272\u9F99\u201D\u52A8\u8BCD\uFF08remember, forget, stop\u7B49\uFF09\uFF0C\u5B83\u4EEC\u4F1A\u6539\u53D8\u53E5\u5B50\u7684\u65F6\u95F4\u7EBF\u3002</li></ul></li><li><p><strong>\u56FA\u5B9A\u642D\u914D</strong>\uFF1A\u5BF9\u4E8E\u53EA\u80FD\u63A5 <code>doing</code> \u6216 <code>to do</code> \u7684\u52A8\u8BCD\uFF0C\u6CA1\u6709\u592A\u591A\u9053\u7406\u53EF\u8BB2\uFF0C\u9700\u8981\u901A\u8FC7\u9605\u8BFB\u548C\u7EC3\u4E60\u6765\u719F\u6089\u5B83\u4EEC\uFF0C\u5F62\u6210\u8BED\u611F\u3002</p></li></ol><p>\u5E0C\u671B\u8FD9\u4E2A\u4ECE\u201C\u6838\u5FC3\u610F\u8C61\u201D\u5165\u624B\u7684\u8BB2\u89E3\uFF0C\u80FD\u8BA9\u4F60\u5BF9\u52A8\u540D\u8BCD\u548C\u4E0D\u5B9A\u5F0F\u7684\u533A\u522B\u6709\u4E00\u4E2A\u5168\u65B0\u7684\u3001\u6DF1\u523B\u7684\u8BA4\u8BC6\u3002\u606D\u559C\u4F60\uFF0C\u4F60\u53C8\u653B\u514B\u4E86\u4E00\u4E2A\u901A\u5F80\u82F1\u8BED\u6D41\u5229\u4E4B\u8DEF\u4E0A\u7684\u91CD\u8981\u5821\u5792\uFF01</p>',35),g=[e];function l(d,i){return r(),o("div",null,g)}var h=t(s,[["render",l],["__file","index.html.vue"]]);export{h as default};
