import{_ as t,o as e,c as r,e as s}from"./app.303070c9.js";const n={},o=s('<h2 id="\u8868\u8BED-subject-complement" tabindex="-1"><a class="header-anchor" href="#\u8868\u8BED-subject-complement" aria-hidden="true">#</a> \u8868\u8BED Subject Complement</h2><p>\u592A\u597D\u4E86\uFF01\u6211\u4EEC\u7EC8\u4E8E\u6765\u5230\u4E86\u6784\u5EFA\u82F1\u8BED\u53E5\u5B50\u7684\u6700\u540E\u4E00\u4E2A\u5173\u952E\u73AF\u8282\u2014\u2014<strong>\u8868\u8BED</strong>\u3002\u5B66\u5B8C\u5B83\uFF0C\u4F60\u5C31\u80FD\u5F7B\u5E95\u638C\u63E1\u201C\u4E3B\u7CFB\u8868\u201D\u8FD9\u4E2A\u6838\u5FC3\u53E5\u578B\u4E86\u3002</p><p>\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4FDD\u8BC1\u7528\u6700\u8F7B\u677E\u7684\u65B9\u5F0F\u628A\u5B83\u8BB2\u660E\u767D\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u8868\u8BED-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u8868\u8BED-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u8868\u8BED\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p>\u8FD8\u8BB0\u5F97\u6211\u4EEC\u521A\u624D\u5B66\u7684\u201C\u7CFB\u52A8\u8BCD\u201D\u5417\uFF1F\u7CFB\u52A8\u8BCD\u662F\u90A3\u6761\u201C\u7EF3\u5B50\u201D\uFF0C\u7528\u6765\u6302\u201C\u6807\u7B7E\u201D\u3002\u90A3\u4E48\uFF0C<strong>\u8868\u8BED\uFF0C\u5C31\u662F\u90A3\u5757\u88AB\u6302\u4E0A\u53BB\u7684\u201C\u6807\u7B7E\u201D\u672C\u8EAB\uFF01</strong></p><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u8868\u8BED\u662F\u7D27\u8DDF\u5728\u7CFB\u52A8\u8BCD\u540E\u9762\u7684\u6210\u5206\uFF0C\u7528\u6765\u544A\u8BC9\u6211\u4EEC\u4E3B\u8BED\u201C\u662F\u4EC0\u4E48\u201D\u3001\u201C\u662F\u8C01\u201D\u6216\u8005\u201C\u600E\u4E48\u6837\u201D\u3002\u5B83\u548C\u7CFB\u52A8\u8BCD\u4E00\u8D77\uFF0C\u6784\u6210\u53E5\u5B50\u7684\u201C\u5FC3\u810F\u201D\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong> \u628A\u4E00\u53E5\u8BDD\u6BD4\u4F5C\u4E00\u4E2A<strong>\u4EBA\u7269\u4ECB\u7ECD\u5361</strong>\u3002</p><ul><li><strong>\u4E3B\u8BED</strong>\uFF1A\u5C31\u662F\u4F60\u8981\u4ECB\u7ECD\u7684<strong>\u90A3\u4E2A\u4EBA</strong>\uFF08\u6BD4\u5982\uFF1A\u6C64\u59C6\uFF09\u3002</li><li><strong>\u7CFB\u52A8\u8BCD</strong>\uFF1A\u5C31\u662F\u4ECB\u7ECD\u8BED\u201C<strong>\u4ED6\u662F</strong>\u2026\u2026\u201D\u91CC\u7684\u201C\u662F\u201D\u3002</li><li><strong>\u8868\u8BED</strong>\uFF1A\u5C31\u662F\u201C\u662F\u201D\u5B57\u540E\u9762\u7684<strong>\u6240\u6709\u4ECB\u7ECD\u5185\u5BB9</strong>\uFF08\u6BD4\u5982\uFF1A\u4E00\u540D\u5DE5\u7A0B\u5E08\u3001\u5F88\u5F00\u5FC3\u3001\u5728\u529E\u516C\u5BA4\u91CC\uFF09\u3002</li></ul><p>\u6CA1\u6709\u8868\u8BED\uFF0C\u4ECB\u7ECD\u5C31\u53EA\u505A\u4E86\u4E00\u534A\uFF0C\u4FE1\u606F\u4E0D\u5B8C\u6574\u3002</p><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u8868\u8BED-\u540D\u79F0\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u8868\u8BED-\u540D\u79F0\u7684\u7531\u6765" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u4E3A\u4EC0\u4E48\u53EB\u201C\u8868\u8BED\u201D\uFF1F\u540D\u79F0\u7684\u7531\u6765</h3><ol><li><p><strong>\u4E2D\u6587\u540D\u201C\u8868\u8BED\u201D</strong>\uFF1A</p><ul><li>\u201C\u8868\u201D\u5B57\u5728\u8FD9\u91CC\u662F\u201C\u8868\u8FBE\u3001\u8868\u8FF0\u201D\u7684\u610F\u601D\u3002</li><li>\u6240\u4EE5\u201C\u8868\u8BED\u201D\u5C31\u662F<strong>\u7528\u6765\u8868\u8FBE\u548C\u8BF4\u660E\u4E3B\u8BED\u72B6\u51B5\u7684\u8BED\u8A00\u6210\u5206</strong>\u3002</li></ul></li><li><p><strong>\u82F1\u6587\u540D\u201CPredicative\u201D \u6216 \u201CSubject Complement\u201D</strong>\uFF1A</p><ul><li>\u8BFB\u97F3\uFF1A/\u02C8pred\u026Ak\u0259t\u026Av/ \u6216 /\u02C8s\u028Cbd\u0292\u026Akt \u02C8k\u0252mpl\u026Am\u0259nt/</li><li><strong>Predicative</strong> \u6765\u6E90\u4E8E\u201C\u8C13\u8BED\u201D\uFF08Predicate\uFF09\uFF0C\u610F\u601D\u662F\u201C\u4F5C\u4E3A\u8C13\u8BED\u4E00\u90E8\u5206\u7684\u201D\u3002\u56E0\u4E3A\u5728\u201C\u4E3B\u7CFB\u8868\u201D\u7ED3\u6784\u4E2D\uFF0C<code>\u7CFB\u52A8\u8BCD+\u8868\u8BED</code> \u5408\u8D77\u6765\u5C31\u76F8\u5F53\u4E8E\u8C13\u8BED\u7684\u529F\u80FD\u3002</li><li><strong>Subject Complement</strong> \u8FD9\u4E2A\u540D\u5B57\u66F4\u76F4\u767D\uFF0C\u4E5F\u66F4\u80FD\u8BF4\u660E\u5B83\u7684\u529F\u80FD\uFF01<code>Subject</code> \u662F\u201C\u4E3B\u8BED\u201D\uFF0C<code>Complement</code> \u662F\u201C\u8865\u8DB3\u8BED\u201D\u3002\u5408\u8D77\u6765\u5C31\u662F\u201C<strong>\u4E3B\u8BED\u7684\u8865\u8DB3\u8BED</strong>\u201D\u2014\u2014<strong>\u7528\u6765\u8865\u5145\u8BF4\u660E\u4E3B\u8BED\uFF0C\u8BA9\u4E3B\u8BED\u7684\u4FE1\u606F\u5B8C\u6574</strong>\u3002</li></ul></li></ol><p>\u6211\u4E2A\u4EBA\u8BA4\u4E3A <code>Subject Complement</code>\uFF08\u4E3B\u8BED\u8865\u8DB3\u8BED\uFF09\u8FD9\u4E2A\u540D\u5B57\u6700\u80FD\u5E2E\u52A9\u6211\u4EEC\u7406\u89E3\u3002</p><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u54EA\u4E9B\u4E1C\u897F\u53EF\u4EE5\u5F53\u8868\u8BED-\u6838\u5FC3\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u54EA\u4E9B\u4E1C\u897F\u53EF\u4EE5\u5F53\u8868\u8BED-\u6838\u5FC3\u5185\u5BB9" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u54EA\u4E9B\u4E1C\u897F\u53EF\u4EE5\u5F53\u8868\u8BED\uFF1F\uFF08\u6838\u5FC3\u5185\u5BB9\uFF09</h3><p>\u8868\u8BED\u7684\u5BB6\u65CF\u975E\u5E38\u5E9E\u5927\uFF0C\u51E0\u4E4E\u4EFB\u4F55\u53EF\u4EE5\u7528\u6765\u63CF\u8FF0\u4E3B\u8BED\u201C\u662F\u4EC0\u4E48\u201D\u6216\u201C\u600E\u4E48\u6837\u201D\u7684\u8BCD\u6216\u7ED3\u6784\u90FD\u53EF\u4EE5\u5145\u5F53\u8868\u8BED\u3002\u6211\u4EEC\u628A\u5B83\u5206\u6210\u51E0\u5927\u7C7B\u6765\u770B\u3002</p><table><thead><tr><th style="text-align:left;">\u5145\u5F53\u8868\u8BED\u7684\u8BED\u6CD5\u5355\u4F4D</th><th style="text-align:left;">\u8BED\u6CD5\u5F62\u6001\u548C\u610F\u601D</th><th style="text-align:left;">\u4F8B\u53E5\uFF08\u4E3B\u8BED\u4E0B\u5212\u7EBF\uFF0C\u7CFB\u52A8\u8BCD<strong>\u52A0\u7C97</strong>\uFF0C\u8868\u8BED<em>\u659C\u4F53</em>\uFF09</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>1. \u540D\u8BCD</strong></td><td style="text-align:left;">\u8BF4\u660E\u4E3B\u8BED\u7684<strong>\u8EAB\u4EFD\u6216\u7C7B\u522B</strong>\u3002</td><td style="text-align:left;">My father <strong>is</strong> <em>a doctor</em>. \uFF08\u6211\u7236\u4EB2<em>\u662F</em>\u4E00\u540D\u533B\u751F\u3002\uFF09- \u8BF4\u660E\u8EAB\u4EFD <br> Today <strong>is</strong> <em>Monday</em>. \uFF08\u4ECA\u5929<em>\u662F</em>\u661F\u671F\u4E00\u3002\uFF09- \u8BF4\u660E\u7C7B\u522B</td></tr><tr><td style="text-align:left;"><strong>2. \u4EE3\u8BCD</strong></td><td style="text-align:left;">\u4EE3\u66FF\u540D\u8BCD\uFF0C\u8BF4\u660E\u4E3B\u8BED\u201C\u662F\u8C01\u7684\u201D\u6216\u201C\u662F\u4EC0\u4E48\u201D\u3002</td><td style="text-align:left;">This book <strong>is</strong> <em>mine</em>. \uFF08\u8FD9\u672C\u4E66<em>\u662F</em>\u6211\u7684\u3002\uFF09<br> The winner <strong>is</strong> <em>you</em>! \uFF08\u83B7\u80DC\u8005<em>\u662F</em>\u4F60\uFF01\uFF09</td></tr><tr><td style="text-align:left;"><strong>3. \u5F62\u5BB9\u8BCD</strong></td><td style="text-align:left;"><strong>\u8FD9\u662F\u6700\u6700\u91CD\u8981\u548C\u5E38\u89C1\u7684\u4E00\u7C7B\uFF01</strong> \u8BF4\u660E\u4E3B\u8BED\u7684<strong>\u72B6\u6001\u3001\u7279\u5F81\u6216\u6027\u8D28</strong>\u3002</td><td style="text-align:left;">She <strong>is</strong> <em>beautiful</em>. \uFF08\u5979\u5F88<em>\u6F02\u4EAE</em>\u3002\uFF09<br> The sky <strong>became</strong> <em>dark</em>. \uFF08\u5929\u7A7A<em>\u53D8\u6697</em>\u4E86\u3002\uFF09<br> The food <strong>smells</strong> <em>good</em>. \uFF08\u98DF\u7269\u95FB\u8D77\u6765<em>\u5F88\u9999</em>\u3002\uFF09-<strong>\u6CE8\u610F</strong>\uFF1A\u611F\u89C9\u7C7B\u7CFB\u52A8\u8BCD\u540E\u8DDF\u5F62\u5BB9\u8BCD\u3002</td></tr><tr><td style="text-align:left;"><strong>4. \u4ECB\u8BCD\u77ED\u8BED</strong></td><td style="text-align:left;">\u7528\u4E00\u4E2A\u77ED\u8BED\u6765\u8BF4\u660E\u4E3B\u8BED\u7684<strong>\u4F4D\u7F6E\u3001\u72B6\u6001\u6216\u7279\u5F81</strong>\u3002</td><td style="text-align:left;">My keys <strong>are</strong> <em>in my bag</em>. \uFF08\u6211\u7684\u94A5\u5319<em>\u5728\u6211\u7684\u5305\u91CC</em>\u3002\uFF09- \u8868\u4F4D\u7F6E <br> He <strong>is</strong> <em>in good health</em>. \uFF08\u4ED6<em>\u8EAB\u4F53\u5065\u5EB7</em>\u3002\uFF09- \u8868\u72B6\u6001 <br> The book <strong>is</strong> <em>about history</em>. \uFF08\u8FD9\u672C\u4E66<em>\u662F\u5173\u4E8E\u5386\u53F2\u7684</em>\u3002\uFF09- \u8868\u7279\u5F81</td></tr><tr><td style="text-align:left;"><strong>5. \u6570\u8BCD</strong></td><td style="text-align:left;">\u8BF4\u660E\u4E3B\u8BED\u7684<strong>\u6570\u91CF\u6216\u987A\u5E8F</strong>\u3002</td><td style="text-align:left;">We <strong>are</strong> <em>seven</em>. \uFF08\u6211\u4EEC<em>\u662F</em>\u4E03\u4E2A\u4EBA\u3002\uFF09<br> I <strong>was</strong> <em>the first</em> to arrive. \uFF08\u6211<em>\u662F</em>\u7B2C\u4E00\u4E2A\u5230\u7684\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>6. \u526F\u8BCD</strong></td><td style="text-align:left;"><strong>\u5C11\u6570\u526F\u8BCD</strong>\uFF08\u4E3B\u8981\u662F\u5730\u70B9\u526F\u8BCD\uFF09\u53EF\u4EE5\u4F5C\u8868\u8BED\uFF0C\u8BF4\u660E\u4E3B\u8BED\u7684<strong>\u4F4D\u7F6E\u6216\u72B6\u6001</strong>\u3002</td><td style="text-align:left;">My father <strong>is</strong> <em>out</em>. \uFF08\u6211\u7236\u4EB2<em>\u51FA\u53BB\u4E86</em>\u3002\uFF09<br> The meeting <strong>is</strong> <em>over</em>. \uFF08\u4F1A\u8BAE<em>\u7ED3\u675F\u4E86</em>\u3002\uFF09<br> I\u2018ll be <em>there</em> soon. \uFF08\u6211\u5F88\u5FEB\u5C31<em>\u5230\u90A3\u513F</em>\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>7. \u975E\u8C13\u8BED\u52A8\u8BCD\uFF08\u4E0D\u5B9A\u5F0F/\u52A8\u540D\u8BCD\uFF09</strong></td><td style="text-align:left;">\u7528\u4E00\u4E2A\u52A8\u4F5C\u7684\u6982\u5FF5\u6765\u8BF4\u660E\u4E3B\u8BED\u7684<strong>\u5185\u5BB9\u6216\u6027\u8D28</strong>\u3002</td><td style="text-align:left;">His dream <strong>is</strong> <em>to become a pilot</em>. \uFF08\u4ED6\u7684\u68A6\u60F3<em>\u662F\u6210\u4E3A\u4E00\u540D\u98DE\u884C\u5458</em>\u3002\uFF09- \u4E0D\u5B9A\u5F0F <br> My hobby <strong>is</strong> <em>collecting stamps</em>. \uFF08\u6211\u7684\u7231\u597D<em>\u662F\u6536\u96C6\u90AE\u7968</em>\u3002\uFF09- \u52A8\u540D\u8BCD</td></tr><tr><td style="text-align:left;"><strong>8. \u4ECE\u53E5\uFF08\u8868\u8BED\u4ECE\u53E5\uFF09</strong></td><td style="text-align:left;">\u7528\u4E00\u4E2A\u5B8C\u6574\u7684\u53E5\u5B50\u6765\u8BF4\u660E\u4E3B\u8BED\u662F\u201C<strong>\u4EC0\u4E48\u4E8B\u60C5</strong>\u201D\u6216\u201C<strong>\u4EC0\u4E48\u60C5\u51B5</strong>\u201D\u3002</td><td style="text-align:left;">The fact <strong>is</strong> <em>that he lied to us</em>. \uFF08\u4E8B\u5B9E<em>\u662F\u4ED6\u5BF9\u6211\u4EEC\u6492\u4E86\u8C0E</em>\u3002\uFF09<br> That <strong>is</strong> <em>what I mean</em>. \uFF08\u90A3\u5C31<em>\u662F\u6211\u7684\u610F\u601D</em>\u3002\uFF09</td></tr></tbody></table><hr><h3 id="\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u603B\u7ED3" aria-hidden="true">#</a> <strong>\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u603B\u7ED3</strong></h3><ol><li><strong>\u8868\u8BED\u7684\u4F4D\u7F6E</strong>\uFF1A\u8868\u8BED<strong>\u6C38\u8FDC</strong>\u51FA\u73B0\u5728<strong>\u7CFB\u52A8\u8BCD</strong>\u4E4B\u540E\u3002\u4F60\u627E\u5230\u4E00\u4E2A\u7CFB\u52A8\u8BCD\uFF0C\u5B83\u540E\u9762\u7684\u6210\u5206\u5C31\u662F\u8868\u8BED\u3002</li><li><strong>\u8868\u8BED\u7684\u529F\u80FD</strong>\uFF1A\u8868\u8BED\u7684\u552F\u4E00\u4EFB\u52A1\u5C31\u662F<strong>\u8865\u5145\u8BF4\u660E\u4E3B\u8BED</strong>\u3002\u5B83\u548C\u4E3B\u8BED\u4E4B\u95F4\u901A\u8FC7\u7CFB\u52A8\u8BCD\u753B\u4E0A\u4E86\u4E00\u4E2A\u201C\u7B49\u53F7\u201D\u3002 <ul><li>\u4E3B\u8BED = \u8868\u8BED</li><li>\uFF08\u6C64\u59C6 = \u5DE5\u7A0B\u5E08\uFF09</li><li>\uFF08\u5929\u7A7A = \u84DD\u8272\u7684\uFF09</li><li>\uFF08\u94A5\u5319 = \u5728\u5305\u91CC\uFF09</li></ul></li><li><strong>\u5982\u4F55\u5FEB\u901F\u627E\u5230\u8868\u8BED\uFF1F</strong> \u95EE\u5173\u4E8E\u4E3B\u8BED\u7684\u8FD9\u4E24\u4E2A\u95EE\u9898\uFF1A <ul><li><strong>\u201C\u4E3B\u8BED\u662F\u4EC0\u4E48/\u662F\u8C01\uFF1F\u201D</strong> -&gt; \u7B54\u6848\u901A\u5E38\u662F\u540D\u8BCD\u3001\u4EE3\u8BCD\u7C7B\u8868\u8BED\u3002</li><li><strong>\u201C\u4E3B\u8BED\u600E\u4E48\u6837\uFF1F\u201D</strong> -&gt; \u7B54\u6848\u901A\u5E38\u662F\u5F62\u5BB9\u8BCD\u3001\u4ECB\u8BCD\u77ED\u8BED\u7C7B\u8868\u8BED\u3002</li></ul></li></ol><p>\u73B0\u5728\uFF0C\u4F60\u5DF2\u7ECF\u638C\u63E1\u4E86\u201C\u4E3B\u8BED - \u7CFB\u52A8\u8BCD - \u8868\u8BED\u201D\u8FD9\u4E2A\u5F3A\u5927\u7684\u6838\u5FC3\u53E5\u578B\uFF01\u8FD9\u4E2A\u7ED3\u6784\u7528\u6765\u63CF\u8FF0\u4E8B\u7269\u7684\u72B6\u6001\u548C\u8EAB\u4EFD\uFF0C\u662F\u82F1\u8BED\u4E2D\u4F7F\u7528\u9891\u7387\u6700\u9AD8\u7684\u53E5\u578B\u4E4B\u4E00\u3002</p><p>\u5E0C\u671B\u8FD9\u4E2A\u8BB2\u89E3\u80FD\u8BA9\u201C\u8868\u8BED\u201D\u8FD9\u4E2A\u6982\u5FF5\u5728\u4F60\u8111\u4E2D\u53D8\u5F97\u6E05\u6670\u7ACB\u4F53\u3002\u5B83\u5C31\u662F\u4F60\u7ED9\u53E5\u5B50\u7684\u4E3B\u89D2\u8D34\u4E0A\u7684\u90A3\u4E2A\u6700\u6838\u5FC3\u7684\u201C\u6807\u7B7E\u201D\u3002</p><hr><h3 id="\u600E\u4E48\u533A\u522B\u4E00\u4E2A\u53E5\u5B50\u4E2D\u662F\u8868\u8BED\u8FD8\u662F\u5BBE\u8BED-\u4EE5\u53CA\u8868\u8BED\u4ECE\u53E5-\u5BBE\u8BED\u4ECE\u53E5" tabindex="-1"><a class="header-anchor" href="#\u600E\u4E48\u533A\u522B\u4E00\u4E2A\u53E5\u5B50\u4E2D\u662F\u8868\u8BED\u8FD8\u662F\u5BBE\u8BED-\u4EE5\u53CA\u8868\u8BED\u4ECE\u53E5-\u5BBE\u8BED\u4ECE\u53E5" aria-hidden="true">#</a> \u600E\u4E48\u533A\u522B\u4E00\u4E2A\u53E5\u5B50\u4E2D\u662F\u8868\u8BED\u8FD8\u662F\u5BBE\u8BED\uFF0C\u4EE5\u53CA\u8868\u8BED\u4ECE\u53E5\uFF0C\u5BBE\u8BED\u4ECE\u53E5</h3><p>\u600E\u4E48\u533A\u522B\u4E00\u4E2A\u53E5\u5B50\u4E2D\u662F\u8868\u8BED\u8FD8\u662F\u5BBE\u8BED\uFF0C\u4EE5\u53CA\u8868\u8BED\u4ECE\u53E5\uFF0C\u5BBE\u8BED\u4ECE\u53E5?</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u6838\u5FC3\u5FC3\u6CD5\u2014\u2014\u5148\u770B-\u52A8\u8BCD-\u7684\u8EAB\u4EFD" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u6838\u5FC3\u5FC3\u6CD5\u2014\u2014\u5148\u770B-\u52A8\u8BCD-\u7684\u8EAB\u4EFD" aria-hidden="true">#</a> <strong>\u7B2C\u4E00\u90E8\u5206\uFF1A\u6838\u5FC3\u5FC3\u6CD5\u2014\u2014\u5148\u770B\u201C\u52A8\u8BCD\u201D\u7684\u8EAB\u4EFD</strong></h3><p>\u533A\u522B\u8868\u8BED\u548C\u5BBE\u8BED\uFF0C\u6700\u5173\u952E\u7684\u4E00\u6B65\u4E0D\u662F\u770B\u540E\u9762\u7684\u8BCD\uFF0C\u800C\u662F\u770B\u5B83\u524D\u9762\u7684<strong>\u52A8\u8BCD</strong>\u662F\u4EC0\u4E48\u7C7B\u578B\u7684\u52A8\u8BCD\u3002</p><ul><li><strong>\u5982\u679C\u52A8\u8BCD\u662F <code>\u7CFB\u52A8\u8BCD</code></strong> -&gt; \u5B83\u540E\u9762\u7684\u6210\u5206\u53EB <strong><code>\u8868\u8BED</code></strong></li><li><strong>\u5982\u679C\u52A8\u8BCD\u662F <code>\u53CA\u7269\u52A8\u8BCD</code></strong> -&gt; \u5B83\u540E\u9762\u7684\u6210\u5206\u53EB <strong><code>\u5BBE\u8BED</code></strong></li></ul><p><strong>\u7B80\u5355\u8BB0\uFF1A\u52A8\u8BCD\u51B3\u5B9A\u4E00\u5207\u3002\u662F\u201C\u7CFB\u201D\u8FD8\u662F\u201C\u53CA\u201D\uFF1F\u540E\u9762\u8DDF\u201C\u8868\u201D\u8FD8\u662F\u201C\u5BBE\u201D\uFF1F</strong></p><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u8868\u8BED-vs-\u5BBE\u8BED-\u662F\u4EC0\u4E48-vs-\u5E72\u4E86\u4EC0\u4E48" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u8868\u8BED-vs-\u5BBE\u8BED-\u662F\u4EC0\u4E48-vs-\u5E72\u4E86\u4EC0\u4E48" aria-hidden="true">#</a> <strong>\u7B2C\u4E8C\u90E8\u5206\uFF1A\u8868\u8BED vs. \u5BBE\u8BED\uFF08\u201C\u662F\u4EC0\u4E48\u201D vs. \u201C\u5E72\u4E86\u4EC0\u4E48\u201D\uFF09</strong></h3><p>\u8BA9\u6211\u4EEC\u7528\u4E00\u4E2A\u6700\u7ECF\u5178\u7684\u5BF9\u6BD4\u6765\u7406\u89E3\uFF1A</p><table><thead><tr><th style="text-align:left;">\u5BF9\u6BD4\u7EF4\u5EA6</th><th style="text-align:left;"><strong>\u8868\u8BED</strong></th><th style="text-align:left;"><strong>\u5BBE\u8BED</strong></th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u6838\u5FC3\u5173\u7CFB</strong></td><td style="text-align:left;"><strong>\u4E3B\u8BED = \u8868\u8BED</strong> <br>\uFF08\u5BF9\u4E3B\u8BED\u8FDB\u884C\u8865\u5145\u8BF4\u660E\uFF09</td><td style="text-align:left;"><strong>\u4E3B\u8BED \u2192 \u52A8\u8BCD \u2192 \u5BBE\u8BED</strong> <br>\uFF08\u52A8\u4F5C\u65BD\u52A0\u4E8E\u5BBE\u8BED\u4E4B\u4E0A\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u529F\u80FD</strong></td><td style="text-align:left;">\u8BF4\u660E\u4E3B\u8BED <strong>\u201C\u662F\u4EC0\u4E48\u201D</strong> \u6216 <strong>\u201C\u600E\u4E48\u6837\u201D</strong></td><td style="text-align:left;">\u662F\u52A8\u4F5C\u7684<strong>\u627F\u53D7\u8005</strong>\uFF0C\u56DE\u7B54 <strong>\u201C\u8C01\u201D</strong> \u6216 <strong>\u201C\u4EC0\u4E48\u201D</strong></td></tr><tr><td style="text-align:left;"><strong>\u524D\u9762\u7684\u52A8\u8BCD</strong></td><td style="text-align:left;"><strong>\u7CFB\u52A8\u8BCD</strong> <br>\uFF08\u5982\uFF1Abe, become, feel, look, seem\uFF09</td><td style="text-align:left;"><strong>\u53CA\u7269\u52A8\u8BCD</strong> <br>\uFF08\u5982\uFF1Alove, eat, buy, hit, make\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u751F\u6D3B\u5316\u6BD4\u55BB</strong></td><td style="text-align:left;">\u7ED9\u4E3B\u8BED\u8D34\u7684 <strong>\u201C\u8EAB\u4EFD/\u72B6\u6001\u6807\u7B7E\u201D</strong></td><td style="text-align:left;">\u4E3B\u8BED\u52A8\u4F5C\u7684 <strong>\u201C\u9776\u5B50\u201D</strong> \u6216 <strong>\u201C\u63A5\u6536\u5668\u201D</strong></td></tr><tr><td style="text-align:left;"><strong>\u4F8B\u53E51</strong></td><td style="text-align:left;">She <strong>is</strong> a <strong>teacher</strong>. <br>\uFF08\u5979<strong>\u662F</strong>\u4E00\u540D<strong>\u8001\u5E08</strong>\u3002\uFF09<br>- \u201C\u8001\u5E08\u201D\u662F\u7ED9\u201C\u5979\u201D\u8D34\u7684\u8EAB\u4EFD\u6807\u7B7E\u3002</td><td style="text-align:left;">She <strong>loves</strong> <strong>teaching</strong>. <br>\uFF08\u5979<strong>\u70ED\u7231****\u6559\u5B66</strong>\u3002\uFF09<br>- \u201C\u6559\u5B66\u201D\u662F\u201C\u70ED\u7231\u201D\u8FD9\u4E2A\u52A8\u4F5C\u7684\u9776\u5B50\u3002</td></tr><tr><td style="text-align:left;"><strong>\u4F8B\u53E52</strong></td><td style="text-align:left;">The cake <strong>tastes</strong> <strong>delicious</strong>. <br>\uFF08\u86CB\u7CD5<strong>\u5C1D\u8D77\u6765****\u5F88\u7F8E\u5473</strong>\u3002\uFF09<br>- \u201C\u5F88\u7F8E\u5473\u201D\u662F\u63CF\u8FF0\u201C\u86CB\u7CD5\u201D\u7684\u72B6\u6001\u3002</td><td style="text-align:left;">I <strong>ate</strong> the <strong>cake</strong>. <br>\uFF08\u6211<strong>\u5403\u4E86</strong>\u90A3\u4E2A<strong>\u86CB\u7CD5</strong>\u3002\uFF09<br>- \u201C\u86CB\u7CD5\u201D\u662F\u201C\u5403\u201D\u8FD9\u4E2A\u52A8\u4F5C\u7684\u627F\u53D7\u8005\u3002</td></tr><tr><td style="text-align:left;"><strong>\u5982\u4F55\u6D4B\u8BD5</strong></td><td style="text-align:left;">\u5728\u4E3B\u8BED\u548C\u8868\u8BED\u4E4B\u95F4\u53EF\u4EE5\u753B\u4E00\u4E2A <strong>\u7B49\u53F7 (=)</strong> <br>She = a teacher. <br>The cake = delicious.</td><td style="text-align:left;"><strong>\u7EDD\u5BF9\u753B\u4E0D\u4E86\u7B49\u53F7</strong> <br>She \u2260 teaching. <br>I \u2260 the cake.</td></tr></tbody></table><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u8868\u8BED\u4ECE\u53E5-vs-\u5BBE\u8BED\u4ECE\u53E5-\u6807\u7B7E\u7684\u5185\u5BB9-vs-\u52A8\u4F5C\u7684\u76EE\u6807\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u8868\u8BED\u4ECE\u53E5-vs-\u5BBE\u8BED\u4ECE\u53E5-\u6807\u7B7E\u7684\u5185\u5BB9-vs-\u52A8\u4F5C\u7684\u76EE\u6807\u5185\u5BB9" aria-hidden="true">#</a> <strong>\u7B2C\u4E09\u90E8\u5206\uFF1A\u8868\u8BED\u4ECE\u53E5 vs. \u5BBE\u8BED\u4ECE\u53E5\uFF08\u201C\u6807\u7B7E\u7684\u5185\u5BB9\u201D vs. \u201C\u52A8\u4F5C\u7684\u76EE\u6807\u5185\u5BB9\u201D\uFF09</strong></h3><p>\u7406\u89E3\u4E86\u8868\u8BED\u548C\u5BBE\u8BED\u7684\u533A\u522B\uFF0C\u4ECE\u53E5\u5C31\u975E\u5E38\u7B80\u5355\u4E86\u3002<strong>\u4ECE\u53E5\uFF0C\u5176\u5B9E\u5C31\u662F\u7528\u4E00\u4E2A\u5B8C\u6574\u7684\u53E5\u5B50\u6765\u5145\u5F53\u8868\u8BED\u6216\u5BBE\u8BED\u3002</strong></p><table><thead><tr><th style="text-align:left;">\u5BF9\u6BD4\u7EF4\u5EA6</th><th style="text-align:left;"><strong>\u8868\u8BED\u4ECE\u53E5</strong></th><th style="text-align:left;"><strong>\u5BBE\u8BED\u4ECE\u53E5</strong></th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u5B83\u662F\u4EC0\u4E48</strong></td><td style="text-align:left;">\u7528\u4E00\u4E2A\u53E5\u5B50\u6765\u5145\u5F53 <strong><code>\u8868\u8BED</code></strong></td><td style="text-align:left;">\u7528\u4E00\u4E2A\u53E5\u5B50\u6765\u5145\u5F53 <strong><code>\u5BBE\u8BED</code></strong></td></tr><tr><td style="text-align:left;"><strong>\u4F4D\u7F6E</strong></td><td style="text-align:left;">\u4F4D\u4E8E <strong><code>\u7CFB\u52A8\u8BCD</code></strong> \u4E4B\u540E</td><td style="text-align:left;">\u4F4D\u4E8E <strong><code>\u53CA\u7269\u52A8\u8BCD</code></strong> \u4E4B\u540E</td></tr><tr><td style="text-align:left;"><strong>\u529F\u80FD</strong></td><td style="text-align:left;">\u8BF4\u660E\u4E3B\u8BED\u662F <strong>\u201C\u4EC0\u4E48\u4E8B\u60C5\u201D</strong> \u6216 <strong>\u201C\u4EC0\u4E48\u60C5\u51B5\u201D</strong></td><td style="text-align:left;">\u8BF4\u660E\u52A8\u4F5C\u7684 <strong>\u201C\u5177\u4F53\u5185\u5BB9\u662F\u4EC0\u4E48\u201D</strong></td></tr><tr><td style="text-align:left;"><strong>\u5E38\u7528\u5F15\u5BFC\u8BCD</strong></td><td style="text-align:left;"><strong>that, whether, because, why, who, what, when...</strong> <br>\uFF08\u8BF4\u660E\u4E00\u4EF6\u4E8B\uFF09</td><td style="text-align:left;"><strong>that, if/whether, who, what, when, why, how...</strong> <br>\uFF08\u9648\u8FF0\u6216\u8BE2\u95EE\u5185\u5BB9\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u751F\u6D3B\u5316\u6BD4\u55BB</strong></td><td style="text-align:left;">\u4E00\u4E2A\u8BE6\u7EC6\u7684 <strong>\u201C\u8EAB\u4EFD/\u72B6\u6001\u8BF4\u660E\u4E66\u201D</strong></td><td style="text-align:left;">\u4E00\u4E2A\u8BE6\u7EC6\u7684 <strong>\u201C\u4EFB\u52A1\u76EE\u6807\u62A5\u544A\u201D</strong></td></tr><tr><td style="text-align:left;"><strong>\u4F8B\u53E51</strong></td><td style="text-align:left;">The fact <strong>is</strong> <strong>(that he is honest)</strong>. <br>\uFF08\u4E8B\u5B9E<strong>\u662F****\u4ED6\u5F88\u8BDA\u5B9E</strong>\u3002\uFF09<br>- \u6574\u4E2A\u4ECE\u53E5 <code>(that he is honest)</code> \u662F\u4E3B\u8BED\u7684\u201C\u8BF4\u660E\u4E66\u201D\u3002</td><td style="text-align:left;">I know <strong>(that he is honest)</strong>. <br>\uFF08\u6211<strong>\u77E5\u9053****\u4ED6\u5F88\u8BDA\u5B9E</strong>\u3002\uFF09<br>- \u6574\u4E2A\u4ECE\u53E5 <code>(that he is honest)</code> \u662F\u201C\u77E5\u9053\u201D\u7684\u5177\u4F53\u5185\u5BB9\u3002</td></tr><tr><td style="text-align:left;"><strong>\u4F8B\u53E52</strong></td><td style="text-align:left;">The question <strong>is</strong> <strong>(whether we can win)</strong>. <br>\uFF08\u95EE\u9898<strong>\u662F****\u6211\u4EEC\u80FD\u5426\u83B7\u80DC</strong>\u3002\uFF09<br>- \u4ECE\u53E5\u8BF4\u660E\u201C\u95EE\u9898\u201D\u7684\u5177\u4F53\u5185\u5BB9\u3002</td><td style="text-align:left;">I wonder <strong>(whether we can win)</strong>. <br>\uFF08\u6211<strong>\u60F3\u77E5\u9053****\u6211\u4EEC\u80FD\u5426\u83B7\u80DC</strong>\u3002\uFF09<br>- \u4ECE\u53E5\u662F\u201C\u60F3\u77E5\u9053\u201D\u7684\u5177\u4F53\u5185\u5BB9\u3002</td></tr><tr><td style="text-align:left;"><strong>\u5982\u4F55\u5224\u65AD</strong></td><td style="text-align:left;">1. \u627E\u5230\u7CFB\u52A8\u8BCD\uFF08is, seem, look\u7B49\uFF09\u3002<br>2. \u7CFB\u52A8\u8BCD\u540E\u9762\u662F\u4E00\u4E2A\u7531\u5F15\u5BFC\u8BCD\u5F00\u5934\u7684\u5B8C\u6574\u53E5\u5B50\u3002<br>3. \u8FD9\u4E2A\u53E5\u5B50\u5C31\u662F\u5728\u7ED9\u4E3B\u8BED\u4E0B\u5B9A\u4E49\u3002</td><td style="text-align:left;">1. \u627E\u5230\u53CA\u7269\u52A8\u8BCD\uFF08think, say, know, believe\u7B49\uFF09\u3002<br>2. \u52A8\u8BCD\u540E\u9762\u662F\u4E00\u4E2A\u7531\u5F15\u5BFC\u8BCD\u5F00\u5934\u7684\u5B8C\u6574\u53E5\u5B50\u3002<br>3. \u8FD9\u4E2A\u53E5\u5B50\u5C31\u662F\u52A8\u4F5C\u7684\u5177\u4F53\u5185\u5BB9\u3002</td></tr></tbody></table><hr><h3 id="\u7EC8\u6781\u603B\u7ED3\u4E0E\u5B9E\u6218\u56DB\u6B65\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u7EC8\u6781\u603B\u7ED3\u4E0E\u5B9E\u6218\u56DB\u6B65\u6CD5" aria-hidden="true">#</a> <strong>\u7EC8\u6781\u603B\u7ED3\u4E0E\u5B9E\u6218\u56DB\u6B65\u6CD5</strong></h3><p>\u5F53\u4F60\u5206\u6790\u4E00\u4E2A\u53E5\u5B50\u65F6\uFF0C\u95EE\u81EA\u5DF1\u8FD9\u56DB\u4E2A\u95EE\u9898\uFF1A</p><ol><li><strong>\u627E\u5230\u6838\u5FC3\u52A8\u8BCD</strong>\uFF1A\u8FD9\u4E2A\u53E5\u5B50\u7684\u4E3B\u8981\u52A8\u8BCD\u662F\u4EC0\u4E48\uFF1F</li><li><strong>\u5224\u65AD\u52A8\u8BCD\u7C7B\u578B</strong>\uFF1A\u8FD9\u4E2A\u52A8\u8BCD\u662F <strong>\u7CFB\u52A8\u8BCD</strong>\uFF08\u662F\u3001\u53D8\u5F97\u3001\u611F\u89C9\u8D77\u6765\u2026\uFF09\u8FD8\u662F <strong>\u53CA\u7269\u52A8\u8BCD</strong>\uFF08\u7231\u3001\u5403\u3001\u77E5\u9053\u3001\u8BA4\u4E3A\u2026\uFF09\uFF1F</li><li><strong>\u786E\u5B9A\u6210\u5206</strong>\uFF1A <ul><li>\u5982\u679C\u662F <strong>\u7CFB\u52A8\u8BCD</strong>\uFF0C\u5B83\u540E\u9762\u7684\u6210\u5206\u5C31\u662F <strong>\u8868\u8BED</strong>\u3002</li><li>\u5982\u679C\u662F <strong>\u53CA\u7269\u52A8\u8BCD</strong>\uFF0C\u5B83\u540E\u9762\u7684\u6210\u5206\u5C31\u662F <strong>\u5BBE\u8BED</strong>\u3002</li></ul></li><li><strong>\u5224\u65AD\u662F\u5426\u662F\u4ECE\u53E5</strong>\uFF1A\u5982\u679C\u8FD9\u4E2A\u8868\u8BED\u6216\u5BBE\u8BED\u672C\u8EAB\u662F\u4E00\u4E2A <strong>\u5B8C\u6574\u7684\u53E5\u5B50</strong>\uFF08\u6709\u4E3B\u8C13\u7ED3\u6784\uFF0C\u7531\u5F15\u5BFC\u8BCD\u5F00\u5934\uFF09\uFF0C\u90A3\u4E48\u5B83\u5C31\u662F <strong>\u8868\u8BED\u4ECE\u53E5</strong> \u6216 <strong>\u5BBE\u8BED\u4ECE\u53E5</strong>\u3002</li></ol><p><strong>\u5B9E\u6218\u6F14\u7EC3\uFF1A</strong></p><ul><li><p>My dream is <strong>to travel the world</strong>. \uFF08\u6211\u7684\u68A6\u60F3\u662F\u73AF\u6E38\u4E16\u754C\u3002\uFF09</p><ul><li>\u52A8\u8BCD\uFF1A<code>is</code> \uFF08\u7CFB\u52A8\u8BCD\uFF09</li><li>\u6240\u4EE5 <code>to travel the world</code> \u662F <strong>\u8868\u8BED</strong>\uFF08\u4E0D\u5B9A\u5F0F\u77ED\u8BED\u4F5C\u8868\u8BED\uFF09\u3002</li></ul></li><li><p>I believe <strong>that he will come</strong>. \uFF08\u6211\u76F8\u4FE1\u4ED6\u4F1A\u6765\u3002\uFF09</p><ul><li>\u52A8\u8BCD\uFF1A<code>believe</code> \uFF08\u53CA\u7269\u52A8\u8BCD\uFF09</li><li>\u6240\u4EE5 <code>that he will come</code> \u662F\u4E00\u4E2A\u5B8C\u6574\u7684\u53E5\u5B50\uFF0C\u4F5C <strong>\u5BBE\u8BED</strong> -&gt; <strong>\u5BBE\u8BED\u4ECE\u53E5</strong>\u3002</li></ul></li><li><p>The truth is <strong>that she never left</strong>. \uFF08\u771F\u76F8\u662F\u5979\u4ECE\u672A\u79BB\u5F00\u3002\uFF09</p><ul><li>\u52A8\u8BCD\uFF1A<code>is</code> \uFF08\u7CFB\u52A8\u8BCD\uFF09</li><li>\u6240\u4EE5 <code>that she never left</code> \u662F\u4E00\u4E2A\u5B8C\u6574\u7684\u53E5\u5B50\uFF0C\u4F5C <strong>\u8868\u8BED</strong> -&gt; <strong>\u8868\u8BED\u4ECE\u53E5</strong>\u3002</li></ul></li></ul>',43),g=[o];function l(d,i){return e(),r("div",null,g)}var h=t(n,[["render",l],["__file","index.html.vue"]]);export{h as default};
