import{_ as t,o as n,c as e,e as r}from"./app.e8649b0f.js";const s={},o=r('<h2 id="\u73B0\u5728\u5206\u8BCD" tabindex="-1"><a class="header-anchor" href="#\u73B0\u5728\u5206\u8BCD" aria-hidden="true">#</a> \u73B0\u5728\u5206\u8BCD</h2><p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4EEC\u4ECA\u5929\u6765\u6DF1\u5165\u63A2\u8BA8\u4E00\u4E2A\u80FD\u8BA9\u4F60\u7684\u82F1\u8BED\u77AC\u95F4\u201C\u52A8\u201D\u8D77\u6765\u3001\u5145\u6EE1\u753B\u9762\u611F\u7684\u8BED\u6CD5\u5143\u7D20\u2014\u2014<strong>\u73B0\u5728\u5206\u8BCD</strong>\u3002</p><p>\u4F60\u5DF2\u7ECF\u77E5\u9053\u4E86\u5B83\u662F\u975E\u8C13\u8BED\u52A8\u8BCD\u5BB6\u65CF\u7684\u91CD\u8981\u6210\u5458\uFF0C\u73B0\u5728\u6211\u4EEC\u5C31\u6765\u7ED9\u5B83\u4E00\u4E2A\u5B8C\u7F8E\u7684\u7279\u5199\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u73B0\u5728\u5206\u8BCD-\u52A8\u6001\u7684\u5F62\u5BB9\u8BCD" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u73B0\u5728\u5206\u8BCD-\u52A8\u6001\u7684\u5F62\u5BB9\u8BCD" aria-hidden="true">#</a> <strong>\u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u73B0\u5728\u5206\u8BCD\uFF1F\uFF08\u201C\u52A8\u6001\u7684\u5F62\u5BB9\u8BCD\u201D\uFF09</strong></h3><p><strong>\u6838\u5FC3\u5FC3\u6CD5\uFF1A\u73B0\u5728\u5206\u8BCD\u662F\u52A8\u8BCD\u7684 <code>-ing</code> \u5F62\u5F0F\uFF0C\u5B83\u8EAB\u517C\u4E8C\u804C\uFF1A\u65E2\u6709\u52A8\u8BCD\u7684\u7279\u6027\uFF08\u53EF\u4EE5\u5E26\u5BBE\u8BED\u3001\u88AB\u72B6\u8BED\u4FEE\u9970\uFF09\uFF0C\u53C8\u6709\u5F62\u5BB9\u8BCD\u7684\u529F\u80FD\uFF08\u7528\u6765\u4FEE\u9970\u540D\u8BCD\uFF09\u3002\u5B83\u7684\u6838\u5FC3\u542B\u4E49\u662F\u201C\u4E3B\u52A8\u7684\u201D\u548C\u201C\u8FDB\u884C\u7684\u201D\u3002</strong></p><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u73B0\u5728\u5206\u8BCD\u5C31\u662F\u628A\u4E00\u4E2A\u52A8\u4F5C\u53D8\u6210\u4E00\u79CD\u7279\u5F81\uFF0C\u7528\u6765\u63CF\u8FF0\u201C\u6B63\u5728\u505A\u67D0\u4E8B\u201D\u6216\u201C\u4EE4\u4EBA\u4EA7\u751F\u67D0\u79CD\u611F\u89C9\u201D\u7684\u72B6\u6001\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong></p><ul><li><strong>\u8C13\u8BED\u52A8\u8BCD</strong> \u662F<strong>\u7535\u5F71\u91CC\u6B63\u5728\u64AD\u653E\u7684\u7247\u6BB5</strong>\uFF08\u5982\uFF1AThe girl is smiling. \u5973\u5B69\u6B63\u5728\u5FAE\u7B11\u3002\uFF09</li><li><strong>\u73B0\u5728\u5206\u8BCD</strong> \u5C31\u50CF\u662F<strong>\u4ECE\u7535\u5F71\u91CC\u622A\u53D6\u7684\u4E00\u5F20\u52A8\u6001GIF\u56FE</strong>\u3002\u8FD9\u5F20GIF\u56FE\uFF08 smiling \uFF09\u53EF\u4EE5\uFF1A <ul><li>\u4F5C\u4E3A<strong>\u6807\u7B7E</strong>\u8D34\u5728\u540D\u8BCD\u4E0A\uFF08a <strong>smiling</strong> girl \u4E00\u4E2A\u5FAE\u7B11\u7684\u5973\u5B69\uFF09\u3002</li><li>\u4F5C\u4E3A<strong>\u80CC\u666F\u8BF4\u660E</strong>\u653E\u5728\u53E5\u5B50\u65C1\u8FB9\uFF08<strong>Smiling</strong>, she answered the phone. \u5979\u5FAE\u7B11\u7740\u63A5\u4E86\u7535\u8BDD\u3002\uFF09\u3002</li></ul></li></ul><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u73B0\u5728\u5206\u8BCD\u7684\u6784\u6210" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u73B0\u5728\u5206\u8BCD\u7684\u6784\u6210" aria-hidden="true">#</a> <strong>\u7B2C\u4E8C\u90E8\u5206\uFF1A\u73B0\u5728\u5206\u8BCD\u7684\u6784\u6210</strong></h3><p>\u6784\u6210\u975E\u5E38\u7B80\u5355\uFF1A</p><p><strong>\u52A8\u8BCD\u539F\u5F62 + -ing</strong></p><ul><li>work -&gt; work<strong>ing</strong></li><li>study -&gt; stud<strong>ying</strong> \uFF08\u4EE5\u91CD\u8BFB\u95ED\u97F3\u8282\u7ED3\u5C3E\uFF0C\u53CC\u5199\u672B\u5C3E\u8F85\u97F3\u5B57\u6BCD\uFF09</li><li>make -&gt; mak<strong>ing</strong> \uFF08\u4EE5\u4E0D\u53D1\u97F3\u7684e\u7ED3\u5C3E\uFF0C\u53BBe\u52A0ing\uFF09</li></ul><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u73B0\u5728\u5206\u8BCD\u7684\u56DB\u5927\u6838\u5FC3\u529F\u80FD" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u73B0\u5728\u5206\u8BCD\u7684\u56DB\u5927\u6838\u5FC3\u529F\u80FD" aria-hidden="true">#</a> <strong>\u7B2C\u4E09\u90E8\u5206\uFF1A\u73B0\u5728\u5206\u8BCD\u7684\u56DB\u5927\u6838\u5FC3\u529F\u80FD</strong></h3><p>\u8FD9\u662F\u6700\u5173\u952E\u7684\u90E8\u5206\uFF01\u73B0\u5728\u5206\u8BCD\u53EF\u4EE5\u5728\u53E5\u5B50\u4E2D\u626E\u6F14\u56DB\u4E2A\u975E\u5E38\u91CD\u8981\u7684\u89D2\u8272\u3002</p><h4 id="\u529F\u80FD\u4E00-\u4F5C\u5B9A\u8BED-\u540D\u8BCD\u7684-\u52A8\u6001\u6807\u7B7E" tabindex="-1"><a class="header-anchor" href="#\u529F\u80FD\u4E00-\u4F5C\u5B9A\u8BED-\u540D\u8BCD\u7684-\u52A8\u6001\u6807\u7B7E" aria-hidden="true">#</a> <strong>\u529F\u80FD\u4E00\uFF1A\u4F5C\u5B9A\u8BED\uFF08\u540D\u8BCD\u7684\u201C\u52A8\u6001\u6807\u7B7E\u201D\uFF09</strong></h4><p>\u8FD9\u662F\u6700\u5E38\u89C1\u3001\u6700\u76F4\u89C2\u7684\u7528\u6CD5\u3002\u5B83\u653E\u5728\u540D\u8BCD\u524D\u540E\uFF0C\u7ED9\u540D\u8BCD\u8D34\u4E0A\u4E00\u4E2A\u201C\u6B63\u5728\u505A\u4EC0\u4E48\u201D\u7684\u52A8\u6001\u6807\u7B7E\u3002</p><table><thead><tr><th style="text-align:left;">\u4F4D\u7F6E</th><th style="text-align:left;">\u4F8B\u53E5</th><th style="text-align:left;">\u89E3\u91CA</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u524D\u7F6E\u5B9A\u8BED</strong> <br>\uFF08\u5355\u4E2A\u5206\u8BCD\uFF09</td><td style="text-align:left;">Look at the <strong>sleeping</strong> baby. \uFF08\u770B\u90A3\u4E2A<strong>\u6B63\u5728\u7761\u89C9\u7684</strong>\u5A74\u513F\u3002\uFF09</td><td style="text-align:left;"><code>sleeping</code> \u63CF\u8FF0\u5A74\u513F\u6B63\u5728\u8FDB\u884C\u7684\u52A8\u4F5C\u3002</td></tr><tr><td style="text-align:left;"></td><td style="text-align:left;">It was an <strong>exciting</strong> game. \uFF08\u90A3\u662F\u4E00\u573A<strong>\u4EE4\u4EBA\u5174\u594B\u7684</strong>\u6BD4\u8D5B\u3002\uFF09</td><td style="text-align:left;"><code>exciting</code> \u63CF\u8FF0\u6BD4\u8D5B\u7684\u7279\u6027\u662F\u201C\u8BA9\u4EBA\u611F\u5230\u5174\u594B\u201D\u3002</td></tr><tr><td style="text-align:left;"><strong>\u540E\u7F6E\u5B9A\u8BED</strong> <br>\uFF08\u5206\u8BCD\u77ED\u8BED\uFF09</td><td style="text-align:left;">The man <strong>standing by the window</strong> is my teacher. \uFF08<strong>\u7AD9\u5728\u7A97\u8FB9\u7684</strong>\u90A3\u4E2A\u7537\u4EBA\u662F\u6211\u7684\u8001\u5E08\u3002\uFF09</td><td style="text-align:left;"><code>standing by the window</code> \u8FD9\u4E2A\u77ED\u8BED\u4FEE\u9970 <code>the man</code>\u3002</td></tr></tbody></table><h4 id="\u529F\u80FD\u4E8C-\u4F5C\u8868\u8BED-\u8BF4\u660E\u4E3B\u8BED\u7684-\u7279\u6027" tabindex="-1"><a class="header-anchor" href="#\u529F\u80FD\u4E8C-\u4F5C\u8868\u8BED-\u8BF4\u660E\u4E3B\u8BED\u7684-\u7279\u6027" aria-hidden="true">#</a> <strong>\u529F\u80FD\u4E8C\uFF1A\u4F5C\u8868\u8BED\uFF08\u8BF4\u660E\u4E3B\u8BED\u7684\u201C\u7279\u6027\u201D\uFF09</strong></h4><p>\u653E\u5728\u7CFB\u52A8\u8BCD\uFF08\u5982 be, become, feel, look, seem\uFF09\u540E\u9762\uFF0C\u8BF4\u660E\u4E3B\u8BED\u7684\u6027\u8D28\u6216\u7279\u5F81\u3002\u6B64\u65F6\uFF0C\u73B0\u5728\u5206\u8BCD\u5DF2\u7ECF\u975E\u5E38\u63A5\u8FD1\u4E00\u4E2A\u7EAF\u7CB9\u7684\u5F62\u5BB9\u8BCD\u4E86\u3002</p><ul><li>The movie is <strong>boring</strong>. \uFF08\u8FD9\u90E8\u7535\u5F71\u5F88<strong>\u65E0\u804A</strong>\u3002\uFF09 <ul><li><strong>\u6838\u5FC3</strong>\uFF1A\u7535\u5F71\u5177\u6709\u201C\u4EE4\u4EBA\u611F\u5230\u65E0\u804A\u201D\u7684\u7279\u6027\u3002</li></ul></li><li>His speech was <strong>encouraging</strong>. \uFF08\u4ED6\u7684\u6F14\u8BB2\u5F88<strong>\u9F13\u821E\u4EBA\u5FC3</strong>\u3002\uFF09 <ul><li><strong>\u6838\u5FC3</strong>\uFF1A\u6F14\u8BB2\u5177\u6709\u201C\u4EE4\u4EBA\u9F13\u821E\u201D\u7684\u7279\u6027\u3002</li></ul></li></ul><p><strong>\u3010\u91CD\u8981\u5BF9\u6BD4\u3011\u73B0\u5728\u5206\u8BCD vs. \u8FC7\u53BB\u5206\u8BCD\uFF08\u4F5C\u8868\u8BED/\u5B9A\u8BED\uFF09</strong> \u8FD9\u662F\u4E2D\u56FD\u5B66\u4E60\u8005\u6700\u5BB9\u6613\u9519\u7684\u5730\u65B9\uFF01\u8BF7\u7262\u8BB0\u8FD9\u4E2A\u9EC4\u91D1\u6CD5\u5219\uFF1A</p><ul><li><p><strong>-ing (\u73B0\u5728\u5206\u8BCD) =&gt; \u201C\u4EE4\u4EBA\u2026\u2026\u7684\u201D</strong>\uFF0C\u63CF\u8FF0<strong>\u4E8B\u7269\u672C\u8EAB</strong>\u7684\u7279\u6027\u3002</p></li><li><p><strong>-ed (\u8FC7\u53BB\u5206\u8BCD) =&gt; \u201C\u611F\u5230\u2026\u2026\u7684\u201D</strong>\uFF0C\u63CF\u8FF0<strong>\u4EBA</strong>\u7684\u611F\u53D7\u3002</p></li><li><p><strong>a boring book</strong> \uFF08\u4E00\u672C\u4EE4\u4EBA\u65E0\u804A\u7684\u4E66\uFF09 -&gt; \u4E66\u7684\u7279\u70B9</p></li><li><p><strong>a bored reader</strong> \uFF08\u4E00\u4E2A\u611F\u5230\u65E0\u804A\u7684\u8BFB\u8005\uFF09 -&gt; \u8BFB\u8005\u7684\u611F\u53D7</p></li><li><p><strong>an exciting story</strong> \uFF08\u4E00\u4E2A\u4EE4\u4EBA\u5174\u594B\u7684\u6545\u4E8B\uFF09</p></li><li><p><strong>an excited child</strong> \uFF08\u4E00\u4E2A\u611F\u5230\u5174\u594B\u7684\u5B69\u5B50\uFF09</p></li></ul><h4 id="\u529F\u80FD\u4E09-\u4F5C\u5BBE\u8BED\u8865\u8DB3\u8BED-\u63CF\u8FF0\u5BBE\u8BED\u7684-\u8FDB\u884C\u72B6\u6001" tabindex="-1"><a class="header-anchor" href="#\u529F\u80FD\u4E09-\u4F5C\u5BBE\u8BED\u8865\u8DB3\u8BED-\u63CF\u8FF0\u5BBE\u8BED\u7684-\u8FDB\u884C\u72B6\u6001" aria-hidden="true">#</a> <strong>\u529F\u80FD\u4E09\uFF1A\u4F5C\u5BBE\u8BED\u8865\u8DB3\u8BED\uFF08\u63CF\u8FF0\u5BBE\u8BED\u7684\u201C\u8FDB\u884C\u72B6\u6001\u201D\uFF09</strong></h4><p>\u653E\u5728\u67D0\u4E9B\u52A8\u8BCD\uFF08\u4E3B\u8981\u662F\u611F\u5B98\u52A8\u8BCD\uFF1Asee, hear, watch, feel, find \u7B49\uFF09\u7684\u5BBE\u8BED\u4E4B\u540E\uFF0C\u8865\u5145\u8BF4\u660E\u8FD9\u4E2A\u5BBE\u8BED<strong>\u6B63\u5728\u8FDB\u884C\u7684\u52A8\u4F5C</strong>\u3002</p><ul><li>I saw him <strong>crossing</strong> the street. \uFF08\u6211\u770B\u89C1\u4ED6<strong>\u6B63\u5728\u7A7F\u8FC7</strong>\u9A6C\u8DEF\u3002\uFF09 <ul><li><strong>\u5F3A\u8C03</strong>\u201C\u7A7F\u8FC7\u9A6C\u8DEF\u201D\u8FD9\u4E2A\u52A8\u4F5C\u5728\u5F53\u65F6\u662F<strong>\u6B63\u5728\u8FDB\u884C</strong>\u7684\u3002</li></ul></li><li>I heard someone <strong>knocking</strong> at the door. \uFF08\u6211\u542C\u89C1\u6709\u4EBA<strong>\u5728\u6572\u95E8</strong>\u3002\uFF09 <ul><li><strong>\u5F3A\u8C03</strong>\u201C\u6572\u95E8\u201D\u58F0\u5728\u201C\u542C\u89C1\u201D\u7684\u77AC\u95F4<strong>\u6301\u7EED\u7740</strong>\u3002</li></ul></li></ul><p><strong>\u3010\u5BF9\u6BD4\u3011\u4E0D\u5B9A\u5F0F\u4F5C\u5BBE\u8865</strong>\uFF1A<code>I saw him cross the street.</code> \uFF08\u6211\u770B\u89C1\u4ED6\u8FC7\u4E86\u9A6C\u8DEF\u3002\uFF09-&gt; \u5F3A\u8C03\u201C\u8FC7\u9A6C\u8DEF\u201D\u8FD9\u4E2A\u52A8\u4F5C\u7684<strong>\u5168\u8FC7\u7A0B</strong>\u6216<strong>\u5DF2\u5B8C\u6210</strong>\u3002</p><h4 id="\u529F\u80FD\u56DB-\u4F5C\u72B6\u8BED-\u53E5\u5B50\u7684-\u80CC\u666F\u677F" tabindex="-1"><a class="header-anchor" href="#\u529F\u80FD\u56DB-\u4F5C\u72B6\u8BED-\u53E5\u5B50\u7684-\u80CC\u666F\u677F" aria-hidden="true">#</a> <strong>\u529F\u80FD\u56DB\uFF1A\u4F5C\u72B6\u8BED\uFF08\u53E5\u5B50\u7684\u201C\u80CC\u666F\u677F\u201D\uFF09</strong></h4><p>\u8FD9\u662F\u6700\u9AD8\u7EA7\u3001\u6700\u5730\u9053\u7684\u7528\u6CD5\uFF01\u73B0\u5728\u5206\u8BCD\u77ED\u8BED\u53EF\u4EE5\u653E\u5728\u53E5\u9996\u6216\u53E5\u672B\uFF0C\u4F5C\u4E3A\u4E00\u4E2A\u72B6\u8BED\uFF0C\u8868\u793A\u65F6\u95F4\u3001\u539F\u56E0\u3001\u6761\u4EF6\u3001\u65B9\u5F0F\u6216\u4F34\u968F\u60C5\u51B5\u3002<strong>\u5B83\u7684\u903B\u8F91\u4E3B\u8BED\u5FC5\u987B\u662F\u53E5\u5B50\u7684\u4E3B\u8BED\u3002</strong></p><table><thead><tr><th style="text-align:left;">\u529F\u80FD</th><th style="text-align:left;">\u4F8B\u53E5</th><th style="text-align:left;">\u89E3\u91CA</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u65F6\u95F4\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>Walking in the park</strong>, I met an old friend. \uFF08<strong>\u5728\u516C\u56ED\u6563\u6B65\u65F6</strong>\uFF0C\u6211\u9047\u5230\u4E86\u4E00\u4F4D\u8001\u670B\u53CB\u3002\uFF09</td><td style="text-align:left;"><code>Walking</code> \u7684\u52A8\u4F5C\u7531\u4E3B\u8BED <code>I</code> \u53D1\u51FA\uFF0C\u4E14\u4E0E\u201C\u9047\u5230\u201D\u540C\u65F6\u53D1\u751F\u3002</td></tr><tr><td style="text-align:left;"><strong>\u539F\u56E0\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>Being sick</strong>, I stayed at home. \uFF08<strong>\u56E0\u4E3A\u751F\u75C5\u4E86</strong>\uFF0C\u6211\u5F85\u5728\u5BB6\u91CC\u3002\uFF09</td><td style="text-align:left;"><code>Being sick</code> \u662F\u201C\u6211\u5F85\u5728\u5BB6\u91CC\u201D\u7684\u539F\u56E0\u3002</td></tr><tr><td style="text-align:left;"><strong>\u65B9\u5F0F/\u4F34\u968F\u72B6\u8BED</strong></td><td style="text-align:left;">She sat by the window, <strong>thinking deeply</strong>. \uFF08\u5979\u5750\u5728\u7A97\u8FB9\uFF0C<strong>\u9677\u5165\u4E86\u6C89\u601D</strong>\u3002\uFF09</td><td style="text-align:left;"><code>thinking</code> \u662F\u4F34\u968F\u201C\u5750\u201D\u8FD9\u4E2A\u52A8\u4F5C\u540C\u65F6\u53D1\u751F\u7684\u3002</td></tr><tr><td style="text-align:left;"><strong>\u6761\u4EF6\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>Working hard</strong>, you will succeed. \uFF08<strong>\u5982\u679C\u52AA\u529B\u5DE5\u4F5C</strong>\uFF0C\u4F60\u5C31\u4F1A\u6210\u529F\u3002\uFF09</td><td style="text-align:left;"><code>Working hard</code> \u662F\u201C\u6210\u529F\u201D\u7684\u6761\u4EF6\u3002</td></tr></tbody></table><hr><h3 id="\u7B2C\u56DB\u90E8\u5206-\u73B0\u5728\u5206\u8BCD\u7684-\u5B8C\u6210\u5F0F-\u548C-\u88AB\u52A8\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u56DB\u90E8\u5206-\u73B0\u5728\u5206\u8BCD\u7684-\u5B8C\u6210\u5F0F-\u548C-\u88AB\u52A8\u5F0F" aria-hidden="true">#</a> <strong>\u7B2C\u56DB\u90E8\u5206\uFF1A\u73B0\u5728\u5206\u8BCD\u7684\u201C\u5B8C\u6210\u5F0F\u201D\u548C\u201C\u88AB\u52A8\u5F0F\u201D</strong></h3><p>\u5F53\u9700\u8981\u8868\u8FBE\u66F4\u590D\u6742\u7684\u65F6\u95F4\u5173\u7CFB\u6216\u4E3B\u88AB\u52A8\u5173\u7CFB\u65F6\uFF0C\u73B0\u5728\u5206\u8BCD\u8FD8\u6709\u66F4\u9AD8\u7EA7\u7684\u5F62\u5F0F\u3002</p><table><thead><tr><th style="text-align:left;">\u5F62\u5F0F</th><th style="text-align:left;">\u7ED3\u6784</th><th style="text-align:left;">\u542B\u4E49\u4E0E\u4F8B\u53E5</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>\u4E00\u822C\u5F0F</strong></td><td style="text-align:left;">doing</td><td style="text-align:left;">\u8868\u793A\u52A8\u4F5C\u4E0E\u8C13\u8BED\u52A8\u8BCD<strong>\u540C\u65F6</strong>\u53D1\u751F\u3002 <br> <strong>Seeing</strong> the police, he ran away. \uFF08<strong>\u4E00\u770B\u5230</strong>\u8B66\u5BDF\uFF0C\u4ED6\u5C31\u8DD1\u4E86\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u5B8C\u6210\u5F0F</strong></td><td style="text-align:left;">having done</td><td style="text-align:left;">\u8868\u793A\u52A8\u4F5C\u5728\u8C13\u8BED\u52A8\u8BCD<strong>\u4E4B\u524D</strong>\u5DF2\u7ECF\u5B8C\u6210\u3002 <br> <strong>Having finished</strong> his work, he went home. \uFF08<strong>\u505A\u5B8C</strong>\u5DE5\u4F5C\u540E\uFF0C\u4ED6\u56DE\u5BB6\u4E86\u3002\uFF09</td></tr><tr><td style="text-align:left;"><strong>\u88AB\u52A8\u5F0F</strong></td><td style="text-align:left;">being done</td><td style="text-align:left;">\u8868\u793A<strong>\u88AB\u52A8</strong>\u542B\u4E49\uFF0C\u4E14\u52A8\u4F5C<strong>\u6B63\u5728\u8FDB\u884C</strong>\u3002 <br> The building <strong>being built</strong> is a library. \uFF08<strong>\u6B63\u5728\u88AB\u5EFA\u9020\u7684</strong>\u90A3\u5EA7\u5EFA\u7B51\u662F\u4E00\u4E2A\u56FE\u4E66\u9986\u3002\uFF09</td></tr></tbody></table><hr><h3 id="\u603B\u7ED3\u4E0E\u5FC3\u6CD5" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3\u4E0E\u5FC3\u6CD5" aria-hidden="true">#</a> <strong>\u603B\u7ED3\u4E0E\u5FC3\u6CD5</strong></h3><ol><li><strong>\u73B0\u5728\u5206\u8BCD</strong> \u662F <code>-ing</code> \u5F62\u5F0F\uFF0C\u6838\u5FC3\u542B\u4E49\u662F <strong>\u201C\u4E3B\u52A8\u201D</strong> \u548C <strong>\u201C\u8FDB\u884C\u201D</strong>\u3002</li><li><strong>\u56DB\u5927\u529F\u80FD</strong>\uFF1A <ul><li><strong>\u4F5C\u5B9A\u8BED</strong>\uFF1A\u7ED9\u540D\u8BCD\u8D34\u201C\u52A8\u6001\u6807\u7B7E\u201D\u3002</li><li><strong>\u4F5C\u8868\u8BED</strong>\uFF1A\u8BF4\u660E\u4E3B\u8BED\u201C\u4EE4\u4EBA\u2026\u201D\u7684\u7279\u6027\uFF08\u7262\u8BB0\uFF1A-ing \u662F\u201C\u4EE4\u4EBA\u201D\uFF0C-ed \u662F\u201C\u611F\u5230\u201D\uFF09\u3002</li><li><strong>\u4F5C\u5BBE\u8865</strong>\uFF1A\u8BF4\u660E\u5BBE\u8BED\u201C\u6B63\u5728\u201D\u7684\u52A8\u4F5C\u3002</li><li><strong>\u4F5C\u72B6\u8BED</strong>\uFF1A\u4E3A\u53E5\u5B50\u6DFB\u52A0\u65F6\u95F4\u3001\u539F\u56E0\u3001\u65B9\u5F0F\u7B49\u80CC\u666F\u4FE1\u606F\uFF08<strong>\u786E\u4FDD\u4E3B\u8BED\u4E00\u81F4</strong>\uFF09\u3002</li></ul></li><li><strong>\u9AD8\u7EA7\u5F62\u6001</strong>\uFF1A<code>having done</code>\uFF08\u8868\u5B8C\u6210\uFF09\uFF0C<code>being done</code>\uFF08\u8868\u88AB\u52A8\u8FDB\u884C\uFF09\u3002</li></ol><p>\u638C\u63E1\u4E86\u73B0\u5728\u5206\u8BCD\uFF0C\u4F60\u7684\u82F1\u8BED\u8868\u8FBE\u5C31\u4E0D\u518D\u662F\u9759\u6001\u7684\u201CShe is a happy girl.\u201D\uFF08\u5979\u662F\u4E2A\u5F00\u5FC3\u7684\u5973\u5B69\uFF09\uFF0C\u800C\u662F\u52A8\u6001\u7684\u3001\u5145\u6EE1\u753B\u9762\u611F\u7684\u201CThe <strong>smiling</strong> girl is talking to her friend, <strong>holding</strong> a flower.\u201D\uFF08\u90A3\u4E2A<strong>\u5FAE\u7B11\u7740\u7684</strong>\u5973\u5B69\u6B63\u548C\u5979\u7684\u670B\u53CB\u8BF4\u8BDD\uFF0C\u624B\u91CC<strong>\u8FD8\u62FF\u7740\u4E00\u6735\u82B1</strong>\u3002\uFF09</p><p>\u63A5\u4E0B\u6765\u60F3\u4E86\u89E3\u5B83\u7684\u5144\u5F1F\u201C\u8FC7\u53BB\u5206\u8BCD\u201D\u7684\u6DF1\u5EA6\u5256\u6790\uFF0C\u8FD8\u662F\u60F3\u8FDB\u884C\u4E00\u4E9B\u5BF9\u6BD4\u7EC3\u4E60\uFF1F\u6211\u968F\u65F6\u5728\u6B64\u7B49\u5019\u3002</p>',40),g=[o];function l(i,d){return n(),e("div",null,g)}var h=t(s,[["render",l],["__file","index.html.vue"]]);export{h as default};
