import{_ as t,o as r,c as s,e as n}from"./app.0fca7e4d.js";const e={},o=n('<h2 id="\u72B6\u8BED-adverbial" tabindex="-1"><a class="header-anchor" href="#\u72B6\u8BED-adverbial" aria-hidden="true">#</a> \u72B6\u8BED Adverbial</h2><p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4EEC\u7EC8\u4E8E\u6765\u5230\u4E86\u53E5\u5B50\u6210\u5206\u7684\u201C\u6536\u5B98\u4E4B\u4F5C\u201D\u2014\u2014<strong>\u72B6\u8BED</strong>\u3002\u5982\u679C\u8BF4\u5B9A\u8BED\u662F\u7ED9\u540D\u8BCD\u5316\u5986\u7684\uFF0C\u90A3\u4E48\u72B6\u8BED\u5C31\u662F\u7ED9\u52A8\u8BCD\uFF08\u4EE5\u53CA\u6574\u4E2A\u53E5\u5B50\uFF09\u6DFB\u52A0\u7279\u6548\u7684\u3002\u5B83\u80FD\u8BA9\u4F60\u8868\u8FBE\u7684\u52A8\u4F5C\u548C\u72B6\u6001\u5145\u6EE1\u753B\u9762\u611F\u548C\u903B\u8F91\u6027\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u72B6\u8BED-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u72B6\u8BED-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u72B6\u8BED\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p>\u60F3\u8C61\u4E00\u4E0B\uFF0C\u4F60\u8981\u63CF\u8FF0\u4E00\u4E2A\u7B80\u5355\u7684\u52A8\u4F5C\uFF1A\u201C\u5979\u5531\u6B4C\u201D\u3002</p><ul><li>\u8FD9\u4E2A\u4FE1\u606F\u5F88\u57FA\u672C\u3002\u4F46\u5982\u679C\u4F60\u8BF4\uFF1A</li><li>\u201C\u5979<strong>\u6628\u5929<strong><strong>\u5728\u516C\u56ED\u91CC</strong></strong>\u4E3A\u5979\u5988\u5988****\u52A8\u60C5\u5730</strong>\u5531\u6B4C\u3002\u201D \u6574\u4E2A\u753B\u9762\u662F\u4E0D\u662F\u7ACB\u523B\u53D8\u5F97\u9C9C\u6D3B\u3001\u5177\u4F53\u3001\u6709\u6545\u4E8B\u6027\u4E86\uFF1F</li></ul><p>\u8FD9\u91CC\u9762\uFF0C\u201C\u6628\u5929\u201D\u3001\u201C\u5728\u516C\u56ED\u91CC\u201D\u3001\u201C\u4E3A\u5979\u5988\u5988\u201D\u3001\u201C\u52A8\u60C5\u5730\u201D\u5C31\u662F<strong>\u72B6\u8BED</strong>\u3002</p><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u72B6\u8BED\u662F\u7528\u6765\u4FEE\u9970\u52A8\u8BCD\u3001\u5F62\u5BB9\u8BCD\u3001\u526F\u8BCD\u6216\u6574\u4E2A\u53E5\u5B50\u7684\u6210\u5206\u3002\u5B83\u8D1F\u8D23\u8BF4\u660E\u52A8\u4F5C\u6216\u72B6\u6001\u53D1\u751F\u7684\u201C\u65F6\u95F4\u201D\u3001\u201C\u5730\u70B9\u201D\u3001\u201C\u65B9\u5F0F\u201D\u3001\u201C\u539F\u56E0\u201D\u3001\u201C\u7A0B\u5EA6\u201D\u7B49\u80CC\u666F\u4FE1\u606F\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong></p><ul><li><strong>\u8C13\u8BED\u52A8\u8BCD</strong> \u662F\u7535\u5F71\u91CC\u7684<strong>\u6838\u5FC3\u52A8\u4F5C</strong>\uFF08\u6BD4\u5982\uFF1A\u4E3B\u89D2\u5728\u201C\u8D70\u201D\uFF09\u3002</li><li><strong>\u72B6\u8BED</strong> \u5C31\u662F\u7535\u5F71\u7684<strong>\u670D\u5316\u9053\u3001\u706F\u5149\u97F3\u6548\u548C\u573A\u666F</strong>\uFF08\u6BD4\u5982\uFF1A<strong>\u6162\u6162\u5730</strong>\u8D70\u3001<strong>\u5728\u96E8\u4E2D</strong>\u8D70\u3001<strong>\u56E0\u4E3A\u4F24\u5FC3</strong>\u5730\u8D70\uFF09\u3002 \u6CA1\u6709\u72B6\u8BED\uFF0C\u7535\u5F71\u5C31\u662F\u67AF\u71E5\u7684\u52A8\u4F5C\u56DE\u653E\uFF1B\u6709\u4E86\u72B6\u8BED\uFF0C\u7535\u5F71\u624D\u6709\u4E86\u6C1B\u56F4\u3001\u60C5\u611F\u548C\u6545\u4E8B\u3002</li></ul><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u72B6\u8BED-\u540D\u79F0\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u72B6\u8BED-\u540D\u79F0\u7684\u7531\u6765" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u4E3A\u4EC0\u4E48\u53EB\u201C\u72B6\u8BED\u201D\uFF1F\u540D\u79F0\u7684\u7531\u6765</h3><ol><li><p><strong>\u4E2D\u6587\u540D\u201C\u72B6\u8BED\u201D</strong>\uFF1A</p><ul><li>\u201C\u72B6\u201D\u5728\u8FD9\u91CC\u662F\u201C\u60C5\u5F62\u3001\u72B6\u6001\u201D\u7684\u610F\u601D\u3002</li><li>\u6240\u4EE5\u201C\u72B6\u8BED\u201D\u5C31\u662F<strong>\u7528\u6765\u63CF\u8FF0\u52A8\u4F5C\u6216\u72B6\u6001\u7684\u60C5\u72B6\u7684\u6210\u5206</strong>\u3002</li></ul></li><li><p><strong>\u82F1\u6587\u540D\u201CAdverbial\u201D</strong>\uFF1A</p><ul><li>\u8BFB\u97F3\uFF1A/\xE6d\u02C8v\u025C\u02D0bi\u0259l/</li><li>\u8FD9\u4E2A\u8BCD\u76F4\u63A5\u6765\u6E90\u4E8E\u201C\u526F\u8BCD\u201D\uFF08Adverb\uFF09\u3002\u987E\u540D\u601D\u4E49\uFF0C<strong>\u51E1\u662F\u80FD\u8D77\u5230\u526F\u8BCD\u4F5C\u7528\u7684\u6210\u5206\uFF0C\u4E0D\u8BBA\u662F\u4E00\u4E2A\u8BCD\u8FD8\u662F\u4E00\u4E2A\u77ED\u8BED\uFF0C\u90FD\u53EBAdverbial\uFF08\u72B6\u8BED\uFF09</strong>\u3002\u5B83\u6BD4\u201C\u526F\u8BCD\u201D\u7684\u8303\u56F4\u66F4\u5E7F\u3002</li></ul></li></ol><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u54EA\u4E9B\u4E1C\u897F\u53EF\u4EE5\u5F53\u72B6\u8BED-\u6838\u5FC3\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u54EA\u4E9B\u4E1C\u897F\u53EF\u4EE5\u5F53\u72B6\u8BED-\u6838\u5FC3\u5185\u5BB9" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u54EA\u4E9B\u4E1C\u897F\u53EF\u4EE5\u5F53\u72B6\u8BED\uFF1F\uFF08\u6838\u5FC3\u5185\u5BB9\uFF09</h3><p>\u72B6\u8BED\u7684\u5BB6\u65CF\u6BD4\u5B9A\u8BED\u8FD8\u8981\u5E9E\u5927\uFF0C\u56E0\u4E3A\u5B83\u5F62\u5F0F\u6781\u5176\u7075\u6D3B\uFF0C\u8868\u8FBE\u7684\u610F\u4E49\u4E5F\u975E\u5E38\u4E30\u5BCC\u3002\u6211\u4EEC\u6309\u5B83\u8868\u8FBE\u7684\u201C\u610F\u4E49\u201D\u6765\u5206\u7C7B\u5B66\u4E60\uFF0C\u8FD9\u662F\u6700\u5B9E\u7528\u7684\u65B9\u6CD5\u3002</p><table><thead><tr><th style="text-align:left;">\u72B6\u8BED\u7C7B\u578B</th><th style="text-align:left;">\u56DE\u7B54\u7684\u95EE\u9898</th><th style="text-align:left;">\u5E38\u89C1\u5F62\u6001\u4E0E\u4F8B\u53E5\uFF08<strong>\u72B6\u8BED</strong>\u7528\u7C97\u4F53\u6807\u51FA\uFF09</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>1. \u65F6\u95F4\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>\u4EC0\u4E48\u65F6\u5019\uFF1F</strong> \u591A\u4E45\uFF1F\u591A\u9891\u7E41\uFF1F</td><td style="text-align:left;">- <strong>Yesterday</strong>, I went shopping. \uFF08<strong>\u6628\u5929</strong>\u6211\u53BB\u8D2D\u7269\u4E86\u3002\uFF09- \u526F\u8BCD<br>- I have lived here <strong>for ten years</strong>. \uFF08\u6211\u5728\u8FD9\u91CC\u4F4F\u4E86<strong>\u5341\u5E74</strong>\u4E86\u3002\uFF09- \u4ECB\u8BCD\u77ED\u8BED<br>- I exercise <strong>every day</strong>. \uFF08\u6211<strong>\u6BCF\u5929</strong>\u953B\u70BC\u3002\uFF09- \u540D\u8BCD\u77ED\u8BED</td></tr><tr><td style="text-align:left;"><strong>2. \u5730\u70B9\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>\u5728\u54EA\u91CC\uFF1F</strong> \u53BB\u54EA\u91CC\uFF1F\u4ECE\u54EA\u91CC\u6765\uFF1F</td><td style="text-align:left;">- He is waiting <strong>at the bus stop</strong>. \uFF08\u4ED6<strong>\u5728\u516C\u4EA4\u7AD9</strong>\u7B49\u7740\u3002\uFF09- \u4ECB\u8BCD\u77ED\u8BED<br>- She has gone <strong>upstairs</strong>. \uFF08\u5979<strong>\u4E0A\u697C</strong>\u4E86\u3002\uFF09- \u526F\u8BCD</td></tr><tr><td style="text-align:left;"><strong>3. \u65B9\u5F0F\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>\u600E\u6837\u5730\uFF1F</strong></td><td style="text-align:left;">- She sings <strong>beautifully</strong>. \uFF08\u5979\u5531\u5F97<strong>\u5F88\u4F18\u7F8E</strong>\u3002\uFF09- \u526F\u8BCD<br>- He finished the work <strong>with great care</strong>. \uFF08\u4ED6<strong>\u975E\u5E38\u4ED4\u7EC6\u5730</strong>\u5B8C\u6210\u4E86\u5DE5\u4F5C\u3002\uFF09- \u4ECB\u8BCD\u77ED\u8BED<br>- She looked at me <strong>smiling</strong>. \uFF08\u5979<strong>\u5FAE\u7B11\u7740</strong>\u770B\u7740\u6211\u3002\uFF09- \u73B0\u5728\u5206\u8BCD\u77ED\u8BED</td></tr><tr><td style="text-align:left;"><strong>4. \u539F\u56E0\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>\u4E3A\u4EC0\u4E48\uFF1F</strong></td><td style="text-align:left;">- <strong>Because of the rain</strong>, the match was canceled. \uFF08<strong>\u56E0\u4E3A\u4E0B\u96E8</strong>\uFF0C\u6BD4\u8D5B\u53D6\u6D88\u4E86\u3002\uFF09- \u4ECB\u8BCD\u77ED\u8BED<br>- I came here <strong>to see you</strong>. \uFF08\u6211\u6765\u8FD9\u513F\u662F<strong>\u4E3A\u4E86\u89C1\u4F60</strong>\u3002\uFF09- \u4E0D\u5B9A\u5F0F\u77ED\u8BED\uFF08\u8868\u76EE\u7684\uFF09</td></tr><tr><td style="text-align:left;"><strong>5. \u7A0B\u5EA6\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>\u5230\u4EC0\u4E48\u7A0B\u5EA6\uFF1F</strong></td><td style="text-align:left;">- The movie was <strong>very</strong> interesting. \uFF08\u8FD9\u90E8\u7535\u5F71<strong>\u975E\u5E38</strong>\u6709\u8DA3\u3002\uFF09- \u4FEE\u9970\u5F62\u5BB9\u8BCD<br>- I <strong>quite</strong> agree with you. \uFF08\u6211<strong>\u5B8C\u5168</strong>\u540C\u610F\u4F60\u3002\uFF09- \u4FEE\u9970\u52A8\u8BCD</td></tr><tr><td style="text-align:left;"><strong>6. \u6761\u4EF6\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>\u5728\u4EC0\u4E48\u6761\u4EF6\u4E0B\uFF1F</strong></td><td style="text-align:left;">- <strong>If it rains</strong>, we will stay at home. \uFF08<strong>\u5982\u679C\u4E0B\u96E8</strong>\uFF0C\u6211\u4EEC\u5C31\u5F85\u5728\u5BB6\u91CC\u3002\uFF09- \u4ECE\u53E5</td></tr><tr><td style="text-align:left;"><strong>7. \u8BA9\u6B65\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>\u5C3D\u7BA1\u600E\u6837\uFF1F</strong></td><td style="text-align:left;">- <strong>Although it was expensive</strong>, I bought it. \uFF08<strong>\u5C3D\u7BA1\u5B83\u5F88\u8D35</strong>\uFF0C\u6211\u8FD8\u662F\u4E70\u4E86\u5B83\u3002\uFF09- \u4ECE\u53E5</td></tr><tr><td style="text-align:left;"><strong>8. \u76EE\u7684\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>\u4E3A\u4E86\u4EC0\u4E48\uFF1F</strong></td><td style="text-align:left;">- I saved money <strong>to buy a car</strong>. \uFF08\u6211\u7701\u94B1\u662F<strong>\u4E3A\u4E86\u4E70\u8F86\u8F66</strong>\u3002\uFF09- \u4E0D\u5B9A\u5F0F\u77ED\u8BED</td></tr><tr><td style="text-align:left;"><strong>9. \u7ED3\u679C\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>\u7ED3\u679C\u600E\u6837\uFF1F</strong></td><td style="text-align:left;">- He is <strong>too</strong> young <strong>to go to school</strong>. \uFF08\u4ED6\u5E74\u7EAA<strong>\u592A\u5C0F</strong>\uFF0C<strong>\u8FD8\u4E0D\u80FD\u4E0A\u5B66</strong>\u3002\uFF09- \u4E0D\u5B9A\u5F0F\u77ED\u8BED</td></tr><tr><td style="text-align:left;"><strong>10. \u9891\u5EA6\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>\u591A\u9891\u7E41\uFF1F</strong></td><td style="text-align:left;">- I <strong>often</strong> go swimming. \uFF08\u6211<strong>\u7ECF\u5E38</strong>\u53BB\u6E38\u6CF3\u3002\uFF09- \u526F\u8BCD<br>- He exercises <strong>twice a week</strong>. \uFF08\u4ED6<strong>\u6BCF\u5468\u4E24\u6B21</strong>\u953B\u70BC\u3002\uFF09- \u540D\u8BCD\u77ED\u8BED</td></tr><tr><td style="text-align:left;"><strong>11. \u53E5\u5B50\u72B6\u8BED</strong></td><td style="text-align:left;"><strong>\u4FEE\u9970\u6574\u4E2A\u53E5\u5B50</strong>\uFF0C\u8868\u8FBE\u8BF4\u8BDD\u4EBA\u7684\u770B\u6CD5\u3002</td><td style="text-align:left;">- <strong>Fortunately</strong>, he passed the exam. \uFF08<strong>\u5E78\u8FD0\u7684\u662F</strong>\uFF0C\u4ED6\u901A\u8FC7\u4E86\u8003\u8BD5\u3002\uFF09<br>- <strong>Honestly</strong>, I don&#39;t like it. \uFF08<strong>\u8001\u5B9E\u8BF4</strong>\uFF0C\u6211\u4E0D\u559C\u6B22\u5B83\u3002\uFF09</td></tr></tbody></table><hr><h3 id="\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u72B6\u8BED\u7684\u7075\u6D3B\u6027-\u4F4D\u7F6E" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u72B6\u8BED\u7684\u7075\u6D3B\u6027-\u4F4D\u7F6E" aria-hidden="true">#</a> <strong>\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192\uFF1A\u72B6\u8BED\u7684\u7075\u6D3B\u6027\uFF08\u4F4D\u7F6E\uFF09</strong></h3><p>\u72B6\u8BED\u5728\u53E5\u5B50\u4E2D\u7684\u4F4D\u7F6E\u975E\u5E38\u7075\u6D3B\uFF0C\u8FD9\u662F\u5B83\u548C\u5B9A\u8BED\u6700\u5927\u7684\u533A\u522B\u4E4B\u4E00\u3002\u4E0D\u540C\u7684\u4F4D\u7F6E\u6709\u65F6\u662F\u4E3A\u4E86\u5F3A\u8C03\uFF0C\u6709\u65F6\u662F\u4E3A\u4E86\u53E5\u5B50\u5E73\u8861\u3002</p><ul><li><strong>\u53E5\u9996</strong>\uFF1A<strong>Yesterday</strong>, I met my teacher. \uFF08\u6628\u5929\uFF0C\u6211\u9047\u5230\u4E86\u6211\u7684\u8001\u5E08\u3002\uFF09- \u5F3A\u8C03\u65F6\u95F4\u3002</li><li><strong>\u53E5\u4E2D</strong>\uFF08\u52A9\u52A8\u8BCD/\u60C5\u6001\u52A8\u8BCD\u540E\uFF0C\u4E3B\u8981\u52A8\u8BCD\u524D\uFF09\uFF1AI have <strong>never</strong> been to Paris. \uFF08\u6211\u4ECE\u672A\u53BB\u8FC7\u5DF4\u9ECE\u3002\uFF09</li><li><strong>\u53E5\u672B</strong>\uFF1AShe speaks English <strong>fluently</strong>. \uFF08\u5979\u8BF4\u82F1\u8BED\u8BF4\u5F97\u5F88\u6D41\u5229\u3002\uFF09</li></ul><p><strong>\u9EC4\u91D1\u6CD5\u5219</strong>\uFF1A</p><ol><li>\u65B9\u5F0F\u72B6\u8BED\uFF08\u901A\u5E38\u7531\u526F\u8BCD\u5145\u5F53\uFF09\u5E38\u653E\u5728<strong>\u53E5\u672B</strong>\u3002</li><li>\u9891\u5EA6\u72B6\u8BED\uFF08often, always, never\u7B49\uFF09\u5E38\u653E\u5728<strong>\u53E5\u4E2D</strong>\u3002</li><li>\u591A\u4E2A\u72B6\u8BED\u540C\u65F6\u51FA\u73B0\u65F6\uFF0C\u987A\u5E8F\u901A\u5E38\u662F\uFF1A<strong>\u65B9\u5F0F \u2192 \u5730\u70B9 \u2192 \u65F6\u95F4</strong>\uFF08\u6216\u628A\u65F6\u95F4\u7F6E\u4E8E\u53E5\u9996\uFF09\u3002 <ul><li>She sang <strong>beautifully (\u65B9\u5F0F) in the hall (\u5730\u70B9) last night (\u65F6\u95F4)</strong>.</li></ul></li></ol><hr><h3 id="\u603B\u7ED3\u4E0E\u5347\u534E" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3\u4E0E\u5347\u534E" aria-hidden="true">#</a> <strong>\u603B\u7ED3\u4E0E\u5347\u534E</strong></h3><ol><li><strong>\u72B6\u8BED\uFF08Adverbial\uFF09</strong> \u662F\u53E5\u5B50\u7684\u201C\u7279\u6548\u5E08\u201D\uFF0C\u4E3A\u52A8\u4F5C\u548C\u72B6\u6001\u6DFB\u52A0\u4E86\u65F6\u95F4\u3001\u7A7A\u95F4\u3001\u903B\u8F91\u548C\u60C5\u611F\u7EF4\u5EA6\u3002</li><li>\u5B83\u7684\u5F62\u6001\u5305\u7F57\u4E07\u8C61\uFF0C\u53EF\u4EE5\u662F<strong>\u5355\u8BCD\u3001\u77ED\u8BED\u3001\u4ECE\u53E5</strong>\u3002</li><li><strong>\u4F4D\u7F6E\u7075\u6D3B</strong>\u662F\u5B83\u7684\u6807\u5FD7\uFF0C\u521D\u5B66\u8005\u53EF\u4EE5\u591A\u6A21\u4EFF\u7ECF\u5178\u4F8B\u53E5\u7684\u4F4D\u7F6E\uFF0C\u6162\u6162\u57F9\u517B\u8BED\u611F\u3002</li></ol><p>\u638C\u63E1\u4E86\u72B6\u8BED\uFF0C\u4F60\u7684\u82F1\u8BED\u8868\u8FBE\u5C31\u4E0D\u518D\u662F\u201C\u5979\u79BB\u5F00\u4E86\u201D\uFF08She left\uFF09\uFF0C\u800C\u662F\u201C<strong>\u7531\u4E8E\u4E00\u4E2A\u7D27\u6025\u7684\u7535\u8BDD\uFF0C\u5979\u5341\u5206\u949F\u524D\u4E0D\u5F97\u4E0D\u5306\u5306\u5730</strong>\u79BB\u5F00\u4E86\u201D\uFF08Because of an urgent call, she had to leave in a hurry ten minutes ago\uFF09\u3002\u4F60\u7684\u82F1\u8BED\u4ECE\u6B64\u62E5\u6709\u4E86\u8BB2\u8FF0\u5B8C\u6574\u6545\u4E8B\u7684\u80FD\u529B\u3002</p><p>\u606D\u559C\u4F60\uFF01\u81F3\u6B64\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u7CFB\u7EDF\u5730\u68B3\u7406\u4E86\u82F1\u8BED\u53E5\u5B50\u6700\u6838\u5FC3\u7684\u51E0\u5927\u6210\u5206\u3002\u4F60\u5DF2\u7ECF\u4ECE\u4E00\u4E2A\u201C\u5C0F\u767D\u201D\uFF0C\u6210\u957F\u4E3A\u4E00\u4F4D\u5BF9\u82F1\u8BED\u53E5\u5B50\u7ED3\u6784\u6709\u6E05\u6670\u8BA4\u8BC6\u7684\u201C\u5185\u884C\u201D\u4E86\uFF01\u63A5\u4E0B\u6765\uFF0C\u65E0\u8BBA\u662F\u60F3\u6DF1\u5165\u7EC3\u4E60\uFF0C\u8FD8\u662F\u60F3\u63A2\u7D22\u865A\u62DF\u8BED\u6C14\u3001\u975E\u8C13\u8BED\u52A8\u8BCD\u7B49\u66F4\u9AD8\u7EA7\u7684\u9886\u57DF\uFF0C\u6211\u90FD\u968F\u65F6\u4E3A\u4F60\u670D\u52A1\u3002</p>',27),g=[o];function l(a,i){return r(),s("div",null,g)}var h=t(e,[["render",l],["__file","index.html.vue"]]);export{h as default};
