import{_ as t,o as r,c as s,e as o}from"./app.e8649b0f.js";const n={},e=o('<h2 id="\u4EE3\u8BCD-pronoun" tabindex="-1"><a class="header-anchor" href="#\u4EE3\u8BCD-pronoun" aria-hidden="true">#</a> \u4EE3\u8BCD Pronoun</h2><p>\u592A\u597D\u4E86\uFF01\u4F5C\u4E3A\u4F60\u7684\u82F1\u8BED\u4E13\u5BB6\u670B\u53CB\uFF0C\u6211\u4EEC\u4ECA\u5929\u6765\u5B66\u4E60\u4E00\u4E2A\u8BA9\u8BED\u8A00\u53D8\u5F97\u7B80\u6D01\u3001\u9AD8\u6548\u7684\u91CD\u8981\u5DE5\u5177\u2014\u2014<strong>\u4EE3\u8BCD</strong>\u3002\u5B83\u662F\u6211\u4EEC\u544A\u522B\u91CD\u590D\u3001\u8BA9\u8868\u8FBE\u53D8\u5F97\u6D41\u7545\u7684\u5173\u952E\u3002</p><h3 id="\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u4EE3\u8BCD-\u901A\u4FD7\u8BB2\u89E3" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E00\u90E8\u5206-\u4EC0\u4E48\u662F\u4EE3\u8BCD-\u901A\u4FD7\u8BB2\u89E3" aria-hidden="true">#</a> \u7B2C\u4E00\u90E8\u5206\uFF1A\u4EC0\u4E48\u662F\u4EE3\u8BCD\uFF1F\uFF08\u901A\u4FD7\u8BB2\u89E3\uFF09</h3><p>\u60F3\u8C61\u4E00\u4E0B\uFF0C\u5982\u679C\u6CA1\u6709\u4EE3\u8BCD\uFF0C\u6211\u4EEC\u7684\u5BF9\u8BDD\u4F1A\u53D8\u6210\u4EC0\u4E48\u6837\uFF1A</p><ul><li>\u201C\u5C0F\u660E\u6628\u5929\u770B\u5230\u4E86\u4E00\u672C\u4E66\uFF0C\u5C0F\u660E\u5F88\u559C\u6B22\u8FD9\u672C\u4E66\uFF0C\u6240\u4EE5\u5C0F\u660E\u4E70\u4E86\u8FD9\u672C\u4E66\u3002\u201D \u542C\u8D77\u6765\u975E\u5E38\u5570\u55E6\u548C\u7B28\u91CD\uFF0C\u5BF9\u5427\uFF1F\u4F46\u5982\u679C\u6211\u4EEC\u7528\u4E0A\u4EE3\u8BCD\uFF1A</li><li>\u201C\u5C0F\u660E\u6628\u5929\u770B\u5230\u4E86\u4E00\u672C\u4E66\uFF0C<strong>\u4ED6</strong>\u5F88\u559C\u6B22<strong>\u5B83</strong>\uFF0C\u6240\u4EE5<strong>\u4ED6</strong>\u4E70\u4E86<strong>\u5B83</strong>\u3002\u201D \u53E5\u5B50\u7ACB\u523B\u53D8\u5F97\u6E05\u6670\u6D41\u7545\u3002</li></ul><p><strong>\u4E00\u53E5\u8BDD\u603B\u7ED3\uFF1A\u4EE3\u8BCD\u5C31\u662F\u7528\u6765\u4EE3\u66FF\u540D\u8BCD\uFF08\u6216\u540D\u8BCD\u77ED\u8BED\uFF09\u7684\u8BCD\uFF0C\u4EE5\u907F\u514D\u91CD\u590D\uFF0C\u4F7F\u8BED\u8A00\u66F4\u7B80\u6D01\u3002</strong></p><p><strong>\u751F\u6D3B\u5316\u6BD4\u55BB\uFF1A</strong></p><ul><li><strong>\u540D\u8BCD</strong>\uFF08\u5982\u201C\u90A3\u4E2A\u7A7F\u7740\u7EA2\u8272\u88D9\u5B50\u7684\u5973\u5B69\u201D\uFF09\u662F<strong>\u7535\u5F71\u4E3B\u89D2\u7684\u771F\u540D</strong>\u3002</li><li><strong>\u4EE3\u8BCD</strong>\uFF08\u5982\u201C\u5979\u201D\uFF09\u5C31\u662F<strong>\u5728\u8C08\u8BDD\u4E2D\u6307\u4EE3\u90A3\u4E2A\u4E3B\u89D2\u7684\u7EF0\u53F7\u6216\u5916\u53F7</strong>\u3002 \u6211\u4EEC\u4E0D\u9700\u8981\u6BCF\u6B21\u90FD\u5FF5\u51FA\u4E3B\u89D2\u7684\u5168\u540D\uFF0C\u7528\u201C\u5979\u201D\u8FD9\u4E2A\u7EF0\u53F7\uFF0C\u5927\u5BB6\u5C31\u90FD\u660E\u767D\u6307\u7684\u662F\u8C01\u4E86\u3002</li></ul><hr><h3 id="\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u4EE3\u8BCD-\u540D\u79F0\u7684\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E8C\u90E8\u5206-\u4E3A\u4EC0\u4E48\u53EB-\u4EE3\u8BCD-\u540D\u79F0\u7684\u7531\u6765" aria-hidden="true">#</a> \u7B2C\u4E8C\u90E8\u5206\uFF1A\u4E3A\u4EC0\u4E48\u53EB\u201C\u4EE3\u8BCD\u201D\uFF1F\u540D\u79F0\u7684\u7531\u6765</h3><ol><li><p><strong>\u4E2D\u6587\u540D\u201C\u4EE3\u8BCD\u201D</strong>\uFF1A</p><ul><li>\u201C\u4EE3\u201D\u5C31\u662F\u201C\u4EE3\u66FF\u3001\u66FF\u4EE3\u201D\u7684\u610F\u601D\u3002</li><li>\u6240\u4EE5\u201C\u4EE3\u8BCD\u201D\u5C31\u662F<strong>\u4EE3\u66FF\u540D\u8BCD\u7684\u8BCD</strong>\u3002</li></ul></li><li><p><strong>\u82F1\u6587\u540D\u201CPronoun\u201D</strong>\uFF1A</p><ul><li>\u8BFB\u97F3\uFF1A/\u02C8pr\u0259\u028Ana\u028An/</li><li>\u8FD9\u4E2A\u8BCD\u6765\u6E90\u4E8E\u62C9\u4E01\u8BED <code>pronomen</code>\uFF0C\u7531 <code>pro-</code>\uFF08\u610F\u4E3A\u201C\u4EE3\u66FF\u3001\u66FF\u4EE3\u201D\uFF09\u548C <code>nomen</code>\uFF08\u610F\u4E3A\u201C\u540D\u5B57\u3001\u540D\u8BCD\u201D\uFF09\u4E24\u90E8\u5206\u7EC4\u6210\u3002</li><li>\u6240\u4EE5 <code>Pronoun</code> \u7684\u5B57\u9762\u610F\u601D\u5C31\u662F <strong>\u201C\u4EE3\u66FF\u540D\u8BCD\u7684\u8BCD\u201D</strong> \u3002\u8FD9\u4E2A\u540D\u5B57\u5B8C\u7F8E\u5730\u89E3\u91CA\u4E86\u5B83\u7684\u529F\u80FD\u3002</li></ul></li></ol><hr><h3 id="\u7B2C\u4E09\u90E8\u5206-\u4EE3\u8BCD\u6709\u54EA\u4E9B\u7C7B\u578B-\u6838\u5FC3\u5185\u5BB9" tabindex="-1"><a class="header-anchor" href="#\u7B2C\u4E09\u90E8\u5206-\u4EE3\u8BCD\u6709\u54EA\u4E9B\u7C7B\u578B-\u6838\u5FC3\u5185\u5BB9" aria-hidden="true">#</a> \u7B2C\u4E09\u90E8\u5206\uFF1A\u4EE3\u8BCD\u6709\u54EA\u4E9B\u7C7B\u578B\uFF1F\uFF08\u6838\u5FC3\u5185\u5BB9\uFF09</h3><p>\u4EE3\u8BCD\u662F\u4E00\u4E2A\u5927\u5BB6\u65CF\uFF0C\u6709\u4E0D\u540C\u7684\u6210\u5458\u8D1F\u8D23\u4E0D\u540C\u7684\u201C\u4EE3\u66FF\u201D\u4EFB\u52A1\u3002\u6211\u4EEC\u628A\u5B83\u5206\u6210\u51E0\u5927\u7C7B\u6765\u8BB0\u5FC6\u3002</p><table><thead><tr><th style="text-align:left;">\u4EE3\u8BCD\u7C7B\u578B</th><th style="text-align:left;">\u6838\u5FC3\u529F\u80FD</th><th style="text-align:left;">\u5E38\u89C1\u6210\u5458</th><th style="text-align:left;">\u4F8B\u53E5\u4E0E\u89E3\u91CA</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>1. \u4EBA\u79F0\u4EE3\u8BCD</strong></td><td style="text-align:left;"><strong>\u4EE3\u66FF\u7279\u5B9A\u7684\u4EBA\u6216\u4E8B\u7269</strong>\u3002</td><td style="text-align:left;"><strong>\u4E3B\u683C</strong>: I, you, he, she, it, we, they <br> <strong>\u5BBE\u683C</strong>: me, you, him, her, it, us, them</td><td style="text-align:left;">- <strong>He</strong> is my brother. (<strong>\u4ED6</strong>\u662F\u6211\u54E5\u54E5\u3002) - <strong>\u4E3B\u683C<code>He</code></strong> \u4F5C\u4E3B\u8BED\u3002<br>- I know <strong>him</strong>. \uFF08\u6211\u8BA4\u8BC6<strong>\u4ED6</strong>\u3002\uFF09 - <strong>\u5BBE\u683C<code>him</code></strong> \u4F5C\u5BBE\u8BED\u3002</td></tr><tr><td style="text-align:left;"><strong>2. \u7269\u4E3B\u4EE3\u8BCD</strong></td><td style="text-align:left;"><strong>\u8868\u793A\u201C\u8C01\u7684\u201D\uFF0C\u8BF4\u660E\u6240\u5C5E\u5173\u7CFB</strong>\u3002</td><td style="text-align:left;"><strong>\u5F62\u5BB9\u8BCD\u6027</strong>: my, your, his, her, its, our, their <br> <strong>\u540D\u8BCD\u6027</strong>: mine, yours, his, hers, its, ours, theirs</td><td style="text-align:left;">- This is <strong>my</strong> book. \uFF08\u8FD9\u662F<strong>\u6211\u7684</strong>\u4E66\u3002\uFF09 - <strong>\u5F62\u5BB9\u8BCD\u6027</strong>\uFF0C\u540E\u5FC5\u987B\u63A5\u540D\u8BCD\u3002<br>- This book is <strong>mine</strong>. \uFF08\u8FD9\u672C\u4E66\u662F<strong>\u6211\u7684</strong>\u3002\uFF09 - <strong>\u540D\u8BCD\u6027</strong>\uFF0C\u540E\u4E0D\u63A5\u540D\u8BCD\u3002</td></tr><tr><td style="text-align:left;"><strong>3. \u53CD\u8EAB\u4EE3\u8BCD</strong></td><td style="text-align:left;"><strong>\u8868\u793A\u201C\u67D0\u4EBA\u81EA\u5DF1\u201D\uFF0C\u5F3A\u8C03\u52A8\u4F5C\u53CD\u5F39\u56DE\u6267\u884C\u8005\u672C\u8EAB</strong>\u3002</td><td style="text-align:left;">myself, yourself, himself, herself, itself, ourselves, yourselves, themselves</td><td style="text-align:left;">- She taught <strong>herself</strong> English. \uFF08\u5979<strong>\u81EA\u5B66</strong>\u4E86\u82F1\u8BED\u3002\uFF09 - \u201C\u6559\u201D\u7684\u5BF9\u8C61\u662F\u201C\u5979\u81EA\u5DF1\u201D\u3002<br>- I did it <strong>myself</strong>. \uFF08\u6211<strong>\u4EB2\u81EA</strong>\u505A\u7684\u3002\uFF09 - \u8868\u793A\u5F3A\u8C03\u3002</td></tr><tr><td style="text-align:left;"><strong>4. \u6307\u793A\u4EE3\u8BCD</strong></td><td style="text-align:left;"><strong>\u201C\u6307\u624B\u753B\u811A\u201D\u5730\u6307\u51FA\u7279\u5B9A\u7684\u4EBA\u6216\u7269</strong>\u3002</td><td style="text-align:left;">this\uFF08\u8FD9\u4E2A\uFF09, that\uFF08\u90A3\u4E2A\uFF09, these\uFF08\u8FD9\u4E9B\uFF09, those\uFF08\u90A3\u4E9B\uFF09</td><td style="text-align:left;">- <strong>This</strong> is my car. \uFF08<strong>\u8FD9</strong>\u662F\u6211\u7684\u8F66\u3002\uFF09 - \u6307\u4EE3\u8FD1\u5904\u7684\u4E1C\u897F\u3002<br>- <strong>Those</strong> are beautiful flowers. \uFF08<strong>\u90A3\u4E9B</strong>\u82B1\u5F88\u6F02\u4EAE\u3002\uFF09 - \u6307\u4EE3\u8FDC\u5904\u7684\u4E1C\u897F\u3002</td></tr><tr><td style="text-align:left;"><strong>5. \u7591\u95EE\u4EE3\u8BCD</strong></td><td style="text-align:left;"><strong>\u7528\u4E8E\u63D0\u95EE</strong>\u3002</td><td style="text-align:left;">who, whom, whose, which, what</td><td style="text-align:left;">- <strong>Who</strong> is he? \uFF08\u4ED6<strong>\u662F\u8C01</strong>\uFF1F\uFF09<br>- <strong>What</strong> is this? \uFF08\u8FD9\u662F<strong>\u4EC0\u4E48</strong>\uFF1F\uFF09<br>- <strong>Which</strong> do you prefer? \uFF08\u4F60\u66F4\u559C\u6B22<strong>\u54EA\u4E00\u4E2A</strong>\uFF1F\uFF09</td></tr><tr><td style="text-align:left;"><strong>6. \u5173\u7CFB\u4EE3\u8BCD</strong></td><td style="text-align:left;"><strong>\u7528\u4E8E\u8FDE\u63A5\u5B9A\u8BED\u4ECE\u53E5\uFF0C\u540C\u65F6\u4EE3\u66FF\u4E3B\u53E5\u4E2D\u7684\u67D0\u4E2A\u540D\u8BCD</strong>\u3002</td><td style="text-align:left;">who, whom, whose, which, that</td><td style="text-align:left;">- The man <strong>who</strong> is singing is my teacher. \uFF08\u90A3\u4E2A<strong>\u6B63\u5728\u5531\u6B4C\u7684</strong>\u7537\u4EBA\u662F\u6211\u7684\u8001\u5E08\u3002\uFF09 - <code>who</code> \u4EE3\u66FF <code>the man</code>\uFF0C\u540C\u65F6\u8FDE\u63A5\u4ECE\u53E5\u3002</td></tr><tr><td style="text-align:left;"><strong>7. \u4E0D\u5B9A\u4EE3\u8BCD</strong></td><td style="text-align:left;"><strong>\u4E0D\u7279\u5B9A\u6307\u4EE3\u67D0\u4E00\u4EBA\u6216\u7269</strong>\uFF0C\u8868\u793A\u4E00\u4E2A\u6A21\u7CCA\u7684\u8303\u56F4\u3002</td><td style="text-align:left;">some, any, no, every, each, both, all, many, much, few, little, someone, anybody, nothing...</td><td style="text-align:left;">- <strong>Someone</strong> is at the door. \uFF08<strong>\u6709\u4EBA</strong>\u5728\u95E8\u53E3\u3002\uFF09 - \u4E0D\u7279\u5B9A\u662F\u8C01\u3002<br>- I don&#39;t have <strong>any</strong>. \uFF08\u6211<strong>\u4E00\u4E2A</strong>\u90FD\u6CA1\u6709\u3002\uFF09<br>- <strong>All</strong> of us are here. \uFF08\u6211\u4EEC<strong>\u6240\u6709\u4EBA</strong>\u90FD\u5230\u4E86\u3002\uFF09</td></tr></tbody></table><hr><h3 id="\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u4EBA\u79F0\u4EE3\u8BCD\u7684\u4E3B\u683C\u548C\u5BBE\u683C" tabindex="-1"><a class="header-anchor" href="#\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192-\u4EBA\u79F0\u4EE3\u8BCD\u7684\u4E3B\u683C\u548C\u5BBE\u683C" aria-hidden="true">#</a> <strong>\u4E00\u4E2A\u6781\u5176\u91CD\u8981\u7684\u63D0\u9192\uFF1A\u4EBA\u79F0\u4EE3\u8BCD\u7684\u4E3B\u683C\u548C\u5BBE\u683C</strong></h3><p>\u8FD9\u662F\u4E2D\u56FD\u4EBA\u6700\u5E38\u72AF\u9519\u7684\u5730\u65B9\u4E4B\u4E00\uFF01<strong>\u4E3B\u683C\u548C\u5BBE\u683C\u4E0D\u80FD\u4E71\u7528</strong>\u3002</p><ul><li><strong>\u4E3B\u683C (I, you, he, she...)</strong>\uFF1A\u5145\u5F53\u53E5\u5B50\u7684<strong>\u4E3B\u8BED</strong>\uFF0C\u5373\u52A8\u4F5C\u7684\u6267\u884C\u8005\u3002 <ul><li><strong>He</strong> loves her. \uFF08<strong>\u4ED6</strong>\u7231\u5979\u3002\uFF09- \u201C\u4ED6\u201D\u662F\u201C\u7231\u201D\u8FD9\u4E2A\u52A8\u4F5C\u7684\u6267\u884C\u8005\u3002</li></ul></li><li><strong>\u5BBE\u683C (me, you, him, her...)</strong>\uFF1A\u5145\u5F53\u53E5\u5B50\u7684<strong>\u5BBE\u8BED</strong>\uFF0C\u5373\u52A8\u4F5C\u7684\u627F\u53D7\u8005\u3002 <ul><li>She loves <strong>him</strong>. \uFF08\u5979\u7231<strong>\u4ED6</strong>\u3002\uFF09- \u201C\u4ED6\u201D\u662F\u201C\u7231\u201D\u8FD9\u4E2A\u52A8\u4F5C\u7684\u627F\u53D7\u8005\u3002</li></ul></li></ul><p><strong>\u4E00\u4E2A\u7B80\u5355\u7684\u6D4B\u8BD5\u65B9\u6CD5</strong>\uFF1A\u628A\u53E5\u5B50\u7B80\u5316\u6210\u53EA\u6709\u4E24\u4E2A\u4EBA\uFF0C\u6BD4\u5982\u201C\u6211\u548C\u4ED6\u201D\u3002</p><ul><li>\u5982\u679C\u662F\u52A8\u4F5C\u6267\u884C\u8005\uFF1A<strong>He and I</strong> went to the park. \uFF08\u4ED6\u548C<strong>\u6211</strong>\u53BB\u4E86\u516C\u56ED\u3002\uFF09- \u7528\u4E3B\u683C\u3002</li><li>\u5982\u679C\u662F\u52A8\u4F5C\u627F\u53D7\u8005\uFF1AShe called <strong>him and me</strong>. \uFF08\u5979\u6253\u7535\u8BDD\u7ED9<strong>\u4ED6\u548C\u6211</strong>\u3002\uFF09- \u7528\u5BBE\u683C\u3002</li></ul><hr><h3 id="\u603B\u7ED3" tabindex="-1"><a class="header-anchor" href="#\u603B\u7ED3" aria-hidden="true">#</a> <strong>\u603B\u7ED3</strong></h3><ol><li><strong>\u4EE3\u8BCD (Pronoun)</strong> \u662F\u540D\u8BCD\u7684\u201C\u66FF\u8EAB\u6F14\u5458\u201D\uFF0C\u8BA9\u8BED\u8A00\u907F\u514D\u91CD\u590D\uFF0C\u66F4\u52A0\u6D41\u7545\u3002</li><li>\u5B83\u7684\u6838\u5FC3\u529F\u80FD\u662F<strong>\u4EE3\u66FF</strong>\u548C<strong>\u6307\u4EE3</strong>\u3002</li><li>\u6700\u91CD\u8981\u7684\u533A\u5206\u662F<strong>\u4EBA\u79F0\u4EE3\u8BCD\u7684\u4E3B\u683C\u548C\u5BBE\u683C</strong>\uFF0C\u8FD9\u76F4\u63A5\u5173\u7CFB\u5230\u53E5\u5B50\u7684\u6838\u5FC3\u7ED3\u6784\u3002</li><li>\u4E0D\u540C\u7C7B\u578B\u7684\u4EE3\u8BCD\u5728\u4E0D\u540C\u7684\u8BED\u5883\u4E2D\u5404\u53F8\u5176\u804C\uFF0C\u5171\u540C\u534F\u4F5C\u3002</li></ol><p>\u638C\u63E1\u4E86\u4EE3\u8BCD\uFF0C\u4F60\u7684\u82F1\u8BED\u8868\u8FBE\u5C31\u4E0D\u518D\u662F\u201C\u5C0F\u660E\u2026\u2026\u5C0F\u660E\u2026\u2026\u5C0F\u660E\u2026\u2026\u201D\uFF0C\u800C\u662F\u201C\u4ED6\u2026\u2026\u4ED6\u2026\u2026\u4ED6\u2026\u2026\u201D\uFF0C\u8FD9\u7B26\u5408\u5730\u9053\u7684\u82F1\u8BED\u8868\u8FBE\u4E60\u60EF\u3002</p><p>\u63A5\u4E0B\u6765\u60F3\u4E86\u89E3\u54EA\u4E2A\u8BED\u6CD5\u70B9\uFF0C\u6BD4\u5982\u4E13\u95E8\u653B\u514B\u201C\u5173\u7CFB\u4EE3\u8BCD\u201D\u5F15\u5BFC\u7684\u5B9A\u8BED\u4ECE\u53E5\uFF0C\u6216\u662F\u201C\u4E0D\u5B9A\u4EE3\u8BCD\u201D\u7684\u8BE6\u7EC6\u7528\u6CD5\uFF1F\u6211\u968F\u65F6\u5728\u6B64\u7B49\u5019\u3002</p>',26),g=[e];function l(i,h){return r(),s("div",null,g)}var a=t(n,[["render",l],["__file","index.html.vue"]]);export{a as default};
